  [10074] = {
    [[Kristallpulver von Oshu'gun]],
    { [[Dieser Stützpunkt war die erste Forschungseinrichtung für die Untersuchung von Naarukristallen.]], [[Wir haben hier eine wahre Goldgrube an Forschungsunterlagen über die Kristalle des "diamantenen" Bergs, Oshu'gun, gefunden. Wie es scheint, ist überall in diesem Gebiet etwas von der übrig gebliebenen Macht der Kristalle von Oshu'gun enthalten. Wenn Ihr während Euren Abenteuern in diesem Gebiet Kristallpulver finden solltet, bringt es zu mir, und ich werde Euch ein Zeichen meiner Wertschätzung geben, dass Ihr bei den Rüstmeistern eintauschen könnt.]], },
    { [[Chief Researcher Amereldine at Halaa wants 20 Oshu'gun Crystal Powder Samples.]], },
  },
  [10075] = {
    [[Kristallpulver von Oshu'gun]],
  },
  [10076] = {
    [[Kristallpulver von Oshu'gun]],
    { [[Dieser Stützpunkt war die erste Forschungseinrichtung für die Untersuchung von Naarukristallen.]], [[Wir haben hier eine wahre Goldgrube an Forschungsunterlagen über die Kristalle des "diamantenen" Bergs, Oshu'gun, gefunden. Wie es scheint, ist überall in diesem Gebiet etwas von der übrig gebliebenen Macht der Kristalle von Oshu'gun enthalten. Wenn Ihr während Euren Abenteuern in diesem Gebiet Kristallpulver finden solltet, bringt es zu mir, und ich werde Euch ein Zeichen meiner Wertschätzung geben, dass Ihr bei den Rüstmeistern eintauschen könnt.]], },
    { [[Chief Researcher Kartos at Halaa wants 20 Oshu'gun Crystal Powder Samples.]], },
  },
  [10077] = {
    [[Kristallpulver von Oshu'gun]],
  },
  [10078] = {
    [[Die Unerwünschten zerstören]],
    { [[Jetzt, da unsere Bergungsaktion ins Rollen gekommen ist, ist es an der Zeit, die zweite Phase unseres Plans einzuläuten.]], [[Am Pfad des Ruhms hat die Horde immer noch ihre alten Klingenwerfer postiert. Lasst Euch von Ihrem Alter nicht täuschen, <name>, sie sind immer noch sehr gefährlich. So sehr wir auch versuchen, alles zurückzuerobern, was wir können, können wir nicht erwarten, dass die Horde ihre Waffen einfach so abbaut.]], [[Nehmt diese Fackel und zündet ihre übriggebliebene Belagerungsausrüstung an. Ich werde die Sicherheit der Ehrenfeste nicht dem Zufall überlassen.]], },
    { [[Zündet die Klingenwerfer der Horde mit der flammenden Fackel am Pfad des Ruhms an. Kehrt dann mit der flammenden Fackel zu Dumphry in der Ehrenfeste zurück.]], },
  },
  [10079] = {
    [[Bringt diese Mine zum Beben!]],
    { [[Die Mine wurde von diesem Gan'argpack überrannt!]], [[Sie haben nicht nur meine Arbeiter aus den Tunneln getrieben, sondern hämmern auch noch Tag und Nacht darin herum! Und jetzt sitzt mir auch noch Danath im Rücken! Sagt, er würde mir meine Bierration streichen, wenn ich mich nicht bald darum kümmere!]], [[Keiner der Minenarbeiter will dort hineingehen, und die Wächter sind zu sehr damit beschäftigt, sich vollaufen zu lassen.]], [[Ich flehe Euch an, <name>, helft mir! Danath mag mir meinen Stolz nehmen, aber niemals mein Bier!]], },
    { [[Tötet 12 Pioniere der Gan'arg und kehrt dann zu Großknecht Biggums zurück.]], },
  },
  [10081] = {
    [[Mutter Kashur treffen]],
    { [[Erschreckt Euch nicht über das, was Ihr sehen werdet, wenn die Wirkung eintritt. Diese Geister sind überall um uns herum - immer. Der Trank erlaubt Euch nur, sie zu verstehen und zu sehen. Ihr werdet seine Wirkung brauchen, wenn Ihr mit Mutter Kashur sprecht.]], [[Ihr findet sie ein gutes Stück im Südwesten beim Ahnengrund.]], },
    { [[Großmutter Geyah möchte, dass Ihr mit Mutter Kashur sprecht.]], },
  },
  [10082] = {
    [[Die aufgebrachten Ahnen]],
    { [[Seht Ihr, wie sie unter uns wandeln? Der Ahnengrund ist normalerweise sehr ruhig und friedlich.]], [[In letzter Zeit haben sich die Geister der Ahnen jedoch erhoben und sind sehr aufgebracht. Etwas muss sie sehr stören. Wir müssen herausfinden, was das sein könnte, aber zuerst müssen wir die direkte Bedrohung beseitigen.]], [[Die Geister, die Ihr hier seht, sind nicht wirklich unsere Ahnen, sondern ein Abbild ihrer gegenwärtigen Lage. Ihr müsst die aufgebrachten Orcgeister zerstören. Tut dies und kehrt dann zu mir zurück.]], },
    { [[Mother Kashur has asked that you put to rest 15 Agitated Orc Spirits.]], },
  },
  [10084] = {
    [[Angriff auf Mageddon]],
    { [[Mit den Fähigkeiten der Ingenieure der Mo'arg und der endlosen Menge an Gan'argdienern werden sie sicher schneller Teufelshäscher produzieren, als wir in Nethergarde Bescheid geben können.]], [[Wir haben keine andere Wahl als sie so schnell wie möglich anzugreifen. Wir können keine Männer von der Ehrenfeste für diesen Angriff abziehen, das wäre purer Selbstmord! Die Höllenorcs und die Horde würden uns bei dem kleinsten Anzeichen von Schwäche überrennen.]], [[Die Ehrenfeste steht nun seit 20 Jahren, und wir haben nicht vor, sie jetzt zu verlieren.]], },
    { [[Tötet Nachtlord Malphas im Konstruktionslager: Mageddon und meldet Euch dann bei Truppenkommandant Danath Trollbann in der Ehrenfeste.]], },
  },
  [10085] = {
    [[Ein Besuch bei den Ahnen]],
    { [[Ich brauche Euch, um zu den Dörfern unserer Ahnen zu reisen, <name>. Geht zu jedem einzelnen und schaut, ob unsere Vorfahren noch da sind.]], [[Geht zum Sonnenwindposten im Norden, zu den Ruinen des Lachenden Schädels ganz im Norden bei den Bergen, besucht Garadar im Nordosten und zuletzt die Ruinen des Blutenden Auges an der Grenze zwischen Nagrand und den Wäldern von Terokkar im Osten.]], [[Berichtet mir, sobald Ihr die Ahnen in ihrem Zuhause beobachtet habt.]], },
    { [[Mutter Kashur im Ahnengrund in Nagrand möchte, dass Ihr den Sonnenwindposten, die Ruinen des Lachenden Schädels, Garadar und die Ruinen des Blutenden Auges besucht.]], },
  },
  [10086] = {
    [[Ich arbeite... für die Horde!]],
    { [[Könnt Ihr das fassen? Diese Orcs haben mich angeheuert, um ihre beschädigten Befestigungsanlagen abzustützen und Sprengstoff herzustellen. Ist das nicht ein Traumjob? Nur zu schade, dass uns die Materialien ausgehen, aber ein gewitzter Goblin wie ich weiß sich immer zu helfen!]], [[Könntet Ihr mich dabei unterstützen? Ich benötige ein wenig gebrauchtes Holz und Metall von den zerstörten Belagerungsmaschinen entlang des Pfads des Ruhmes. Geht dorthin und sammelt, aber gebt auf diese rothäutigen Orcs Acht!]], [[Wenn Ihr fertig seid, kommt zurück und holt Euch eine fette Belohnung ab!]], },
    { [[Bringt 8 Stück gebrauchtes Metall und 8 Stück gebrauchtes Holz zu Megzeg Knaxknöchel in Thrallmar.]], },
  },
  [10087] = {
    [[Zündet es an... für die Horde!]],
    { [[Ich bin ein glücklicher, glücklicher Goblin! Ich darf hier Sprengstoff und Killermaschinen herstellen in der Gewissheit, dass sie sofort bei wichtigen Missionen eingesetzt werden!]], [[Wo wir gerade von Missionen sprechen, ich habe einen Befehl aus Thrallmar erhalten: Verbrennt die Kanonen der Allianz entlang des Pfads des Ruhms... die Kanonen, die gefährlich auf die Befestigungsanlagen der Horde gerichtet sind. Nehmt diese Fackel und begebt Euch zu jeder Kanone. Zündet die Fackel an und bestaunt den Funkenflug!]], },
    { [[Zündet die östliche und die westliche Kanone der Allianz mit der flammenden Fackel an und kehrt danach zu Megzeg Knaxknöchel in Thrallmar zurück.]], },
  },
  [10088] = {
    [[Bringt diese Mine zum Beben!]],
    { [[Die Mine wurde von diesem Gan'argpack überrannt, und zu allem Übel sind die Peons der Meinung, dass jetzt die richtige Zeit für ein Schläfchen ist! Ich weiß nicht, wie sie bei all dem Lärm aus der Mine schlafen können, aber das ist mein geringstes Problem.]], [[Wenn wir dieses Gan'arggesindel nicht bald loswerden, wird Thrallmars Nachschub an Erzen und anderen Materialien abgeschnitten. Ich habe schon Wachkommandant Krunk um Hilfe gebeten, aber er sagt, dass er keine Männer schicken kann, solange die Stadt angegriffen wird. Könnt Ihr mir helfen?]], },
    { [[Tötet 12 Pioniere der Gan'arg und kehrt dann zu Großknecht Razelcraz zurück.]], },
  },
  [10089] = {
    [[Konstruktionslager der Legion]],
    { [[Thrallmar steht seit seiner Gründung unter Beschuss der Lager der Legion. Bis jetzt haben wir standhalten können, aber die Situation wird von Tag zu Tag schlimmer.]], [[Schattenjäger Ty'jin hat mehrere Versuche unternommen, mehr über die Lager herauszufinden, aber keiner seiner Späher ist je zurückgekehrt.]], [[Die Zeit für Zurückhaltung ist vorbei, <name>. Begebt Euch ins Konstruktionslager: Zorn und findet heraus, was die Legion vorhat.]], },
    { [[Tötet 10 Diener der Gan'arg, 5 Legionäre des Konstruktionslagers und 5 Schwestern des Leids. Durchsucht danach des Konstruktionslager: Zorn nach einem Hinweis auf die Absichten der Legion.]], },
  },
  [10090] = {
    [[Die Pläne der Legion]],
    { [[Ihr schreibt die leuchtenden Glyphen, die in den Boden geritzt wurden, ab, aber ohne die zweite Hälfte ergibt das Diagramm keinen Sinn.]], [[Die Glyphen verweisen darauf, dass ein weiteres Konstruktionslager im Westen, das Konstruktionslager: Bosheit, an dem Projekt beteiligt ist. Vielleicht bewahrt die Legion dort die zweite Hälfte der Pläne auf?]], [[Wenn die Legion etwas wirklich Gewaltiges herstellt, macht es Sinn, dass die Teile in verschiedenen Lagern zusammengebaut werden.]], },
    { [[Besorgt die Pläne des Konstruktionslagers: Bosheit und bringt sie zu Oberanführer Hun Stümmelfaust im Häschersturz.]], },
  },
  [10091] = {
    [[Seeleninstrumente]],
    { [[Pah! Sie haben mich gefoltert und versucht, etwas über die Verteidigungsanlagen von Shattrath aus mir herauszuquetschen.]], [[Ich habe mich geweigert - da haben sie die anderen Spione an Höllenschlund verfüttert, und ich sollte der Nächste sein. Aber ich habe ihnen nichts verraten!]], [[Sie sind wahnsinnig! Sie sind versessen darauf, dämonische Mächte zu beschwören, die sie auf gar keinen Fall kontrollieren können!]], [[Sie benutzen besondere Seeleninstrumente, um ihre Seelensplitter herzustellen und ihre Beschwörungen noch mächtiger zu machen. Ihr müsst ihnen diese Instrumente stehlen!]], [[Ich würde Euch ja begleiten, aber ich muss nach Shattrath zurück um der Spionagemeisterin des Unteren Viertels zu berichten.]], },
    { [[Stehlt 5 Seeleninstrumente und bringt sie zu Spionagemeisterin Mehlisah Hochkrone auf der Terrasse des Lichts in Shattrath.]], },
  },
  [10092] = {
    [[Angriff auf Mageddon]],
    { [[Mit den Fähigkeiten der Ingenieure der Mo'arg und der endlosen Menge an Gan'argdienern werden sie sicher schneller Teufelshäscher produzieren, als wir in Orgrimmar Bescheid geben können.]], [[Wir haben keine andere Wahl als sie so schnell wie möglich anzugreifen. Wir können keine Männer von Thrallmar für diesen Angriff abziehen, dann wären wir zu leicht angreifbar.]], [[Es gibt zu viele, die sich, ungeachtet der Allianz, nur zu gerne auf unseren ungeschützten Außenposten stürzen würden. Versagt nicht, <name>. Es hängt zu viel von Euch ab.]], },
    { [[Tötet Nachtlord Malphas im Konstruktionslager: Mageddon und meldet Euch dann bei Oberanführer Hun Stümmelfaust im Häschersturz.]], },
  },
  [10093] = {
    [[Der Tempel von Telhamat]],
    { [[Zum Glück ist der Pfad des Ruhms nicht das einzige Zeichen meines Volks in diesen Ländereien. Im Westen befinden sich immer noch ein paar unserer geheiligten Stätten, auch wenn einige von ihnen überrannt wurden.]], [[Während Velen sich auf seine Reise nach Azeroth vorbereitete, haben diejenigen von uns, die hier bleiben sollten, versucht, sich wieder in unseren heiligen Orten niederzulassen.]], [[Im westlichen Teil der Höllenfeuerhalbinsel könnt Ihr beim Tempel von Telhamat eine kleine Gruppe von Draenei finden, die von Amaan dem Weisen angeführt werden. Ich bin mir sicher, dass er Euch gerne kennenlernen würde.]], },
    { [[Sprecht mit Amaan dem Weisen beim Tempel von Telhamat im Westen der Höllenfeuerhalbinsel.]], },
  },
  [10094] = {
    [[Der Kodex des Blutes]],
    { [[Es ist nun zwei Jahre her, dass die Beschwörung des Schattenrats dieses einst großartige Mausoleum explodieren und die Knochenwüste entstehen lies.]], [[Jetzt befinden sie sich im Krieg mit einer unglaublich mächtigen Kraft im Inneren.]], [[<name>, Ihr müsst in das Schattenlabyrinth eindringen und herausfinden, wogegen sie kämpfen und ob es eine Gefahr für uns sein könnte. Unserer Spione haben berichtet, dass sie ein verbotenes Buch mit unheiligen Beschwörungen verwenden, bekannt als der Kodex des Blutes.]], [[Sucht in ihm nach Antworten.]], },
    { [[Lest den Kodex des Blutes im Schattenlabyrinth von Auchindoun.]], },
  },
  [10095] = {
    [[In das Herz des Labyrinths]],
    { [[Der Schattenrat hat die Kontrolle über das kosmische Wesen, das sie vor fast zwei Jahren beschworen haben, verloren! Es hat die Explosion von Auchindoun verursacht und die Knochenwüste erschaffen.]], [[Jetzt nur noch als Murmur bekannt, droht es, den Schattenrat zu überrennen und zu entkommen!]], [[Nun, da der Großmeister und der Rest der Führung tot sind, seid Ihr die einzige Hoffnung der Scherbenwelt im Kampf gegen dieses Monster!]], [[<name>, tief in Eurem Herzen wisst Ihr, dass Ihr und Eure Freunde nicht versagen dürfen!]], },
    { [[Vernichtet Murmur und informiert danach Spionagemeisterin Mehlisah Hochkrone auf der Terrasse des Lichts in Shattrath über die Geschehnisse im Schattenlabyrinth.]], },
  },
  [10096] = {
    [[Rettung der Sporloks]],
    { [[Ikeyen hat mir erzählt, was ihm in diesen Höhlen widerfahren ist, und ich finde es einfach nur schrecklich!]], [[Wisst Ihr, vielleicht sollten wir diesen armen Sporloks helfen. Ikeyen sagt, dass sie dort in der Funggorhöhle leben, aber die Marschenbestien sind kürzlich dort eingefallen.]], [[Warum geht Ihr nicht dort hin und schaut, dass Ihr ein paar von diesen Marschenbestien erlegt? Die Höhle ist ganz nah im Südwesten.]], },
    { [[Tötet 10 Marschengräber und 10 Marschenlauerer und kehrt dann zu Lauranna Thar'well in der Zuflucht des Cenarius in den Zangarmarschen zurück.]], },
  },
  [10097] = {
    [[Bruder gegen Bruder]],
    { [[Die Sethekk haben Skettis mit Fanfaren verlassen, als Auchindoun explodiert ist. Das war sicher ein Vorbote für die Ankunft unseres Meisters.]], [[Mein Bruder, Syth, war einer ihrer Anführer und hat uns gesagt, dass es unsere Pflicht wäre, in die Tempelruinen zu gehen und unserem Gott zu begegnen.]], [[Nachdem wir unser Lager in den Ruinen aufgeschlagen und sie die Sethekkhallen getauft hatten, kamen mir Zweifel an den Fähigkeiten meines Bruders und seines Verbündeten Ikiss.]], [[Nach einiger Zeit hat mich Syth aus den Hallen geworfen und mir verboten, meine Schwester Lakka mitzunehmen. Helft Ihr mir, sie zu retten?]], },
    { [[Tötet Dunkelwirker Syth in den Sethekkhallen und befreit Lakka. Kehrt zu Isfar außerhalb der Sethekkhallen zurück, wenn Ihr die Aufgabe abgeschlossen habt.]], },
  },
  [10098] = {
    [[Terokks Erbe]],
    { [[Mein Volk verehrt einen uralten Helden namens Terokk. Arakkoaküken erfreuen sich an den Geschichten über seine Taten und den Beschreibungen seines prächtigen Federkleids.]], [[Er lebte für hunderte von Jahren unter meinem Volk, doch eines Tages verschwand er urplötzlich, und ließ nur seine Maske, seinen Speer und seine Schriften zurück.]], [[Diese Relikte werden von den Arakkoa hoch verehrt, und es brach ihnen das Herz, als die Sethekk die Gegenstände bei ihrem Aufbruch aus Skettis verschleppten.]], [[Ich habe alles, was ich über den Verbleib der Schriftrollen in den Hallen weiß, niedergeschrieben.]], },
    { [[Bringt Terokks Maske, Terokks Federkiel und die Sage von Terokk zu Isfar außerhalb der Sethekkhallen.]], },
  },
  [10099] = {
    [[Das Genie]],
    { [[Diese verfluchten Gan'arg wuseln immer noch in die Mine! Für jeden, den wir umbringen, scheinen zwei oder drei weitere aufzutauchen!]], [[Irgendwas in den Tiefen der Mine muss diese Menge steuern. Ihr scheint das letzte Mal ganz gut klargekommen zu sein, und das Licht weiß, dass Ihr <der/die> Einzige seid, <der/die> mutig genug ist, wieder in die Mine zu gehen. Findet heraus, wer für dieses Ärgernis verantwortlich ist und bestraft ihn dafür!]], },
    { [[Sucht und tötet Z'kral in den Minen unter der Ehrenfeste und meldet Euch dann bei Großknecht Biggums zurück.]], },
  },
  [10100] = {
    [[Das Genie]],
    { [[Versteht mich nicht falsch, <name>, ich bin sehr froh, dass Ihr Euch bereit erklärt habt, uns mit den Minen zu helfen, aber für jeden Pionier, den Ihr getötet habt, sind zwei neue dazugekommen.]], [[Ich habe Wachkommandant Krunk schon die Ohren vollgeheult, aber er weigert sich immer noch, mir Hilfe zu schicken. Er murmelt immer irgendwas von Höllenebern. Wenn ich mir nicht so sicher wäre, dass er mich verprügeln würde, würde ich ihm sagen, dass jetzt nicht die Zeit ist, um an Rippchen zu denken!]], [[Wie auch immer, es sieht so aus, als ob irgendjemand tief in den Minen die ganze Sache steuert. Findet ihn und tötet ihn!]], },
    { [[Sucht und tötet Urga'zz in den Minen unter Thrallmar und kehrt dann zu Großknecht Razelcraz zurück.]], },
  },
  [10101] = {
    [[Wenn Geister sprechen]],
    { [[Oshu'gun, der diamantene Berg, war einst ein heiliger Ort meines Volkes. Dort versammelten sich die Klans jedes Jahr, um das Frühlingsfest zu feiern. Dort kommunizierten unsere Schamanen mit unseren altehrwürdigen Vorfahren. Er bildete das Zentrum unserer Kultur - bis die Schatten uns übernahmen. Als die Klans zu einer Horde wurden, verstummten die Geister von Oshu'gun. Unsere Vorfahren haben sich von uns abgewendet.]], [[Doch nun werden sie in ihrer Ruhe gestört. Reist nach Oshu'gun und findet heraus, was die Geister beunruhigt.]], },
    { [[Mutter Kashur im Ahnengrund in Nagrand bittet Euch, Eurer Vision auf den Grund zu gehen. Findet K'ure in Oshu'gun.]], },
  },
  [10102] = {
    [[Ein gelüftetes Geheimnis]],
    { [[Als meine Energien sich über die Jahrhunderte nach und nach verflüchtigten, nahm eine Leere meinen Platz ein und verschlang die Seelen in der Nähe. Hilflos musste ich zusehen, wie ganze Generationen von Orcseelen in den Wirbel hineingezogen wurden. Vor Kurzem bemächtigten meine Feinde sich des Wirbels und benutzten ihn, um zahllose Leerdiener in ihre Brennende Legion zu rufen.]], [[Nun liegt der Schlüssel zur Beendigung dieses Kreislaufs und der Befreiung der Orcgeister bei einem anderen Naaru, A'dal. Ich konnte seine Essenz in der uralten Stadt Shattrath in den Wäldern von Terokkar fühlen.]], },
    { [[Sprecht mit A'dal in Shattrath.]], },
  },
  [10103] = {
    [[Bericht an Zurai]],
    { [[Mit der Ankunft von Feldkommandeur Romus und seinen Streitmächten sind die Allianz und ihre Draeneifreunde mutiger geworden. Sie wandeln alle alten Draeneiruinen von hier bis Shattrath in Heerlager um.]], [[Als Antwort darauf haben die Trolle einen neuen Außenposten im östlichen Teil der Zangarmarschen errichtet, südlich des Todesmoors. Sie nennen ihn Sumpfrattenposten. Ich bin froh, dass sie sich in einer solchen Umgebung ihren Humor bewahrt haben. Ich wollte jedenfalls nicht mit ihnen tauschen.]], [[Der Kommandeur des Posten hat mich gebeten, ihm alle Mann zu schicken, die ich entbehren kann.]], },
    { [[Meldet Euch bei Zurai im Sumpfrattenposten in den Zangarmarschen.]], },
  },
  [10104] = {
    [[Sorgen um Tuurem]],
    { [[Die Gerüchte über Tuurem sind beunruhigend. Manche sagen, die Ruinen der Stadt laufen vor verrückten Zerschlagenen und Verirrten über, während gierige Artefaktjäger Draeneirelikte stehlen.]], [[Es ist schwer zu sagen, welche wahr und welche falsch sind, aber es liegt in unserer Verantwortung, die Ruinen so gut es geht zu schützen. Sucht Andarl in der Feste in den Wäldern von Terokkar. Er ist eine Art Forscher für magische Relikte. Er weiß wahrscheinlich besser als alle anderen Bescheid, was dort vor sich geht.]], },
    { [[Sprecht mit Andarl in der Feste in den Wäldern von Terokkar.]], },
  },
  [10105] = {
    [[Nachrichten für Rakoria]],
    { [[Now that Swamprat Post is complete, our control of the roads in and out of the marsh is much less tenuous. Our presence in Terokkar Forest is much less secure. Even so, at Stonebreaker Hold, Rakoria awaits word of our progress.]], [[She's sharp and capable, <name>, but be careful around her. She's got a lot of a goods she's eager to move along the roads, but don't assume she's got our best interests at heart. Rakoria goes where she believes there's profit to be found.]], },
    { [[Sprecht mit Rakoria in der Steinbrecherfeste in den Wäldern von Terokkar.]], },
  },
  [10106] = {
    [[Höllenfeuerbefestigungen]],
    { [[Die Höllenfeuerzitadelle steht im ständigen Kampf mit unseren Streitmächten. Wir halten uns wacker, aber wir können keinen richtigen Angriff gegen sie starten. Um dies zu tun, müssen wir weiter vorrücken.]], [[Unsere Späher haben drei Befestigungsanlagen in der Nähe der Höllenfeuerzitadelle entdeckt. Wir möchten, dass Ihr uns helft, diese drei Anlagen einzunehmen. Meldet Euch bei mir, sobald Ihr eine in Beschlag nehmen konntet.]], [[Möge das Glück in den vor Euch liegenden Kämpfen bei Euch sein, <name>, denn Ihr werdet ohne Zweifel auf Streitkräfte der Horde treffen, die dasselbe im Schilde führen.]], },
    { [[Nehmt den Rundblick, das Stadion und die Trümmerbastion ein, und kehrt dann zu Stabsfeldwebel Tracy Stolzbrunn in der Ehrenfeste auf der Höllenfeuerhalbinsel zurück.]], },
  },
  [10107] = {
    [[Diplomatische Maßnahmen]],
    { [[Kilrath hat Neuigkeiten über den Anführer der Felsfäuste geschickt: Es ist ein Orc! Ein uralter Orc, der in der Zeit unserer Väter das Dunkle Portal durchschritten hat und in sein Heimatland zurückgekehrt ist. Ich möchte, dass Ihr diesen Orc ausfindig macht und ihn davon überzeugt, die Felsfäuste zurückzuziehen. Er lebt auf einer der höheren Ebenen bei den Ruinen der Brennenden Klinge im Südosten, nahe der Grenze zu Terokkar. Viel Erfolg, <name>!]], [[Seid auf der Hut, er ist ein Klingenmeister...]], },
    { [[Jorin Totauge in Garadar in Nagrand möchte, dass Ihr Lantresor von der Klinge in den Ruinen der Brennenden Klinge findet und mit ihm sprecht.]], },
  },
  [10108] = {
    [[Diplomatische Maßnahmen]],
    { [[Die Agenten der Jägerin haben Neuigkeiten über den Anführer der Felsfäuste geschickt: Es ist ein Orc! Ein uralter Orc, der in der Zeit unserer Väter das Dunkle Portal durchschritten hat und in sein Heimatland zurückgekehrt ist. Ich möchte, dass Ihr diesen Orc ausfindig macht und ihn davon überzeugt, die Felsfäuste zurückzuziehen. Er lebt auf einer der höheren Ebenen bei den Ruinen der Brennenden Klinge im Osten. Viel Erfolg, <name>!]], [[Seid auf der Hut, er ist ein Klingenmeister...]], },
    { [[Mo'mor der Brecher in Telaar in Nagrand möchte, dass Ihr Lantresor von der Klinge in den Ruinen der Brennenden Klinge findet und mit ihm sprecht.]], },
  },
  [10109] = {
    [[Ich muss sie haben!]],
    { [[Könnt Ihr sie sehen?? Bei Gazlows wundervollem Mundgeruch, ich muss diese Eier haben! Ihr seht aus wie jemand, den man gebrauchen kann.]], [[Ich sag Euch was, <race>, wenn Ihr ein paar Dinge für mich sammelt, damit ich meinen Spring-o-Mat 4000 zum Laufen bekomme, werde ich Euch einen Schlüssel geben, mit dem auch Ihr die Maschine bedienen könnt! Was man mit einem Spring-o-Mat 4000 machen kann? Wohl eher was man damit nicht machen kann...]], [[Ich brauche Luftelementargas von den Luftelementaren in Nagrand. Sie sind überall, fragt mich also nicht, wo Ihr sie finden könnt!]], },
    { [[Wazdaz gegenüber der Klanwacht im Süden von Nagrand möchte, dass Ihr ihm 3 Einheiten Luftelementargas bringt.]], },
  },
  [10110] = {
    [[Höllenfeuerbefestigungen]],
    { [[Die Höllenorcs der Höllenfeuerzitadelle müssen von unserem Heimatland entfernt werden! Um sie anzugreifen, müssen wir strategisch wichtige Punkte in der Nähe der Zitadelle einnehmen.]], [[Unsere Kundschafter haben drei Befestigungsanlagen ausgemacht, die für unsere Zwecke geeignet sind, aber die Allianz hatte dieselbe Idee und schickt schon Streitkräfte, um sie einzunehmen!]], [[Wir möchten, dass Ihr die Allianz aufhaltet und die drei Forts einnehmt. Helft bei der Einnahme jeder Anlage und kehrt dann zu mir zurück.]], [[Für die Horde!]], },
    { [[Nehmt den Rundblick, das Stadion und die Trümmerbastion ein, und kehrt dann zu Kriegsherold Blauauge in Thrallmar zurück.]], },
  },
  [10111] = {
    [[Bringt mir das Ei!]],
    { [[Jetzt, da Ihr initiiert wurdet, möchte ich, dass Ihr mir dieses Ei bringt! Benutzt den Spring-o-Mat und versucht, das Nest genau in der Mitte zu treffen. Aber seid vorsichtig! Schlechte Sprünge können in körperlichen Schäden oder dauerhaften schweren Wunden resultieren.]], },
    { [[Wazdaz in Nagrand möchte, das Ihr den Spring-o-Mat 4000 benutzt, um in das Nest in dem nahe gelegenen Baum zu springen und das geheimnisvolle Ei zu stehlen.]], },
  },
  [10112] = {
    [[Ein persönlicher Gefallen]],
    { [[Hmmm? Eine Kiste sagt Ihr? Oder waren es mehrere? Wisst Ihr aus welchem Material sie war?]], [[Und wie heißt der Händler? Empoor? Und Ihr seid Euch sicher, dass er ein Zerschlagener ist? Ich mache nicht viele Geschäfte mit ihresgleichen.]], [[Vielleicht hilft es meinem Gedächtnis ein wenig auf die Sprünge, wenn Ihr mir einen Gefallen tut.]], [[Die Arakkoa der Skith - diese primitiven Zauberer - haben neulich eine große Schiffladung meiner magischen Waren gestohlen. Ich will alles zurückhaben!]], [[Ihr könnt das Skithversteck südlich von Shattrath finden.]], },
    { [[Holt 5 von Lathrais gestohlenen Waren zurück und bringt sie zu Windhändler Lathrai bei der Taverne Weltenend im Unteren Viertel in Shattrath.]], },
  },
  [10113] = {
    [[Nesingwarys Safari]],
    { [["Geht und sucht ein paar Vorräte", hat er gesagt. Ich bin fast fünfzehn Mal getötet worden, bevor ich diesen Ort hier überhaupt gefunden habe! Diese Zerschlagenen sind vielleicht nicht das gastfreundlichste Volk, das ich kenne, aber immerhin haben sie nicht versucht, mir die Haut von den Knochen zu ziehen!]], [[Wie auch immer, ich gehe nicht zurück. Meine Tage als Großwildjäger sind vorbei. Wenn ihr zufällig nach Nordosten Richtung Zangarmarschen geht und bei Nesingwarys Safari vorbeikommt, sagt dort Bescheid, dass "die Flinte" gekündigt hat.]], [[Hey, vielleicht könnt Ihr ja meinen Platz einnehmen! Viel Glück, <Fremder/Fremde>.]], },
    { [["Die Flinte" Jones möchte, dass Ihr mit Hemet Nesingwary in Nesingwarys Safari am nordöstlichen Rand von Nagrand sprecht.]], },
  },
  [10114] = {
    [[Nesingwarys Safari]],
    { [[Diese Orcs haben noch nie zuvor einen Tauren gesehen, könnt Ihr das glauben? Wenn Hauptmann Kroghan nicht wäre, würden sie mich wahrscheinlich in diesem Moment zum Abendessen verschlingen!]], [[Aber wo sind meine Manieren? Ich bin Ohlorn Fernreiser, herausragender Jäger! Ich gehöre zu Nesingwarys Expedition, die sich von Azeroth aus auf die Jagd nach Großwild hierher begeben hat. Wenn Ihr Euch für die Jagd interessiert, solltet Ihr meine Truppe in Nesingwarys Safari östlich von hier an den Grenzen zu den Zangarmarschen besuchen.]], },
    { [[Ohlorn Fernreiser möchte, dass Ihr mit Shado 'Fitz' Fernreiser in Nesingwarys Safari in Nagrand sprecht.]], },
  },
  [10115] = {
    [[Dolchfennkrise]],
    { [[Die Kurenai und die Draenei sind nicht immer der gleichen Meinung, besonders nicht, wenn es um die Verirrten geht. Wir helfen den Verirrten, die sich an uns wenden, gerne, aber wir werden sie niemals verfolgen. Manche Draenei sind andererseits überzeugt davon, dass sie alle 'erlöst' werden müssen.]], [[Es gibt jedoch Zeiten, in denen sogar die Verirrten zu weit gehen, wie zum Beispiel der Dolchfennstamm. In ihrem Dorf im Westen hantieren sie mit Giften herum, lügen und betrügen, was für uns sehr unnatürlich ist.]], [[Wir müssen dem ein Ende bereiten, bevor der bösartige Dolchfennstamm seinen Einfluss noch weiter ausbreiten kann.]], },
    { [[Tötet 3 Auftragsmörder von Dolchfenn und 15 Matschwirbler von Dolchfenn.]], },
  },
  [10116] = {
    [[Gesucht: Häuptling Mummaki]],
    { [[Gesucht: Häuptling Mummaki - Anführer des verdorbenen und degenerierten Dolchfennstamms der Verirrten, wegen seiner brutalen Angriffe auf die Oreborzuflucht.]], [[Der Häuptling und seine Truppe von Auftragsmördern haben mehrere Kurenai und Draenei auf dem Gewissen. Nur erfahrene Kämpfer sollten sich an sie heranwagen.]], [[Reisenden wird nahe gelegt, die Gegend um Dolchfenn westlich der Oreborzuflucht zu meiden.]], [[Das Kopfgeld kann bei Vorlage eines Beweises für das Ableben des Häuptlings bei Ikuti eingefordert werden.]], },
    { [[Bringt Häuptling Mummakis Totem zu Ikuti in der Oreborzuflucht.]], },
  },
  [10117] = {
    [[Gesucht: Häuptling Mummaki]],
    { [[Gesucht: Häuptling Mummaki - Anführer des verdorbenen und degenerierten Dolchfennstamm der Verirrten, wegen seiner brutalen Angriffe auf Zabra'jin und seine Einwohner.]], [[Der Häuptling und seine Truppe von Auftragsmördern haben mehrere Späher und Boten auf dem Gewissen. Nur erfahrene Kämpfer sollten sich an sie heranwagen.]], [[Reisenden wird nahe gelegt, die Gegend um Dolchfenn nordwestlich von Zabra'jin zu meiden.]], [[Das Kopfgeld kann bei Vorlage eines Beweises für das Ableben des Häuptlings bei Schattenjäger Denjai eingefordert werden.]], },
    { [[Bringt Häuptling Mummakis Totem zu Schattenjäger Denjai.]], },
  },
  [10118] = {
    [[Nachricht für Dolchfenn]],
    { [[Von allen Stämmen der Verirrten in den Zangarmarschen ist der Dolchfennstamm der gerissenste und aggressivste. Er ist sehr gut organisiert und macht keinen Hehl daraus, dass er gerne die Kontrolle über alle Verirrten in den Marschen hätte.]], [[Ihren Angriffen sind schon viele meiner Späher und Boten zum Opfer gefallen. Ich kann es mir nicht leisten, noch mehr Männer zu verlieren.]], [[Wenn wir nicht bald handeln, werden sie die komplette Kontrolle über alle Straßen erlangen und Zabra'jin einkesseln.]], },
    { [[Tötet 3 Auftragsmörder von Dolchfenn und 15 Matschwirbler von Dolchfenn und kehrt dann zu Schattenjäger Denjai in Zabra'jin zurück.]], },
  },
  [10119] = {
    [[Durch das Dunkle Portal]],
    { [[Zwanzig Jahre lang war es ruhig um das Dunkle Portal – es wurde zu einem Symbol längst vergangener Zeiten.]], [[<Reldorn sieht sich um.>]], [[Ich habe nicht geglaubt, dass ich den heutigen Tag erleben würde, aber hier stehen wir nun und schauen tief in den Abgrund.]], [[Auf der anderen Seite des Portals sind wir in einem grausamen Krieg gefangen – ein Krieg, den wir nicht ohne Hilfe gewinnen können. Ihr müsst durch das Dunkle Portal reisen und unseren Truppen helfen, <class>.]], [[Meldet Euch umgehend bei Kommandant Duron. Er erwartet Euch in der Scherbenwelt...]], },
    { [[Reist durch das Dunkle Portal und sucht Kommandant Duron. Er wartet auf der anderen Seite auf Eure Ankunft - in der Scherbenwelt.]], },
  },
  [10120] = {
    [[Ankunft in der Scherbenwelt]],
    { [[Mit der Unterstützung der Allianz können meine Leute die Verteidigung hier aufrechterhalten, aber Ihr müsst dringend meinen Bericht zu General Krakork in Thrallmar bringen. Mit seiner Hilfe können wir diese Dämonen zurück in den Abgrund drängen, aus dem sie gekommen sind! Sprecht mit Vlagga Freyfeder auf der Anhöhe hinter uns. Sie wird Euch schnell auf den Weg dorthin bringen.]], },
    { [[Generalleutnant Orion beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Orions Bericht zu Vlagga Freyfeder bringt.]], },
  },
  [10121] = {
    [[Die Brennende Legion ausrotten]],
    { [[Auch wenn dieses Land voller neuer, unbekannter Feinde ist, ist die Brennende Legion immer noch die größte Bedrohung für unsere Expedition. Einer meiner vertrauenswürdigsten Krieger, Unteroffizier Schädelbrecher, hat die Taktiken der Legion untersucht und versucht, das wahre Ziel der Legion in diesem Land herauszufinden. Ihr findet ihn bei der Versorgungskarawane außerhalb des südlichen Walls.]], [[Und <name>, hütet Euch vor der Legion. Ihre Bösartigkeit darf nicht unterschätzt werden.]], },
    { [[Sprecht mit Unteroffizier Schädelbrecher bei der Versorgungskarawane außerhalb von Thrallmar im Osten.]], },
  },
  [10122] = {
    [[Die Erstreckung der Zitadelle]],
    { [[Jeden Tag öffnen sich die Tore der Höllenfeuerzitadelle und noch mehr Höllenorcs strömen heraus und in die Lager, die sie entlang des Bollwerks südlich der Zitadelle errichtet haben.]], [[Ich habe einen meiner besten Späher, Gefreiter Imarion, losgeschickt, um ihre Vorgehensweise zu erkunden, aber er ist noch nicht mit seinem Bericht zurückgekehrt.]], [[Imarion sollte sich auf das nördlichste Lager entlang des Südlichen Bollwerks, nahe bei seiner Verbindung zur Zitadelle, konzentrieren. Findet ihn, <name>. Ohne seinen Bericht sitzen wir hier hilflos herum.]], },
    { [[Durchsucht das nördlichste Lager entlang des Südlichen Bollwerks nach einer Spur von Gefreiter Imarion.]], },
  },
  [10123] = {
    [[Teufelsfunkenklamm]],
    { [[Verdammnislord Kazzak hat das Kommando über die Brennende Legion in der Scherbenwelt. Da wir Kazzak nicht direkt angreifen können - noch nicht - müssen wir zunächst seine Leutnants, die die Truppen vom Boden aus kommandieren, auslöschen.]], [[Sie haben Schreckenslords in der Teufelsfunkenklamm östlich von hier postiert. Sie sind für diese höllischen Stürme, die auf uns herabprasseln, verantwortlich. Sie und ihre Diener zu vernichten wäre ein schwerer Schlag für die Legion.]], },
    { [[Unteroffizier Schädelbrecher bei der Versorgungskarawane östlich von Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet.]], },
  },
  [10124] = {
    [[Vorposten: Häschersturz]],
    { [[Eure Fähigkeiten in der Schlacht sind dem obersten Kommando aufgefallen, <class>. Sie wollen Euch an die Front zu Stümmelfausts Truppe schicken. Sie sind sehr radikal und haben sich der vollständigen Auslöschung der Legion verschrieben, also passt Ihr wahrscheinlich genau dazu...]], [[Geht etwa 200 Schritte nach Osten und biegt bei der hervorragenden Landmasse nach Süden ab. Ihr findet den Häschersturz dort, wo einst ein alter Bauernhof stand. Wenn Ihr angekommen seid, meldet euch bei Vorpostenkommandant To'arch.]], [[Und jetzt weggetreten!]], },
    { [[Meldet Euch bei Vorpostenkommandant To'arch im Häschersturz.]], },
  },
  [10125] = {
    [[Mission: Die Kommunikation unterbrechen]],
    { [[Direkt im Osten liegt die Front der Legion. Die Front dient der Brennenden Legion als mobile Kommandobasis. Ihr könnt die Sender der Legion aus der Erde herausragen sehen. Mit ihrer Hilfe kommuniziert die Legion mit ihren Konstruktionslagern in der ganzen Scherbenwelt. Sie dienen ebenfalls als Schutzeinrichtung zur Verschlüsselung sämtlicher Übertragungen, sodass eine magische Überwachung nicht mehr möglich ist.]], [[Ich möchte, dass Ihr diesen Sprengstoff nehmt und die Sender damit zerstört. Zerstört sie alle, <name>! Sie sind von Süden nach Norden an der Grenze der Front der Legion aufgereiht.]], },
    { [[Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr To'archs Sprengstoff benutzt, um den Sender der Legion: Bosheit, Sender der Legion: Zorn, Sender der Legion: Hass und Sender der Legion: Furcht zu zerstören.]], },
  },
  [10126] = {
    [[Kriegsboss Nekroggs Befehle]],
    { [[Die Anweisungen sind mit Kriegsboss Nekrogg unterzeichnet. Sie befehlen den Höllenorcs in diesem Lager, ihren menschlichen Gefangenen zum Verhör nach Süden zu bringen.]], [[Leutnant Amadi hat erwähnt, dass das Südliche Bollwerk über mehrere Höllenorclager verfügt. Vielleicht haben sie Gefreiter Imarion zum nächsten größeren Abschnitt des Walls Richtung Süden gebracht.]], [[Da dieser Befehl von Verhör spricht, scheint es den Höllenorcs wichtig zu sein, dass Gefreiter Imarion am Leben bleibt. Nehmt schnell ihre Spur auf, bevor sie es sich anders überlegen!]], },
    { [[Reist entlang der westlichen Seite des Südlichen Bollwerks nach Süden und durchsucht das nächste Lager nach einer Spur von Gefreiter Imarion.]], },
  },
  [10127] = {
    [[Mission: Das Band zerreißen]],
    { [[Es wäre Vernachlässigung meiner Pflicht, wenn ich Euch nicht befehlen würde, die Dämonen an der Front der Legion, östlich von dieser Basis, zu vernichten.]], [[Beide Seiten der Front sind nach dem selben Muster aufgebaut: Teufelstreiber wandeln zwischen den Lagern der Legion hin und her, die von Zornwächtern bewacht werden. Die ganze Operation wird von einer Unterwerferin der Shivan beaufsichtigt. Tötet sie alle und überlasst Kazzak den Rest.]], [[Und passt auf die patrouillierenden Teufelshäscher auf. Ich weiß, dass Ihr stark seid, aber diese Biester machen mit Euch kurzen Prozess.]], },
    { [[Vorpostenkommandant To'arch am Häschersturz auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Shi'aziv tötet.]], },
  },
  [10128] = {
    [[Der Soldat Imarion]],
    { [[Die Spuren führen von Imarions Ausrüstung aus nach Süden, anscheinend entlang des Südlichen Bollwerks. Aus den Furchen lässt sich schließen, dass die Entführer es eilig hatten, ihn an den nächsten Ort zu bringen, aber warum?]], [[Hat ihn dort jemand erwartet? Vielleicht Kriegsboss Nekrogg, der Anführer, der in den Befehlen, die Ihr weiter nördlich gefunden habt, erwähnt wurde?]], [[Das Schild und die Spuren werfen mehr Fragen auf, als sie beantworten, aber es scheint sicher, dass Ihr dort, wo Ihr Gefreiter Imarion findet, auch Kriegsboss Nekrogg finden werdet.]], },
    { [[Setzt Euren Weg entlang des Südlichen Bollwerks fort und sucht nach dem Ort, an dem Kriegsboss Nekrogg Gefreiter Imarion gefangen hält.]], },
  },
  [10129] = {
    [[Mission: Tore Murketh und Shaadraz]],
    { [[Wir haben die beiden Haupttransporter, mit denen die Brennende Legion ihre Verstärkung herbeiholt, ausgemacht: Tor Murketh und Tor Shaadraz.]], [[Wenn Ihr den Pfad des Ruhmes nach Osten hinaufschaut, könnt Ihr sie an beiden Seiten der Straße erkennen. Ich möchte, dass Ihr sie beide in Stücke sprengt!]], [[Hier sind die Bomben, die Ihr dafür benötigt. Sie wurden speziell von einem Goblin in Area 52 entworfen. Sprecht mit Schwadronskommandant Brack - er wird Euch einen Wyvern zur Verfügung stellen.]], },
    { [[Sprecht mit Schwadronskommandant Brack, bombardiert Tor Murketh und Tor Shaadrez und kehrt dann zu Vorpostenkommandant To'arch im Häschersturz zurück.]], },
  },
  [10130] = {
    [[Die westliche Flanke]],
    { [[Zusätzlich zu den anderen Anweisungen werden die Orc in den Befehlen angewiesen, ihre Ausrüstung und Verpflegung für einen Angriff vorzubereiten.]], [[Nach den Plänen sollen Kriegsboss Nekroggs Mannen sich zu einer westlichen Flanke formieren, um die Ehrenfeste in die Zange zu nehmen.]], [[Da die Garnison der Feste sich auf die Verteidigung der östlichen Seite konzentriert, wäre ein Angriff von Westen fatal. Die Ehrenfeste kann nur gehalten werden, wenn der Angriff von Westen niemals stattfindet.]], },
    { [[Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Leutnant Amadi im westlichen Wachturm der Ehrenfeste.]], },
  },
  [10131] = {
    [[Fluchtplan]],
    { [[Wie ich schon gesagt habe, hier rauszukommen wird nicht leicht. Kriegsboss Nekrogg mag vielleicht wie ein übergroßer Schläger aussehen, aber er ist kein Dummkopf.]], [[Er wird den Käfigschlüssel nicht aus den Augen lassen und kommt nicht aus seinem Turm auf dieser massiven Mauer heraus, bis er bereit ist, mich oder diese Orcfrau, die er gefangen hat, zu verhören.]], [[Wir brauchen schon etwas Besonderes, um ihn aus diesen Mauern zu locken, aber ich habe einen Plan. Sucht einen der Grenadiere, die am Südlichen Bollwerk entlang patrouillieren, und bringt mir etwas von seinem Sprengstoff.]], },
    { [[Tötet einen Grenadier des Lachenden Schädels und bringt seine primitive Sprengladung zu Gefreiter Imarion im Außenposten von Gor'gaz.]], },
  },
  [10132] = {
    [[Eine kolossale Bedrohung]],
    { [[Seht Ihr diesen riesigen roten Kristall im Norden? Er ist vor Kurzem vom Himmel gefallen und hat dabei ein großes Erdbeben ausgelöst.]], [[Als wir hierher kamen, um den Grund für den Aufruhr zu erforschen, haben wir all die Kolosse hier gesehen, die zuvor noch nicht da waren.]], [[Es ist eindeutig etwas faul an dem Kristall und den Energien, die er ausstrahlt, aber ich habe keine Zeit dafür. Ich muss mich zuerst um diese Bergriesen kümmern.]], [[Ich möchte, dass Ihr zum Himmelssturzgrat geht und Euch um sie kümmert.]], },
    { [[Tötet 5 tobende Kolosse und kehrt dann zu Tola'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück.]], },
  },
  [10133] = {
    [[Mission: Tötet den Boten]],
    { [[Euer Geschick bereitet der Legion große Schwierigkeiten, <name>. Meine Späher haben mich informiert, dass die Legion Kuriere benutzt, um untereinander zu kommunizieren. Wir haben es geschafft, ihre Truppen vom Hauptlager abzuschneiden!]], [[Worum ich Euch nun bitte ist mehr, als ich von einem normalen Soldaten verlangen würde.]], [[Der Kurier der Legion reist zwischen den Teichen von Aggonar und dem Konstruktionsbasis: Mageddon, nördlich von hier, hin und her. Ihr müsst den Kurier bei Mageddon abfangen, ihn erledigen und mit dem Schriftwechsel der Legion zu mir zurückkehren.]], },
    { [[Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr Arix'maladish findet und tötet und den Schriftwechsel der Legion besorgt.]], },
  },
  [10134] = {
    [[Hinweise des purpurnen Kristalls]],
    { [[Als der Koloss Eurem Angriff zum Opfer fällt, bemerkt Ihr, dass er ein rotes Kristallfragment in der Hand hält.]], [[In der Hand des Riesen schien es sehr klein und unauffällig, aber für Euch ist das Stück sehr schwer zu tragen. Es sieht sehr verdächtig nach einem Stück des riesigen roten Kristalls in der Mitte des Grats aus.]], [[Dies ist ganz sicher ein Hinweis auf die merkwürdigen Dinge, die hier geschehen. Ihr seid Euch sicher, dass die Druiden beim Posten des Cenarius im Süden den Splitter sehen wollen.]], },
    { [[Bringt den Purpurkristallsplitter zu Tola'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel.]], },
  },
  [10135] = {
    [[Mission: Seid der Bote]],
    { [[Ich bin nur ein Feldkommandeur, <class>. Die Befugnis, auf solche Informationen hin zu handeln, fällt nur unserem Anführer, Nazgrel, zu. Ihr müsst den Schriftwechsel nach Thrallmar zu ihm bringen. Reitet schnell wie der Wind, Soldat. Lasst Euch durch nichts aufhalten!]], },
    { [[Bringt den Schriftwechsel der Legion zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [10136] = {
    [[Grausame Pläne]],
    { [[Magister Blutfalke hat endlich den Schriftwechsel der Legion, den Ihr gefunden habt, entziffert. Wie es aussieht, handelt es sich dabei um Angriffspläne, die von einem Leutnant des Hochlords Kazzak geschickt wurden - Arazzius der Grausame. Offensichtlich hat Arazzius den Befehl über die Bodentruppen auf der Höllenfeuerhalbinsel. Es ist an der Zeit, dass wir seinen Befehl beenden, <name>. Vernichtet ihn mit größter Vorsicht.]], [[Begebt Euch zu den Teichen von Aggonar und setzt dem so genannten 'Legionslord' ein Ende. Heute fordert die Horde die Höllenfeuerhalbinsel zurück. Die Legion möge verdammt sein.]], },
    { [[Tötet Arazzius den Grausamen und kehrt danach zu Nazgrel in Thrallmar zurück.]], },
  },
  [10137] = {
    [[Den Kriegsboss provozieren]],
    { [[Der Erfolg des Plans hängt von der Qualität des Sprengstoffs ab...]], [[So ungern ich mich auch auf etwas verlasse, das von einem Orc hergestellt wurde, ist dies meine einzige Möglichkeit hier rauszukommen. Nehmt dies und werft es gegen das Banner, das vom Turm da oben weht.]], [[Wenn es explodiert wird Kriegsboss Nekrogg es sich nicht nehmen lassen, ein Exempel an dem Störenfried zu statuieren. Tötet ihn und nehmt den Schlüssel, um meinen Käfig zu öffnen. Viel Erfolg, <name>.]], },
    { [[Benutzt die primitiven Sprengladungen um einen Tumult zu veranstalten und Kriegsboss Nekrogg aus seinem Turm zu locken. Tötet ihn, besorgt Nekroggs Schlüssel und öffnet damit Gefreiter Imarions Käfig. Wenn Ihr Gefreiter Imarion befreit habt, meldet Euch bei Leutnant Amadi.]], },
  },
  [10138] = {
    [[Auf wessen Befehl hin?]],
    { [[Eines ist klar, <name>: wir können nicht so weitermachen. Die Höllenorcs haben uns eingekreist, und sobald wir sie erledigen, nehmen gleich mehrere ihren Platz ein. Wir können nicht hoffen, dass wir diesen Krieg gewinnen können, indem wir uns hinter den zerschlagenen Mauern der Ehrenfeste verstecken und die Tage bis zu unserem Tod durch die Hand der Höllenorcs zählen.]], [[Ihr habt beeindruckende Arbeit geleistet und uns vor den schlimmsten Angriffen der Höllenorcs bewahrt, aber jetzt müsst Ihr Euch den Weg entlang des Pfads des Ruhmes freikämpfen und herausfinden, wer diese Angriffe steuert.]], },
    { [[Tötet 20 Grunzer der Zerschmetterten Hand, 10 Hexenmeister der Zerschmetterten Hand und besorgt Euch Drillmeister Zurok's Anweisungen. Kehrt dann zu Leutnant Amadi im westlichen Wachturm der Ehrenfeste zurück.]], },
  },
  [10139] = {
    [[Die Beseitigung des Kommandanten]],
    { [[So wie ich das sehe, war Drillmeister Zurok die rechte Hand des Anführers der Höllenorcs auf der Höllenfeuerhalbinsel, Truppenkommandant Gorax.]], [[Dieser Gorax ist anders als die gewöhnlichen Schläger der Höllenorcs. Ich bewundere fast, wie er uns schon so lange in der Defensive halten konnte.]], [[Zuroks Befehle deuten an, dass Gorax' Kommandozentrale am Ende des Pfads des Ruhms liegt, dort, wo die Mauern der Zitadelle auf das Nördliche Bollwerk treffen. Ich habe keine Ahnung, was Euch dort erwartet, <name>, aber Ihr solltet besser mit ein paar Gefährten reisen.]], },
    { [[Tötet Truppenkommandant Gorax und meldet Euch dann bei Leutnant Amadi im westlichen Turm der Ehrenfeste zurück.]], },
  },
  [10140] = {
    [[Reise zur Ehrenfeste]],
    { [[Wenn Ihr bereit seid, werde ich Euch einen Flug direkt zur Ehrenfeste besorgen.]], [[Sprecht dort mit Marschall Isildor und gebt ihm Durons Bericht. Er wird sich freuen, einen weiteren Rekrut aus der Heimat zu sehen, und ich bin mir sicher, dass die Informationen in diesem Bericht sehr wichtig sind!]], },
    { [[Fliegt zur Ehrenfeste und übergebt Marschall Isildor Durons Bericht.]], },
  },
  [10141] = {
    [[Wiedergeburt der Brennenden Legion]],
    { [[Als die Brennende Legion in Azeroth eingefallen ist, war sie hier in der Scherbenwelt nicht untätig. Wir haben ihre Bewegungen über die Jahre verfolgt, aber ihre letzte Tat, <class>, ist schlimmer als alles, was wir je gesehen haben.]], [[Sprecht mit Unteroffizier Altumus in der Nähe der Östlichen Versorgungskarawane. Er hat die Eindringlinge beobachtet und weiß vielleicht mehr über ihre Pläne.]], [[Seid schnell und passt auf, <name> - die Brennende Legion darf auf keinen Fall unterschätzt werden.]], },
    { [[Sprecht mit Unteroffizier Altumus bei der Östlichen Versorgungskarawane außerhalb der Ehrenfeste im Osten.]], },
  },
  [10142] = {
    [[Der Pfad der Pein]],
    { [[Verdammnislord Kazzak hat das Kommando über die Brennende Legion in der Scherbenwelt. Da wir Kazzak nicht direkt angreifen können - noch nicht - müssen wir zunächst seine Leutnants, die die Truppen vom Boden aus kommandieren, auslöschen.]], [[Sie haben Schreckenslords am Pfad der Pein östlich von hier postiert. Sie sind für diese teuflischen Stürme, die auf uns herabprasseln, verantwortlich. Sie und ihre Diener zu vernichten wäre ein schwerer Schlag für die Legion.]], },
    { [[Unteroffizier Altumus bei der Östlichen Versorgungskarawane östlich der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet.]], },
  },
  [10143] = {
    [[Expeditionsposten]],
    { [[Eure Fähigkeiten in der Schlacht sind dem obersten Kommando aufgefallen, <class>. Sie wollen Euch an die Front zu Rohamus' Truppe schicken. Sie sind sehr radikal und haben sich der vollständigen Auslöschung der Legion verschrieben, also passt Ihr wahrscheinlich genau dazu...]], [[Geht etwa 400 Schritte nach Osten, durch das Tal der Knochen, bis Ihr auf einen zerstörten Teufelshäscher stoßt. Ihr findet den Expeditionsposten dort, wo einst ein Lager der Legion war. Wenn Ihr angekommen seid, meldet Euch bei Vorpostenkommandant Königstein.]], [[Und jetzt weggetreten!]], },
    { [[Meldet euch bei Vorpostenkommandant Königstein im Expeditionsposten.]], },
  },
  [10144] = {
    [[Die Verstärkung abschneiden]],
    { [[Direkt im Nordosten liegt die Front der Legion. Entlang der Front befinden sich die infernalen Portale, mit deren Hilfe Verstärkung auf die Höllenfeuerhalbinsel und in die ganze Scherbenwelt gebracht wird. Wir müssen diese Portale zerstören, <name>!]], [[Nehmt diesen Zünder. Er wurde in Area 52 hergestellt und benutzt die eigene Macht der Dämonen als Sprengstoff! Besiegt die Legion beim Portal Grimh und Portal Kaalez und haltet nach dämonischen Runensteinen Ausschau. Wenn Ihr genug Steine beisammen habt, benutzt den Zünder, um die Steine in den Portalen zu sprengen... und dann genießt das Feuerwerk!]], [[Geht, <name>, und kehrt zu mir zurück, wenn Ihr Eure Aufgabe erfüllt habt.]], },
    { [[Besiegt die Feinde um Portal Grimh und Portal Kaalez herum, um dämonische Runensteine zu sammeln.]], [[Benutzt Königsteins Zünder in Portal Grimh und Portal Kaalez.]], [[Kehrt danach mit dem Zünder zu Vorpostenkommandant Königstein zurück.]], },
  },
  [10145] = {
    [[Mission: Das Band zerreißen]],
    { [[Es wäre Vernachlässigung meiner Pflicht, wenn ich Euch nicht befehlen würde, die Dämonen an der Front der Legion, nördlich von dieser Basis, zu vernichten.]], [[Beide Seiten der Front sind nach dem selben Muster aufgebaut: Teufelstreiber wandeln zwischen den Lagern der Legion hin und her, die von Zornwächtern bewacht werden. Die ganze Operation wird von einer Unterwerferin der Shivan beaufsichtigt. Tötet sie alle und überlasst Kazzak den Rest.]], [[Und passt auf die patrouillierenden Teufelshäscher auf. Ich weiß, dass Ihr stark seid, aber diese Biester machen mit Euch kurzen Prozess.]], },
    { [[Vorpostenkommandant Königstein beim Expeditionsposten auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Shi'aziv tötet.]], },
  },
  [10146] = {
    [[Mission: Tor Murketh und Tor Shaadraz]],
    { [[Westlich des Dunklen Portals befinden sich zwei riesige Transporter: Tor Murketh und Tor Shaadraz.]], [[Wenn Ihr den Pfad des Ruhmes nach Osten hinaufschaut, könnt Ihr sie an beiden Seiten der Straße erkennen. Ich möchte, dass Ihr sie in Stücke sprengt!]], [[Hier sind die Bomben, die Ihr benutzen sollt. Sie wurden speziell von einem Goblin in Area 52 entworfen. Sprecht nun mit Schwadronskommandant Dabir'ee - er wird Euch einen Greifen zur Verfügung stellen und Euch losschicken.]], },
    { [[Sprecht mit Schwadronskommandant Dabir'ee, bombardiert Tor Murketh und Tor Shaadraz und kehrt dann zu Vorpostenkommandant Königstein im Häschersturz zurück.]], },
  },
  [10147] = {
    [[Mission: Tötet den Boten]],
    { [[Euer Geschick bereitet der Legion große Schwierigkeiten, <name>. Meine Späher haben mich informiert, dass die Legion Kuriere benutzt, um untereinander zu kommunizieren. Wir haben es geschafft, ihre Truppen vom Hauptlager abzuschneiden!]], [[Worum ich Euch nun bitte ist mehr, als ich von einem normalen Soldaten verlangen würde.]], [[Der Kurier der Legion reist zwischen den Teichen von Aggonar und dem Konstruktionsbasis: Mageddon, nördlich von hier, hin und her. Ihr müsst den Kurier bei Mageddon abfangen, ihn erledigen und mit dem Schriftwechsel der Legion zu mir zurückkehren.]], },
    { [[Vorpostenkommandant Königstein im Expeditionsposten auf der Höllenfeuerhalbinsel möchte, dass Ihr Arix'maladish findet und tötet und den Schriftwechsel der Legion besorgt.]], },
  },
  [10148] = {
    [[Mission: Seid der Bote]],
    { [[Ich bin nur ein Feldkommandeur, <class>. Die Befugnis, auf solche Informationen hin zu handeln, fällt nur unserem Anführer, Truppenkommandant Danath, zu. Ihr müsst den Schriftwechsel zur Ehrenfeste zu ihm bringen. Reitet schnell wie der Wind, Soldat. Lasst Euch durch nichts aufhalten!]], },
    { [[Bringt den Schriftwechsel der Legion zu Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel.]], },
  },
  [10149] = {
    [[Mission: Beendet alles, seid alles]],
    { [[Wisst Ihr, was passiert wäre, wenn Ihr den Boten nicht abgefangen hättet, <name>?]], [[<Danath nickt.>]], [[Die gesamte Armee der Legion des Höllenfeuers hätte vor unserer Tür gestanden. Einen solchen Angriff hätten wie niemals überlebt.]], [[Es gibt nur eine Möglichkeit zu verhindern, dass diese Befehle jemals die Front erreichen. Ihr müsst zu den Teichen von Aggonar, westlich von hier, reisen und diesen Arazzius töten.]], [[Wenn Ihr das tut, werde ich Euch eine Auszeichnung zukommen lassen.]], },
    { [[Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr Arazzius den Grausamen tötet.]], },
  },
  [10150] = {
    [[Die Erstreckung der Zitadelle]],
    { [[Jeden Tag öffnen sich die Tore der Höllenfeuerzitadelle und noch mehr Höllenorcs strömen heraus und in die Lager, die sie entlang des Bollwerks südlich der Zitadelle errichtet haben.]], [[Ich habe einen meiner besten Spurenleser, Späherin Makha, losgeschickt, um ihre Vorgehensweise zu erkunden, aber sie ist noch nicht mit ihrem Bericht zurückgekehrt.]], [[Makha sollte sich auf das nördlichste Lager entlang des Nördlichen Bollwerks, nahe bei seiner Verbindung zur Zitadelle, konzentrieren. Findet sie, <name>. Ohne ihren Bericht sitzen wir hier hilflos herum.]], },
    { [[Durchsucht das nördlichste Lager entlang des Nördlichen Bollwerks nach einer Spur von Späherin Makha.]], },
  },
  [10151] = {
    [[Kriegsboss Nekroggs Befehle]],
    { [[Die Anweisungen sind mit Kriegsboss Nekrogg unterzeichnet. Sie befehlen den Höllenorcs in diesem Lager, ihren menschlichen Gefangenen zum Verhör nach Süden zu bringen.]], [[Schattenjäger Ty'jin hat erwähnt, dass das Nördliche Bollwerk über mehrere Höllenorclager verfügt. Vielleicht haben sie Späherin Makha zum nächsten größeren Abschnitt des Walls Richtung Süden gebracht.]], [[Da dieser Befehl von Verhör spricht, scheint es den Höllenorcs wichtig zu sein, dass sie am Leben bleibt. Nehmt schnell ihre Spur auf, bevor sie es sich anders überlegen!]], },
    { [[Reist entlang der westlichen Seite des Nördlichen Bollwerks nach Süden und durchsucht das nächste Lager nach einer Spur von Späherin Makha.]], },
  },
  [10152] = {
    [[Die westliche Flanke]],
    { [[Zusätzlich zu den anderen Anweisungen werden die Orc in den Befehlen angewiesen, ihre Ausrüstung und Verpflegung für einen Angriff vorzubereiten.]], [[Nach den Plänen sollen Kriegsboss Nekroggs Mannen sich zu einer westlichen Flanke formieren, um Thrallmar in die Zange zu nehmen.]], [[Da die Garnison der Feste sich auf die Verteidigung der östlichen Seite konzentriert, wäre ein Angriff von Westen fatal. Thrallmar kann nur gehalten werden, wenn der Angriff von Westen niemals stattfindet.]], },
    { [[Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth'Gor.]], },
  },
  [10153] = {
    [[Die Späherin Makha]],
    { [[Die Spuren führen von Makhas Ausrüstung aus nach Süden, anscheinend über den Pfad des Ruhms. Aus den Furchen lässt sich schließen, dass die Entführer es eilig hatten, sie an den nächsten Ort zu bringen, aber warum?]], [[Hat sie dort jemand erwartet? Vielleicht Kriegsboss Nekrogg, der Anführer, der in den Befehlen, die Ihr weiter nördlich gefunden habt, erwähnt wurde?]], [[Das Schild und die Spuren werfen mehr Fragen auf, als sie beantworten, aber es scheint sicher, dass Ihr dort, wo Ihr Späherin Makha findet, auch Kriegsboss Nekrogg finden werdet.]], },
    { [[Setzt Euren Weg über den Pfad des Ruhms fort und sucht nach dem Ort, an dem Kriegsboss Nekrogg Späherin Makha gefangen hält.]], },
  },
  [10154] = {
    [[Fluchtplan]],
    { [[Wie ich schon gesagt habe, hier rauszukommen wird nicht leicht. Kriegsboss Nekrogg mag vielleicht wie ein übergroßer Schläger aussehen, aber er ist kein Dummkopf.]], [[Er wird den Käfigschlüssel nicht aus den Augen lassen und kommt nicht aus seinem Turm auf dieser massiven Mauer heraus, bis er bereit ist, mich oder diese Orcfrau, die er gefangen hat, zu verhören.]], [[Wir brauchen schon etwas Besonderes, um ihn aus diesen Mauern zu locken, aber ich habe einen Plan. Sucht einen der Grenadiere, die am Südlichen Bollwerk entlang patrouillieren, und bringt mir etwas von seinem Sprengstoff.]], },
    { [[Tötet einen Grenadier des Lachenden Schädels und bringt seine primitive Sprengladung zu Späherin Makha im Außenposten von Gor'gaz.]], },
  },
  [10155] = {
    [[Den Kriegsboss provozieren]],
    { [[Der Erfolg des Plans hängt von der Qualität des Sprengstoffs ab...]], [[So ungern ich mich auch auf etwas verlasse, das von einem Höllenorc hergestellt wurde, ist dies meine einzige Möglichkeit hier rauszukommen. Nehmt dies und werft es gegen das Banner, das vom Turm da oben weht.]], [[Wenn es explodiert wird Kriegsboss Nekrogg es sich nicht nehmen lassen, ein Exempel an dem Störenfried zu statuieren. Tötet ihn und nehmt den Schlüssel, um meinen Käfig zu öffnen. Viel Erfolg, <name>.]], },
    { [[Benutzt die primitive Sprengladung um einen Tumult zu veranstalten und Kriegsboss Nekrogg aus seinem Turm zu locken. Tötet ihn, besorgt Nekroggs Schlüssel und öffnet damit Späherin Makhas Käfig. Wenn Ihr Späherin Makha befreit habt, meldet Euch bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth'Gor.]], },
  },
  [10156] = {
    [[Auf wessen Befehl hin?]],
    { [[Eines ist klar, <name>: wir können nicht so weitermachen. Die Höllenorcs haben uns eingekreist, und sobald wir sie erledigen, nehmen gleich mehrere ihren Platz ein. Wir können nicht hoffen, dass wir diesen Krieg gewinnen können, wenn unsere Streitkräfte zweigeteilt sind und sich im ständigen Kampf mit den Höllenorcs und den Dämonen der Legion befinden.]], [[Ihr habt beeindruckende Arbeit geleistet und uns vor den schlimmsten Angriffen der Höllenorcs bewahrt, aber jetzt müsst Ihr Euch den Weg entlang des Pfads des Ruhmes freikämpfen und herausfinden, wer diese Angriffe steuert.]], },
    { [[Tötet 20 Grunzer der Zerschmetterten Hand, 10 Hexenmeister der Zerschmetterten Hand und besorgt Euch Drillmeister Zurok's Anweisungen. Kehrt dann zu Steingardist Ambelan im Rückenbrechergrat zurück.]], },
  },
  [10157] = {
    [[Die Beseitigung des Kommandanten]],
    { [[So wie ich das sehe, war Drillmeister Zurok die rechte Hand des Anführers der Höllenorcs auf der Höllenfeuerhalbinsel, Truppenkommandant Gorax.]], [[Dieser Gorax ist anders als die gewöhnlichen Schläger der Höllenorcs. Ich bewundere fast, wie er uns schon so lange in der Defensive halten konnte.]], [[Zuroks Befehle deuten an, dass Gorax' Kommandozentrale am Ende des Pfads des Ruhms liegt, dort, wo die Mauern der Zitadelle auf das Nördliche Bollwerk treffen. Ich habe keine Ahnung, was Euch dort erwartet, <name>, aber Ihr solltet besser mit ein paar Gefährten reisen.]], },
    { [[Tötet Truppenkommandant Gorax und meldet Euch dann bei Schattenjäger Ty'jin in Thrallmar zurück.]], },
  },
  [10158] = {
    [[Vorräte des Blutenden Auges]],
    { [[Die orcischen Ruinen von Zeth'Gor im Südosten dienen als Basislager für die immer weiter steigende Flut von Höllenorcs, die aus der Höllenfeuerzitadelle strömt.]], [[Schattenjäger Ty'jin hat mich geschickt, um den Außenposten und seine merkwürdigen Bewohner zu beobachten.]], [[Etwas hat diese Orcs auf eine Art und Weise verändert, die ich noch nie zuvor gesehen habe. Wir müssen alles in unserer Macht Stehende tun, um zu verhindern, dass sie noch stärker werden. Geht nach Zeth'Gor und zerstört sämtliche Vorräte, die Ihr dort finden könnt.]], },
    { [[Zerstört 6 Stapel Vorräte des Blutenden Auges und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten zurück.]], },
  },
  [10159] = {
    [[Haltet den Dornnebelhügel sauber!]],
    { [[Wir kämpfen schon die ganze Zeit, um den Pass durch den Dornnebelhügel von den Verheerern freizuhalten. Es wäre schlecht, wenn wir ganz von der Zuflucht in den Zangarmarschen abgeschnitten wären.]], [[Wir möchten die Tiere nicht vertreiben, aber wir haben kaum einen andere Wahl, sie werden Reisenden in dieser Gegend gegenüber immer aggressiver.]], [[Löscht ein paar von den Felshetzern aus, wenn Ihr könnt.]], },
    { [[Tötet 8 Dornenfangfelshetzer und 8 Dornenfanggiftspucker und kehrt dann zu Mahuram Starkhuf im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück.]], },
  },
  [10160] = {
    [[Den Feind kennen]],
    { [[Es gibt Gerüchte, dass eine neue Brut von Orcs aufkommt. Größer, bösartiger und listiger als die Rüpel, mit denen wir uns sonst herumschlagen müssen. Wenn diese Gerüchte wahr sind, müssen unsere Streitkräfte, die ohnehin schon überansprucht sind, sich bis über ihre Grenzen hinaus belasten.]], [[Ich habe meinen Leutnant, Amadi, damit betraut, die Sache zu untersuchen. Wenn Ihr uns in dieser dunklen Stunde beistehen wollt, dann meldet Euch bei ihr. Sie befindet sich in dem zerbrochenen Turm im Westen der Feste.]], [[Und <name>, seid vorsichtig da draußen. Ihr stürzt Euch in eine neue, gefährliche Welt.]], },
    { [[Sprecht mit Leutnant Amadi im alten Wachturm im Westen der Ehrenfeste.]], },
  },
  [10161] = {
    [[Im Notfall...]],
    { [[Bei einem Notfall ist es die Aufgabe des Piloten, einen kühlen Kopf zu bewahren, besonders, wenn die einzigen Befehle, die der Kopilot befolgt, die seines Magens sind.]], [[Die gute Nachricht ist, dass wir nicht tagelang darauf warten müssen, dass uns Ersatzteile geliefert werden. Die schlechte Nachricht ist, dass die Ersatzteile überall von hier bis zum Tal der Knochen im Osten verstreut sind.]], [[Die andere schlechte Nachricht? Die Ersatzteile sind nicht so leicht ersetzbar. Sie, äh, sind bei unserem schnellen Abgang abgefallen. Könntet Ihr mir vielleicht helfen, genügend davon wieder vom Boden aufzusammeln?]], },
    { [[Sammelt 30 Zeppelintrümmer und bringt sie zu "Schreihals" Quack Lufthans bei der Zeppelinabsturzstelle.]], },
  },
  [10162] = {
    [[Mission: Die Abyssische Untiefe]],
    { [[Jetzt, da Ihr Eure Flügel habt, habt Ihr vielleicht Interesse an einem weiteren Bombardement?]], [[Dieses Mal haben wir die Hauptproduktionsstätte der Legion isoliert, hinter den Konstruktionslagern im Norden. Schwingt Euch auf einen Wyvern und zerstört das verdammte Ding!]], [[Hier sind die Bomben. Sprecht mit Schwadronskommandant Brack wenn Ihr zum Abflug bereit seid.]], },
    { [[Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen vernichtet.]], },
  },
  [10163] = {
    [[Mission: Die Abyssische Untiefe]],
    { [[Jetzt, da Ihr Eure Flügel habt, habt Ihr vielleicht Interesse an einem weiteren Bombardement?]], [[Dieses Mal haben wir die Hauptproduktionsstätte der Legion isoliert, hinter den Konstruktionslagern im Norden. Schwingt Euch auf einen Greifen und zerstört das verdammte Ding!]], [[Hier sind Eure Bomben. Sprecht mit Greifnerin Heulwind wenn Ihr zum Abflug bereit seid.]], },
    { [[Schwadronskommandant Greifgar im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen vernichtet.]], },
  },
  [10164] = {
    [[Alles wird gut]],
    { [[Ihr habt ein ganz schönes Abenteuer hinter Euch, <name>. Und das noch bevor Ihr überhaupt einen Fuß in den Auchenaikrypta gesetzt habt.]], [[Ich bin einer der glücklichen Geister. Ich war nie an Levixus gebunden oder Teil eines der bösartigen Experimente in der Nekromantie von Exarch Maladaar. Während Levixus durch Eure Hand gestorben ist, lebt Maladaar immer noch weiter und quält die Geister in der Krypta.]], [[Worum ich Euch bitte ist ganz einfach. Geht in die Auchenaikrypta und tötet Exarch Maladaar, damit die gefangenen Geister endlich Frieden finden können.]], },
    { [[Großvater Aldrimus außerhalb der Auchenaikrypta von Auchindoun möchte, dass Ihr in die Auchenaikrypta geht und Exarch Maladaar vernichtet.]], },
  },
  [10165] = {
    [[Die Konkurrenz untergraben]],
    { [[Dieser Shaffar ist ganz schön gerissen, das muss man ihm lassen... Er hat es geschafft, das Konsortium fern zu halten, während er in aller Seelenruhe diese Gruft all ihrer Schätze beraubt. Ich glaube nicht, dass diese Dummköpfe es tatsächlich schaffen, durch seine Verteidigung zu brechen, deshalb mache ich Euch einen Vorschlag, <race>.]], [[Dringt in die Managruft ein und tötet den Nexusprinzen Shaffar. Bringt mir seine Körperbandagen als Beweis Eurer Tat, und ich werde Euch fürstlich belohnen.]], },
    { [[Nexusprinz Haramad außerhalb der Managruft möchte, dass Ihr Nexusprinz Shaffar tötet und ihm Shaffars Bandagen bringt.]], },
  },
  [10166] = {
    [[Weißborkes Erinnerung]],
    { [[Dieser Anhänger bringt Erinnerungen an eine andere Zeit zurück. Eine bessere Zeit.]], [[Leider sind Erinnerungen das Letzte, was ich jetzt gebrauchen kann.]], [[Ich möchte, dass Ihr dies beim Runenstein im Versengten Hain vergrabt. Dort habe ich den Treantältesten zum ersten Mal getroffen. Bettet die Erinnerung an meinen alten Freund in Frieden.]], },
    { [[Larianne Flusswind möchte, dass Ihr Weißborkes Anhänger beim Runenstein im Versengten Hain vergrabt.]], },
  },
  [10167] = {
    [[Auchindoun...]],
    { [[Tief in der Auchenaikrypta befindet sich ein Grab: ein Grab, dass keinesgleichen in den Zerschlagenen Weiten findet. Aus diesem Grab, dem ersten Grab, wurde das riesige Mausoleum von Auchindoun geboren.]], [[Ihr müsst zu der Auchenaikrypta reisen und dieses Grab finden. Tötet den Grabwächter und befreit den Geist, der darin gefangen ist. Der Geist ist der Schlüssel...]], },
    { [[Reist zur Auchenaikrypta in der Knochenwüste der Wälder von Terokkar und tötet Exarch Maladaar um den Geist von D'ore zu befreien.]], },
  },
  [10168] = {
    [[Was die Seele sieht]],
    { [[Seit mehreren Jahrhunderten verbinden sich die Geister von Auchindoun nun mit meiner getrübten Essenz. Viele der Katastrophen, die diese Krypta heimgesucht haben, sind auf meinen geschwächten Zustand zurückzuführen.]], [[Unglücklicherweise kann nichts diesen Kreis aufhalten. Das gehört zur Natur der Naaru - ohne die Leere kann das Licht nicht existieren.]], [[Eure einzige Möglichkeit besteht darin, die Geister von Nagrand zu vernichten, bevor sie sich in Wesen der Leere verwandeln. Benutzt die Seelenspiegel, die sich überall in der Krypta befinden, um sie zu befreien...]], },
    { [[Sucht einen Seelenspiegel in der Auchenaikrypta und benutzt ihn, um einen getrübten Geist eines uralten Orcahnen zu rufen. Zerstört 15 getrübte Geister, damit die Ahnen in Frieden ruhen können.]], [[Kehrt zu Mutter Kashur im Ahnengrund zurück, wenn Ihr fertig seid.]], },
  },
  [10169] = {
    [[In Würde verlieren]],
    { [[Die Aldor haben sich heute das Wohlwollen der Sha'tar verdient. Als ob das etwas Überraschendes wäre!]], [[Schaut nur, wie sie nur ein paar Fuß von A'dal entfernt mit ihrer tollen Beinarbeit angeben. Könnten sie sich wohl noch mehr bemühen, sich bei den Naaru einzuschmeicheln?]], [[Herold Haronems Schützen sind so engagiert, dass sie sogar ihren eigenen Kochtopf mitbringen, um keine langen Pausen zum Mittagessen machen müssen. Das kann nicht ohne Strafe vonstatten gehen, <name>.]], [[Ich möchte, dass Ihr diese Phiole in ihr Essen kippt. Die Folgen sollten... sehr amüsant sein.]], },
    { [[Magister Falris in Shattrath möchte, dass Ihr die Phiole mit Beruhigungsserum zur Terrasse des Lichts bringt und sie in den Kochtopf von Haronems Regiment schüttet.]], },
  },
  [10170] = {
    [[Rückkehr zur Großmutter]],
    { [[Kehrt zu Großmutter Geyah in Garadar zurück und berichtet ihr von Eurem Sieg, <name>. Das wird den Mag'har sicher neuen Mut geben. Vielleicht erwacht sogar der junge Höllschrei aus seiner Melancholie.]], },
    { [[Kehrt nach Garadar zurück und sprecht mit Großmutter Geyah.]], },
  },
  [10171] = {
    [[Der untröstliche Häuptling]],
    { [[Ihr habt so viel für die Mag'har getan, <name>. Alle wissen von Euren Taten, auch Garrosh. Geht nun zu ihm und erzählt ihm von eurem letzten Erfolg. Sagt ihm, was Ihr für ihn und sein Volk getan habt. Eure Worte werden ihn sicher trösten.]], },
    { [[Großmutter Geyah in Garadar möchte, dass Ihr mit Garrosh sprecht.]], },
  },
  [10172] = {
    [[Es gibt keine Hoffnung]],
    { [[Ihr seid <ein ehrenwerter/eine ehrenwerte><race> <race>, <name>. Ihr habt viel für die Mag'har getan. Niemand könnte Eure Dienste für mein Volk je leugnen. Dennoch ist die Zeit der Mag'har abgelaufen. Ihr habt mir mehr als alles andere gezeigt, dass ich nicht in der Lage bin, diese Leute anzuführen. Zu stark ist der Fluch meines Blutes. Ich werde nicht... ich kann nicht der nächste Höllschrei sein, der die Orcs in die Verdammnis führt.]], [[Bitte, <name>, kehrt zur Großmutter zurück und erklärt ihr, was ich Euch gesagt habe. Ich schäme mich zu sehr, um zu ihr zu gehen... ihr in die Augen zu schauen.]], },
    { [[Sprecht mit Großmutter Geyah und erzählt ihr, was Garrosh Euch gesagt hat.]], },
  },
  [10173] = {
    [[Der Stab des Erzmagiers]],
    { [[Erlaubt, dass ich mich vorstelle, <class>.]], [[Mein Name ist Ravandwyr, Lehrling des Erzmagiers Vargoth.]], [[Mein Meister ist... nennen wir es indisponiert. Ich bin hier in einer Angelegenheit von höchster Wichtigkeit.]], [[Die Ruinen von Arklon südöstlich der Stadt sind eine Brutstätte für die Aktivitäten der Brennenden Legion. Was sie mit den Ruinen vorhaben interessiert weder mich noch meinen Meister. Aber ihre Anführerin, Ekkorash, hat den Stab meines Meisters.]], [[Mit diesem Beschwörungspulver könnt Ihr die Dämonin aus ihrem Versteck locken. Streut es über die Kohlenpfanne in der Mitte der Ruinen.]], },
    { [[Besorgt Erzmagier Vargoths Stab von Ekkorash der Inquisitorin und bringt ihn zu Ravandwyr in Area 52. Um Ekkorash aus ihrem Versteck hervorzulocken, streut das Beschwörungspulver auf die Kohlenpfanne in der Mitte der Ruinen.]], },
  },
  [10174] = {
    [[Der Fluch des Violetten Turms]],
    { [[Jetzt könnt Ihr mithilfe des Stabs Kontakt zu dem Erzmagier aufnehmen.]], [[Er lebt im Violetten Turm, auf einer Insel im Süden. Der verfluchte Turm ist sowohl eine Zuflucht als auch ein Gefängnis für ihn. Mitglieder und Verbündete der Kirin Tor können weder hinein noch hinaus, weswegen ich meinen Meister nicht sehen kann.]], [[Der Stab kann die Verteidigung des Turms durchbrechen, jedoch nur, wenn er von jemandem, der nicht zu den Kirin Tor gehört, aktiviert wird.]], [[Nehmt den Stab und benutzt ihn, um ein Abbild des Erzmagiers zu erschaffen. Sagt ihm, was passiert ist und beweist ihm, dass ich ihn nicht ganz enttäuscht habe.]], },
    { [[Benutzt Erzmagier Vargoths Stab um Kontakt mit Erzmagier Vargoth aufzunehmen.]], [[Falls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz.]], },
  },
  [10175] = {
    [[Thrall, Sohn Durotans]],
    { [[Kehrt in Eure Welt zurück, <name>. Sucht meinen Enkelsohn und sagt ihm, dass sein Blut hier ist - in dieser zerschlagenen Welt.]], [[Sagt ihm, dass das Erbe seines Vaters weiterlebt.]], [[Sagt ihm alles...]], },
    { [[Reist nach Orgrimmar und überbringt Thrall Geyahs Nachricht.]], },
  },
  [10176] = {
    [[Arkelos der Wächter]],
    { [[Jetzt, da Ihr alle Gegenstände, die ich benötige, um Kael'thas Fluch zu brechen, gesammelt habt, gibt es nur noch Eines zu tun. Als Kael'thas den Turm versiegelt hat, hat er einen arkanen Golem, der als Ar'kelos bekannt ist, auf dem Grund des Turms platziert.]], [[Ihr müsst Ar'kelos besiegen, damit ich innerhalb des Turms wieder meine Magie wirken kann.]], [[Wenn Ihr den Violetten Turm betretet, wird der Fluch Euch treffen.]], [[Sobald Ihr Ar'kelos besiegt habt, steigt den Turm hinauf und unterstützt mich bei den Vorbereitungen für das Ritual.]], },
    { [[Betretet den Violetten Turm in der Mitte von Kirin'Var und besiegt Ar'kelos. Sprecht danach mit Erzmagier Vargoth.]], },
  },
  [10177] = {
    [[Ärger in Auchindoun]],
    { [[Meine Spione in Auchindoun haben uns erschreckende Berichte geschickt. Seit seiner Explosion vor ein paar Jahren hat dieser Ort nichts als Ärger gebracht.]], [[Dort ist etwas - mal wieder - ganz schön schief gegangen, und der Schattenrat bereitet sich auf einen Krieg vor. Auch wenn die Berichte viele Details verschweigen, müssen sie sich auf einen Krieg im Inneren vorbereiten.]], [[Ihr müsst Euch zum Eingang des Schattenlabyrinths begeben. Mein Feldkommandeur, Mahfuun, wird Euch genau erklären, was zu tun ist.]], },
    { [[Meldet Euch bei Feldkommandeur Mahfuun beim Eingang zum Schattenlabyrinth in Auchindoun in den Wäldern von Terokkar.]], },
  },
  [10178] = {
    [[Findet Spion To'gun]],
    { [[To'gun, dieser Narr! Wir können hier kaum die Angriffe zurückhalten und er geht wieder hinein um Informationen zu sammeln und die anderen Spione zu retten!]], [[Ich weiß nicht, ob ich ihn umarmen oder töten soll!]], [[Der Schattenrat muss uns mithilfe von Magie ausfindig gemacht haben, denn all unsere Spionen mussten hastig von dort fliehen. Die meisten von uns haben es nicht geschafft.]], [[Bitte, wenn Ihr mutig genug seid, um das Schattenlabyrinth zu betreten, könntet Ihr dann nach To'gun Ausschau halten? Er ist schon eine ganze Weile fort, und ich befürchte das Schlimmste.]], },
    { [[Findet Spion To'gun im Schattenlabyrinth von Auchindoun.]], },
  },
  [10179] = {
    [[Der Hüter von Kirin'Var]],
    { [[Als klar wurde, dass unsere Expedition ohne Hoffnung auf Rückkehr nach Azeroth in der Scherbenwelt gefangen war, reisten die Kirin Tor zu den Ebenen von Farahlon und erbauten Kirin'Var. Ich habe die Verwaltung des Dorfs Hüter Dierwert übertragen, aber als Kael'thas und seine Streitkräfte mich hier im Violetten Turm einsperrten, ist mein Kontakt zu ihm abgebrochen.]], [[Sucht ihn in Kirin'Var und helft ihm, wo Ihr nur könnt, <name>. Ich bin sicher, dass er seit dem Angriff alle Hände voll zu tun hat.]], },
    { [[Speak with Custodian Dierworth in Kirin'Var Village.]], },
  },
  [10180] = {
    [[Kann es einfach nicht lassen]],
    { [[Ich habe versucht, Isfar davon zu überzeugen, hier in den sicheren Mauern von Shattrath zu bleiben, aber er wollte nicht auf mich hören. Stattdessen hat er sich entschlossen, wieder auf eigene Faust nach Auchindoun zu gehen.]], [[Er hatte zwar das Glück, den Sethekkhallen einmal zu entkommen, aber wenn sie ihn erwischen wird es kein zweites Mal geben.]], [[Ich bewundere seine Entschlossenheit, seine Schwester zu befreien, aber ohne Hilfe wird er es nicht schaffen.]], [[Findet Isfar und sprecht mit ihm, <name>. Wenn Ihr ihn nicht davon überzeugen könnt, zurückzukommen, könntet Ihr ihm dann vielleicht helfen Lakka zu retten?]], },
    { [[Sprecht mit Isfar in den Sethekkhallen in Auchindoun.]], },
  },
  [10182] = {
    [[Kampfmagier Dathric]],
    { [[Das Schwert ist von außergewöhnlicher Qualität, fein geschmiedet und perfekt für einen Kampfmagier ausbalanciert. Auf dem Knauf der Waffe sind das Siegel der Kirin Tor und der Name "Dathric" eingraviert.]], [[Während Ihr die Waffe in den Händen hin und her dreht, bemerkt Ihr, dass etwas mit dem Schwert nicht stimmt. Es fühlt sich an, als wäre es zwischen zwei Welten hin- und hergerissen. Könnte dies der Gegenstand sein, der Kampfmagier Dathrics Geist an diese Welt bindet?]], [[Vielleicht zerbricht das Band, wenn Ihr es an seinen angestammten Platz zurückbringt, und befreit Dathrics Geist endgültig.]], },
    { [[Sucht die Wohnung von Kampfmagier Dathric in der Zaubergasse und bringt die Klinge zurück in sein Waffenregal. Sprecht danach mit Hüter Dierwert in Kirin'Var.]], },
  },
  [10183] = {
    [[Auf nach Area 52]],
    { [[Ich studiere hier draußen den Nether - die astralen Strömungen und so weiter. Dies ist ein idealer Platz, weit weg von den Lichtern und dem Getöse der Stadt. Wenn ich diesen Schrott doch nur am Laufen halten könnte!]], [[Hey, wisst Ihr, wenn Ihr nach Arbeit sucht, solltet Ihr nach Area 52 gehen! Das liegt gleich den Pfad hoch und bei der Weggabelung rechts.]], [[Warum bringt Ihr nicht einfach meine neusten Forschungsnotizen zu Raketenchef Doppeldecker? Ich bin sicher, er hat genug Arbeit für Euch, wegen des Raketenschiffs X-52 und so.]], },
    { [[Bringt die Notizen des Netheorologen zu Raketenchef Doppeldecker in Area 52 im Nethersturm.]], },
  },
  [10184] = {
    [[Bösartige Überbleibsel]],
    { [[Die meisten Geister im Dorf sind nicht mehr als blasse Überbleibsel der Magier der Kirin Tor, die sie einst waren. Sie erinnern sich nicht mehr an ihr früheres Ich und streunen blind vor Wut um die Ruinen herum.]], [[Das war jedoch nicht immer so. Direkt nach dem Angriff waren die Geister zwar rastlos, aber nicht bösartig. Mit der Zeit sind sie jedoch immer aggressiver geworden. Ich traue mich nicht, in den südlichen Teil des Dorfs zu gehen, denn sie machen nicht einmal vor mir Halt. Beruhigt sie und lasst uns hoffen, dass Ihr Ärger dann verfliegt.]], },
    { [[Hüter Dierwert in Kirin'Var möchte, dass Ihr 20 abtrünnige Geister auf dem Ratsplatz tötet.]], },
  },
  [10185] = {
    [[Schlimmer als der Tod]],
    { [[Nachdem die Blutelfen Kirin'Var dem Erdboden gleichgemacht hatten, haben sie mit dem Bau eines großen Gebäudes im Nordwesten begonnen. Ohne die Kräfte des Erzmagiers können wir nicht genau sagen, was dort vor sich geht.]], [[Eines ist jedoch klar. Die Anwesenheit der Blutelfen hat eine Flut von gefräßigen Manaelementaren entfesselt. Sie scheinen von den Ruinen der Zaubergasse angelockt zu werden und verursachen noch weitere Zerstörung... Wir können das nicht weiter zulassen.]], },
    { [[Tötet 10 Magiertöter und 10 Manasucher und kehrt zu Hüter Dierwert in Kirin'Var zurück.]], },
  },
  [10186] = {
    [[Ihr seid eingestellt!]],
    { [[Lasst mich der Erste sein, der Euch an Bord willkommen heißt, Juniortechniker dritten Grades <name>!]], [[Wir bauen hier draußen mein Meisterwerk - die X-52 Netherrakete!]], [[Doch zuerst müssen wir alle Teile und den Treibstoff für sie besorgen. Und da kommt Ihr ins Spiel.]], [[Zuerst brauche ich Kristalle, VIELE Kristalle, für die astrallithische Antriebsmatrix. Die Draenei hatten so viele, dass sie ihnen schon zu den Ohren herauswuchsen!]], [[Begebt Euch also zu den Ruinen von Enkaat und sammelt ein paar für mich! Und kümmert Euch nicht um die Draeneigeister.]], },
    { [[Sammelt 10 Astrallithiummatrixkristalle und kehrt dann zu Raketenchef Doppeldecker in Area 52 im Nethersturm zurück.]], },
  },
  [10187] = {
    [[Eine Nachricht für den Erzmagier]],
    { [[Auch wenn es hier sehr ungemütlich ist, muntert es uns ein wenig auf, dass der Erzmagier die Misere des Dorfs nicht vergessen hat.]], [[<Der Hüter schaut zum Turm hinauf.>]], [[Es ist lächerlich, oder? Manchmal scheint es, als trenne uns nur eine Wand aus einfachem Stein. Wie leicht vergisst man die Wand aus Magie.]], [[Wenn Ihr so nett wärt, erklimmt den Turm und sagt Erzmagier Vargoth, dass wir Fortschritte im Dorf machen und bald einen Weg finden werden, um die Manakreaturen fernzuhalten.]], },
    { [[Speak to Archmage Vargoth at the top of the Violet Tower.]], },
  },
  [10188] = {
    [[Das Siegel von Krasus]],
    { [[Bevor wir uns vor so vielen Jahren von Azeroth aus aufmachten, haben die Kirin Tor unserer Expedition ein mächtiges Artefakt anvertraut, das Siegel von Krasus. Es hat uns im Kampf gegen die Orcs große Dienste erwiesen und könnte eine wichtige Rolle beim Brechen des Zaubers, der mich gefangen hält, spielen.]], [[Als Kael'thas mich jedoch meiner Besitztümer beraubt hat, gab er das Siegel an Zauberbinderin Maryana, die mit der Garnison zurückblieb. Hätte er gewusst, was er in seinem Besitz hatte, hätte er es nicht so einfach weggegeben.]], },
    { [[Retrieve the Sigil of Krasus from Spellbinder Maryana at Manaforge Coruu, northwest of the Violet Tower. When you have gained the sigil, use Archmage Vargoth's Staff to project an Image of Archmage Vargoth and inform him of your progress.]], [[If you lose Archmage Vargoth's Staff, speak to Ravandwyr at Area 52 to receive a replacement.]], },
  },
  [10189] = {
    [[Manaschmiede B'naar]],
    { [[Vielleicht sind Euch Kaels Manaschmieden auf dem Weg hierher aufgefallen. Das sind diese großen violetten Gebäude, die Energie aus dem Himmel saugen.]], [[Wir wurden von den Sha'tar hierher geschickt, um sie zu erkunden. Ihr könnt uns dabei helfen, indem Ihr eine Stammrolle von dort besorgt. Wenn wir eine Vorstellung davon hätten, wer dort als was arbeitet, wäre uns über alle Maßen geholfen.]], [[Wir glauben, dass Hauptmann Arathyn, der eine Ballistentruppe direkt außerhalb der Manaschmiede B'naar leitet, eine solche Rolle besitzen könnte. Holt sie für uns.]], },
    { [[Spionagemeister Thalodien in Area 52 möchte, dass Ihr zum Rand der Manaschmiede B'naar geht und die Stammrolle von B'naar von Hauptmann Arathyn besorgt.]], [[Das Abschließen von Aufgaben für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10190] = {
    [[Batterien aufladen]],
    { [[Ich habe die wichtige Aufgabe, die Maschinerie in diesen Ruinen zu sammeln, aber ich habe ein paar Probleme damit, alle meine Bots am Laufen zu halten.]], [[Die Phasenjäger - diese wurmähnlichen Dinger - stecken voller Energie! Man kann sie überall finden, wie sie aus dem Nether herausströmen.]], [[Füllt die Batterie mit diesem Ladeblaster, indem Ihr ihn bei einem Phasenjäger benutzt, wenn er sehr schwach ist, und ihm dann die restliche Energie entzieht.]], },
    { [[Benutzt den Batterieladeblaster bei genügend Phasenjägern, um den Batterieladestatus auf 10 zu bringen. Bringt die Batterie danach zu Bot-Spezialistin Alley in den Ruinen von Enkaat im Nethersturm zurück.]], },
  },
  [10191] = {
    [[Nummer V lebt!]],
    { [[Grüße, <race>. Ich bin aufgeladen und voll einsatzfähig!]], [[In den Ruinen gibt es ein paar zerbrechliche Draeneimaschinen, die ich wieder beschaffen muss. Ich habe sehr edle Servos in den Stellgliedern meiner Handgelenke und bin mit den neusten verbrennungspneumatischen Beulensaugern ausgestattet. Die Maschinen mit der nötigen Sorgfalt zu behandeln ist also kein Problem für mich.]], [[Leider werden die Geister, die in den Ruinen spuken, mich sehr wahrscheinlich angreifen, daher brauche ich Euch und Eure Freunde als Beschützer.]], },
    { [[Begleitet Maxx E. Million Nr. V durch die Ruinen von Enkaat und wieder in Sicherheit. Sprecht dann mit Bot-Spezialistin Alley außerhalb der Ruinen von Enkaat im Nethersturm.]], },
  },
  [10192] = {
    [[Krasus' Kompendium]],
    { [[Die Kirin Tor haben unserer Expedition einen weiteren Gegenstand aus Krasus' Besitz mitgegeben. Es handelt sich dabei um sein Zauberbuch, das Krasus' Kompendium genannt wird. Da es alle Zauber enthält, die Krasus beherrschte, war es uns eine unersetzliche Hilfe im Krieg.]], [[Bevor Kael'thas und seine Truppen Kirin'Var besetzt haben, wurde mir das Buch von einem rebellischen Schüler, der begierig darauf war, die Nekromantie zu erlernen, gestohlen. Der Lehrling und seine Anhänger haben sich aufgeteilt und das Buch heimlich studiert, wobei sie sich in ihren Häusern im Süden der Stadt eingeschlossen haben.]], },
    { [[Reclaim Krasus's Compendium - Chapter 1, Krasus's Compendium - Chapter 2, and Krasus's Compendium - Chapter 3 from Kirin'Var Village's Town Square. Use Archmage Vargoth's Staff to contact the archmage once you have the chapters.]], [[If you lose Archmage Vargoth's Staff, speak to Ravandwyr at Area 52 to receive a replacement.]], },
  },
  [10193] = {
    [[Hochrangige Ziele]],
    { [[Von den Informationen aus der Stammrolle wissen wir nun, dass die geistigen Führer der Manaschmieden die Warpmeister und Warpingenieure sind. Sie überwachen die Gewinnung der so genannten "Warpenergie" in den Manaschmieden und kümmern sich um ihre Lieferung an entfernte Orte über ein unterirdisches Rohrsystem.]], [[Die einzigen weiteren Spezialisten in dieser Gegend sind die Geologen. Alle anderen sind entweder Teil des Militärs oder Magister - beide sehr leicht zu ersetzen.]], [[Geht dort hinein und tötet Kaels Spezialisten. Das wird ihn am meisten treffen.]], },
    { [[Spionagemeister Thalodien in Area 52 möchte, dass Ihr in die Manaschmiede B'naar geht und 2 Warpmeister des Sonnenzorns, 6 Warpingenieure des Sonnenzorns und 8 Geologen des Sonnenzorns tötet.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10194] = {
    [[Heimlicher Flug]],
    { [[Während Ihr weg wart, haben meine Männer eine weitere Manaschmiede entdeckt. Sie befindet sich östlich von hier und ist als Manaschmiede Coruu bekannt.]], [[Zwei meiner besten Spione haben einen geheimen Landeplatz außerhalb der Manaschmiede eingerichtet. Ihr sollt ihnen helfen, Kaels Truppen zu unterwandern und so viele Informationen wie möglich zu sammeln.]], [[Sprecht mit Veronia außerhalb der Stadtmauer. Sie hat einen Windreiter vorbereitet, der Euch zur Manaschmiede Coruu bringen wird. Wenn Ihr dort seid, meldet Euch bei Caldis Morgenhell. Er wird Euch mit Eurer Mission vertraut machen.]], },
    { [[Sprecht mit Veronia außerhalb von Area 52 und fliegt zur Manaschmiede Coruu.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10195] = {
    [[Söldner sieht, Söldner handelt]],
    { [[Seit Ihr den ersten Stapel Feder gebracht und Bertelms Hut fertiggestellt habt, wollen alle anderen Söldner auch einen haben, darunter auch Thander. Ich habe ihnen gesagt, dass ich mich gleich darum kümmern werde, aber ich werde mehr Arakkoafedern dafür brauchen.]], [[Wenn Ihr auf Euren Abenteuern in den Wäldern noch mehr davon finden solltet, nehme ich sie Euch gerne ab.]], },
    { [[Bringt 30 Arakkoafedern zu Ros'eleth in Allerias Feste.]], },
  },
  [10196] = {
    [[Mehr Arakkoafedern]],
    { [[Ich habe erste Erfolge darin, die Arakkoageister näher zu mir zu locken, aber sie sind immer noch sehr schüchtern. Vielleicht trauen sie sich näher, wenn ich meine Verkleidung verbessere.]], [[Dazu benötige ich jedoch mehr Arakkoafedern wie die, die Ihr mir bereits gebracht habt. Wenn Ihr noch ein paar Federn bekommen könntet, würde ich sie Euch gerne abnehmen.]], },
    { [[Bringt 30 Arakkoafedern zu Malukaz in der Steinbrecherfeste.]], },
  },
  [10197] = {
    [[Eine überzeugende Verkleidung]],
    { [[Die Reihen von Kaels Armee zu unterwandern wird keine leichte Aufgabe. Mit meinen geringen Künsten der Illusion könnte ich einen von uns wie einen Anhänger Kaels aussehen lassen, aber es gibt einige Details in ihrer Uniform, die ich nicht nachbilden kann.]], [[Bringt mir ein Set ihrer Roben, ein Paar ihrer Handschuhe und ein Gardistenmedaillon des Sonnenzorns. Das wird die Ergebnisse meiner Magie überzeugender machen. Sie tragen alle das gleiche, also solltet Ihr keine Probleme haben, diese Gegenstände zu finden.]], },
    { [[Bringt ein Set Roben des Sonnenzorns, ein Paar Handschuhe des Sonnenzorns und ein Gardistenmedaillon des Sonnenzorns zu Caledis Morgenhell bei der Manaschmiede Coruu im Nethersturm.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10198] = {
    [[Informationen gesammelt]],
    { [[Ich habe aus den Teilen, die Ihr mir besorgt habt, eine Uniform hergestellt. Sie ist magisch verbessert, um die Illusion perfekt zu machen.]], [[Wir haben beobachtet, wie Kommandant Morgenschmied vor ein paar Minuten hineingegangen ist, um Arkanist Ardonis zu treffen. Er hatte sein gesamtes Gefolge an Wachen bei sich, daher ist das, was sie zu besprechen haben, sicher von größter Wichtigkeit. Versucht, etwas darüber herauszufinden.]], [[Ach, das hätte ich fast vergessen. Haltet Euch von den arkanen Golems fern, die in der Manaschmiede patrouillieren. Sie werden Eure Illusion mühelos durchschauen.]], },
    { [[Use the Sunfury Disguise, go into Manaforge Coruu and listen to the conversation between Commander Dawnforge and Arcanist Ardonis.  Report back to Caledis Brightdawn after completing this task.]], [[Completing tasks for the Scryers will cause your Aldor reputation level to decrease.]], },
  },
  [10199] = {
    [[Der kleine Extrakick]],
    { [[So, nun fehlt mir noch die eine Zutat, die meinem Gebräu den entscheidenden kleinen Kick geben sollte, den wir brauchen.]], [[Der Stich der Netherrochen, die sich in der Nähe der Schieferhautschinder herumtreiben, ist ganz schön heftig. Bringt mir ein paar ihrer Stachel, damit ich das Gift aus ihnen gewinnen kann.]], [[Ich bin mir sicher, dass die Ergebnisse explosiv sind!]], },
    { [[Besorgt 5 Netherrochenstachel und bringt sie zu Stiefels in Area 52 im Nethersturm.]], },
  },
  [10200] = {
    [[Rückkehr zu Thalodien]],
    { [[Es wäre besser, wenn diese Information so schnell wie möglich zu Thalodien gelangen würde. Ich bleibe hier und verwische unsere Spuren.]], [[Es wäre alles andere als gut, wenn Kael'thas herausfinden würde, was wir hier getan haben. Geheime Informationen sind immer am besten, wenn der Feind nicht weiß, dass man sie hat.]], },
    { [[Kehrt zu Spionagemeister Thalodien in Area 52 im Nethersturm zurück.]], },
  },
  [10201] = {
    [[Der Moment der Wahrheit]],
    { [[Das Öl ist endlich für einen ersten Test bereit. Es scheint dickflüssig genug zu sein, um als hochwertiges Waffenöl zu dienen.]], [[Der einzige Weg, dies herauszufinden ist, einen Profi zu fragen. Seht Ihr Grek da drüben bei den Fahndungsanschlägen? Ich weiß, er ist nicht gerade ein Poet, aber mit Waffen kennt sich der Orc aus!]], [[Bringt ihm eine Probe des Öl und bittet ihn, es an seiner Waffe auszuprobieren. Wenn er das getan hat, berichtet mir von seiner Meinung.]], },
    { [[Geht zu Grek und bittet ihn, das Öl an seiner Waffe auszuprobieren. Bringt das Ergebnis zu Rakoria, wenn er fertig ist.]], },
  },
  [10202] = {
    [[Ein Abtrünniger]],
    { [[Wir wurden vor Kurzem von einem Magister des Sonnenzorns kontaktiert, der uns beitreten möchte und schwört, dass er uns Informationen geben kann, die wir dringend benötigen. Wir haben uns mit unseren Informanten in Verbindung gesetzt, um zu überprüfen, ob es sich bei ihm um einen Doppelagenten handeln könnte, und er scheint sauber zu sein.]], [[Er befindet sich in der Manaschmiede Ara, was die Kommunikation etwas erschwert. Geht zu ihm und hört Euch an, was er zu sagen hat. Wenn er nur halb so hilfreich ist, wie er behauptet, können wir ihn sicher gut einsetzen.]], },
    { [[Geht zur Manaschmiede Ara und sprecht mit Magister Theledorn.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10203] = {
    [[Unschätzbare Ausrüstung beamen]],
    { [[Habt Ihr den Riss gesehen, der mitten durch Area 52 verläuft? Wir waren das! <Grinsen>]], [[Im Riss befinden sich allerlei Schätze für die X-52 Netherrakete! Das einzige Problem ist nur, dass wir nicht genug Ausrüstung haben.]], [[Bis die Brennende Legion dort aufgetaucht ist, führten wir eine Operation in den Ruinen von Arklon aus. Ich möchte, dass Ihr diesen ultrastarken Dekonsolidierungsstrahler zu den Ruinen bringt. Benutzt ihn, um unsere Ausrüstung zum leitenden Ingenieur Zischeldruck im Riss zu beamen.]], [[Juniortechniker dritten Grades <name>, dies ist Eure Chance auf eine Beförderung. Vergeigt sie nicht!]], },
    { [[Benutzt den ultrastarken Dekonsolidierungsstrahler, um den Hyperdreh-Grab-o-Matik, die servopneumatische Baggerklaue, den multispektralen Geländeabtaster und den großen Wagen voller Sprengstoff zurück nach Area 52 zu beamen. Meldet Euch dann beim leitenden Ingenieur Zischeldruck beim östlichen Ende des Risses in Area 52 im Nethersturm.]], },
  },
  [10204] = {
    [[Blutjuwelenkristalle]],
    { [[Kaels Magister haben den Gebrauch von Manakristallen so weit perfektioniert, dass sie innerhalb von Sekunden große Mengen von Energie aufnehmen können.]], [[Das tun sie, indem sie kleinere Kristallsplitter von Blutjuwelen an größeren und vollen Kristallen aufladen.]], [[Geht zur Manaschmiede B'naar und besorgt einen dieser Splitter von einem Magister des Sonnenzorns und benutzt ihn, um Energie von einem größeren Kristall aufzunehmen.]], [[Kehrt zu mir zurück, damit ich die Wirkung der Aura untersuchen kann. Keine Sorge, Nebenwirkungen sollten nur temporär sein.]], },
    { [[Magistrix Larynna möchte, dass Ihr zur Manaschmiede B'naar geht und einen Blutjuwelensplitter von einem Magister des Sonnenzorns besorgt. Benutzt den Splitter in der Nähe von größeren Blutjuwelenkristallen und kehrt zu ihr in Area 52 zurück.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10205] = {
    [[Sphärenräuber Nesaad]],
    { [[Sphärenräuber Nesaad muss erledigt werden! Ich habe keine Ahnung, wie er meinen Angriff überleben konnte, aber er darf seine Taten dort auf keinen Fall fortführen.]], [[Geht zurück und vernichtet Nesaad. Er hat ein kleines Lager des Astraleums im südwestlichen Teil des Hügels.]], [[Versagt nicht, wie ich es tat, <name>.]], },
    { [[Tötet Sphärenräuber Nesaad und kehrt dann zu Netherpirscher Khay'ji in Area 52 im Nethersturm zurück.]], },
  },
  [10206] = {
    [[Wählt Eure Rolle]],
    { [[Diese astralen Hampelmänner sind genauso opportunistisch wie wir, das muss man ihnen lassen. Aber niemand, und ich meine NIEMAND, wagt sich auf das Gebiet von B.U.M.M.!]], [[Der Hügel im Süden gehört uns!]], [[Wenn wir die X-52 Netherrakete fertigstellen wollen, brauchen wir alle neuen Technologien, die wir in die Finger bekommen können. Und da die Astralen von uns stehlen, ist es nur gerecht, wenn wir es ihnen mit gleicher Münze heimzahlen.]], [[Warum geht Ihr nicht zum Hügel und schaut, ob Ihr ihnen etwas von ihrer Technologie vor der Nase wegschnappen könnt.]], },
    { [[Sammelt 10 Stück Astraltechnologie und bringt sie zu Papa Mauschel in Area 52 im Nethersturm.]], },
  },
  [10207] = {
    [[Vorposten: Häschersturz]],
    { [[Eure Fähigkeiten in der Schlacht sind dem obersten Kommando aufgefallen, <class>. Sie wollen Euch an die Front zu Stümmelfausts Truppe schicken. Sie sind sehr radikal und haben sich der vollständigen Auslöschung der Legion verschrieben, also passt Ihr wahrscheinlich genau dazu...]], [[Geht etwa 200 Schritte nach Osten und biegt bei der hervorragenden Landmasse nach Süden ab. Ihr findet den Häschersturz dort, wo einst ein alter Bauernhof stand. Wenn Ihr angekommen seid, meldet euch bei Vorpostenkommandant To'arch.]], [[Und jetzt weggetreten!]], },
    { [[Meldet Euch bei Vorpostenkommandant To'arch im Häschersturz.]], },
  },
  [10208] = {
    [[Die Verstärkung abschneiden]],
    { [[Direkt im Osten liegt die Front der Legion. Entlang der Front befinden sich die höllischen Portale und Tore, mit deren Hilfe Verstärkung auf die Höllenfeuerhalbinsel und in die ganze Scherbenwelt gebracht wird. Wir müssen diese Portale zerstören!]], [[Nehmt diese Zünder. Sie wurden in Area 52 hergestellt und benutzten die eigene Macht der Dämonen als Sprengstoff! Besiegt die Legion beim Portal Xilus und Portal Kruul und haltet nach dämonischen Runensteinen Ausschau. Benutzt den Zünder, um die vier Steine in den Portalen zu sprengen, sobald Ihr genug Steine beisammen habt... und dann genießt das Feuerwerk! Kehrt zu mir zurück, wenn Ihr Eure Aufgabe erfüllt habt.]], },
    { [[Besiegt die Feinde um Portal Xilus und Portal Kruul herum, um dämonische Runensteine zu sammeln.]], [[Benutzt To'archs Zünder in Portal Xilus und Portal Kruul.]], [[Kehrt danach mit To'archs Zünder zu To'arch im Häschersturz auf der Höllenfeuerhalbinsel zurück.]], },
  },
  [10209] = {
    [[Beschwörer Kanthins Preis]],
    { [[Als nächstes müssen wir meinen Vertrauten, Gletscharius, aus den Händen des Blutelfenbeschwörers, der ihn gefangen hält, befreien. Beschwörer Kanthin war einst einer der erfahrensten Magister in der Streitmacht, die Kael'thas losgeschickt hat, um Kirin'Var auszulöschen. Es ist ganz normal, dass er meinen Vertrauten als Trophäe haben will.]], [[Ich bin mir sicher, dass Kanthin, dumm wie er ist, versucht hat, Gletscharius zu seinem Diener zu machen. Ich habe herausgefunden, dass Kanthin sich jetzt in der Manaschmiede Duro aufhält, falls dies Euch etwas sagt.]], },
    { [[Defeat Summoner Kanthin at Manaforge Duro, on the island to the north of Sunfury Hold, and reclaim the Stone of Glacius from him. When you have obtained the stone, use Archmage Vargoth's Staff to contact the archmage.]], [[If you lose Archmage Vargoth's Staff, speak to Ravandwyr at Area 52 to receive a replacement.]], },
  },
  [10210] = {
    [[A'dal]],
    { [[Willkommen in Shattrath, <class>. Dies ist vielleicht der einzige Ort in der Scherbenwelt, in der Ihr so etwas wie Sicherheit empfinden könnt.]], [[Eine Gruppe von Naaru, die als Sha'tar bekannt sind, hat die Dämonen von hier fortgejagt.]], [[Wenn Ihr noch nie einen Naaru gesehen habt, solltet Ihr A'dal auf der Terrasse des Lichts besuchen. Der Anführer der Sha'tar ist der ehrfurchtgebietendste Anblick, den ich je gesehen habe. Ganz anders, als alles andere in diesem öden Land.]], },
    { [[Sprecht mit A'dal, Anführer der Sha'tar in der Mitte der Terrasse des Lichts.]], },
  },
  [10211] = {
    [[Stadt des Lichts]],
    { [[Belästigt A'dal nicht mit Euren unbedeutenden Fragen, <class>. Er ist vielleicht das mächtigste Wesen, das Ihr je zu Gesicht bekommen werdet.]], [[Ihr kommt mir nicht bekannt vor. Ich nehme an, Ihr wollt etwas über die Stadt erfahren?]], [[Ihr müsst schon entschuldigen, dass ich selbst keine Führungen mache, aber ich muss einige Pläne mit A'dal besprechen. Vielleicht wärt Ihr ja mit einem herbeigerufenen Diener an meiner statt zufrieden? Ausgezeichnet!]], },
    { [[Folgt Khadgars Diener und hört Euch an, was er zu erzählen hat. Kehrt nach dieser Aufgabe zu Khadgar zurück.]], },
  },
  [10212] = {
    [[Held der Mag'har]],
    { [[Ich muss erst alles in Ordnung bringen, bevor ich gehe. Kehrt zur Großmutter zurück und sagt ihr, dass ihr Enkelsohn kommt.]], },
    { [[Kehrt zu Großmutter Geyah in Garadar in Nagrand zurück.]], },
  },
  [10213] = {
    [[Untersuchung der Absturzstelle]],
    { [[Als ich verwundet in der Wüste lag, habe ich gesehen, wie ein goblinscher Zeppelin abgestürzt ist. Er kam im Nordwesten runter, hinter dem Expeditionsrüstlager.]], [[Der Zeppelin hat gebrannt. Schreiende, brennende Goblins fielen wie Bomben vom Himmel. Ich hatte natürlich sehr große Schmerzen und viel Blut verloren. Vielleicht habe ich mir das alles auch nur eingebildet.]], [[Wenn Ihr irgendwelche Überlebenden finden solltet, bietet ihnen eine Zuflucht hier im Rückenbrecherposten an.]], },
    { [[Durchsucht die Absturzstelle nach Überlebenden.]], },
  },
  [10214] = {
    [[Bringt diese Mine zum Beben!]],
    { [[Die Mine wurde von diesem Gan'argpack überrannt, und zu allem Übel sind die Peons der Meinung, dass jetzt die richtige Zeit für ein Schläfchen ist! Ich weiß nicht, wie sie bei all dem Lärm aus der Mine schlafen können, aber das ist mein geringstes Problem.]], [[Wenn wir dieses Gan'arggesindel nicht bald loswerden, wird Thrallmars Nachschub an Kupfer und anderen Materialien abgeschnitten. Ich habe schon Wachkommandant Krunk um Hilfe gebeten, aber er sagt, dass er keine Männer schicken kann, solange die Stadt angegriffen wird. Könnt Ihr mir helfen?]], },
    { [[Tötet 15 Pioniere der Gan'arg und kehrt dann zu Großknecht Razelcraz zurück.]], },
  },
  [10216] = {
    [[Sicherheit geht vor]],
    { [[Shaffars Astrale haben uns ganz aus der Managruft ausgeschlossen. Zum Glück habe ich einen Plan. Auch wenn wir nicht durch die Eingangstür können, bin ich mir sicher, dass es innen viele Transporter gibt.]], [[Ich möchte, dass Ihr zuerst den Weg für meine Gruppe freiräumt und dann einen der Transporter aktiviert, damit wir hineinteleportieren können.]], [[Aktiviert den Transporter in der Kammer genau vor Shaffars Kammern, wenn Ihr den Weg freigemacht habt. Keine Sorge, ich werde Euch dafür entlohnen...]], },
    { [[Konstrukteur Morphalius möchte, dass Ihr 10 astrale Grufträuber, 5 astrale Zauberer, 5 Nexuswandler und 5 astrale Zauberbinder tötet.]], [[Wenn Ihr damit fertig seid, sucht den astralen Transporter in der Kammer vor Shaffars Kammer und aktiviert ihn.]], },
  },
  [10218] = {
    [[Jemandes Arbeit zahlt sich aus]],
    { [[Wen habt Ihr erwartet?! Dieser Idiot Morphalius wurde entpriorisiert. Jetzt bin ich hier, um sein Chaos aufzuräumen. Alles, was ich von Euch will, ist, dass Ihr mir den Rücken freihaltet, während ich Shaffars Astralsammler durchgehe und ihnen alles entnehme, was sie gesammelt haben. Wenn ich meinen Auftrag erfolgreich abschließe, wird Haramad selbst Euch belohnen!]], [[Was sagt Ihr? Begleitet mich durch die Managruft und sammelt ein paar unglaubliche Schätze! Die meiste Arbeit habt Ihr ohnehin schon erledigt! Hinter uns ist nichts mehr am Leben, oder?]], },
    { [[Begleitet Kryoingenieur Sha'heen sicher durch die Managruft, damit er die Astralmaterie aus Shaffars Astralsammlern entnehmen kann.]], [[Meldet Euch bei Nexusprinz Haramad außerhalb der Managruft beim Lager des Konsortiums, wenn Ihr erfolgreich wart.]], },
  },
  [10219] = {
    [[Gassi gehen]],
  },
  [10220] = {
    [[Bringt sie zum Zuhören]],
    { [[Ich wurde hierher geschickt, Kontakt zu den unerschütterlichen Geistern herzustellen. Diese einst stolzen Soldaten der Allianz wurden vor langer Zeit besiegt, und nun spuken ihre Geister in den Ruinen im Westen.]], [[Wenn sie sich uns anschließen würden, hätten wir einen mächtigen Verbündeten. Stellt Euch nur vor, eine Armee von untoten Kriegern, die sich unserem Feind entgegenstellt. Aber diese Untoten sind blind vor Hass und wollen sich meinen Vorschlag nicht anhören.]], [[Vielleicht kann man sie zwingen, sich uns anzuschließen.]], },
    { [[Mordiba möchte, dass Ihr 12 unerschütterliche Fußsoldaten, 8 unerschütterliche Krieger und 6 unerschütterliche Zauberer beim Expeditionsrüstlager westlich des Rückenbrechergrats tötet.]], },
  },
  [10221] = {
    [[Dr. Bumm!]],
    { [[<name>, mein früherer Schützling, Sparky Überschub, hat sich geweigert, während seiner Arbeit hier seinen Helm zu tragen und hat sich in den berüchtigten Dr. Bumm verwandelt!]], [[Er hat sich mit einem Großteil unserer Pionierausrüstung nach Nordosten aufgemacht! Seitdem sind er und seine Bummbots eine große Bedrohung für Area 52!]], [[Ich möchte, dass Ihr zu seinem Lager außerhalb der Stadt geht und ihm ein Ende macht! Benutzt diese Bummbomben um ihn auszulöschen.]], [[Und passt auf, dass Ihr ihm oder seinen Bummbots nicht zu nahe kommt!]], },
    { [[Tötet Dr. Bumm und kehrt dann mit den guten Nachrichten zu dem leitenden Ingenieur Zischeldruck in Area 52 im Nethersturm zurück.]], },
  },
  [10222] = {
    [[Die Garnison des Sonnenzornpostens]],
    { [[Nachdem Prinz Kael'thas' Streitkräfte das Dorf zerstört und den Erzmagier im Turm eingesperrt hatten, haben sie sich zum Sonnenzornposten im Nordosten zurückgezogen. Die gleichen Männer, die unser Dorf zerstört und unsere Freunde getötet haben, stoßen nun auf ihren 'unglaublichen Sieg' an, während wir unsere Gefallenen nicht rächen können.]], [[Wir dürfen diese Verbrechen nicht ungestraft lassen! Seid unser Instrument der Rache, <name>. Geht zum Sonnenzornposten und fordert das Leben der Verantwortlichen für dieses Verbrechen ein!]], },
    { [[Hüter Dierwert in Kirin'Var möchte, dass Ihr 10 Bogenschützen des Sonnenzorns tötet.]], },
  },
  [10223] = {
    [[Nieder mit Daellis]],
    { [[Den Blutelf, der den Angriff geführt hat, werde ich nie vergessen. Er trug einen Wappenrock mit einem Ding, was ich noch nie zuvor gesehen habe, über einer roten Rüstung.]], [[Er war wild und erbarmungslos, und kämpfte an der Front, wo man kaum einen anderen Blutelfenoffizier je zu Gesicht bekommt. Er war an diesem Tag für den Tod vieler Kirin Tor verantwortlich, aber er blieb nicht lange im Sonnenzornposten. Kael'thas hat ihn zur Manaschmiede Duro beordert, um bei ihrem Versuch, das Gebiet zu kontrollieren, zu helfen. Findet ihn, <name>, und bringt seinen Bogen für immer zum Schweigen.]], },
    { [[Hüter Dierwert in Kirin'Var möchte, dass Ihr Meister Daellis Morgenschlag tötet.]], },
  },
  [10224] = {
    [[Essenzen für die Motoren]],
    { [[Als Chefingenieur der X-52 ist es meine Pflicht dafür zu sorgen, dass die Hauptmotoren der Netherrakete tiptop in Schuss sind.]], [[Unglücklicherweise habe ich sie noch nicht gebaut.]], [[<class>, ich bin hier in einer Notlage! Ich habe fast alle Teile. Ich brauche nur noch einen Antriebskern.]], [[Zum Glück bin ich der Meinung, dass wir die Essenz der Managespenster in der Nähe nutzen können, um die Motoren zu überbrücken.]], [[Ihr findet die Managespenster überall bei diesen komischen leuchtenden Gräben im Norden der Insel. Außer hier in Area 52 natürlich.]], },
    { [[Sammelt 7 Essenzen eines Managespensts und bringt sie zu Chefingenieur Trep bei Area 52 im Nethersturm.]], },
  },
  [10225] = {
    [[Meldung beim Chefingenieur]],
    { [[<name>! Ich möchte, dass Ihr all diese Astrallithiumkristalle zu Chefingenieur Trep, meinem, äh, Ingenieurschef bringt.]], [[Er sollte wissen, was mit ihnen zu tun ist. Und wenn er es nicht weiß, dann sorgt dafür, dass er es herausfindet.]], },
    { [[Bringt die Astrallithiummatrixkristalle zu Chefingenieur Trep in Area 52 im Nethersturm.]], },
  },
  [10226] = {
    [[Elementarenergieextraktor]],
    { [[Verdammt, das hat nicht geklappt.]], [[Ok, ich habe eine andere Idee. Ich habe Xyrol einen Elementarenergieextraktor herstellen lassen. Ich weiß nicht genau, was man mit dieser Energie anfangen kann, aber ich werde schon einen Verwendungszweck für sie finden.]], [[Die zerrissenen Rumpler und Sphärenentartungen auf dem Felsvorsprung der Schwindenden Weite im Süden haben genau das, was wir für die Motoren brauchen!]], [[Der Extraktor wird ihre Energie in eine feste Form bringen, die Ihr aufsammeln könnt, nachdem Ihr sie besiegt habt.]], },
    { [[Extrahiert 5 Einheiten Elementarenergie und bringt sie zu Chefingenieur Trep in Area 52 im Nethersturm.]], },
  },
  [10227] = {
    [[Ich sehe tote Draenei]],
    { [[Ich sehe tote Draenei. Sie sind überall.]], [[Pah, niemand glaubt mir, weil ich ein Kind bin. Nichtmal meine Mama glaubt mir.]], [[<Ha'lei zeigt auf Ramdor.>]], [[Er glaubt mir. Er sieht sie auch. Und er regt sich darüber auf!]], [[Irgendwie ist es lustig. Ich sehe Geister und die Leute lächeln mich nur an und tätscheln mir den Kopf, um mich zu beruhigen. Ramdor sieht Geister und sie halten ihn für verrückt. Ich hoffe echt, dass ich nie erwachsen werde!]], [[Vielleicht solltet Ihr erst mit Ramdor reden, bevor Ihr da hinein geht. Er war einst ein Totenpriester.]], },
    { [[Sprecht mit Ramdor dem Wahnsinnigen auf dem Knochenhaufen außerhalb der Auchenaikrypta.]], },
  },
  [10228] = {
    [[Ezekiel]],
    { [[Ich bin der Meinung, dass die Toten, um die wir uns seit den letzten 500 Jahren gekümmert haben, in Ruhe gelassen werden sollten. Aber Maladaar hat andere Pläne. Er hat sich in die Krypta des Gedenkens zurückgezogen, um seine Experimente zu beginnen... Nekromantie.]], [[Ich hoffe, Ihr hattet nicht vor, dorthin zu gehen... nicht bevor Ihr mir geholfen habt, diese verdammten Geister loszuwerden. Ich weiß, wo sie ihre Schätze aufbewahren, <race>. Jetzt habe ich Eure Aufmerksamkeit, was?]], [[Bringt mir das Buch der Toten, das sich im Besitz des Aldors Ezekiel von Shattrath befindet.]], },
    { [[Sprecht mit Ezekiel von Shattrath in Shattrath.]], },
  },
  [10229] = {
    [[Den Folianten entziffern]],
    { [[Dieser Foliant scheint eine Art Geschichtsbuch zu sein, das in einer Geheimschrift der Allianz geschrieben wurde. Althen der Historiker im Rückenbrecherposten hat die Unerschütterlichen eine Zeit lang studiert. Vielleicht kann er die Geheimschrift der Allianz entziffern und das Buch lesen.]], },
    { [[Bringt den geheimnisvollen Folianten zu Althen dem Historiker im Rückenbrecherposten.]], },
  },
  [10230] = {
    [[Das Kampfhorn]],
    { [[Vielleicht haben wir uns ganz falsch an diese Unerschütterlichen herangewagt.]], [[Dieser Foliant sagt, dass das Rüstlager der Allianz vor langer Zeit von den Orcs und den Todesrittern zerstört wurde. Die Unerschütterlichen wurden dort als rachedurstige Geister in einer ewigen Schlacht gegen die Klans der Höllenorcs gefangen.]], [[Sie denken nur an Rache. Wir müssen ihnen die Chance geben, ihre meist verhassten Feinde zu besiegen. Kehrt noch einmal in die Ruinen des Rüstlagers zurück und sucht Feldkommandant Thalvos. Er trägt ein Kampfhorn, das die Unerschütterlichen rufen kann. Bringt es mir.]], },
    { [[Besorgt Euch das Kampfhorn der Unerschütterlichen von Feldkommandant Thalvos und bringt es zu Althen dem Historiker im Rückenbrecherposten.]], },
  },
  [10231] = {
    [[Welches Buch? Ich sehe kein Buch.]],
    { [[Ramdor? Hmm, mal überlegen... Ramdor... Oh ja, der Totenpriester. Was will er denn mit dem Buch? Nun, es ist gleichgültig. Ja, ich habe Euch angelogen und nein, ich habe das Buch nicht. Nicht mehr zumindest. Es wurde gestohlen. Wahrscheinlich von einem dieser widerlichen Flüchtlinge.]], [[Im Übrigen existiert Auchindoun nicht mehr. Wir haben keine Verwendung mehr für das Buch. Wenn Ihr es findet, könnt Ihr es behalten.]], [[Wie schon gesagt, ich habe ein paar dieser Grobiane letzte Woche um die Terrasse herumschleichen sehen. Fangt mit Eurer Suche bei ihnen an. Wahrscheinlich spielen sie irgendwo im Armenviertel Würfelspiele.]], },
    { [[Ezekiel sagt, dass das Buch gestohlen wurde. Eure einzige Spur führt Euch zu den Grobianen. Schaut nach, ob sie irgendwelche Informationen haben.]], },
  },
  [10232] = {
    [[Legion in Trümmern]],
    { [[Die Brennende Legion ist auf unserem Grund im Trümmerfeld, den Pfad hinunter und über die Brücke nach Osten, einmarschiert!]], [[Das könnte ein großes Problem für uns sein! Die Legion in den Ruinen von Arklon und jetzt auch noch hier! Langsam sieht es für uns und das Raketenprojekt hier in Area 52 schlecht aus!]], [[<name>, ich möchte, dass Ihr dorthin geht und mit den Dämonen fertigwerdet! Ich würde es ja tun, aber ich bin kein Kämpfer.]], [[Ja, das riecht nach Ärger, ich fühle es.]], },
    { [[Tötet 5 Schicksalsschmiede der Mo'arg und 15 Ingenieure der Gan'arg. Kehrt danach zu Papa Mauschel in Area 52 im Nethersturm zurück.]], },
  },
  [10233] = {
    [[Den Sonnenzornposten anzünden]],
    { [[Die Garnison des Sonnenzornpostens auszulöschen ist nicht genug, um die Blutelfen davon abzuhalten, ihn erneut zu beziehen. Kael'thas Anhänger sind eifrig und hartnäckig. Sobald im Sonnenzorn noch ein Stein auf dem anderen steht, ist dies praktisch eine Einladung für sie, zurückzukehren.]], [[Nein, der Posten muss völlig zerstört werden. Sorgt dafür, dass ihre Zelte und Artillerie zu Asche zerfallen!]], [[<Der Geist ballt die Fäuste.>]], [[Stehlt die Fackeln von den Flammenbewahrern des Sonnenzorns um Eure Aufgabe zu erfüllen, <name>.]], },
    { [[Benutzt eine flammende Fackel, zerstört 4 Sonnenzornballisten und 4 Sonnenzornzelte und kehrt dann zu Zaubererleutnant Morran in Kirin'Var zurück.]], },
  },
  [10234] = {
    [[Der Müll eines Dämons]],
    { [[Ihr seht aus, als wärt Ihr <ein fähiger junger Mann/eine fähige junge Frau>! Ich habe mich gefragt, ob Ihr mir vielleicht zur Hand gehen könntet.]], [[Im Osten befindet sich das Trümmerfeld, auf dem die Brennende Legion Einzug gehalten hat. Es ist voller Teufelshäscherteile. Dort liegen auch ein paar kleinere Teile herum, um die sie sich nicht zu scheren scheinen.]], [[Ich möchte, dass Ihr dorthin geht, und mir ein paar dieser kleineren Teile eines Teufelshäschers besorgt!]], },
    { [[Sammelt 10 Teile eines Teufelshäschers und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10235] = {
    [[Verdammnisklaue die Krallen stutzen]],
    { [[Wisst Ihr, was sie entmutigen würde? Wenn wir ihren Anführer erlegen!]], [[Wie ich gehört habe, klappt das immer. Und wenn sie sehen, dass so <ein knallharter/eine knallharte><class> <class> wie Ihr ihren Anführer erledigt, dann ziehen sie sicher den Schwanz ein und rennen weg!]], [[Geht dorthin zurück und erlegt den Großen, den sie Verdammnisklaue nennen. Ich habe gehört, dass er sich im östlichen Teil den Trümmerfelds unter dem Rohr aufhält.]], [[Bringt mir seine Klaue! Vielleicht können wir sie brauchen.]], },
    { [[Besorgt Euch Verdammnisklaues Hand und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10236] = {
    [[Die Scherbenwelt ist doof!]],
    { [[Ich kann es kaum fassen, dass ich Beutebucht verlassen habe, um in die Scherbenwelt zu kommen. Mein Vetter Quack Lufthans hat mir versprochen, dass ich hier ein Vermögen machen würde, aber stattdessen ist er in seinem Zeppelin auf und davon und hat mich hier zurückgelassen. Nun versuche ich, mir einen sehr bescheidenen Lebensunterhalt mit der Mine zu verdienen.]], [[Aber wie soll ich denn überleben, wenn ich dauernd von Höllenebern und Dämonen und Höllenorcs angegriffen werde? Mein Schredder hier ist gestern kaputt gegangen und die Höllenorcs haben meine Ersatzteile gestohlen. Sie haben die Teile in die Große Kluft im Südwesten gebracht und vielleicht dort fallenlassen.]], },
    { [[Großknecht Razelcraz möchte, dass Ihr ihm 6 Schredderteile besorgt. Kehrt danach zu ihm bei der Mine nordwestlich von Thrallmar zurück.]], },
  },
  [10237] = {
    [[Warnt Area 52!]],
    { [[<name>, Ihr müsst so schnell wie möglich nach Area 52 zurückkehren! Beeilt Euch!]], [[Sagt dem Raketenchef, dass die Brennende Legion hinter uns her ist!]], },
    { [[Überbringt Raketenchef Doppeldecker in Area 52 die schlechten Nachrichten.]], },
  },
  [10238] = {
    [[Wie man Goblins zubereitet]],
    { [[Warum sollten die Höllenorcs denn meine Schredderteile stehlen wollen? Ich hasse sie so. Sie schleichen sich einfach in mein Lager hier und klauen immer meine Sachen. Vor ein paar Tagen haben sie drei meiner Peons gestohlen! Jetzt sind alle anderen Peons abgehauen, weil sie Angst hatten, von den Höllenorcs gefressen zu werden.]], [[Ich kann meine Mine ohne Peons nicht betreiben. Wenn Ihr meine gestohlenen Peons retten könntet, kommen die anderen vielleicht auch wieder zurück. Die Höllenorcs haben ihre Lager entlang des Walls im Südwesten.]], },
    { [[Rettet Manni, Moh und Jakk aus den Lagern der Höllenorcs und bringt sie zu Großknecht Razelcraz bei der Mine nordwestlich von Thrallmar zurück.]], },
  },
  [10239] = {
    [[Mögliche Energiequelle]],
    { [[Wir sind nicht die Einzigen, die unter dem Zufluss von Manakreaturen leiden. Auch die Blutelfen sind darüber besorgt, ich weiß jedoch nicht, warum.]], [[Was immer sie da drüben tun mögen, sie scheinen nicht damit aufzuhören. Stattdessen erfinden sie immer neue Methoden, um sie zu töten. Die wirksamste davon beinhaltet eine magische Verteidigung, die von einer Art konzentrierter Energie gespeist wird.]], [[Sammelt einige dieser Energiequellen und bringt sie mir. Scheut Euch nicht, die Blutelfen zu töten, wenn sich eine Gelegenheit ergeben sollte.]], },
    { [[Zaubererleutnant Morran in Kirin'Var möchte, dass Ihr ihm 10 Energieisolationswürfel von den Blutelfen der Manaschmiede Coruu bringt.]], },
  },
  [10240] = {
    [[Eine Verteidigung errichten]],
    { [[Wir sind dabei, eine Verteidigung um das Dorf herum zu errichten, die mit der Energiequelle der Blutelfen gespeist wird. Ich kann nicht garantieren, dass sie funktionieren wird, aber wir müssen es versuchen.]], [[Wenn nicht werden die Manakreaturen weiterhin die Geister der gefallenen Kirin Tor verspeisen.]], [[Benutzt dieses Gerät bei den kleinen Runen um die Verteidigung des Dorfes herum, um die Energie dort freizusetzen. Wenn ich das Feld korrekt gewebt habe, sollten wir die Wirkung bemerken, sobald Ihr das Gerät bei jedem Punkt benutzt habt.]], },
    { [[Benutzt das Gerät zur Aktivierung von Runen bei der östlichen Rune, der nordöstlichen Rune und der westlichen Rune. Die Runen befinden sich entlang der Verteidigung der Zaubergasse und des Ratsplatz von Kirin'Var. Kehrt zu Zaubererleutnant Morran zurück, wenn Ihr die Aufgabe erfüllt habt.]], },
  },
  [10241] = {
    [[Ablenkung in der Manaschmiede B'naar]],
    { [[Wir wurden von Sha'tar hierher geschickt, um die Manaschmieden, die Kael'thas' Armee hier betreibt, zu untersuchen. Aber die Seher drohen damit, ihre Spionagearbeiten nicht durchzuführen, wenn wir sie nicht militärisch unterstützen.]], [[Wir können uns nicht erlauben, dass diese Mission wegen politischen Differenzen den Bach hinunter geht, daher haben wir uns einverstanden erklärt, die Verteidigung der Manaschmiede zu schwächen, damit die Seher ihre Arbeit verrichten können.]], [[Beweist Eure Loyalität gegenüber den Aldor und tötet die Magister und Blutwärter des Sonnenzorns, die die Manaschmiede B'naar bewachen.]], },
    { [[Tötet 8 Magister des Sonnenzorns und 8 Blutwärter des Sonnenzorns und kehrt dann zu Exarch Orelis in Area 52 zurück.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Ruf bei den Sehern.]], },
  },
  [10242] = {
    [[Der Rückenbrechergrat]],
    { [[<name>, meine Ausrüstung hier ist leider nicht für alle Tests, die ich durchführen möchte, geeignet. Mein Kollege Apotheker Albreck im Rückenbrecherposten verfügt jedoch über alle Hilfsmittel, die man dazu braucht.]], [[Ich habe eine Blutprobe des Blutenden Auges, die mir ein Agent aus Thrallmar besorgt hat. Bringt diese Probe zu Albreck und erlaubt ihm, sie zu untersuchen.]], [[Sprecht mit Schwadronskommandant Brack, um zum Rückenbrecherposten zu kommen. Er befindet sich in der Nähe der Wyvern im Süden.]], },
    { [[Apothekerin Zelana beim Häschersturz möchte, dass Ihr mit Schwadronskommandant Brack sprecht, um auf einem Wyvern zum Rückenbrecherposten zu fliegen. Bringt die Blutprobe des Blutenden Auges zu Apotheker Albreck im Rückenbrecherposten.]], },
  },
  [10243] = {
    [[Die Technologie der Naaru]],
    { [[Die Seher haben eine Stammrolle der Manaschmiede B'naar in die Hände bekommen und sind nun dabei, gezielt nach Anschlagsopfern zu suchen.]], [[Sie haben jedoch eine kleine Information übersehen, die an den Rand der Schriftrolle gekritzelt wurde. Es scheint eine Art Passwort oder Code zu sein, wie sie für den Zugang zu von den Naaru erschaffenen Technologien wie der Exodar oder der Festung der Stürme benutzt werden.]], [[Geht in die Manaschmiede und haltet nach einer Steuerungskonsole Ausschau. Schreibt die Anzeige so gut es geht auf dieser Rolle ab. Wir müssen unseren Verdacht über die Herkunft von Kaels Technologie erhärten.]], },
    { [[Geht in die Manaschmiede B'naar und sucht die Steuerungskonsole von B'naar.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10244] = {
    [[Liesmich]],
    { [[Ihr zerbrecht das Siegel des ziemlich großen Folianten und könnt nicht verstehen, was dort geschrieben steht. Es schein die Sprache der Dämonen zu sein.]], [[Ihr könnt jedoch die Bilder erkennen, die ihre eigene Geschichte erzählen.]], [[Es handelt sich eindeutig um eine Bauanleitung für Teufelshäscher, und Ihr wisst ganz sicher, dass Mama Mauschel sich das gerne anschauen würde.]], },
    { [[Bringt die Bauanleitung für Teufelshäscher zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10245] = {
    [[Steuerungsabschrift von B'naar]],
    { [[Ihr versucht die Formen abzuschreiben, als sie an Euren Augen vorbeiziehen. Die Ergebnisse sind nicht gerade genau, aber sie sollten genügen, um den Verdacht der Aldor zu bestätigen.]], },
    { [[Bringt die Steuerungsabschrift von B'naar zu Anachoret Karja in Area 52 im Nethersturm.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10246] = {
    [[Angriff auf Manaschmiede Coruu]],
    { [[Euer Erfolg in der Manaschmiede B'naar war sehr bemerkenswert. Ich bin sicher, dass Anachoret Karja Euch bitten wird, Eure Leistung als Nächstes in der Manaschmiede Coruu zu wiederholen.]], [[Wir haben erfahren, dass ein besonders wichtiges Regiment von Arkanisten und Forschern des Sonnenzorns dort stationiert wurde. Wir würden sie gerne angreifen. Wenn es Euch gelingt, wird das ein vernichtender Schlag für die Streitkräfte unseres Feindes sein.]], },
    { [[Reist zur Manaschmiede Coruu und tötet 8 Arkanisten des Sonnenzorns und 5 Forscher des Sonnenzorns. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10247] = {
    [[Dr. h. c. tüft. Vomisa]],
    { [[Am Rand des Dorfes im Osten befinden sich die Versuchsgründe. Unser Spitzenobertüftler, Doktor Vomisa, hat hier ein wenig mit einer Kombination aus Teufelshäschertechnologie, Astraltechnologie und natürlich Goblintechnologie herumgespielt.]], [[Das verdanken wir nicht zuletzt Euren Sammelbemühungen, Juniortechniker dritten Grades <name>!]], [[Sucht ihn auf! Ich bin mir sicher, dass er einen Weg weiß, wie Area 52 gerettet werden kann.]], [[Und weil er Euch sicher als Versuchskaninchen einsetzen möchte, würde ich an Eurer Stelle lieber ein paar Freunde mitbringen. Nur für den Fall.]], },
    { [[Sprecht mit Dr. h. c. tüft. Vomisa in den Versuchsgründen im Nethersturm.]], },
  },
  [10248] = {
    [[Du, der Roboter]],
    { [[Dr. h. c. tüft. Vomisa zu Euren Diensten. Hier, nehmt diese Steuerung. Ich möchte, dass Ihr sie aktiviert und meinen neu erfundenen Schrotthäscher X6000 mal ausprobiert.]], [[Bleibt in der Nähe! Die Steuerung ist an meine Konsole hier gebunden, wenn Ihr zu weit weggeht, wird der Roboter aufhören zu funktionieren.]], [[Probiert alles aus... Ich schaue zu und stelle dann die Servos gemäß Eures Testlaufs ein.]], },
    { [[Aktiviert die Steuerung für Schrotthäscher X6000 und testet seine Fähigkeiten aus. Gebt Dr. h. c. tüft. Vomisa bei den Versuchsgründen im Nethersturm danach Eure Rückmeldung.]], },
  },
  [10249] = {
    [[Zurück zum Chef!]],
    { [[Ich bin mir sicher, dass der Raketenchef ein oder zwei Takte mit Euch reden möchte!]], [[An Eurer Stelle würde ich mich gleich nach Area 52 aufmachen und mit ihm sprechen!]], },
    { [[Meldet Euch sofort bei Raketenchef Doppeldecker in Area 52 im Nethersturm!]], },
  },
  [10250] = {
    [[Blutige Rache]],
    { [[Im Osten befindet sich ein alter Belagerungsturm der Allianz. Jetzt suchen Plünderer dort nach alten Nägeln und Alteisen. Vor langer Zeit haben bei diesem Turm viele Krieger der Allianz ihr Leben gelassen. Ihr Banner wurde von einem widerlichen Orckommandanten namens Urtrak gestohlen.]], [[Stoßt bei diesem Belagerungsturm in das Kampfhorn der Unerschütterlichen, um Urtraks Aufmerksamkeit zu erregen. Vielleicht ruft es auch Krieger herbei, die vor langer Zeit in der Schlacht gestorben sind.]], },
    { [[Stoßt am alten Belagerungsturm der Allianz in das Kampfhorn der Unerschütterlichen. Tötet Urtrak und kehrt danach zu Althen dem Historiker im Rückenbrechergrat zurück.]], },
  },
  [10251] = {
    [[Der große Plan des Meisters?]],
    { [[Gestern Abend haben uns ein paar Draenei einen Besuch abgestattet. Sie haben irgendwie erfahren, dass wir das Buch in unserem Besitz haben und uns das großzügige Angebot gemacht, es uns abzunehmen.]], [[Wir haben sie nicht gefragt, was sie mit dem Buch wollen, aber der Jüngere sprach die ganze Zeit vom "großen Plan des Meisters", was immer das zu bedeuten hat.]], [[Ich stelle keine Fragen, wenn es ums Geld geht. Sie haben uns viel Gold geboten und wir haben ihnen das Buch gegeben.]], [[Er sagte, er lebe in Nagrand, im alten verlassenen Rüstlager nördlich von Telaar.]], },
    { [[Sucht Nitrin den Gelehrten im verlassenen Rüstlager in Nagrand.]], },
  },
  [10252] = {
    [[Vision der Toten]],
    { [[Es hat schon begonnen. Levixus steht auf den Ruinen von Auchindoun und liest aus dem Buch der Toten. Er ruft die Toten beim Namen und unterwirft die gequälten Seelen durch ein dämonisches Ritual seinem Willen. Wenn er kann, wird er ganz Auchindoun erwecken!]], [[Aber... Ihr müsst Euch erst vorbereiten. Ihr könnt nicht mit etwas kämpfen, dass Ihr nicht seht.]], [[<Nitrin reicht Euch ein Pergament.>]], [[Bringt mir die Gegenstände, die ich für die Tinktur benötige. Sie wird Euren Geist erweitern und Euch ermöglichen, die Geisterwelt zu sehen.]], },
    { [[Nitrin im verlassenen Rüstlager in Nagrand möchte, dass Ihr ihm 1 Augapfel eines Berggronns, 1 fehlerloser Schnabel eines großen Windrocs und 1 Tran eines gealterten Grollhufs bringt.]], },
  },
  [10253] = {
    [[Levixus der Seelenrufer]],
    { [[Geht jetzt, <name>. Ihr könnt Levixus auf den höchsten Ruinen von Auchindoun finden. Nähert Euch vom Süden der Knochenwüste. Er befindet sich mitten in einem Sturm von Seelen...]], [[Vernichtet ihn und nehmt ihm das Buch der Toten ab. Wenn Ihr das verfluchte Buch in Eurem Besitz habt, dann zerstört es! Zerstört es, damit eine solche Grausamkeit nie wieder geschehen kann.]], },
    { [[Vernichtet Levixus und nehmt Euch das Buch der Toten aus seinem Besitz.]], [[Bringt das Buch der Toten zu Ramdor dem Wahnsinnigen außerhalb der Auchenaikrypta am Eingang zu Auchindoun.]], [[Ihr hattet eine Vereinbarung, erinnert Ihr Euch?]], },
  },
  [10254] = {
    [[Truppenkommandant Danath]],
    { [[<Marschall Isildor spuckt in den Staub.>]], [[Nun, es ist an der Zeit, dass Ihr Euch Euren Sold verdient. Meldet Euch sofort bei Truppenkommandant Trollbann in der Feste. Er und seine Berater werden sich sicher freuen, Euch zu sehen.]], [[<Marschall Isildor salutiert teilnahmslos.>]], [[Für die Allianz!]], },
    { [[Marschall Isildor möchte, dass Ihr Euch bei Truppenkommandant Danath Trollbann in der Ehrenfeste meldet.]], },
  },
  [10255] = {
    [[Das Gegengift testen]],
    { [[Ich glaube, in dem Blut ist etwas, das wir als Grundlage für ein Gegengift verwenden können. Schaut mich nicht an, als hätte ich den Verstand verloren!]], [[Das ist in der Natur ganz normal. Oft kann das Heilmittel aus dem Gift selbst gewonnen werden. Und genau das habe ich vor.]], [[Wenn wir die Eber von dem dämonischen Einfluss befreien können, verlieren sie hoffentlich ihre Aggressivität und können uns zeigen, wie wir auch den andern Arten helfen können.]], },
    { [[Probiert das cenarische Gegengift an einem bulligen Hölleneber aus und beobachtet die Folgen. Sobald Ihr die Ergebnisse des Experiments habt, kehrt zu Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück.]], },
  },
  [10256] = {
    [[Den Schlüsselmeister finden]],
    { [[Wenn ich zurückblicke, hätte ich eigentlich wissen müssen, dass Kael'thas' Magie so widerstandsfähig ist. Er war schon immer ein talentierter Magier, aber nie zuvor habe ich etwas so Mächtiges gesehen, wie die Magie, die er beim Angriff auf den Turm gewirkt hat.]], [[Der Schutzschild verhindert, dass ich die Macht, die er aufrechterhält, ausspionieren kann, aber Ihr könnt das trotzdem.]], [[Ihr werdet jedoch einen Fokus brauchen. Apex, ein Riese, der Kristalle mit sich herumträgt, streift auf den Weiten westlich der Manaschmiede Duro umher. Einer seiner Kristalle würde einen ausgezeichneten Fokus abgeben.]], },
    { [[Sucht Apex und besorgt Euch Apex' Kristallfokus. Kehrt dann zum Violetten Turm zurück und benutzt Apex' Kristallfokus in der Nähe von Erzmagier Vargoths Kugel um herauszufinden, wer den Schlüssel zum Fluch des Turms hat.]], },
  },
  [10257] = {
    [[Den Schlüsselstein erobern]],
    { [[Es überrascht mich nicht, dass Kael'thas den Schlüssel zu diesem Schutzschild in die Hände von jemandem wie Kommandantin Sarannis gegeben hat. Prinz Kael'thas mag vielleicht vieles sein, aber sicher kein Narr.]], [[Um diesen Fluch ein für alle Mal zu brechen, müsst Ihr in die Festung der Stürme gehen und den Schlüsselstein besorgen.]], [[Wenn der Schlüsselstein zerstört ist, werde ich meine Freiheit wiedererlangen.]], },
    { [[Reist zur Botanika der Festung der Stürme und besorgt Euch den Schlüsselstein von Kommandantin Sarannis. Bringt ihn danach zu Erzmagier Vargoth im Violetten Turm.]], },
  },
  [10258] = {
    [[Die Gefallenen ehren]],
    { [[Hogarth, der Kommandant des Rüstlagers, befindet sich im östlichen Turm. Ihr müsst ihm von Eurem Sieg über Urtrak berichten. Vielleicht ist er dann freundlich? Natürlich könnte er auch versuchen, Eure Seele zu fressen. Bei Geistern wie diesen weiß man nie.]], [[Die Unerschütterlichen sind uns immer noch feindlich gesonnen und werden versuchen zu töten, wenn sie können. Seid vorsichtig, <name>.]], },
    { [[Meldet Euch bei Hogarth, dem Kommandanten des Rüstlagers.]], },
  },
  [10259] = {
    [[In die Bresche springen]],
    { [[Die Kämpfe bei dem Portal nehmen kein Ende! Die Argentumdämmerung kämpft in einer verzweifelten Schlacht - sollten wir verlieren, wird das Feuer der Dämonen niemals erlöschen.]], [[Helft uns, <name>. Zieht in den Kampf und helft unseren Brüdern und Schwestern. Helft ihnen, sich gegen die Welle der Dunkelheit zu erheben! Die Argentumdämmerung - und die Welt - brauchen Euch!]], },
    { [[Tötet 6 einfallende Teufelswachen und kehrt dann zum Argentumabgesandten Stolzbrunn zurück.]], },
  },
  [10260] = {
    [[Netheorologe Kupfernickel]],
    { [[Ihr müsst über die Hauptbrücke vom Schergrat in den Nethersturm gekommen sein. Habt Ihr dort Netheorologe Kupfernickel mit seiner verrückten Ausrüstung gesehen?]], [[Merkwürdig, ich erwarte einen Bericht von ihm, und er ist schon lange überfällig. Wahrscheinlich hat er es vergessen. Ihr kennt ja diese Wissenschaftler.]], [[Geht zurück zur Brücke und nagelt ihn fest. Lasst Euch nicht mit Entschuldigungen abspeisen, bringt mir einfach seinen Bericht.]], [[Danke!]], },
    { [[Sprecht mit Netheorologe Kupfernickel bei der Brücke, die vom Nethersturm zum Schergrat führt.]], },
  },
  [10261] = {
    [[Gesucht: Vernichterservo!]],
    { [[GESUCHT: 1 Vernichterservo, um den kaputten in meinem langhalsigen Spektrumanalysator zu ersetzen.]], [[Ihr und Eure Freunde sollten bei den Blutelfen in der Manaschmiede B'naar im Westen nachschauen, ob sie ein paar Ersatzteile haben. Wie es scheint, haben sie dort ein paar Arkanvernichter.]], [[Bitte bringt den Servo in unbeschadetem Zustand zu Papa Mauschel in Area 52.]], },
    { [[Besorgt einen Vernichterservo und bringt ihn zu Papa Mauschel in Area 52 im Nethersturm.]], },
  },
  [10262] = {
    [[Ein Hügel voll Astraler]],
    { [[Es gibt noch etwas anders, bei dem ich Eure Hilfe brauche.]], [[Eine Gruppe von Astralen, die als Zaxxis bekannt sind, haben ihre Verbindungen zum Konsortium abgebrochen um ihre eigenen Geschäfte zu machen. Sie machen sogar Geschäfte mit den Blutelfen! Das können wir nicht hinnehmen!]], [[Ich habe ihre Gruppe bei dem Hügel im Süden unterwandert, aber als ich ihren Anführer ausgeschaltet hatte, musste ich zurück in die Sicherheit von Area 52 fliehen.]], [[<class>, geht dorthin und bringt mir ihre Insignien. Natürlich tragen nicht alle Insignien, weil sie Feiglinge sind.]], },
    { [[Sammelt 10 Insignien der Zaxxis und kehrt dann zu Netherpirscher Khay'ji in Area 52 im Nethersturm zurück.]], },
  },
  [10263] = {
    [[Dem Konsortium helfen]],
    { [[Die Naaru von Shattrath haben versucht, dem Konsortium beizubringen, die Dinge auf ihre Weise zu sehen.]], [[Das Glück wollte, dass die Geschäftsbeziehungen zwischen den Astralen und den Blutelfen im Nethersturm sich in letzter Zeit drastisch verschlechtert haben.]], [[Im Dorf gibt es einen Mitarbeiter des Konsortiums namens Khay'ji, der vor Kurzem ein paar Probleme mit einer Truppe von abtrünnigen Astralen hatte. Als Mitglied der Aldor möchte ich Euch bitten, ihm zu helfen, wo Ihr nur könnt.]], },
    { [[Sprecht mit Netherpirscher Khay'ji in Area 52 im Nethersturm.]], },
  },
  [10264] = {
    [[Dem Konsortium helfen]],
    { [[Die Naaru von Shattrath haben versucht, dem Konsortium beizubringen, die Dinge auf ihre Weise zu sehen.]], [[Das Glück wollte, dass die Geschäftsbeziehungen zwischen den Astralen und den Blutelfen im Nethersturm sich in letzter Zeit drastisch verschlechtert haben.]], [[Im Dorf gibt es einen Mitarbeiter des Konsortiums namens Khay'ji, der vor Kurzem ein paar Probleme mit einer Truppe von abtrünnigen Astralen hatte. Als Mitglied der Seher möchte ich Euch bitten, ihm zu helfen, wo Ihr nur könnt.]], },
    { [[Sprecht mit Netherpirscher Khay'ji in Area 52 im Nethersturm.]], },
  },
  [10265] = {
    [[Kristallsammlung des Konsortiums]],
    { [[Unser Anführer, Nexusprinz Haramad, hat mich gebeten, ein Kristallartefakt aus den Ruinen von Arklon im Südosten zu besorgen.]], [[Es sind alte Draeneiruinen. Zum Pech für mich und die Goblins hier hat die Brennende Legion beschlossen, selbst danach zu graben.]], [[Wenn Ihr mir weiterhin helfen wollt, nehmt dem Schreckenslord namens Pentatharon das Kristallartefakt ab.]], },
    { [[Besorgt ein Kristallartefakt von Arklon und bringt es zu Netherpirscher Khay'ji in Area 52 im Nethersturm.]], },
  },
  [10266] = {
    [[Bitte um Unterstützung]],
    { [[Während Ihr in den Ruinen von Arklon wart, habe ich eine Bitte um Unterstützung von einem unserer Mitarbeiter im Feld erhalten. Wie es scheint hat das Konsortium einige Probleme damit, Waren von den Blutelfen zurückzufordern, die sie nicht bezahlen wollen.]], [[Bitte begebt Euch von Area 52 aus nach Osten und überquert die Brücke zur nächsten Insel. Ihr findet meinen Gefährten Gahruj im Mittelreichposten in der Biokuppel Mittelreich.]], },
    { [[Sucht Gahruj und bietet ihm Eure Hilfe an. Er befindet sich im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10267] = {
    [[Rechtmäßiger Besitz]],
    { [[Unsere Geschäftsbeziehungen mit den Blutelfen scheinen zu Ende zu sein. Vor ein paar Wochen wurde ihnen bei der Manaschmiede Duro eine große Ladung mit Vermessungsausrüstung geliefert. Sie haben sich geweigert, dafür zu bezahlen.]], [[<name>, ich möchte, dass Ihr den Blutelfen in der Manaschmiede Duro im Osten einen Besuch abstattet. Sie scheinen ein paar Probleme mit angreifenden Manakreaturen zu haben, also nutzt aus, dass sie beschäftigt sind.]], [[Mir ist egal, was mit ihnen passiert, aber ich hätte meinen rechtmäßigen Besitz gerne zurück.]], },
    { [[Sammelt 10 Kästen mit Vermessungsausrüstung und bringt sie zu Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10268] = {
    [[Eine Audienz beim Prinzen]],
    { [[Nexusprinz Haramad hat mir zu verstehen gegeben, dass er seinen Besitz unverzüglich und persönlich zurückerhalten möchte. Ich wäre Euch sehr dankbar, wenn Ihr ihm die Waren an meiner statt zurückbringen könntet.]], [[Eine Audienz beim Nexusprinzen ist eine große Ehre, und ich bin mir sicher, dass er sehr gerne etwas über Eure Abenteuer im Nethersturm erfahren würde.]], [[Sucht ihn in seiner Kammer oben in der Sturmsäule im Norden auf. Er ist oft nur als holografisches Bild anwesend, dass Ihr ansprechen könnt.]], },
    { [[Bringt die Vermessungsausrüstung zum Bild von Nexusprinz Haramad in der Sturmsäule im Nethersturm.]], },
  },
  [10269] = {
    [[Triangulationspunkt Eins]],
    { [[Da ist noch ein anderer Grund, warum ich die Vermessungsausrüstung zurückhaben wollte. Es gibt da einen Kristall aus einer alten Draeneilegende, der mir sehr wichtig ist.]], [[Man sagt, dass der Kristall seinem Träger gottgleiche Macht verleiht! Aber es besteht die Gefahr, dass jemand anderes ihn ausgräbt, nämlich die Brennende Legion.]], [[<name>, nehmt die Ausrüstung und benutzt sie, um den ersten Triangulationspunkt zu finden. Wir wissen, dass er sich irgendwo östlich der Manaschmiede Ultris befinden muss.]], [[Meldet Euch danach bei meinem Agenten, Händler Hazzin, auf der Insel der Manaschmiede Ultris.]], },
    { [[Benutzt das Triangulationsgerät, um den Weg zum ersten Triangulationspunkt zu bestimmen. Wenn Ihr ihn gefunden habt, meldet die Position an Händler Hazzin beim Wachposten des Protektorats auf der Insel der Manaschmiede Ultris im Nethersturm.]], },
  },
  [10270] = {
    [[Ein nicht-so-bescheidenes Angebot]],
    { [[Aus Eurem Gesicht kann ich lesen, dass Ihr <ein/eine><race> <race> mit Sinn fürs Geschäft seid.]], [[Ich habe mir einen Ruf als Händler für exotische und teure Waren erarbeitet, und bin gerade dabei, ein neues Unternehmen zu starten.]], [[Ich könnte einen fähigen Unterhändler brauchen, um die Waren vom Sternensturz weiterzuleiten.]], [[Zuerst müsst Ihr eine Teleporterplattform wieder aufladen. Im Zugangsschacht Zeon auf der südlichen Seite der Manaschmiede Ultris findet Ihr eine Ersatzenergiezelle. Das Protektorat befindet sich zurzeit dort, deshalb könnt Ihr einfach ihren Signalgebern zu Eurem Ziel folgen.]], },
    { [[Besorgt Euch die Teleporterenergiezelle und bringt sie zur Astralteleportplattform im Sternensturz östlich der Manaschmiede Ultris. Aktiviert die Plattform und bringt die Teleporterenergiezelle zu Windhändler Marid, damit er sie einbauen kann.]], },
  },
  [10271] = {
    [[Zur Sache]],
    { [[Wir Ihr sicher gesehen habt, wimmelt es in dieser Gegend von Netherdrachen aller Altersstufen. Alle Netherdrachen, die jungen wie die alten, verfügen über eine Essenz, die bei K... - ähh, meinem Kunden sehr begehrt ist.]], [[Die Meisten sind nicht bereit, dafür ihr Leben aufs Spiel zu setzen, deswegen haben wir die Gegend hier ganz für uns alleine. Ihr könnt damit anfangen, die Essenz von allen Drachen hier zu sammeln, aber es ist von größter Notwendigkeit, dass wir sofort einen steten Nachschub aufbauen. Kehrt zu mir zurück, wenn Ihr eine erste Ladung zusammen habt.]], },
    { [[Ladet den Essenzsammler auf, indem Ihr ihn bei Netherdrachen und Nethergroßdrachen auf dem Sternensturz anwendet, und kehrt dann zu der Gegend um die Astralteleportplattform zurück.]], },
  },
  [10272] = {
    [[Ein viel versprechender Anfang]],
    { [[Wir sind hierhergekommen, um den Gerüchten über eine merkwürdige Drachenbrut, die vielleicht die Nachkommen von Todesschwinge selbst sein könnten, nachzugehen. Ganz offensichtlich handelt es sich nicht um gewöhnliche schwarze Drachen.]], [[Wenn wir eine Gelegenheit hätten, ihre Eier zu untersuchen, könnten wir sicher mehr über sie herausfinden. Ihr Hass auf meinesgleichen sitzt so tief, dass ich nicht nahe genug herangehen kann, um die Eier selbst zu sammeln. Werdet Ihr mir helfen?]], },
    { [[Sammelt 15 Netherdrachkineier und kehrt dann zu Tyri und Jorad im Sternensturz zurück.]], },
  },
  [10273] = {
    [[Störende Ablenkung]],
    { [[Your former employer, Wind Trader Marid, must've gotten wind that you're helping us now. Through my projection, I've seen his nether-stalkers entering the area, hoping to track us down.]], [[We've been able evade them so far, but Marid must be dealt with before the problem grows more severe.]], [[If your impressions of Marid are correct, he'll do just about anything to secure the supply of his product. Return to him and tell him you're prepared to offer a deal. Lure him away and slay him.]], },
    { [[Sprecht mit Windhändler Marid und lockt ihn von seinem Handelsposten fort. Vernichtet ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück.]], },
  },
  [10274] = {
    [[Den Sternensturz sichern]],
    { [[Die Netherdrachen verfügen über dasselbe wilde Temperament wie ihre Vorfahren, die schwarzen Drachen. Etwas an ihnen ist jedoch vollkommen anders. Sie schlüpfen schnell und werden schnell reif, indem sie die fremdartigen Energien, die ihre Art zur Zerstörung Draenors getrieben hat, nutzen.]], [[Es gibt so Vieles, was ich noch über sie in Erfahrung bringen muss, aber ich glaube, dass die Mächte für meinen eigenen Schwarm von Vorteil sein könnten. Wenn wir den Anführer dieser Drachen, Veraku, als Zeichen unserer Dominanz vernichten könnten, könnten wir den Rest vielleicht so einschüchtern, dass sie sich uns unterwerfen.]], },
    { [[Benutzt die Herausforderung des blauen Drachenschwarms um Veraku am südlichen Ende des Sternensturzes zu provozieren. Besiegt ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück.]], },
  },
  [10275] = {
    [[Triangulationspunkt Zwei]],
    { [[Ich habe das Triangulationsgerät mit den Informationen des ersten Punkts neu eingestellt. Ihr könnt es genauso wie beim ersten Mal benutzen, um den zweiten Punkt zu bestimmen.]], [[Alles, was ich Euch sagen kann, ist, dass der zweite Punkt sich weit im Westen befindet.]], [[Wenn Ihr ihn gefunden habt, sprecht mit Windhändler Tuluman, ein weiterer Agent des Nexusprinzen. Er steht als Waffenhändler getarnt in einem kleinen Handelsposten neben der Brücke, die zur Insel, auf der sich die Manaschmiede Ara befindet, führt.]], },
    { [[Benutzt das Triangulationsgerät, um den Weg zum zweiten Triangulationspunkt zu bestimmen. Wenn Ihr ihn gefunden habt, meldet die Position an Windhändler Tuluman auf Tulumans Landeplatz auf der anderen Seite der Brücke zu der Insel der Manaschmiede Ara im Nethersturm.]], },
  },
  [10276] = {
    [[Das volle Dreieck]],
    { [[Mit den Daten vom ersten und zweiten Punkt kann ich sicher sagen, dass der Kristall sich auf den Ruinen von Farahlon im Nordosten befindet.]], [[Unglücklicherweise sagen die Daten, dass der Kristall sich bewegt, was bedeutet, dass er bereits gefunden wurde. Wahrscheinlich von der Brennenden Legion!]], [[Falls das tatsächlich so ist, empfehle ich Euch, ein paar Freunde mitzunehmen.]], [[Tötet den Dämon, der den Kristall hat, und bringt ihn zu Nexusprinz Haramad.]], },
    { [[Besorgt Euch den Kristall von Ata'mal und bringt ihn zu Nexusprinz Haramad in der Sturmsäule im Nethersturm.]], },
  },
  [10277] = {
    [[Die Höhlen der Zeit]],
    { [[Grüße, <name>. Bitte folgt der Bewahrerin. Sie wird ein wenig... Licht in die Angelegenheit bringen.]], },
    { [[Andormu in den Höhlen der Zeit möchte, dass Ihr der Bewahrerin der Zeit durch die Höhle folgt.]], },
  },
  [10278] = {
    [[Sphärenrisse]],
    { [[Ich bin lange auf der Suche nach der Wahrheit der Leere durch die Scherbenwelt gereist. Jetzt bin ich alt und schwach. Ich sehe merkwürdige Dinge und bin nicht mehr ich selbst. Vielleicht wollt Ihr mir helfen?]], [[Ich muss die Geheimnisse der Leerwandler aufdecken. Westlich des Expeditionsrüstlagers befindet sich ein Ort namens Sphärenfelder. Zeit und Raum sind dort sehr schwach, da die Felder nahe beim Abgrund liegen. Geht dorthin und benutzt den instabilen Sphärenrissgenerator. Er wird einen Sphärenriss erzeugen. Ihr müsst den Sphärennether von den Kreaturen, die durch ihn herauskommen, sammeln.]], },
    { [[Benutzt den instabilen Sphärenrissgenerator in den Sphärenfeldern. Sammelt 3 Einheiten Sphärennether von instabilen Leerwandlern und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück.]], },
  },
  [10279] = {
    [[Zum Hort des Meisters]],
    { [[Ah, <name>. Wir haben Euch erwartet. Bitte erlaubt, dass einer meiner Brüder Euch zum Meister fliegt. Er wartet unten auf Euch...]], },
    { [[Sprecht mit Andormu in den Höhlen der Zeit.]], },
  },
  [10280] = {
    [[Sonderlieferung nach Shattrath]],
    { [[Ich bin ein Astraler, der sein Wort hält, <name>. Ihr habt mich wirklich inspiriert.]], [[Ich werde Euch den Kristall von Ata'mal geben, damit Ihr ihn den Naaru überreicht. Da Euer Heldenmut dies möglich gemacht hat, soll Euch die Ehre zuteilwerden, ihn zu übergeben.]], [[Ihr könnt meinen persönlichen Teleporter benutzen, um direkt nach Shattrath zu gelangen. Tretet einfach hindurch und er wird Euch dorthin bringen.]], [[Gebt den Kristall dem Naaru namens A'dal auf der Terrasse des Lichts.]], },
    { [[Bringt den Kristall von Ata'mal zu A'dal auf der Terrasse des Lichts in Shattrath.]], },
  },
  [10281] = {
    [[Formelle Vorstellung]],
    { [[Grüße, <name>. Keine Angst, dies ist nur eine Projektion meiner gegenwärtigen Form. Es ist die einzige Möglichkeit, wie mein Gefährte und ich Wache halten können, ohne uns selbst zu offenbaren.]], [[Der Astrale, der Euch angesprochen hat, Marid, ist nur ein Schwindler, der sich als anständiger Händler ausgibt. Überlegt Euch daher, ob Ihr nicht lieber mit mir zusammenarbeiten wollt. Mein Ziel ist es, mehr über die ungewöhnlichen Drachkin des Sternensturzes herauszufinden. Trefft mich am nördlichen Ende des Sturzes, damit wir über die Details sprechen können.]], },
    { [[Sucht Tyri am nördlichen Ende des Sternensturzes und sprecht mit ihr.]], },
  },
  [10282] = {
    [[Das Alte Hügelland]],
    { [[Im Westen findet Ihr die Pfade der Zeit in das Hügelland der Vergangenheit - vor 7 Jahren um genau zu sein.]], [[Die Streitmächte kämpfen gegen den Kriegshäuptling der Horde, Thrall, um ihn davon abzuhalten, aus der Burg Durnholde zu fliehen.]], [[Wenn es Thrall nicht gelingt zu fliehen, wird die Welt, wie Ihr sie jetzt kennt, aufhören zu existieren.]], [[Ruft eine Gruppe von Abenteurern zusammen und reist auf dem Pfad der Zeit ins Alte Hügelland. Zwei unserer Agenten warten dort auf Euch.]], [[Sprecht mit Erozion auf der anderen Seite.]], },
    { [[Andormu in den Höhlen der Zeit bittet Euch, ins Alte Hügelland zu reisen und mit Erozion zu sprechen.]], },
  },
  [10283] = {
    [[Tarethas Ablenkungsmanöver]],
    { [[Taretha spielte eine entscheidende Rolle bei Thralls Überleben und Erziehung und prägte Thralls Leben maßgeblich.]], [[Es war Taretha, die die Wachen von Durnholde ablenkte, damit Thrall entkommen konnte.]], [[Jetzt haben die Ewigen Taretha gefangen und halten sie in Tarrens Mühle fest. Ohne unsere Einmischung wird das Ablenkungsmanöver niemals stattfinden. Ihr müsst den Brandsatz, den ich Euch gegeben habe, nehmen und die Internierungsbaracken in Durnholde anzünden. Wenn Ihr das erledigt habt, sprecht mit dem jungen Thrall im Kellergefängnis der Burg.]], },
    { [[Reist zur Burg Durnholde und platziert mithilfe des Bündels mit Brandbomben, das Ihr von Erozion erhalten habt, 5 Brandsätze auf den Fässern in jeder Internierungsbaracke.]], [[Sprecht mit Thrall im Kellergefängnis der Burg Durnholde, wenn Ihr die Internierungsbaracken angezündet habt.]], },
  },
  [10284] = {
    [[Flucht aus Durnholde]],
    { [[Ihr schaut mich an und seht nur einen Jungen, aber tief in meinem Herz und meiner Seele halte ich meine Träume versteckt. Ich habe Pläne für mein Volk, Fremder. Eines Tages werden sie ihre Unterdrücker überwältigen und in Einheit auferstehen. Sie werden ihre Fesseln abwerfen und aus diesem Aufstand soll die orcische Horde entstehen.]], [[Ich verspreche Euch, Fremder, dass ich Euch suchen werde, wenn wir heil hier herauskommen und ich die Möglichkeiten habe, Eure selbstlose Tat zu belohnen... Ich werde Euch finden.]], [[<Thrall nickt.>]], [[Wir fliehen, wenn Ihr bereit seid.]], },
    { [[Gebt Thrall Bescheid, wenn Ihr bereit seid. Folgt Thrall aus der Burg Durnholde und helft ihm, Taretha zu befreien und sein Schicksal zu erfüllen.]], [[Sprecht mit Erozion im Alten Hügelland, wenn Ihr diese Aufgabe erfüllt habt.]], },
  },
  [10285] = {
    [[Rückkehr zu Andormu]],
    { [[Diese Zeitachse wurde wiederhergestellt und der Angriff des ewigen Drachenschwarms auf das Hügelland vorerst abgewendet.]], [[Kehrt sofort zu Andormu zurück. Er wartet im Hort des Meisters auf Eure Ankunft.]], },
    { [[Kehrt zu dem jungen Andormu in den Höhlen der Zeit in Tanaris zurück.]], },
  },
  [10286] = {
    [[Arelions Geheimnis]],
    { [[Arelions Tagebuch enthält überhaupt keine wissenschaftlichen Studien. Er beschreibt seine Eskapaden mit einer jungen Blutelfe! Wie konnte ich nur so dumm sein?]], [[Unglücklicherweise nennt das Tagebuch den Namen des Flittchens nicht. Aledis, ein Kollege von Arelion, war sein bester Freund und Vertrauter. Befragt ihn und findet heraus, mit wem er eine Affäre hatte.]], [[Ich habe gesehen, wie Aledis sich vor Kurzem aus der Falkenwacht in Richtung Zangarmarschen aufgemacht hat. Folgt der Straße nach Norden aus dem Dorf heraus. Sie biegt nach einer Weile nach Westen in Richtung Zangarmarschen ab.]], },
    { [[Magistrix Carinda möchte, dass Ihr Magister Aledis befragt. Aledis wurde zuletzt auf einer Reise von der Falkenwacht zu den Zangarmarschen gesehen.]], },
  },
  [10287] = {
    [[Die enttarnte Geliebte]],
    { [[Ich bin Arelions Freund, aber ich werde für ihn nicht die Prügel meines Lebens einstecken. Seine Geliebte ist Viera Sonnenwisper.]], [[Sagt das seinem Drachen von Ehefrau. Was will sie schon dagegen tun?]], },
    { [[Return to Magistrix Carinda at Falcon Watch.]], },
  },
  [10288] = {
    [[Ankunft in der Scherbenwelt]],
    { [[Ich möchte, dass Ihr mit Amish Wildhammer sprecht. Ihr findet ihn etwa 30 Schritt südlich von hier. Nehmt den ersten Flug zur Ehrenfeste. Wenn Ihr dort angekommen seid, werdet Ihr von Marschall Isildor begrüßt werden. Gebt ihm diese Empfehlung, in der ich notiert habe, wo Ihr meiner Meinung nach am hilfreichsten sein könntet.]], [[Ich habe Euch einen Befehl erteilt, Soldat, nun fort mit Euch! Und auch wenn Ihr darauf brennt, uns im Kampf gegen den Feind hier zu unterstützen, dürft Ihr keine Zeit verschwenden. Die Ehrenfeste braucht Euch sogar noch mehr als wir.]], },
    { [[Kommandant Duron beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Durons Bericht zu Flugmeister Amish Wildhammer bringt.]], },
  },
  [10289] = {
    [[Reise nach Thrallmar]],
    { [[Wenn Ihr bereit seid, rufe ich einen Wyvern, der Euch nach Thrallmar bringt. Wenn Ihr angekommen seid, bringt Orions Bericht zu General Karkork.]], [[Lok'tar ogar, <name> - Viel Glück.]], },
    { [[Nehmt Vlagga Freyfeders Windreiter nach Thrallmar. Bringt Orions Bericht zu General Krakork.]], },
  },
  [10290] = {
    [[Auf der Suche nach Farahlit]],
    { [[Im Nordosten gibt es eine Region, die Netherstein genannt wird. Früher war sie unter einem fruchtbaren und grünen Land verborgen. Die neu gefundenen Kristallformationen haben uns ein Mineral gezeigt, das wir zur Ehre der früheren Identität des Landes Farahlit genannt haben.]], [[Die kleine Menge Farahlit, die wir bis jetzt abgebaut haben, erfreut sich jetzt schon großer Beliebtheit bei unseren Kunden. Das hochwertigste Mineral stammt jedoch nicht von den Ablagerungen, sondern von den Riesen, die um den Netherstein herum leben. Ich biete Euch einen Teil des Gewinns an, wenn Ihr mir helft, hochwertiges Farahlit zu besorgen.]], },
    { [[Sammelt 4 Stück Rohfarahlit und kehrt dann zu Zuben Elgenubi in der Sturmsäule zurück.]], },
  },
  [10291] = {
    [[Bericht an Nazgrel]],
    { [[Auch wenn unsere Krieger keine ebenbürtigen Gegner haben, ist unsere Stellung in diesem Ödland immer noch angreifbar. Meldet Euch bei Nazgrel in der Kaserne. Er braucht gute Agenten... wie Euch. Wenn wir unsere Stellung hier nicht halten können, sind wir für niemanden eine Hilfe! Geht jetzt.]], },
    { [[General Krakork in Thrallmar möchte, dass Ihr mit Nazgrel sprecht.]], },
  },
  [10292] = {
    [[Mehr Energie!]],
    { [[Ein wertvolles Gut in kleinen Häppchen zu sammeln hat das malerische Flair eines Familienunternehmens, aber auf dem heutigen Markt ist es einfach nicht konkurrenzfähig.]], [[Nein, wir brauchen einen Weg, Farahlit in Massenproduktion zu beschaffen! Dann wäre der Hauptanteil des Marktes unser!]], [[Ich habe eine Idee, aber sie erfordert eine mächtige, konzentrierte Energiequelle. Solch eine Energiequelle kann man nur an einem Ort finden. Geht zu den Lagern der Legion nördlich von hier und besorgt Euch ein paar ihrer Schusswaffen.]], },
    { [[Zuben Elgenubi möchte, dass Ihr 8 Schusswaffen der Legion besorgt und sie zur Sturmsäule bringt.]], },
  },
  [10293] = {
    [[Die Kristallader anzapfen]],
    { [[Jetzt, da wir ein solides Grundprodukt haben, benötigen wir etwas von höherer Qualität. Das Farahlit, das wir bisher gesammelt haben, ist zwar ganz nett, aber es gibt noch besseres da draußen.]], [[Wir müssen eine Probe direkt von Felsnarbe, dem Patriarchen der Riesen von Farahlon, besorgen! Ihr solltet ihn bei den anderen Riesen in der Gegend um den Nethersteine herum finden können. Aus seinem Rücken ragen große Farahlitstücke heraus; genau was wir brauchen. Tötet den Riesen und sammelt einen Farahlitkern von ihm.]], },
    { [[Zuben Elgenubi in der Sturmsäule möchte, dass Ihr einen Farahlitkern besorgt.]], },
  },
  [10294] = {
    [[Der Leerengrat]],
    { [[Wir müssen meinen Sphärenrissgenerator verbessern. Ihr müsst ihn zum Leerengrat bringen. Geht von hier aus nach Osten, um die verdorbene Festung Zeth'Gor herum. Im Osten hinter Zeth'Gor befindet sich ein kleiner Pass durch die Berge. Hinter diesem Pass findet Ihr den Leerengrat, einen schrecklichen Ort direkt am Abgrund.]], [[Ihr müsst die Wandler, die Ihr dort findet, töten und mir Splitter ihrer Seele bringen. Mit ihrer Macht werden wir den Sphärenrissgenerator verbessern und ein Portal in die jenseitige Dimension öffnen, das nicht mehr geschlossen werden kann.]], },
    { [[Geht zum Leerengrat und tötet die Kreaturen, die Ihr dort findet. Sammelt 40 Seelensplitter des Leerengrats und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück.]], },
  },
  [10295] = {
    [[Aus dem Abgrund]],
    { [[Mein Sphärenrissgenerator ist fertig. Ihr könnt damit jetzt einen wahren Prinzen der Leere rufen, eine Kreatur, deren Seelensplitter uns deutlich mehr darüber verraten wird, was dahinter liegt.]], [[Geht nach Süden in den Leerengrat und benutzt meinen Sphärenrissgenerator in der Nähe der Steine der Leere. Haltet nach einer Gruppe von Kristallen am Ende des Abgrunds Ausschau. Ein riesiger Sphärenriss wird sich auftun, aus dem dann sicher der Baron der Leere Galaxis entsteigen wird.]], [[Zerstört diese Kreatur und bringt mir seinen Seelensplitter.]], },
    { [[Tötet Galaxis und sammelt seinen Seelensplitter. Bringt den Splitter zu Ogath dem Verrückten im Rückenbrecherposten.]], },
  },
  [10296] = {
    [[Der Schwarze Morast]],
    { [[Der ewige Drachenschwarm versucht nun nicht einmal mehr, seine Pläne geheim zu halten. Sie haben vor, den Wächter, Medivh, zu töten und zu verhindern, dass das Dunkle Portal jemals geöffnet wird. Auf den ersten Blick scheint das ein Gefallen für die Streitkräfte der Allianz zu sein, aber tatsächlich wären die Folgen katastrophal und würden in der Zerstörung Eurer Welt enden.]], [[Ihr müsst auf dem Pfad der Zeit zum Schwarzen Morast reisen. Geht zuerst nach Westen und dann nach Süden, vorbei an meinem zukünftigen Ich, bis Ihr zu dem Tunnel kommt, in dem die Sumpfwesen leben. Durchquert ihn und sprecht mit Sa'at.]], },
    { [[Reist durch die Höhlen der Zeit in den Schwarzen Morast während der Öffnung des Dunklen Portals und sprecht mit Sa'at.]], },
  },
  [10297] = {
    [[Die Öffnung des Dunklen Portals]],
    { [[Hinter diesem Pfad der Zeit, im Herzen des Schwarzen Morasts, arbeitet Medivh verbissen an der Öffnung des Dunklen Portals.]], [[Überall im Morast werden sich Zeitlöcher öffnen, aus denen die Ewigen herausströmen werden mit dem Ziel, Medivhs Schilde und schließlich auch Medivh selbst zu vernichten.]], [[Tötet die Lords des Zeitlochs und ihre Anführer, bis Medivh erfolgreich war.]], },
    { [[Sa'at im Schwarzen Morast der Höhlen der Zeit möchte, dass Ihr Medivh verteidigt, bis er das Dunkle Portal geöffnet hat.]], [[Kehrt zu Sa'at zurück, wenn Ihr erfolgreich wart.]], },
  },
  [10298] = {
    [[Held des Schwarms]],
    { [[Kehrt zum Kind, Andormu, zurück, <name>. Er erwartet Euch außerhalb dieser Zeitfalte im Hort des Meisters.]], [[Ich höre Flügelschlagen. Die Wächter der Sande interessieren sich für Eure Fähigkeiten. Vielleicht möchten auch sie mit Euch sprechen.]], },
    { [[Kehrt zum Kind, Andormu, in den Höhlen der Zeit in Tanaris zurück.]], },
  },
  [10299] = {
    [[Manaschmiede B'naar abschalten]],
    { [[Wir können die Manaschmieden nicht mit Gewalt abschalten, da wir sonst eine Explosion auslösen könnten, die die Scherbenwelt bis in den Kern erschüttern würde.]], [[Laut Eurer Abschrift gibt es einen Notschalter, der die Technologie dauerhaft abschaltet - ein Sicherheitsmechanismus, der bei der Technologie der Naaru Standard ist.]], [[Für diesen Vorgang braucht Ihr einen Kristall, der Euch die Kommunikation mit der Konsole ermöglicht. Dieser Zugangskristall befindet sich unseren Informationen zufolge im Besitz eines Vorarbeiters namens Theredis.]], },
    { [[Anachoret Karja möchte, dass Ihr in die Manaschmiede B'naar geht und einen Zugangskristall von Vorarbeiter Theredis besorgt. Benutzt ihn bei der Steuerkonsole von B'naar, um sie abzuschalten und meldet Euch danach bei Anachoret Karja zurück.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10300] = {
    [[Den Stab wiederherstellen]],
    { [[<Ravandwyr untersucht den Stab und bemerkt, dass das Kopfstück fehlt.>]], [[Das ist einfach barbarisch! Ekkorash muss das kristallene Kopfstück abgenommen haben, um es mit einem anderen Schmuckstück zu verwenden, und hat gar nicht gemerkt, dass es zum Rest des Stabs dazugehört!]], [[Solange er nicht komplett ist, ist die Macht des Stabs stark verringert. Ich könnte ihn jedoch mit einem geeigneten Draeneikristall reparieren. Ich habe gehört, wie die Goblins von den Kristallen der Geister in den Ruinen von Enkaat im Norden gesprochen haben.]], },
    { [[Besorgt 4 fehlerlose Kristallsplitter von den Geistern in den Ruinen von Enkaat und bringt sie zu Ravandwyr in Area 52.]], },
  },
  [10301] = {
    [[Das Kompendium öffnen]],
    { [[Auch wenn ich froh bin, dass ich das meiste von Krasus' Kompendium zurück habe, ist es ohne das heliotrope Oculus nutzlos. Seht Ihr, Krasus war sich der unermesslichen Macht des Buchs bewusst und hat Vorkehrungen getroffen, damit es vor gierigen Blicken geschützt bleibt.]], [[Nur ein Magier mit großem Wissen kann dem Kompendium mithilfe des Oculus' seine Geheimnisse entlocken.]], [[Während Kael'thas' Angriff hat einer seiner Leutnants, Zauberhäscherin Marathelle, das Octulus aus dem Turm gestohlen und es zum Sonnenzornposten gebracht. Sie hält es sicher für ein hübsches kleines Schmuckstück.]], },
    { [[Obtain the Heliotrope Oculus from Spellreaver Marathelle at Sunfury Hold. Use Archmage Vargoth's Staff to contact the archmage once you have achieved your goal.]], [[If you lose Archmage Vargoth's Staff, speak to Ravandwyr at Area 52 to receive a replacement.]], },
  },
  [10302] = {
    [[Unberechenbare Mutanten]],
    { [[Die verstrahlten Kraftkerne der Exodar haben der Umwelt hier verheerenden Schaden zugefügt. Die deutlichste Auswirkung ist das Auftauchen merkwürdiger Kreaturen, die anscheinend in ihrer Nähe wie Pilze aus dem Boden schießen.]], [[Vielleicht gibt es etwas, was wir für sie tun können, aber im Augenblick sind sie einfach zu viele für uns.]], [[Ich möchte, dass Ihr losgeht und ihre Zahl reduziert. Vielleicht fällt mir danach eine andere Lösung ein.]], },
    { [[Tötet 8 unberechenbare Mutanten und kehrt dann zu Botanikerin Taerix bei der Absturzstelle im Am'mental zurück.]], },
  },
  [10303] = {
    [[Die Blutelfen]],
    { [[Ihr müsst... die Bedrohung durch die Blutelfen... auslöschen. Findet heraus... was sie... vorhaben!]], [[Sie sind auf dem... Kamm hinter mir.]], },
    { [[Tötet 10 Blutelfenspäher und kehrt dann zu Tolaan im Schattenkamm im Am'mental zurück.]], },
  },
  [10304] = {
    [[Verteidiger Aldar]],
    { [[Mit Eurer Hilfe konnten wir schneller, als wir gehofft hatten, eine Lösung für unser Problem finden.]], [[Ich habe keine weitere Arbeit für Euch, meine Lehrlinge schaffen den Rest alleine.]], [[Ihr solltet mit Verteidiger Aldar auf der anderen Seite des Schiffs sprechen.]], },
    { [[Sprecht mit Verteidiger Aldar bei der Absturzstelle im Am'mental.]], },
  },
  [10305] = {
    [[Bannzauberin Belmara]],
    { [[Dieser abgenutzte, dicke Foliant ist kein Zauberbuch, wie Ihr es vielleicht von einem Magier der Kirin Tor erwartet hättet. Die Seiten dieses Bandes sind voll von handgeschriebenen Geschichten mit bunten Zeichnungen.]], [[Der Foliant scheint viel älter zu sein, als seine Besitzerin und stammt vielleicht aus ihrem Familienerbe. Möglicherweise war das starke Band zu diesem Buch das, was Belmaras Geist an die Ruinen von Kirin'Var gefesselt hat. Wenn Ihr es auf seinen rechten Platz im Bücherregal zurückbringt, kommt ihr Geist vielleicht zur Ruhe.]], },
    { [[Sucht die Wohnung von Bannzauberin Belmara in der Zaubergasse und bringt das Buch zurück auf seinen Platz im Bücherregal. Sprecht danach mit Hüter Dierwert in Kirin'Var.]], },
  },
  [10306] = {
    [[Beschwörer Luminrath]],
    { [[Der Mantel, der ohne Zweifel von Hochelfen geschneidert wurde, ist sogar für die Verhältnisse der Kirin Tor reich verziert. Die Symbole und Zeichen ergeben für Euch keinen Sinn, aber sie scheinen die Kleidung eindeutig mit Magie zu verstärken.]], [[Vielleicht bindet dieser Gegenstand seinen früheren Besitzer an diese Existenzebene. Wenn das so ist, würde die Verbindung des Geistes mit dieser Welt vielleicht zerbrechen, wenn Ihr den Mantel zurück in die Kommode in Beschwörer Luminraths Wohnung bringt.]], },
    { [[Sucht die Wohnung von Beschwörer Luminrath in der Zaubergasse und bringt den Mantel zurück in seine Kommode. Sprecht danach mit Hüter Dierwert in Kirin'Var.]], },
  },
  [10307] = {
    [[Cohlien Frostwirker]],
    { [[Während Ihr die Kappe betrachtet, fragt Ihr Euch, ob ein Magier der Kirin Tor überhaupt so einen Hut besitzen würde. Der Zustand der Kappe lässt darauf schließen, dass der Gnom das Haus nie ohne sie verlassen hätte.]], [[Ihr probiert den Hut kurz auf und bemerkt keine magischen Eigenschaften außer einem schwachen Zwang zu tanzen.]], [[Vielleicht bindet dieser geschätzte Besitz den Geist des toten Magiers an das Dorf. Wenn Ihr die Kappe zurück in die Schließkiste in Cohliens Wohnung legen würdet, könntet Ihr den Geist des Magiers vielleicht befreien.]], },
    { [[Sucht die Wohnung von Cohlien Frostwirker in der Zaubergasse und bringt die Kappe zurück in seine Schließkiste. Sprecht danach mit Hüter Dierwert in Kirin'Var.]], },
  },
  [10308] = {
    [[Noch ein Hügel voller Astraler]],
  },
  [10309] = {
    [[Teufelshäscher mit Herz]],
    { [[Seht Ihr den kaputten Teufelshäscher im Süden? Ist er nicht schön?]], [[Ich möchte sein Herz!]], [[Was? Natürlich hat er ein Herz... wer hat denn keins?]], [[<N.D. sieht für einen kurzen Moment beleidigt aus.>]], [[Geht dort hinüber, öffnet ihn und bringt mir, was ich möchte, dann bekommt Ihr auch einen schönen Finderlohn.]], [[Oh, und ich würde an Eurer Stelle ein oder zwei Freunde mitnehmen. Diese Astralen der Zaxxis sind eine ziemlich verschlagene Truppe!]], [[Nehmt diesen Teufelsschocker, um seinem Herz einen Stromimpuls zu geben, wenn Ihr bereit seid, loszulegen.]], },
    { [[Besorgt das Herz des Teufelshäschers und bringt es zu N. D. Mieskamp beim Hügel im Nethersturm.]], },
  },
  [10310] = {
    [[Sabotage am Warptor!]],
    { [[Jetzt, da die Brennende Legion sich auf dem Trümmerfeld der Teufelshäscher in der Nähe befindet, müssen wir alles tun, um den Zuwachs der Dämonen in dieser Gegend aufzuhalten.]], [[Sie haben ein Warptor im Süden in einer Gegend, die sie bezeichnenderweise Invasionspunkt: Zerstörer genannt haben.]], [[Ich möchte, dass Ihr mit mir dorthin geht und mich beschützt, während ich das Tor sabotiere.]], [[Geht danach zum Mittelreichposten in der Biokuppel Mittelreich im Norden und meldet Gahruj unseren Erfolg.]], [[Wir werden ein oder zwei Freunde von Euch für diesen Auftrag brauchen.]], },
    { [[Begleitet Drijya zum Warptor beim Invasionspunkt: Zerstörer und sorgt für seine Sicherheit, während er das Tor sabotiert. Sprecht danach mit Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10311] = {
    [[Drijya braucht Eure Hilfe]],
    { [[<class>, schön ein hilfreiches Gesicht hier zu sehen.]], [[Die Brennende Legion bei dem Teufelshäscherhügel hier in der Nähe wird immer mutiger, und ich mache mir Sorgen um unsere Sicherheit. Die Dämonen erhalten immer mehr Verstärkung aus dem Invasionspunkt: Zerstörer im Süden.]], [[Mein Gefährte, Drijya, hat sich dorthin aufgemacht, um die Gegend zu erkunden und zu schauen, ob er etwas gegen die Warptore der Legion ausrichten kann.]], [[Bitte findet ihn und helft ihm, wo Ihr könnt.]], },
    { [[Sprecht mit Drijya in der Nähe des Invasionspunkts: Zerstörer im Nethersturm.]], },
  },
  [10312] = {
    [[Die Annalen von Kirin'Var]],
    { [[Die Geister zum Schweigen zu bringen hat leider nichts an Ihrer Aggressivität geändert. Die bösartigen Geister, die Ihr vernichtet habt, tauchen nun wieder in den Straßen des Dorfs auf.]], [[Wir müssen herausfinden, was dahinter steckt. Die Zerstörung Kirin'Vars war nur der Anfang, sonst hätten sich die Geister schon längst beruhigt.]], [[Im Rathaus im Süden von Kirin'Var gibt es ein Stadtregister, in dem alle Einwohner eingetragen sind. Ich habe gehört, dass Kampfmagier Dathric es zuletzt hatte. Vielleicht trägt sein Geist es noch mit sich herum.]], },
    { [[Besorgt das Stadtregister von Kirin'Var aus dem Rathaus von Kirin'Var und bringt es zu Hüter Dierwert.]], },
  },
  [10313] = {
    [[Warpenergien messen]],
    { [[Wenn uns unser Instinkt nicht täuscht und diese Manaschmieden die Technologie der Festung der Stürme nutzen, könnte die ganze Scherbenwelt in großer Gefahr schweben.]], [[Die Energieauflademechanismen der Festung der Stürme waren niemals dafür gedacht, sie so nahe an bewohntem Land zu benutzen. Die Warpenergien könnten dieses Land zerstören, sogar wenn sie durch Kaels' Rohrleitungen fließen.]], [[Bringt diese Kugel zur Manaschmiede B'naar. Sie ist so eingestellt, dass sie die Warpenergien messen kann. Benutzt sie an der Stelle, an der die Rohrleitungen in die Erde führen.]], },
    { [[Exarch Orelis in Area 52 möchte, dass Ihr die Warpmesskugel zur Manaschmiede B'naar bringt und sie in der Nähe der vier Rohrleitungen benutzt.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eurer Ehre bei den Sehern.]], },
  },
  [10314] = {
    [[Ein hartnäckiger Verdacht]],
    { [[Das merkwürdige magische Gerät ähnelt nichts, was Ihr je zuvor gesehen habt. Es wurde mit Sicherheit nicht von Blutelfen hergestellt.]], [[Als Ihr bemerkt, dass die Geister sich näher an das Haus bewegen, stellt Ihr fest, dass die Kugel stärker zu leuchten beginnt, und wieder schwächer wird, als die Geister sich entfernen. Es steht außer Frage, dass sie mit der Beeinflussung der Geister in Verbindung steht.]], [[Wie sie jedoch funktioniert, bleibt ein Geheimnis. Vielleicht kann Hüter Dierwert sich ja einen Reim darauf machen, wenn Ihr ihm die Überreste einiger Geister und Eure Beobachtungen zuteilwerden lasst.]], },
    { [[Sammelt 10 abscheuliche Überreste von den wahnsinnigen Geistern auf dem Kapellenhof in Kirin'Var und bringt sie zu Hüter Dierwert.]], },
  },
  [10315] = {
    [[Die Nethermanten neutralisieren]],
    { [[Die Blutelfen sind Dummköpfe! Ich bin froh, dass unsere Handelsbeziehungen mit ihnen auseinander gebrochen sind. Auf all meinen Reisen durch den Kosmos habe ich nie so selbstsüchtige Wesen gesehen!]], [[Sie zerstören weiterhin unser Land mit ihren Manaschmieden. Wollen wir mal schauen, ob wir ihnen bei der Manaschmiede Ara jenseits der Brücke Einhalt gebieten können.]], [[Die Nethermanten kanalisieren die Energie der Blutjuwelen, um den Energiefluss zu stabilisieren. Geht dort hin und löscht sie aus!]], },
    { [[Tötet 15 Nethermanten des Sonnenzorns und kehrt dann zu Netherpirscher Oazul auf Tulumans Landeplatz im Nethersturm zurück.]], },
  },
  [10316] = {
    [[Auf der Suche nach Beweisen]],
    { [[Wer auch immer hinter den Veränderungen der Geister stehen mag, hat seine Ziele mit Bedacht ausgewählt, und darauf geachtet, dass er zuerst die mächtigsten Magier erwischt. Aber warum? Die Magie, die eingesetzt wird, um die Geister der getöteten Kirin Tor zu beeinflussen, muss sehr mächtig sein.]], [[Warum würde ein mächtiger Magier all diese Mühen auf sich nehmen? Ich bin versucht zu glauben, dass dies ein weiterer teuflischer Plan von Kael'thas ist, um hier weiteres Unheil anzurichten. Wenn es hier in der Nähe Blutelfen gibt, verstecken sie sich bestimmt in den Häusern hinter der überdachten Brücke.]], },
    { [[Durchsucht die Häuser auf dem Kapellenhof von Kirin'Var nach Hinweisen darauf, wer sich hinter der Beeinflussung der Geister verbirgt.]], },
  },
  [10317] = {
    [[Den Großknecht erledigen]],
    { [[Die Blutelfen nennen die Mine unter der Manaschmiede Ara Trelleummine. Seit sie die Mine unter der Manaschmiede Ultris verloren haben, ist sie der einzige Ort, von dem sie den Nachschub zum Betrieb ihrer Schmieden bekommen.]], [[Ich brauche nicht zu erwähnen, dass es ein großer Vorteil für uns wäre, wenn wir ihre Bergbauaktivitäten dort verhindern könnten. Vielleicht könnten wir sogar dort einfallen und die Mine für uns selbst nutzen?]], [[Wie auch immer, ich möchte, dass Ihr Euch um Großknecht Sonnenend kümmert. Die Mine befindet sich am hinteren Ende der Insel links von uns.]], },
    { [[Vernichtet Großknecht Sonnenend in der Trelleummine, die sich unter der Manaschmiede Ara im Nethersturm befindet.]], },
  },
  [10318] = {
    [[Den Übermeister erledigen]],
    { [[Ich war früher hier der Großknecht. Aber jetzt, da ich degradiert wurde, geht mir auf, dass unsere ganze Pilgerreise von Quel'Thalas in die Scherbenwelt eine einzige Lüge war!]], [[Eine Lüge, die von Kael'thas eingefädelt wurde, um seine Streitkräfte hier im Nethersturm zu stärken.]], [[Ich habe versucht, meine Brüder davon zu überzeugen, dass wir fortgehen sollten, besonders wegen der Brennenden Legion, aber sie sind alle zu stur.]], [[Könnt Ihr mir einen Gefallen tun? Tötet Übermeister Grindgarr. Kehrt dann zu Eurem Astralen zurück und sagt ihm, dass ich kein Feind mehr bin.]], },
    { [[Tötet Übermeister Grindgarr. Kehrt dann zu Windhändler Tuluman auf Tulumans Landeplatz im Nethersturm.]], },
  },
  [10319] = {
    [[Das Phylakterium entwenden]],
    { [[Ich glaube, es wird Zeit für eine Erklärung. Ihr habt einen der Lehrlinge des Erzmagiers getroffen, Ravandwyr. Es gab jedoch noch einen weiteren mit dem Namen Naberius.]], [[Er war ein sehr fleißiger Schüler, hegte aber großes Interesse an der Nekromantie. Als Kael'thas mit seinen Streitmächten auftauchte, nutzte Naberius die Gelegenheit, um den Erzmagier zu verraten und die geheimen Verteidigungsmechanismen des Turms preiszugeben.]], [[Jetzt, so scheint es, ist er zu einem Lich geworden. Wenn wir ihn aufhalten wollen, müssen wir uns sein Phylakterium besorgen. So wie ich ihn kenne, hat er es sicher irgendwo in seinem Gebiet sicher vergraben.]], },
    { [[Holt Naberius' Phylakterium vom Kapellenhof in Kirin'Var und bringt es zu Hüter Dierwert.]], },
  },
  [10320] = {
    [[Vernichtet Naberius!]],
    { [[Erzmagier Vargoth hat die Gefahr in Naberius' Interesse an der Nekromantie erkannt, aber ich bin mir sicher, dass er niemals geglaubt hätte, dass sein Lehrling den Kirin Tor so viel Schaden zufügen würde.]], [[Ihr müsst ihn besiegen, <name>, sonst wird er weiterhin die Geister unserer gefallenen Brüder versklaven, sie ihrer Ruhe berauben und das letzte bisschen, was noch von Kirin'Var übrig ist, zerstören.]], [[Ohne sein Phylakterium kann er mit den vereinten Kräften von zwei oder drei Kämpfern vernichtet werden, aber wer weiß, was seine Magie mehr Gegnern gegenüber anrichten könnte?]], },
    { [[Hüter Dierwert in Kirin'Var möchte, dass Ihr Naberius auf dem Kapellenhof des Dorfes besiegt.]], },
  },
  [10321] = {
    [[Manaschmiede Coruu abschalten]],
    { [[Euer Erfolg in B'naar hat und sehr große Hoffnung gemacht, <name>.]], [[Wir haben unsere Entdeckungen den Sehern mitgeteilt, und sie haben uns im Gegenzug mehr Informationen über die übrigen Manaschmieden gegeben.]], [[Es stehen immer noch drei weitere Manaschmieden, die von der Armee des Sonnenzorns kontrolliert werden.]], [[Die Manaschmiede Coruu findet Ihr im Osten. Vorarbeiterin Seylanna ist dort im Besitz des Zugangskristalls. Besorgt ihn und schaltet damit die Steuerungskonsole ab.]], [[Wir zählen auf Euch, <name>.]], },
    { [[Reist zur Manaschmiede Coruu, östlich von Area 52, und besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Manaschmiede abzuschalten, und kehrt dann zu Anachoret Karja zurück.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10322] = {
    [[Manaschmiede Duro abschalten]],
    { [[Wir haben Neuigkeiten von einer der Manaschmieden, die noch in Betrieb sind, erhalten. Die Armee des Sonnenzorns ist im Begriff, die Kontrolle über die Manaschmiede Duro zu verlieren.]], [[Wegen ihrer erhöhten Energieproduktion werden ganze Schwärme von Manakreaturen von ihr angelockt - die Manaschmiede versinkt im Chaos.]], [[Eine Explosion von Duro könnte eine Kettenreaktion auslösen, die die ganze Scherbenwelt in Gefahr bringen würde. Vorarbeiter Athanel ist im Besitz des Zugangskristalls, mit dem Ihr die Manaschmiede abschalten könnt.]], },
    { [[Anachoret Karja möchte, dass Ihr zur Manaschmiede Duro geht und den Zugangskristall von Vorarbeiter Athanel besorgt. Benutzt ihn, um die Steuerungskonsole von Duro abzuschalten.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10323] = {
    [[Manaschmiede Ara abschalten]],
    { [[Nur noch eine Manaschmiede hält die Festung der Stürme in Betrieb. Nachdem wir die Manaschmiede Ara abgeschaltet haben, muss sich Kael mit seinen gespeicherten Energien zufrieden geben.]], [[Reist zur Manaschmiede Ara im Nordwesten und gebt Kael den Rest, indem Ihr sie abschaltet.]], [[Der letzte Zugangskristall befindet sich im Besitz von Vorarbeiter Azarad. Seid jedoch vorsichtig; wir erwarten heftigen Widerstand in Kaels letzter funktionierender Schmiede. Ihr werdet wahrscheinlich die Unterstützung einiger Freunde brauchen.]], },
    { [[Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10324] = {
    [[Die große Mondweidenjagd]],
    { [[Gut gemacht, <name>! Gut gemacht!]], [[Jetzt, da Ihr Euch die Hörner an den Mondweidenhirschen abgestoßen habt, ist es an der Zeit, dass Ihr Euch einer würdigeren Beute zuwendet. Lasst Euch von der Gelassenheit der Mondweidenböcke nicht täuschen, wenn sie sich provoziert fühlen, sind sie ein gefährlicher Gegner.]], [[Ihr findet die Böcke nördlich von hier. Weidmannsheil.]], },
    { [[Bringt 6 Bälge eines Mondweidenbocks zu Acteon in der Azurwacht.]], },
  },
  [10325] = {
    [[Male von Kil'jaeden]],
    { [[Wie Ihr vielleicht wisst, <name>, sind die Aldor die Hüter des Lichts und die Diener der Naaru.]], [[Uns direkt gegenüber stehen die Agenten der Brennenden Legion - Dämonen, die die Welten zerstören und die Unschuldigen verderben wollen.]], [[Wenn Ihr Euch den Aldor gegenüber beweisen wollt, bringt mir die Insignien der niederrangigen Mitglieder der Legion und ihrer Anhänger des Schattenrats. Uns ist ein Versteck des Schattenrats in der Knochenwüste bekannt. Die Lager der Legion findet Ihr in Nagrand und dem Schergrat.]], },
    { [[Bringt 10 Male von Kil'jaeden zu Adyen dem Lichtwächter in Shattrath.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10326] = {
    [[Mehr Male von Kil'jaeden]],
  },
  [10327] = {
    [[Einzelne Male von Kil'jaeden]],
  },
  [10328] = {
    [[Anweisungen des Sonnenzorns]],
    { [[Etwas an diesen Manaschmieden bereitet mir Kopfzerbrechen, <name>. Die Naarutechnologie ist sehr fortschrittlich; Kael'thas hätte Jahre brauchen müssen, um sie auch nur im Ansatz zu verstehen.]], [[Das Sammeln von Informationen zu diesem Thema ist sehr schwierig, da Kaels Armee sehr diszipliniert ist und jegliche schriftliche Kommunikation sofort vernichtet.]], [[Wir haben gehört, dass die Manaschmiede Duro von Manakreaturen angegriffen wird. Vielleicht haben die Verteidiger ihre Anweisungen noch nicht zerstört. Besorgt sie Euch und bringt sie zu mir.]], },
    { [[Reist zur Manaschmiede Duro und beschafft die Truppenbefehle des Sonnenzorns und die Arkanbefehle des Sonnenzorns von den Einheiten des Sonnenzorns, die dort postiert sind. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10329] = {
    [[Manaschmiede B'naar abschalten]],
    { [[Während Ihr fort wart, hatte ich eine freundliche Diskussion mit den Aldor. Die Manaschmieden bestehen aus der Naarutechnologie der Festung der Stürme. Offensichtlich gibt es bei dieser Technologie Mechanismen, die ein dauerhaftes Abschalten im Notfall ermöglichen.]], [[Alles, was wir brauchen, um diesen Vorgang einzuleiten, ist ein Zugangskristall zur Steuerungskonsole. Der praktischen Stammrolle, die Ihr uns gebracht habt, nach, befinden sich die Kristalle im Besitz eines Typen namens Vorarbeiter Theredis. Besorgt ihn und schaut, was Ihr tun könnt.]], },
    { [[Geht zurück zur Manaschmiede B'naar und besorgt Euch den Zugangskristall von Vorarbeiter Theredis. Benutzt ihn bei der Steuerungskonsole von B'naar um sie abzuschalten und meldet Euch danach bei Spionagemeister Thalodien zurück.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10330] = {
    [[Manaschmiede Coruu abschalten]],
    { [[Thalodien hat uns von Eurem Erfolg beim Abschalten der Manaschmiede B'naar erzählt. Er erwartet, dass Ihr Eure Leistung hier wiederholt und hat uns gebeten, ein Auge darauf zu haben, wer einen Kristall benutzt, um Zugang zu der Konsole im Inneren zu erhalten. Vorarbeiterin Seylanna hat den Zugangskristall, da sind wir uns sicher.]], [[Geht hinein und vollendet das Werk. Es besteht kein Grund zur Heimlichkeit mehr. Besorgt Euch den Kristall, schaltet die Konsole ab, tötet alle Techniker, die Euch daran hindern wollen, und macht Euch dann aus dem Staub.]], [[Beeilt Euch, Kaels Spürhunde kommen uns sicher bald auf die Schliche.]], },
    { [[Besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Steuerungskonsole der Manaschmiede abzuschalten und meldet Euch dann bei Caledis Morgenhell.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10331] = {
    [[Unverzichtbare Werkzeuge]],
    { [[Ihr habt hier nicht zufällig einen schweren Schmiedehammer herumliegen sehen, oder? Ich habe ihn hierher mitgenommen, um ein paar Hufeisen für das Pferd des alten Amorlin fertig zu stellen, aber ich kann ihn nicht finden.]], [[Ich habe die ganze Scheune mindestens ein dutzend Mal abgesucht, aber ohne Erfolg. Vielleicht hat einer der anderen Lehrlinge ihn gefunden und ihn zurück zur Schmiede gebracht? Könntet Ihr für mich dorthin gehen und danach suchen? Sie befindet sich südöstlich des Turms auf dem Ratsplatz.]], },
    { [[Besorgt den Schmiedehammer von den Lehrlingen von Kirin'Var und bringt ihn zurück zu Lehrling Andrethan in Kirin'Var.]], },
  },
  [10332] = {
    [[Meisterschmied Rhonsus]],
    { [[Endlich ist mein Gesellenstück fertig! Ich kann es kaum abwarten, es Meister Rhonsus zu zeigen.]], [[<Der Lehrling schaut Euch an, als Ihr ihm erzählt, was passiert ist, als Ihr den Hammer zurückholen wolltet.>]], [[Sie haben Euch angegriffen? Alle? Beim Licht, sie sind verrückt geworden!]], [[Was sollen wir tun, <name>? Es schmerzt mich, das zu sagen, aber vielleicht müssen wir Meister Rhonsus umbringen. Dann wäre ich der einzige Schmied in Kirin'Var, aber ich denke, dass meine Ausbildung ausreicht, um die Bedürfnisse des Dorfs zu befriedigen.]], },
    { [[Lehrling Andrethan in Kirin'Var möchte, dass Ihr den Geist von Rhonsus, dem Meisterschmied des Dorfes, vernichtet.]], },
  },
  [10333] = {
    [[Helft Mama Mauschel]],
    { [[Sagt mal, <name>, ich habe mich gefragt, ob Ihr mir noch einen Gefallen tun könntet.]], [[Meine Partnerin da draußen im Feld könnte sicher etwas Hilfe gebrauchen. Könnt Ihr ihr vielleicht helfen?]], [[Ich bin sicher, dass Mama Mauschel dafür sehr dankbar wäre. Kümmert Euch nicht um ihre scharfe Zunge.]], [[Sie bemüht sich um unsere Interessen im Osten. Ihr findet sie in der Biokuppel Mittelreich beim Mittelreichposten.]], },
    { [[Sprecht mit Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm.]], },
  },
  [10334] = {
    [[Braucht mehr Kuhglocke]],
    { [[Wo, oh, wo ist meine Bessy? Nach dem Angriff habe ich die Felder und den Kapellenhof nach Ihr abgesucht, aber ich konnte sie nicht finden!]], [[Alles, was ich von ihr noch habe, ist diese alte Kuhglocke. Normalerweise kommt sie gleich gerannt, wenn sie sie hört, aber vielleicht können diese alten Hände sie nicht stark genug läuten.]], [[Ich leihe Euch Bessys Glocke, wenn Ihr hinaus auf die Felder geht und sie so fest Ihr nur könnt läutet. Vielleicht wartet Bessy einfach nur auf ein wenig mehr Kuhglocke.]], },
    { [[Find Bessy in the fields near Kirin'Var Village's Chapel Hill.]], },
  },
  [10335] = {
    [[Die Ruinen beobachten]],
    { [[Ich muss in die Ruinen von Farahlon gehen, um meine Ausgrabungen zu beginnen! Das Problem ist, dass dieser Ort von Dämonen heimgesucht wird.]], [[Vielleicht könntet Ihr schon einmal ein wenig Vorarbeit für mich leisten?]], [[Es gibt drei Orte in den Ruinen im Nordosten, an dem Ihr für mich Vermessungsmarkierungen aufstellen sollt. Jede befindet sich in der Nähe eines Draeneibanners.]], [[Kehrt zu mir zurück, wenn Ihr alle drei Markierungen erfolgreich aufgestellt habt.]], },
    { [[Platziert die drei Vermessungsmarkierungen und kehrt dann zu Zephyrion in der Sturmsäule im Nethersturm zurück.]], },
  },
  [10336] = {
    [[Diener von Culuthas]],
    { [[Vor ein paar Wochen hat die Brennende Legion in den Ruinen von Farahlon Einzug gehalten und damit all unsere Pläne, die Artefakte dort auszugraben, zunichtegemacht.]], [[Dann habe ich heute erfahren, dass einer ihrer Schreckenslords, Culuthas, aus irgendeinem Grund alle anderen Mitglieder der Brennenden Legion dort getötet hat. Seitdem ruft er Dämonen, die ihm dienen.]], [[<class>, ich möchte, dass Ihr zu den Ruinen im Nordosten geht und die Dämonen vernichtet!]], },
    { [[Tötet 10 Hunde von Culuthas und 5 Augen von Culuthas]], },
  },
  [10337] = {
    [[Almabtrieb]],
    { [[Als Ihr merkt, dass Bessy Euch immer noch anstarrt, deutet Ihr nach Westen, zu dem Ort, an dem Thadell sich befindet. Ihr erwischt Euch selbst dabei, wie Ihr der Kuh versprecht, sie auf ihrem Nachhauseweg zu beschützen. Als Ihr Euren Satz beendet habt, wird Euch klar, dass die Kuh gar nicht verstehen kann, was Ihr sagt.]], [[Mit einem Schulterzucken bereitet Ihr Euch darauf vor, das Rindvieh zu Thadell zurückzubegleiten.]], },
    { [[Begleitet Bessy sicher zu ihrem Besitzer Thadell zurück.]], },
  },
  [10338] = {
    [[Manaschmiede Duro abschalten]],
    { [[Die Manaschmiede Duro wird also angegriffen und die Streitkräfte des Sonnenzorns haben die Kontrolle verloren? Was für ein Dilemma! Wenn Duro fällt, fällt die Festung der Stürme. Der einzige Nachteil ist... wenn die Festung der Stürme zerstört wird, wird die Explosion wahrscheinlich die kläglichen Überreste dieses verlassenen Landes mit sich reißen.]], [[Kael'thas Tod ist gut. Jedermanns Tod ist... nicht so gut, glaube ich. Es schmerzt mich, Euch darum zu bitten, aber geht zur Manaschmiede Duro und schaltet sie ab, bevor sie in Flammen aufgeht. Vorarbeiter Athanel hat den Zugangskristall.]], },
    { [[Kehrt zur Manaschmiede Duro zurück und besorgt Euch den Zugangskristall von Vorarbeiter Athanel. Benutzt ihn, um die Steuerungskontrolle von Duro abzuschalten und meldet Euch danach bei Spionagemeister Thalodien.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10339] = {
    [[Das Astraleum]],
    { [[Direkt südlich von hier findet Ihr den Stützpunkt des Astraleums. Das Astraleum bereitet sich dort auf irgendetwas vor... Vielleicht einen Angriff auf Ultris.]], [[Wir können jedoch nichts dem Zufall überlassen, <class>. Durchbrecht ihre Verteidigung und zerstört ihr Trainingslager.]], [[Sobald Ihr ihre Soldaten und Hauptmann Zovax vernichtet habt, sucht den Transponder des Astraleums Zeta und benutzt ihn, um mit mir Kontakt aufzunehmen. Dann werdet Ihr Eure neuen Befehle erhalten.]], },
    { [[Kommandant Ameer möchte, dass Ihr 5 Auftragsmörder des Astraleums, 5 Stoßtruppsoldaten des Astraleums, 2 Forscher des Astraleums und Hauptmann Zovax tötet und dann den Transponder des Astraleums Zeta aktiviert.]], },
  },
  [10340] = {
    [[Trümmerposten]],
    { [[<name>, Ihr habt gute Arbeit hier an der Front geleistet und großes Talent mit den gepanzerten Greifen bewiesen... Ich glaube, ich habe eine weitere Aufgabe für Euch.]], [[Startpunkt für Eure neue Mission ist der Trümmerposten, eine Angriffsbasis der Wildhämmer auf einer schwebenden Insel in der Nähe des Portals. Es ist etwas anstrengend dort, aber Ihr habt starke Nerven. Ihr packt das schon.]], [[Sprecht mit Schwadronskommandant Dabir'ee und lasst Euch von ihm zum Trümmerposten schicken. Meldet Euch dann bei Greifenmeister Runetog Wildhammer.]], },
    { [[Vorpostenkommandant Königstein möchte, dass Ihr mit Schwadronskommandant Dabir'ee sprecht, auf einem Greifen zum Trümmerposten fliegt und Euch bei Runetog Wildhammer meldet.]], },
  },
  [10341] = {
    [[Tretet sie, solange sie am Boden liegen]],
    { [[Das sind günstige Nachrichten, die Ihr bringt, <name>. Ihr könnt nicht nur ungehindert in die Manaschmiede Duro reinspazieren, es eröffnet sich auch noch eine großartige Gelegenheit, Kaels Armee einen Schlag zu versetzen.]], [[Nutzt den Angriff der Manakreaturen und vernichtet alle Nachzügler des Sonnenzorns, die Euch in die Arme laufen.]], [[Ich habe keinen besonderen Spaß daran, mein eigenes Volk abzuschlachten, wie Tahlodien, aber diesen taktischen Vorteil dürfen wir nicht ungenutzt vorübergehen lassen.]], },
    { [[Tötet 8 Beschwörer des Sonnenzorns, 6 Jagdschützen des Sonnenzorns und 4 Zenturios des Sonnenzorns. Kehrt zu Magister Larynna in Area 52 zurück, wenn Ihr die Aufgabe erledigt habt.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10342] = {
    [[Schieferhautschiefer]],
    { [[Ich verdiene gut daran, den Trunkenbolden hier Getränke zu verkaufen. Aber ich will mehr!]], [[Der Raketenchef sagt mir immer, dass er den Raketentreibstoff, den er braucht, um die X-52 abheben zu lassen, nicht verbessern kann.]], [[Wir können dieses kleine Problem für ihn lösen.]], [[Zuerst muss ich das Öl aus dem Schiefer der hiesigen Schieferhautschinder pressen. Daraus mache ich einen konzentrierten, wenn auch leicht entzündlichen, Grundstoff für die Mischung, die mir vorschwebt.]], [[Sucht die Schinder und ihren Schiefer! Sie sind überall bei den violetten Felsen.]], },
    { [[Sammelt 5 Stück Schieferhautschiefer und bringt sie zu Stiefels in Area 52 im Nethersturm.]], },
  },
  [10343] = {
    [[Die unendliche Invasion]],
    { [[Kael'thas Angriff auf Kirin'Var begann mit einem donnernden Geräusch und einem hellen Lichtblitz im Offiziersquartier im Nordwesten. Die Zerstörung war dort am größten. Nach dem Rückzug der Blutelfen geschah etwas Merkwürdiges.]], [[Wesen aus reinem Mana begannen, von überall her in das Dorf zu strömen. Bis jetzt versuchen wir, sie so gut es geht im Zaum zu halten, aber ich hoffe, dass wir den Ansturm komplett abwehren können, wenn wir mehr über die Waffe in Erfahrung bringen, die Kael'thas benutzt hat.]], },
    { [[Sucht die von Manakreaturen heimgesuchte Gegend in Kirin'Var nach einem Manabombenfragment ab und bringt sie zu Zaubererleutnant Morran.]], },
  },
  [10344] = {
    [[Schwadronskommandant Greifgar]],
    { [[Schwadronskommandant Greifgar hat die Leitung über die Wildhämmer hier. Wenn Ihr zum ersten Mal im Trümmerposten seid, solltet Ihr Euch bei ihm melden.]], [[Ihr findet ihn in den Überresten des Turms da drüben.]], },
    { [[Sprecht mit Schwadronskommandant Greifgar im Trümmerposten.]], },
  },
  [10345] = {
    [[Fleisch geworden]],
    { [[Seht Ihr diese Leichen, <race>? Arconus ist für diese Massaker verantwortlich. Er hat sie ausgesaugt, sodass nur noch eine Fleischhülle übrig geblieben ist. Aber lasst Euch von den Körpern nicht täuschen! In diesen Leichen liegen Fleischbestien, die nur darauf warten, ahnungslose Dummköpfe, die sich zu nahe heranwagen, anzufallen.]], [[Ich habe meine Befehle. Ich muss hierbleiben, bis meine Verstärkung eintrifft. Das bedeutet aber nicht, dass Ihr nicht meinen Anzünder nehmen und die siechenden Leichen, die überall in den Minen herumliegen, zerstören könnt. So oder so muss diese Aufgabe erledigt werden.]], },
    { [[Benutzt den Anzünder des Protektorats um 12 siechende Leichen zu zerstören. Bringt den Anzünder des Protektorats zu Agent Araxes außerhalb des Zugangsschachts Zeon zurück, wenn Ihr erfolgreich wart.]], },
  },
  [10346] = {
    [[Rückkehr zur Abyssischen Untiefe]],
    { [[<name>, Ihr habt Euch als fähiger Greifenreiter bewiesen. Wir könnten Euch für weitere Einsätze bei der Abyssischen Untiefe brauchen. Was sagt Ihr, <class>? Wollt Ihr noch ein paar Flugstunden sammeln?]], },
    { [[Greifnerin Heulwind im Expeditionsposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört.]], },
  },
  [10347] = {
    [[Rückkehr zur Abyssischen Untiefe]],
    { [[<name>, seid Ihr bereit für einen weiteren Flug zu Abyssischen Untiefe? Ich fürchte, dass uns die Dämonen der Legion von Norden her angreifen werden, wenn wir sie nicht weiter unter Druck setzen und zulassen, dass sie Ihre Festung verstärken.]], [[Was sagt Ihr, <name>? Habt Ihr Lust, Euch erneut auf einen Windreiter zu schwingen und zur Abyssischen Untiefe zu fliegen?]], },
    { [[Schwadronskommandant Brack im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört.]], },
  },
  [10348] = {
    [[Neue Möglichkeiten]],
    { [[Ich habe gehört, dass eine Gruppe von 'Druiden' aus Azeroth sich für die Wirkung unserer Biokuppeltechnologie interessiert.]], [[Die Expedition des Cenarius ist sehr begierig darauf, Zugang zu den Pflanzen in den Kuppeln zu erlangen, besonders zu der einen, die sie Elfenbeinglocke nennen, und die für ihre heilenden Kräfte bekannt ist. Sie wächst in der Biokuppel Mittelreich in kleinen Büschen um größere Pflanzen herum.]], [[Ich teile den Gewinn mit Euch, wenn Ihr mir ein paar der Kräuter für meine erste Lieferung sammelt.]], },
    { [[Sammelt 15 Elfenbeinglocken und kehrt dann zu Mehrdad im Mittelreichposten zurück.]], },
  },
  [10349] = {
    [[Die Erdbinderin]],
    { [[Ihr solltet dieses Kristallstück zur Erdbinderin bringen. Sie ist wie Ihr... immer muss sie einem Geheimnis auf die Spur kommen und darüber nachgrübeln.]], [[Wenn Ihr mich fragt, denkt sie zu viel.]], [[Gebt ihr den Kristall und ich bin sicher, dass sie Euch beim Versuch herauszufinden, was im Grat vor sich geht, in ganz schöne Schwierigkeiten bringen wird.]], },
    { [[Bringt den Purpurkristallsplitter zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel.]], },
  },
  [10350] = {
    [[Behomat]],
    { [[Ihr habt viele harte Lektionen in unserer Zeit zusammen von mir gelernt, doch nun ist es an der Zeit, dass ich Euch in weisere Hände übergebe.]], [[Behomat, der mich ausbildete, als ich noch grün wie Gras hinter den Ohren war, wird Euer neuer Lehrer sein. Ich habe ihm gesagt, dass ich Euch zu ihm schicken werde, er erwartet Euch also.]], [[Ihr findet ihn in der Mitte der Terrasse der Krieger, am höchsten Punkt der Händlertreppe der Exodar.]], },
    { [[Sprecht mit Behomat bei der Händlertreppe in der Exodar.]], },
  },
  [10351] = {
    [[Natürliche Heilmittel]],
    { [[Es müssen die bösen Strahlungen von dem riesigen roten Kristall sein, die die Bergriesen herbeirufen und sie wahnsinnig machen.]], [[Er ist zu groß, um ihn weg zu bringen, und unsere Fertigkeiten reichen nicht aus, um ihn zu zerstören, aber mir ist ein Weg eingefallen, wie wir seine Macht bezwingen können.]], [[Ich habe einen Zirkel beim nordwestlichen Ende des Kristalls auf dem Grat beschworen. Bringt diesen Samen dorthin und setzt seine Macht ein, um das Land durch die natürlichen Kräfte des Regens, des Wachstums und der Insekten zu heilen.]], [[Seid vorsichtig, <name>. Ich spüre, dass hier eine außenstehende Macht am Werk ist... und sie ist nicht natürlich!]], },
    { [[Benutzt den Samen der Wiederbelebung beim Zirkel des Erdbinders, um das Land um den Kristall herum zu heilen. Kehrt dann zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel mit Euren Beobachtungen zurück.]], },
  },
  [10352] = {
    [[Spendet Wolle]],
  },
  [10353] = {
    [[Arconus der Unersättliche]],
    { [[Araxes und ich wurden vor zwei Tagen nach Ultris geschickt. Unser Auftrag: findet und vernichtet den Leerenfürsten Arconus. Wir haben ihn bis zu seiner Mine verfolgt und herausgefunden, dass er sich von den Energien der Kreaturen in dieser Gegend ernährt.]], [[Es brach ein Feuerwechsel aus, bei dem Araxes und ich getrennt wurden. Jetzt bin ich hier gefangen und schwer verletzt. Unser Auftrag ist dahin!]], [[Wenn Ihr Arconus erledigen könntet, wäre die oberste Führung Euch sehr dankbar. Wenn Ihr den Auftrag annehmt, werde ich Euch eine Reflexion meiner selbst als Wächter mitschicken.]], },
    { [[Agent Sechs-Uhr beim Zugangsschacht Zeon im Nethersturm möchte, dass Ihr Arconus den Unersättlichen tötet und dann zu Kommandant Ameer im Wachposten des Protektorats im Nethersturm zurückkehrt.]], },
  },
  [10354] = {
    [[Spendet Seide]],
  },
  [10355] = {
    [[Siechendes Fleisch]],
    { [[Wir müssen herausfinden, wie weit der Schaden reicht. Das Basidium, das Ihr mir gebracht habt, stammt von einem Sumpfriesen. Ihre größeren, zäheren Vettern, die Sumpflords, sollten der Degenerierung, die die Riesen befallen hat, gegenüber unempfindlicher sein.]], [[Es wäre auch schön zu wissen, inwiefern die Veränderungen die anderen Kreaturen des Todesmoors betroffen haben.]], [[Kehrt zum Todesmoor zurück und schaut, ob Ihr Proben von den Sumpflords und Hydras in der Gegend sammeln könnt.]], },
    { [[Sammelt 5 Proben einer verdurstenden Hydra und 5 Proben eines siechenden Sumpflords und bringt sie zu Ruam in Telredor.]], },
  },
  [10356] = {
    [[Spendet Magiestoff]],
  },
  [10357] = {
    [[Spendet Runenstoff]],
  },
  [10358] = {
