  [10362] = {
    [[Spendet Runenstoff]],
  },
  [10363] = {
    [[Noch mehr Runenstoff]],
  },
  [10364] = {
    [[Caedmos]],
    { [[Ihr seid in kurzer Zeit sehr weit gekommen, <name>. Schneller, als ich erwartet oder gehofft hätte. Es ist an der Zeit, dass Ihr Eure Ausbildung unter der Anleitung meines Meisters, Caedmos, fortsetzt.]], [[Ich habe ihm gesagt, dass er Euch in der Exodar erwarten soll. Ihr findet ihn im Sanktum der Anachoreten in der Nähe des Eingangs zur Halle des Lichts.]], },
    { [[Sprecht mit Caedmos in der Halle des Lichts der Exodar.]], },
  },
  [10365] = {
    [[Manaschmiede Ara abschalten]],
    { [[Ihr habt Eure Arbeit besser erledigt, als wir dachten. Die Manaschmiede Ara ist die letzte von Kaels Schmieden, die noch in Betrieb ist. Ihr findet sie nordwestlich von hier, aber Ihr müsst wahrscheinlich um sie herumgehen, um dorthin zu gelangen. Wir möchten, dass Ihr sie abschaltet.]], [[Der letzte Zugangskristall befindet sich im Besitz von Vorarbeiter Azarad. Seid jedoch vorsichtig; wir erwarten heftigen Widerstand in Kaels letzter funktionierender Schmiede. Ihr werdet wahrscheinlich die Unterstützung einiger Freunde brauchen.]], [[Hmm... Azarad. Was ein komischer Name für einen Sin'dorei.]], },
    { [[Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10366] = {
    [[Jol]],
    { [[Der Pfad des Lichts und der Reinheit ist steinig, <name>. Sogar ich habe manchmal Probleme, ihm zu folgen.]], [[Ihr solltet meine Meisterin, Jol, in der Exodar aufsuchen. Sie hat mich zu Beginn meiner Reise ausgebildet. Ich bin mir sicher, dass sie in Euch ihren neuen Lieblingsschüler finden wird.]], [[Ihr findet Jol in der Halle des Lichts in der Exodar beim Sanktum der Verteidiger, einem Raum, der von einem pulsierenden Lichtschein umgeben ist.]], },
    { [[Sprecht mit Jol im Sanktum der Verteidiger in der Exodar.]], },
  },
  [10367] = {
    [[Ein Verräter unter uns]],
    { [[Wo immer ihr einen Stamm von versklavten Zerschlagenen seht, könnt Ihr auch einen Verräter finden. Ein verachtungswürdiges Wesen, das seine Brüder für das Versprechen von Macht verkauft hat.]], [[Oben an der Treppe in der Mitte der Ruinen könnt Ihr eine Hütte finden. Das ist die Hütte des Verräters.]], [[Er ist im Augenblick nicht dort, Ihr könnt Euch also hineinschleichen, wenn Ihr Geduld habt. Innen findet Ihr einen Kasten mit einem Schlüssel darin. Dieser Schlüssel passt zu den Ketten, die die Ältesten des Lumpenpacks gefangen halten. Bringt ihn mir.]], [[Seid jedoch vorsichtig, Arzeth der Erbarmungslose treibt sich in der Nähe herum.]], },
    { [[Naladu in den Ruinen von Sha'naar möchte, dass Ihr den Schlüssel von Sha'naar aus einer Hütte in der Nähe besorgt.]], },
  },
  [10368] = {
    [[Die Ältesten des Lumpenpacks]],
    { [[Der Geist des Lumpenpacks ist gebrochen, weil sich unsere Ältesten in Gefangenschaft befinden. Nehmt diesen Schlüssel und befreit sie.]], [[Es sind Morod der Windtreiber, Akoru der Feuerrufer und Aylaan der Wasserrufer. Sobald sie frei sind, werden die Geister zum Stamm des Lumpenpacks zurückkehren.]], },
    { [[Befreit Morod den Windtreiber, Akoru den Feuerrufer und Aylaan den Erderwecker aus den Ruinen von Sha'naar. Kehrt zu Naladu zurück, wenn Ihr diese Aufgabe erledigt habt.]], },
  },
  [10369] = {
    [[Arzeths Untergang]],
    { [[Ja, die Elementargeister sind zu uns zurückgekehrt! Wir sind nicht mehr so stark wie einst, aber auch geringe Magie kann in den richtigen Händen weit reichen.]], [[Arzeth wurde von seinem dunklen Herrn große Macht verliehen. Nehmt diesen Stab. Er war einst ein großartiges Symbol unseres Volkes. Setzt ihn gegen Arzeth ein, und seine Macht wird für kurze Zeit verschwinden. Besiegt ihn, damit mein Volk endlich frei sein kann!]], },
    { [[Benutzt den Ältestenstab des Lumpenpacks gegen Arzeth den Erbarmungslosen und tötet ihn, nachdem er seine Macht verloren hat. Kehrt danach zu Naladu in den Ruinen von Sha'naar zurück.]], },
  },
  [10370] = {
    [[Nazgrels Befehl]],
  },
  [10371] = {
    [[Yorus Gerstenbräu]],
    { [[Ein Zwergenkrieger namens Yorus Gerstenbräu hat eine Herausforderung für alle tüchtigen Krieger der Allianz verkündet.]], [[Er wartet im Gasthaus von Seenhain, im sogenannten Rotkammgebirge in den Östlichen Königreichen. Es wird eine lange Reise, aber sie lohnt sich, wenn Ihr Euch selbst testen wollt, <name>.]], },
    { [[Sprecht mit Yorus Gerstenbräu.]], },
  },
  [10372] = {
    [[Eine diskrete Anfrage]],
    { [[Eralan, eine Verbündete von mir in Tristessa, hat angefragt, ob ihr jemand bei einem eiligen Auftrag helfen könnte.]], [[Ich weiß nicht, was sie vorhat, denn ich stelle keine Fragen, aber Ihr habt Euch mehr als fähig erwiesen. Ich möchte, dass Ihr nach Tristessa in den Geisterlanden reist, und mit Eralan sprecht.]], [[Sie mag vielleicht ein wenig ruppig sein, aber man kann ihr vertrauen.]], },
    { [[Sprecht mit Eralan in Tristessa.]], },
  },
  [10373] = {
    [[Der Ruf zu den Waffen: Die Pestländer!]],
    { [[Hört her, hört her! Alle Helden werden ersucht, aufzumerken!]], [[Das Königreich Sturmwind ruft zu den Waffen! Alle einsatzfähigen Individuen sind hiermit angehalten, gegen die dunkle Bedrohung durch die Geißel in den nördlichen Ländern der Östlichen Königreiche zu den Waffen zu greifen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Pestländer erheben!]], [[Helden der Welt... Sucht Kommandant Ashlam Ehrenschlag auf, der seinen Stützpunkt an der Zugwindspitze errichtet hat, nördlich des Alteracgebirges!]], },
    { [[Sucht Kommandant Ashlam Ehrenschlag auf. Sein Stützpunkt befindet sich an der Zugwindspitze, nördlich des Alteracgebirges.]], },
  },
  [10374] = {
    [[Der Ruf zu den Waffen: Die Pestländer!]],
    { [[Helden der Welt, höret mich - die Horde ruft zum schnellen Aufmarsch!]], [[Alle einsatzfähigen Personen werden zu den Waffen gerufen, um gegen die Geißel zu ziehen! Es gibt Gerüchte über neue Bedrohungen, die sich aus den Ruinen der Östlichen Königreiche erheben! Sie lauern östlich von Unterstadt, in den nördlichen Ausläufern der Länder, die als Pestländer bekannt sind!]], [[Helden der Welt - sucht Hochexekutor Derrington auf. Er hat seinen Stützpunkt am Bollwerk östlich von Tirisfal aufgeschlagen!]], },
    { [[Sucht Hochexekutor Derrington auf. Sein Stützpunk befindet sich am Bollwerk, östlich von Tirisfal und Unterstadt.]], },
  },
  [10375] = {
    [[Obsidiankriegsperlen]],
    { [[Ihr habt Euch meinem Volk gegenüber als würdig erwiesen, <name>. Jetzt ist es an der Zeit, uns zu beweisen, dass Ihr bereit seid, uns jederzeit beizustehen, auch gegen unserer ärgsten Feind.]], [[Die Oger der Scherbenwelt sind]], },
  },
  [10377] = {
    [[Keine Angst]],
  },
  [10379] = {
    [[Berührung der Schwäche]],
  },
  [10380] = {
    [[Ein dunkler Pakt]],
    { [[Wir waren so blind, dass wir nicht gesehen haben, dass unsere beiden größten Feinde sich verbündet haben, <name>.]], [[Ich wäre nicht überrascht, wenn die Seher uns diese Information absichtlich vorenthalten hätten, aber wir müssen uns nun der Wirklichkeit stellen. Die Brennende Legion und Kael'thas Sonnenzornarmee dienen unter dem gleichen Banner.]], [[Die Dämonen sind der einzige Grund, aus dem Kael'thas je in der Lage war, die Technologie der Festung der Stürme nutzen zu können. Ich muss Euch auf Orelis' Befehl hin bitten, sie zu zerstören.]], },
    { [[Kaylaan auf Tulumans Landeplatz möchte, dass Ihr 6 Warptüftler der Gan'arg, 3 Töchter des Schicksals und 3 Warpmeister der Mo'arg tötet.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10381] = {
    [[Nie mehr Aldor]],
    { [[Meine Mission hier ist zu Ende. Ich habe getan, was Orelis mir befohlen hat - nie habe ich ihn enttäuscht.]], [[Nie wieder. Ich sehe ein großes Gemetzel voraus - einen Kreuzzug werden sie es nennen - das auf das neue Bündnis zwischen Kael'thas und der Brennenden Legion folgen wird.]], [[Zuerst werden sie unsere heiligste Stadt dem Feind übergeben und uns dann bitten, im Kampf gegen ihren Feind zu sterben. Geht zurück zu Orelis und sagt ihm, dass die Naaru von nun an ihre Kämpfe alleine austragen können.]], },
    { [[Meldet Euch bei Exarch Orelis in Area 52.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10382] = {
    [[Geht zur Front]],
    { [[Nun, <name>, ich hoffe, Ihr seid für die nächste Phase unserer Operation bereit. Die Legion schläft nicht - und wir auch nicht! Nehmt einen Greifen und fliegt zum Ehrenposten, einem kleinen Lager in der Nähe der Front der Legion.]], [[Sprecht mit Feldmarschall Brock. Er wird bei Eurer Ankunft Anweisungen für Euch bereithalten. Oh, und sagt diesem teiggesichtigen Schildschmuser einen Gruß von mir.]], },
    { [[Nehmt einen Greifen zum Ehrenposten und sprecht mit Feldmarschall Brock.]], },
  },
  [10383] = {
    [[Dies ist ein Brotkrumen!]],
    { [[Hallo. Ihr solltet mehr über diese Kaninchen herausfinden.]], },
    { [[Sprecht mit Jonathan LeCraft über die Kaninchen.]], },
  },
  [10384] = {
    [[Daten der Astralen]],
    { [[Seht Euch um, Fleischling. Ganz in der Nähe des Transponders sollte sich eine Datenzelle des Astraleums befinden. Sucht sie, öffnet sie und entnehmt den Inhalt.]], [[Bringt sie dann sofort zu diesem Transponder zurück!]], },
    { [[Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr die Datenzelle des Astraleums besorgt.]], },
  },
  [10385] = {
    [[Hirnschadengefahr = hoch]],
    { [[Sie benutzen Melder, um die Informationen im Stützpunkt zu verteilen.]], [[Es gibt nur einem Weg, wie Ihr die Melder sehen und ihre Informationen extrahieren könnt. Unglücklicherweise könntet Ihr dadurch einen irreparablen Hirnschaden erlangen.]], [[Wenn Ihr immer noch interessiert seid, müsst Ihr einige der Anhänger des Astraleums hier erschlagen und ihre Essenz nehmen. Wenn Ihr eine Essenz habt, trinkt sie. Mit ihrer Hilfe werdet Ihr für kurze Zeit in der Lage sein, die Melder des Astraleums zu sehen. Die Zeit reicht aus, um einen Melder zu zerstören und seine Informationen zu extrahieren.]], },
    { [[Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr 15 Melderdaten des Astraleums besorgt.]], },
  },
  [10386] = {
    [[Der Teufelshäschertöter]],
    { [[Oberanführer Hun Stümmelfaust sucht immer nach Freiwilligen für seine selbstmörderischen Angriffe auf die Brennende Legion. Ihr verfügt definitiv über dem Mut für seine "Missionen".]], [[Sprecht mit ihm, wenn Ihr Appetit auf noch mehr Gemetzel habt. Er feiert gerade da drüben bei dem gefallenen Teufelshäscher. Er und seine Wachen haben ihn vor ein paar Stunden vernichtet. Hätte er nicht so wild und geschickt gekämpft, wären wir jetzt alle tot.]], },
    { [[Sprecht mit Oberanführer Hun Stümmelfaust.]], },
  },
  [10387] = {
    [[Der Teufelshäschertöter]],
    { [[Oberanführer Hun Stümmelfaust sucht immer nach Freiwilligen für seine selbstmörderischen Angriffe auf die Brennende Legion. Ihr verfügt definitiv über dem Mut für seine "Missionen".]], [[Sprecht mit ihm, wenn Ihr Appetit auf noch mehr Gemetzel habt. Er feiert gerade da drüben bei dem gefallenen Teufelshäscher. Er und seine Wachen haben ihn vor ein paar Stunden vernichtet. Hätte er nicht so wild und geschickt gekämpft, wären wir jetzt alle tot.]], },
    { [[Sprecht mit Oberanführer Hun Stümmelfaust.]], },
  },
  [10388] = {
    [[Rückkehr nach Thrallmar]],
    { [[Ich hoffe, Ihr hattet einen guten Flug, mein Freund. Während Ihr fort wart, ist ein Bote angekommen. Wie es aussieht, hat Nazgrel einen neuen Auftrag für Euch. Am besten geht Ihr nach Thrallmar zurück und sucht ihn auf. Ich habe das Gefühl, dass die Legion gerade erst warm geworden ist.]], },
    { [[Kehrt nach Thrallmar zurück und sprecht mit Nazgrel.]], },
  },
  [10389] = {
    [[Das Leid und die Dunkelheit]],
    { [[Grüße, <name>. Ich habe den Schriftwechsel der Legion entziffert und ihre strategischen Pläne für die Halbinsel aufgedeckt. Offensichtlich sammelt sich in der Nähe der Teiche von Aggonar im Westen eine große Gruppe von teuflischen Terrorbestien. Das dürfen wir nicht ohne Kampf zulassen. Ihr müsst Euch dorthin begeben und ihre verfluchten Reihen ausdünnen.]], },
    { [[Magister Blutfalke möchte, dass Ihr 10 Terrorbestien bei den Teichen von Aggonar tötet.]], },
  },
  [10390] = {
    [[Konstruktionslager: Mageddon]],
    { [[<name>, wir haben guten Grund zu vermuten, dass das Konstruktionslager, das sich am östlichsten befindet, noch nicht fertig ist. Die Gan'argsklaven der Legion arbeiten rund um die Uhr, um die Einrichtung zum Laufen zu bringen. Das können wir nicht zulassen.]], [[Begebt Euch nach Osten zum Konstruktionslager: Mageddon und tötet 10 Gan'arg. Bringt mir den Kopf ihres Meisters, Schmiedebestie Sägeklinge! So werden wir die Teufelshäscherproduktion der Legion zum Erliegen bringen!]], },
    { [[Nazgrel in Thrallmar möchte, dass Ihr das Konstruktionslager: Mageddon angreift, 10 Diener der Gan'arg tötet und mit Schmiedebestie Sägeklinges Kopf zurückkehrt.]], },
  },
  [10391] = {
    [[Kanonen des Zorns]],
    { [[Es gibt eine weitere Produktionsstätte. Das Konstruktionslager: Zorn liegt direkt im Nordosten von Thrallmar. Die Dämonen haben eine Reihe von Teufelskanonen am Grat entlang aufgestellt - und sie zielen direkt auf uns! Ihr müsst in das Lager eindringen und diese Kanonen außer Gefecht setzen.]], [[<name>, Ihr müsst sichergehen, dass Ihr alle zerstört - sonst kann die Legion nichts daran hindern, uns mit den Feuern der Hölle zu versengen!]], },
    { [[Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Nazgrel in Thrallmar zurück.]], },
  },
  [10392] = {
    [[Die Schwelle zum Abgrund]],
    { [[Ihr ehrt uns, <name>. Aber nun müssen wir zuschlagen, bevor die Legion sich erholt! Nördlich von Thrallmar liegt der Invasionspunkt: Vernichter. Dort befindet sich ein Warpportal, durch das die Legion ihre Verstärkung bezieht.]], [[Ihr müsst dieses Portal überlasten - und es zurück in den Abgrund schicken, aus dem es gekommen ist. Den Aktivierungsschlüssel trägt vermutlich der Kommandant des Punkts, Kriegshetzer Arix'Amal, bei sich. Tötet ihn. Benutzt dann den Schlüssel bei dem Portal und tut, was getan werden muss. Möge der Segen des Kriegshäuptlings auf Euch ruhen.]], },
    { [[Tötet Arix'Amal und nehmt Euch den Torschlüssel der Brennenden Legion. Benutzt den Schlüssel bei der Rune der Bosheit und kehrt danach zu Nazgrel in Thrallmar zurück.]], },
  },
  [10393] = {
    [[Boshafte Pläne]],
    { [[Der Text auf dem Brief ist in dunklem, unentzifferbarem Gekrakel geschrieben. Je mehr Ihr sie anstarrt, desto mehr Symbole scheinen sich vor Euren Augen zu drehen und zu leuchten. Es gibt nur einen in Thrallmar, der so eine bösartige Schrift entziffern kann - der Blutelfenmagister Blutfalke.]], },
    { [[Bringt den Schriftwechsel der Legion zu Magister Blutfalke in Thrallmar.]], },
  },
  [10394] = {
    [[Unterbrechung beim Konstruktionslager: Mageddon]],
    { [[Passt genau auf, <name>. Wir haben die Lager der Legion, die entlang des Grats vor uns liegen, überwacht. Wir haben guten Grund zur Annahme, dass die Dämonen in diesen so genannten 'Konstruktionslagern' ihre verdammten Teufelshäscher herstellen.]], [[Ihr könnt das am nächsten gelegene Ziel, das Konstruktionslager: Mageddon von hier aus im Westen sehen. Unsere Geheimagenten melden, dass es sich immer noch im Bau befindet. Wenn Ihr die Gan'argdiener und ihren Meister, Schmiedebestie Sägeklinge, tötet, können wir der Teufelshäscherproduktion der Legion ein schnelles Ende machen!]], },
    { [[Tötet 10 Diener der Gan'arg und Schmiedebestie Sägeklinge im Konstruktionslager: Mageddon und kehrt danach zu Feldmarschall Brock beim Ehrenposten zurück.]], },
  },
  [10395] = {
    [[Der dunkle Schriftwechsel]],
    { [[Der Text auf dem Brief ist in dunklem, unentzifferbarem Gekrakel geschrieben. Je mehr Ihr sie anstarrt, desto mehr Symbole scheinen sich vor Euren Augen zu drehen und zu leuchten. Es gibt nur einen in der Ehrenfeste, der so eine bösartige Schrift entziffern kann - der Draeneimystiker Kryv.]], [[Bringt den Schriftwechsel der Brennenden Legion zu Warpseher Kryv im Kriegsraum der Ehrenfeste.]], },
    { [[Bringt den Schriftwechsel zu Warpseher Kryv in der Haupthalle der Ehrenfeste.]], },
  },
  [10396] = {
    [[Der Feind meines Feindes...]],
    { [[Jetzt müssen wir uns auf unser zweites Ziel konzentrieren, das Konstruktionslager: Zorn. Es liegt direkt westlich von Mageddon. Das Problem ist, dass die Dämonen eine Reihe von Teufelskanonen direkt auf den Außenposten der Horde in Thrallmar gerichtet haben! Ich kann es zwar kaum fassen, aber wir müssen der Horde helfen, um unsere Linien beim Dunklen Portal aufrechtzuerhalten. Wir können es uns nicht erlauben, Thrallmar fallen zu lassen. Ihr müsst alle Kanonen zerstören, bevor die Legion unsere Verbündeten in die Luft jagt!]], },
    { [[Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Feldmarschall Brock beim Ehrenposten zurück.]], },
  },
  [10397] = {
    [[Invasionspunkt: Vernichter]],
    { [[Wir haben die letzte Basis der Legion auf dem Grat ausgemacht. Der Invasionspunkt: Vernichter liegt nordwestlich des Konstruktionslagers: Zorn. Dort befindet sich ein Warpportal, mit dessen Hilfe die Legion Verstärkung herbeiholt!]], [[Ihr müsst an dieses Portal herankommen und verhindern, dass noch mehr Dämonen dadurch in unsere Welt kommen. Der Oberanführer des Punkts, Kriegshetzer Arix'Amal, hat den Aktivierungsschlüssel für das Portal. Nehmt ihn ihm ab und schaltet dieses verdammte Tor ab!]], },
    { [[Tötet Arix'Amal. Nehmt den Torschlüssel der Brennenden Legion und benutzt ihn bei der Rune der Bosheit.]], },
  },
  [10398] = {
    [[Rückkehr zur Ehrenfeste]],
    { [[Geht zu Ehrenfeste.]], },
    { [[Geht zu Ehrenfeste.]], },
  },
  [10399] = {
    [[Das Herz der Dunkelheit]],
    { [[Es ist genau so, wie ich befürchtet habe. Der Dämon, den Ihr unlängst getötet habt, Arix'Amal, stand im Dienst des heimlichen Legionslords Arazzius des Grausamen. Wie es scheint hat dieser Bösewicht eine neue Streitmacht von höllischen Terrorbestien in der Nähe der Teiche von Aggonar aufgestellt.]], [[Ihr müsst Euch dorthin begeben und die Terrorbestien vernichten, bevor sie uns angreifen können. Aber seid vorsichtig, Freund. Die Teiche von Aggonar liegen in einem gemarterten, giftigen Tal. Ihr findet die verfluchten Teiche nordwestlich von hier, hinter der Höllenfeuerzitadelle.]], [[Das Licht sei mit Euch, <name>.]], },
    { [[Tötet 10 Terrorbestien bei den Teichen von Aggonar und kehrt dann zu Warpseher Kryv in der Haupthalle der Ehrenfeste zurück.]], },
  },
  [10400] = {
    [[Oberanführer]],
    { [[Der ehrwürdige Warpseher hat mir von Euren waghalsigen Abenteuern erzählt, <name>. Er sprach auch von dem neuen Feind, den Ihr entdeckt habt. Dieser Arazzius der Grausame ist der mächtigste und bösartigste Feind, dem die Söhne Lothars sich je stellen mussten.]], [[Werdet Ihr uns zur Seite stehen, <name>? Werdet Ihr diesem Legionslord gegenübertreten und ihn der Gerechtigkeit des Lichts übergeben? Wenn ja, dann begebt Euch zu den Teichen von Aggonar. Wenn Ihr ihn nicht findet, wird seine Bosheit sicher Euch finden.]], },
    { [[Tötet Arazzius den Grausamen bei den Teichen von Aggonar und kehrt danach zu Truppenkommandant Danath Trollbann in der Ehrenfeste zurück.]], },
  },
  [10401] = {
    [[Mission: Beendet alles, seid alles]],
    { [[Wisst Ihr, was passiert wäre, wenn Ihr den Boten nicht abgefangen hättet, <name>?]], [[<Nazgrel nickt.>]], [[Die gesamte Armee der Legion des Höllenfeuers hätte vor unserer Tür gestanden. Einen solchen Angriff hätten wie niemals überlebt.]], [[Es gibt nur eine Möglichkeit zu verhindern, dass diese Befehle jemals die Front erreichen. Ihr müsst zu den Teichen von Aggonar, westlich von hier, reisen und diesen Arazzius töten.]], [[Wenn Ihr das tut, werde ich dafür sorgen, dass der Kriegshäuptling Euch eine Auszeichnung zukommen lässt.]], },
    { [[Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr Arazzius den Grausamen tötet.]], },
  },
  [10403] = {
    [[Naladu]],
    { [[Vielleicht ist noch nicht alle Hoffnung für das Lumpenpack verloren. Ich bin einer der vier Ältesten, die diesen Stamm leiteten, bevor wir von den Illidari versklavt wurden. Ich dachte, dass die anderen tot seien, aber dann habe ich erfahren, dass zumindest zwei weitere noch am leben sind - angekettet und machtlos, so wie ich.]], [[Ich habe Naladu noch nicht finden können und befürchte das Schlimmste. Oben in den Ruinen gibt es noch mehr Hütten, vielleicht könntet Ihr dort nach ihm suchen. Es wäre ein großes Glück für unseren Stamm, wenn alle Ältesten am Leben wären.]], },
    { [[Sucht Naladu in den Ruinen von Sha'naar.]], },
  },
  [10404] = {
    [[Gegen die Legion]],
    { [[Wir haben erfahren, dass die Brennende Legion Kael'thas mit technologischem Wissen unterstützt hat, und so die Bedrohung, die von ihm ausgeht, noch um ein Vielfaches vergrößert hat.]], [[Da die Legion in der Gegend so zahlreich vertreten ist, können wir den größten Schaden nur durch einen gezielten Angriff auf die Konstruktionsbasen im Norden erreichen.]], [[Geht zur Konstruktionsbasis: Vergessenheit und zur Konstruktionsbasis: Gehenna und zerstört unsere Feinde. Möge das Licht Eure Klinge führen, <name>.]], },
    { [[Exarch Orelis in Area 52 möchte, dass Ihr zur Konstruktionsbasis: Vergessenheit und zur Konstruktionsbasis: Gehenna geht und 12 Feuerfäuste der Gan'arg, 6 Konstruktionsherren der Gan'arg und 6 Zornschaffer tötet.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10405] = {
    [[S-A-B-O-T-A-G-E]],
    { [[Wir müssen die Maschinen zerstören, die diese abnormalen Dunkelpirscher hervorbringen. Ihren Protobeschleuniger anzugreifen ist sinnlos, da es sich um eine astrale Erfindung handelt. Wir müssen stattdessen die Rohre der Manaschmiede, die sie als Energieleitung verwenden, zerstören.]], [[Ich habe fast alles, was ich brauche, um ein Gerät herzustellen, das die Energieleitung überlasten kann. Ich brauche nur noch die Bandagen eines Archon oder Oberanführers, der sich bald verwandeln wird, um das Gerät einzustellen.]], },
    { [[Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr eine vorbereitete Bandage des Astraleums besorgt.]], },
  },
  [10406] = {
    [[Die Nachricht überbringen]],
    { [[Ich schicke einen meiner Demolierer los, um das Störgerät zu platzieren. Sorgt nur dafür, dass ihm kein Leid geschieht, während er den Störer aufstellt.]], [[Beschützt ihn! Er wird sich nicht wehren, wenn er von Feinden angegriffen wird, da er nur ein holografisches Bild ist, aber auch das kann zerstört werden!]], [[Kehrt zu mir zurück, wenn Ihr fertig seid.]], },
    { [[Das Bild von Kommandant Ameer bei dem Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Demolierer des Protektorats zur Energieleitung der Manaschmiede Ultris begleitet und ihn beschützt, während er die Leitung sabotiert.]], },
  },
  [10407] = {
    [[Socrethars Schatten]],
    { [[Der berüchtigte Eredar, Socrethar, führt die Brennende Legion im Nethersturm an. Allein seinen Namen zu nennen lässt die meisten Draenei erzittern. Er erteilt seine Befehle von einer Insel im Nordwesten aus, die nur seine vertrauenswürdigsten Leutnants betreten können.]], [[Um dorthin zu gelangen, müsst Ihr einen Teleportationsstein besorgen, was nicht einfach wird. Die erste Hälfte des Steins befindet sich im Besitz von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit. Die zweite Hälfte hat der Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna.]], },
    { [[Anachoret Karja in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10408] = {
    [[Nexuskönig Salhadaar]],
    { [[Ich habe einen weiteren Störer in meinem Besitz. Einer, der in der Lage ist, die drei Energieleitungen, die Salhadaar umgeben, zu zerstören. Wenn die Energieleitungen einmal vernichtet sind, werden sich Salhadaars schützende Kraftschilde senken. Für diese Tat wird er Euch mit einer Wildheit angreifen, die Ihr noch nie zuvor gesehen habt, <name>.]], [[Wenn Ihr bereit seid, die Welt von Salhadaar zu befreien, gebe ich Euch den Störer.]], },
    { [[Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr den Störer des Protektorats benutzt, um Salhadaar Kraftfelder zu senken und Nexuskönig Salhadaar zu vernichten.]], },
  },
  [10409] = {
    [[Todesstoß für die Legion]],
    { [[Wenn man mit mächtigen Eredar zu tun hat, sind große Armeen meist nutzlos. Ihre verderbte Macht über die sterblichen Seelen ist so stark, dass nur die mit einem starken Willen ihnen gegenübertreten können.]], [[Bringt diesen Stein zum Invasionspunkt: Oberanführer zwischen der Konstruktionsbasis: Vergessenheit und der Konstruktionsbasis: Gehenna und nehmt Eure engsten und stärksten Freunde mit. Orelis und Karja erwarten Euch bei Socrethars Sitz. Ich werde Adyen mit ihnen losschicken.]], [[Möge das Licht sich heute behaupten, <name>. Möge Socrethars Ende nahen.]], },
    { [[Hohepriesterin Ishanah möchte, dass Ihr zum Transporter der Legion am nordwestlichsten Ende des Nethersturms geht und Euch zu Socrethars Sitz teleportiert. Wenn Ihr dort seid, besiegt Socrethar.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10410] = {
    [[Ishanahs Hilfe]],
    { [[Die Zeit ist gekommen, da Ihr Euch Socrethar stellen müsst, <name>. Einem Eredar entgegen zu treten ist eine Aufgabe, die keiner von uns auf die leichte Schulter nimmt. Schon ein kleiner Fehler kann uns das Leben kosten, wenn wir unvorbereitet sind.]], [[Bringt Socrethars Teleportationsstein zu Hohepriesterin Ishanah beim Schrein des Endlosen Lichts in Shattrath. Sie wird entscheiden, wie mit der Situation umzugehen ist.]], },
    { [[Bringt Socrethars Teleportationsstein zu Hohepriesterin Ishanah beim Schrein des Endlosen Lichts in Shattrath.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10411] = {
    [[Elektroschock!]],
    { [[Um die zerstörte Manaschmiede herum findet Ihr die Überbleibsel von Dimensius' Taten. Sickernder Schlick verschmutzt die Teiche um Ultris herum. Direkt unter diesen Teichen, wo sich nun der Stützpunkt des Astraleums befindet, findet Ihr noch mehr Müll in Form von Leerenabschaum.]], [[Der beste Weg, ihn zu entsorgen, ist, den Schleim mithilfe von Elektroschocks in Kugeln zu verwandeln. Benutzt dieses Elixier, um Eure Angriffe unter Strom zu setzen. Die Entladung, die dabei entsteht, löst den Schleim in seine Urform auf. Zerstört die Kugeln!]], },
    { [[Forscher Navuud im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 30 Leerenabschaumkugeln und 30 Sickerschlickkugeln zerstört.]], },
  },
  [10412] = {
    [[Siegel der Feuerschwingen]],
    { [[Es ist kein Geheimnis, dass Kael'thas Sonnenwanderer uns als große Bedrohung sieht. Nüchtern betrachtet gibt es nicht viele Sin'dorei, die frisch aus Silbermond kommen und bereit sind, sich der Gesellschaft, die Kael'thas unterhält, anzuschließen.]], [[Es ist auch kein Geheimnis, dass es sich hier nicht nur um Wortgefechte handelt. Die Seher und die vielen Armeen, die Kael'thas dienen, haben und werden sich angreifen, sobald sie sich begegnen. Der einzige Weg, wie Ihr beweisen könnt, dass Ihr auf unserer Seite steht, ist, sich dem Kampf anzuschließen, <name>.]], [[Kaels niederrangige Anhänger tragen Siegel der Feuerschwingen bei sich, die ihren Rang anzeigen. Bringt sie mir.]], },
    { [[Magistrix Fyalenn in Shattrath möchte, dass Ihr ihr 10 Siegel der Feuerschwingen bringt.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10413] = {
    [[Die Schrecken der Verschmutzung]],
    { [[Ihr befüllt vorsichtig eine Phiole mit dem Schlamm des gefallenen Leerenschreckens.]], [[Forscher Navuud im Wachposten des Protektorats wäre an einem solchen Fund sicher interessiert. Bringt ihn sofort zu ihm!]], },
    { [[Bringt die Phiole mit Leerenschreckensschlamm zu Forscher Navuud im Wachposten des Protektorakts im Nethersturm.]], },
  },
  [10414] = {
    [[Einzelne Siegel der Feuerschwingen]],
  },
  [10415] = {
    [[Mehr Siegel der Feuerschwingen]],
  },
  [10416] = {
    [[Bündelung der Macht]],
    { [[Arkane Folianten sind keine gewöhnlichen Bücher, die sich mit arkanen Themen befassen. Ihnen wohnt eine wesenhafte Macht inne - eine Macht, die ich in eine einzelne arkane Rune bündeln kann.]], [[Solltet Ihr auf Euren Reisen einen arkanen Folianten finden, werde ich Euch eine solche Rune geben. Nur die mächtigsten magiebegabten Wesen tragen die Bücher, die ich brauche, bei sich. In der Scherbenwelt sind das meistens Kaels Blutelfen, aber Ihr wärt überrascht, wo solche Bücher des Öfteren auftauchen.]], },
    { [[Voren'thal der Seher in Shattrath möchte, dass Ihr einen arkanen Folianten besorgt.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10417] = {
    [[Führt eine Diagnose durch!]],
    { [[Wo auch immer das Konsortium Einzug hält, bringen wir unsere Biokuppeltechnologie mit. Diese Technologie erlaubt uns, in einer kontrollierten und lebenserhaltenden Atmosphäre zu arbeiten, da wir uns meist in feindlichen Gebieten befinden.]], [[Die bösartigen Bedingungen und Kreaturen des Nethersturms haben begonnen, die Ausrüstung, die unsere Biokuppeln aufrechterhält, zu zerstören, besonders den Generator im Osten. Ich habe einen Diagnoselauf dort begonnen, damit ich hoffentlich herausfinden kann, was gegen den Schaden getan werden kann. Könntet Ihr die Resultate dort abholen und zu mir bringen?]], },
    { [[Beschafft die Untersuchungsergebnisse und bringt sie zu Mehrdad im Mittelreichposten.]], },
  },
  [10418] = {
    [[Die Saboteure ausschalten]],
    { [[Als ich das Gebiet nach Hinweisen auf den Schaden unserer Ausrüstung abgesucht habe, habe ich Teile der Schildgeneratoren auf dem Grund des kleinen Sees im Norden gesehen. Ich bin mir nicht sicher, warum die Krokilisken sich für unsere Ausrüstung interessieren, aber wir müssen dieses Problem beseitigen.]], [[Das Konsortium ist sich in dieser Sache einig. Wenn es zu einer Entscheidung zwischen der Ausrüstung und Krokilisken kommt, müssen die Tiere gehen. Ich werde Euch angemessen belohnen, wenn Ihr Euch darum kümmert.]], },
    { [[Mehrdad beim Mittelreichposten möchte, dass Ihr 8 Stachelschuppenkrokilisken tötet.]], },
  },
  [10419] = {
    [[Arkane Folianten]],
  },
  [10420] = {
    [[Ein reinigendes Licht]],
    { [[Dieser Tempel ist eine der heiligsten Stätten der ganzen Scherbenwelt. Die Macht des Lichts ist hier so stark, dass es weltliche Gegenstände sogar von der schlimmsten teuflischen Verderbnis befreien kann.]], [[Falls Ihr bei Euren Kämpfen mit den dämonischen Feinden teuflische Waffen finden solltet, bringt sie zu mir. Dann werde ich Euch die reinigende Kraft des Lichts beweisen.]], },
    { [[Bringt eine Ladung teuflische Waffen zu Ishanah in Shattrath.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10421] = {
    [[Teuflische Waffen]],
  },
  [10422] = {
    [[Hauptmann Tyralius]],
    { [[Hauptmann Tyralius hat eine Truppe von Soldaten vor 10 Tagen in Richtung des Stützpunkts des Astraleums geführt. Seitdem haben wir nichts von ihm oder seinen Leuten gehört.]], [[Es ist sehr wahrscheinlich, dass sie überfallen und getötet wurden, aber ich bekomme ein merkwürdiges Signal auf meinen Instrumenten.]], [[Ich glaube, es ist ein Notsignal, aber diese Dinge haben sich schon öfter geirrt. Wenn Tyralius noch am Leben ist, halten sie ihn in einem Ihrer Gefängnisse fest. Ihr müsst das Gefängnis finden und es mit einem Schlüssel, den Ihr wahrscheinlich bei einem Wärter finden könnt, öffnen.]], },
    { [[Flesh Handler Viridius at the Protectorate Watch Post in Netherstorm has asked that you find and free Captain Tyralius.]], },
  },
  [10423] = {
    [[Zur Sturmsäule]],
    { [[Meisteringenieur Ghabar möchte sicher die Ergebnisse der Diagnose erfahren. Sie zeigen uns wenigstens, dass wir die Funktionalität der Kuppelgeneratoren genau im Auge behalten müssen. Wenn die Felder immer instabiler werden, können wir ihre Auswirkungen auch immer schwerer kontrollieren. Das könnte ein Desaster für unsere Expedition und alles, was sich in den Kuppeln befindet, bedeuten.]], [[Ihr findet Ghabar in der Sturmsäule. Folgt einfach der Straße nördlich dieser Biokuppel und überquert die Brücke. Sie führt Euch direkt zum Aufzug unten in der Säule.]], },
    { [[Speak with Ghabar at the Stormspire.]], },
  },
  [10424] = {
    [[Diagnose: kritisch]],
    { [[Wenn Mehrdads Ergebnisse richtig sind - und ich habe keinen Grund daran zu zweifeln - könnte der Rest der Kuppeln in Gefahr sein.]], [[Ich glaube jedoch, dass Mehrdad nicht den richtigen Grund für die Defekte gefunden hat. Tiere können die Generatoren bisweilen schon beschädigen, aber niemals so schwer.]], [[Um sicher zu gehen, möchte ich, dass Ihr ein paar Diagnoseläufe bei dem Hauptkuppelgenerator der Sturmsäule am östlichen Rand in der Biokuppel Sutheron durchführt.]], },
    { [[Benutzt das Diagnosegerät in der Nähe des Generators der Biokuppel Sutheron und bringt die Diagnoseergebnisse zu Gabhar in der Sturmsäule zurück.]], },
  },
  [10425] = {
    [[Ausbruch aus dem Stützpunkt]],
    { [[K'aresh sei Dank! Er hätte mich in Stücke gerissen!]], [[Ich bin ein Soldat des Protektorats, Fleischling. Meine Truppe wurde vor etwa 10 Tagen auf eine Aufklärungsmission geschickt. Als wir uns über die oberen Regionen dieser Insel bewegten, wurden wir von einer Gruppe des Astraleums überfallen. Zumindest denke ich, dass es das Astraleum war. Sie hatten die gleichen Energiefarben, aber ihre Bandagen sahen irgendwie leer aus.]], [[Sie haben nur Tyralius verschont. Ich glaube, sie haben ihn in ein Gefängnis eingesperrt.]], [[Ich muss zurück zum Wachposten des Protektorats! Könnt Ihr mich dorthin begleiten?]], },
    { [[Begleitet den gefangenen Vorposten des Protektorats zurück zum Wachposten des Protektorats im Nethersturm.]], },
  },
  [10426] = {
    [[Flora der Biokuppel]],
    { [[Schaut Euch nur um, <class>! Ist es nicht erstaunlich? Natürlich ist diese unglaubliche Fülle von Leben ein Nebenprodukt der Kuppeln der Astralen, aber stellt Euch nur vor, was diese Technologie in den Händen der Expedition bedeuten könnte.]], [[Ich bereite mich darauf vor, mit den Astralen zu verhandeln, um Zugang zu ihrer Technologie zu bekommen, aber ich wüsste zuvor gerne genauer, welche Auswirkungen sie auf das pflanzliche Leben hat.]], [[Ich habe ein Gerät gebaut, mit dem ich die Energien auf die Peitscher in dieser Gegend richten kann. Wärt Ihr so nett und würdet es für mich ausprobieren und mir die Ergebnisse mitteilen?]], },
    { [[Benutzt den Energiefeldmodulator bei den Farahlonpeitschern. Beobachtet die Ergebnisse und meldet Euch bei Aurine Mondflamm in der Sturmsäule zurück, wenn Ihr 10 Tests durchgeführt habt.]], },
  },
  [10427] = {
    [[Fauna der Biokuppel]],
    { [[Eure Arbeit bei den Peitschern stellte sich als erstaunliche Information heraus, aber ich frage mich, ob das auch für die Tiere gilt.]], [[Ich fürchte jedoch, dass es schwieriger wird, Informationen über die Talbuks zu sammeln, als über die Peitscher.]], [[Mit diesem Markierungsgerät könnt Ihr einen geschwächten Talbuk schlafen legen und ihn mit einer Markierung versehen, mit deren Hilfe ich das Tier während seiner Wachstumsphase verfolgen kann. Der Mechanismus des Markierungsgeräts funktioniert erst, wenn der Talbuk schon sehr erschöpft ist.]], },
    { [[Markiert 12 Talbuks für Aurine Mondflamm in der Sturmsäule. Ihr könnt mit dem Talbukmarkierer sowohl Talbukböcke als auch Talbukkühe markieren.]], },
  },
  [10428] = {
    [[Der vermisste Fischer]],
    { [[Von fiebrigen Träumen geplagt sprach unser verletzter Gast von einem Fischer, Cowlen, der auf der Silbermythosinsel im Südwesten vor der Küste der Azurmythosinsel, in Schwierigkeiten war.]], [[Nach all den Geschehnissen hier hatte keiner die Gelegenheit, nachzuschauen, aber ich mache mir doch ein paar Sorgen. Vielleicht könnt Ihr etwas tun, <name>?]], },
    { [[Sucht Cowlen bei den südöstlichen Docks der Silbermythosinsel vor der Küste der Azurmythosinsel.]], },
  },
  [10429] = {
    [[Wenn die Natur zu weit geht]],
    { [[Die Technologie der Kuppeln ist so mächtig! Sicher gibt es Grenzen, die wir nicht zu überschreiten wagen, <name>.]], [[Wesen, die zu schnell oder zu groß wachsen, können genauso schlimm sein wie eine unfruchtbare Landschaft.]], [[Ich habe Gerüchte gehört, dass sich genau so eine Kreatur im Teich im Osten herumtreibt. Die Händler der Astralen wollen die übergroße Hydra aus Spaß erlegen, aber ich habe etwas anderes mit dem Riesen vor. Bringt mir das Herz dieser Kreatur, damit ich es untersuchen kann.]], },
    { [[Aurine Mondflamm in der Sturmsäule möchte, dass Ihr ihr ein titanisches Hydraherz bringt.]], },
  },
  [10430] = {
    [[Den Prototyp testen]],
    { [[Ich habe an einem tragbaren Reparatursatz für den Generator gearbeitet, der in den kälteren Regionen, in denen das Konsortium Geschäfte macht, funktioniert. Zugegeben, er war nicht für den Nethersturm entwickelt worden, aber wir sollten trotzdem keine Probleme damit haben.]], [[Ich habe bereits einen Test mit einem meiner besten Techniker bei der Biokuppel Fernfeld hinter dem Netherstein im Nordosten vereinbart. Bringt diesen Prototypen zu Tashar. Er wird wissen, was damit zu tun ist. Wenn er funktioniert können wir ihn benutzen, um die Schildgeneratoren bei den größeren Kuppeln zu verstärken.]], },
    { [[Bring the Repair Apparatus to Tashar at Eco-Dome Farfield.]], },
  },
  [10431] = {
    [[Hilfe von außen]],
    { [[Die Befehle des Sonnenzorns bestätigen den Verdacht, dass Kael'thas bei dem Bau und dem Betrieb der Manaschmieden Hilfe von außen bekommen hat. Wer dahinter steckt, ist immer noch ein Geheimnis, doch wir können es wahrscheinlich in der Manaschmiede Ara herausfinden.]], [[Ich habe eine Gruppe meiner Leute losgeschickt, um die Gegend zu erkunden. Geht zu Tulumans Landeplatz nördlich von hier und sprecht mit Kaylaan. Hört Euch an, was er in der Manaschmiede Ara gefunden hat. Findet heraus, wer die Armee des Sonnenzorns unterstützt, und kümmert Euch um sie.]], },
    { [[Geht zu Tulumans Landeplatz, nördlich von Area 52, und sprecht mit Kaylaan.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10432] = {
    [[Eindeutige Beweise]],
    { [[Wie Ihr sicher bemerkt habt, laufen in der Manaschmiede Ara überall Diener der Brennenden Legion herum. Kael'thas ist einen Bund mit der Brennenden Legion eingegangen. Er muss gehört haben, dass Illidan dem Wahnsinn verfallen ist, und den Meister gewechselt haben.]], [[Wenn die Seher dafür einen stichhaltigen Beweis hätten, wäre das ein deutlicher politischer Vorteil für sie. Ich weiß genau, dass die Streitkräfte der Legion bei der Manaschmiede hier Befehle haben, die Kaels Siegel tragen.]], [[Besorgt diese Befehle und bringt sie zu Thalodien.]], },
    { [[Besorgt Euch 8 Befehle von Kael'thas von den Dämonen bei der Manaschmiede Ara und bringt sie zu Spionagemeister Thalodien in Area 52.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10433] = {
    [[Den Schein wahren]],
    { [[Die Astralen sind sehr klug und aufmerksam, daher ist es kein Zufall, dass ich hier als einfacher Fellhändler auftrete. Tatsächlich wurde ich von Area 52 hierher geschickt, um die Technologie, die die Astralen in ihren Biokuppeln einsetzen, zu bewerten.]], [[Ich würde gerne Eure Hilfe in Anspruch nehmen, <class>. Wir wurden darum gebeten, wo es nur geht andere Völker als Goblins als Spione einzusetzen.]], [[Ich möchte, dass Ihr mir als Jäger verkleidet ein paar Reißzahnpelze bringt. Ihr findet die Luchse überall bei der Biokuppel Mittelreich.]], },
    { [[Bringt 10 Reißzahnpelze zu Shauly Pore im Mittelreichposten.]], },
  },
  [10434] = {
    [[Das dynamische Duo]],
    { [[Ausgezeichnete Arbeit.]], [[<Shauly zwinkert Euch zu.>]], [[Ich glaube, sie kaufen es uns ab. Wenn ich Euch jetzt zu meinen Verbündeten in der Sturmsäule schicke, werden sie nicht misstrauisch werden.]], [[Haltet nach Aktion Jaxon und Audi der Nadel Ausschau. Sie werden Euch alles sagen, was Ihr wissen müsst. Sagt ihnen, dass Shauly Euch geschickt hat.]], },
    { [[Speak with Audi the Needle at the Stormspire.]], },
  },
  [10435] = {
    [[Die Waren zurückholen]],
    { [[<Audi sieht sich um und senkt die Stimme.>]], [[Ich bin sicher, dass Shauly Euch ein wenig von unserer Arbeit hier erzählt hat. Als Jaxon und ich draußen im Feld auf der Jagd waren, haben wir Teile von den Generatoren der Biokuppeln der Astralen gesammelt und sie in den Nestern unserer Beute, den Raptoren bei der Biokuppel Fernfeld, versteckt.]], [[Das ist eine abgelegene Gegend nordöstlich der Sturmsäule, in der Nähe des Nethersteins. Ich nehme nicht an, dass Ihr dort auf viele Astrale treffen werdet, aber würdet Ihr uns helfen, die Kuppelgeneratorenteile zu sammeln?]], },
    { [[Audi die Nadel bei der Sturmsäule möchte, dass Ihr 10 Kuppelgeneratorsegmente besorgt.]], },
  },
  [10436] = {
    [[Freie Bahn!]],
    { [[Bevor wir den Test beginnen können, müssen wir sicherstellen, dass das Gebiet frei von allem ist, was uns dabei stören könnte. In unserem Fall sind das diese zu groß geratenen Echsen.]], [[<Tashar zeigt auf die Sichelzahnraptoren.>]], [[Es ist nicht voraussehbar, was passiert, wenn einer von ihnen auf die Idee kommt, genüsslich in den Generator zu beißen, während er gerade auflädt. Ich habe in all den Jahren als Techniker schon viele merkwürdige Dinge gesehen, darunter auch ein paar Mutationen, die einst ganz normale Tiere waren.]], },
    { [[Tashar bei der Biokuppel Fernfeld möchte, dass Ihr 12 Sichelzahnraptoren tötet.]], },
  },
  [10437] = {
    [[Anleitung zur Zerstörung]],
    { [[Auf der Manaschmiede Ultris befindet sich der Riss im Nether, durch den Dimensius, der Leerenfürst, gekommen ist. Dieser Riss bildet eine Verbindung zwischen Dimensius und der Leere und macht ihn unverwundbar. Wenn es uns gelingt, diese Energieleitung, mit der Dimensius sich aus dieser Existenzebene zurückziehen kann, zu verschließen, können wir sie und Dimensius vernichten.]], [[Dazu müsst Ihr zunächst Fragmente von Dimensius von den Leerkreaturen, die für ihn die Drecksarbeit erledigen, sammeln.]], },
    { [[Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr 8 Fragmente von Dimensius besorgt.]], },
  },
  [10438] = {
    [[Auf netherischen Schwingen]],
    { [[Hier ist es, <name>. Nehmt den Phasenstörer und steigt auf! Der Netherdrachen des Protektorats wird Euch nahe genug an Ultris heranfliegen, damit Ihr den Störer auf die Leerleitung werfen könnt.]], [[Sendet so viele Störsignale wie möglich aus, um eine komplette Zerstörung zu gewährleisten!]], [[Wenn Ihr bereit seid, sprecht mit dem Netherdrachen und er wird mit Euch in die Lüfte steigen.]], [[Wenn Ihr das Ziel verfehlt, müsst Ihr mehr Bomben von mir besorgen, bevor Ihr weitermachen könnt.]], },
    { [[Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr den Phasenstörer bei der Leerleitung auf der Manaschmiede Ultris benutzt. Sendet so viele Störsignale wie möglich aus, um eine komplette Zerstörung zu gewährleisten. Die Entladungen des Phasenstörers werden automatisch das Ziel treffen, solange Ihr nahe genug seid, um ihn zu benutzen.]], [[Kehrt zu Professor Dabiri zurück, wenn Ihr erfolgreich wart!]], },
  },
  [10439] = {
    [[Dimensius der alles Verschlingende]],
    { [[Eure mutigen Taten haben Ameer zum handeln bewegt! Er hat Hauptmann Saeed und die Rächer Aufstellung nehmen lassen!]], [[Jetzt müssen wir nur noch Dimensius vernichten.]], [[Wenn Ihr bereit seid, lasse ich Saeed rufen. Wenn er nicht gerade einen anderen Auftrag ausführt, wartet er östlich von hier auf Eure Befehle.]], [[Lasst ihn wissen, wenn Ihr bereit seid zu kämpfen, damit er seine Truppen in Bewegung setzen kann.]], [[Viel Glück, Fleischling! Wir jagen Dimensius schon seit tausend Jahren!]], },
    { [[Professor Dabiri im Wachposten des Protektorats im Nethersturm möchte, dass Ihr mit Hauptmann Saeed sprecht, seiner Armee zur Manaschmiede Ultris folgt und Dimensius den alles Verschlingenden tötet!]], },
  },
  [10440] = {
    [[Erfolg!]],
    { [[Der Test war ein großer Erfolg! Wir können das Gerät benutzen, um die Kuppeln zu reparieren.]], [[Berichtet in der Sturmsäule von den Testergebnissen und lasst Ghabar wissen, dass ich ein Muster in den Schäden an den Schildgeneratoren entdeckt habe. Wichtige Teile der Generatoreinheiten sind an verschiedenen Orten verschwunden. Das ist kein Zufall, <name>, kein technischer Fehler. Wir werden bestohlen.]], },
    { [[Return to the Stormspire and speak with Ghabar.]], },
  },
  [10441] = {
    [[Die Waren unters Volk bringen.]],
    { [[<Audi senkt die Stimme wieder.>]], [[Jetzt, da wir die Waren haben, werde ich die meisten davon nach Area 52 schicken. Wir haben hier mehr, als wir für einen Generator brauchen, was uns eine perfekte Geschäftsmöglichkeit eröffnet.]], [[Ich habe gesehen, dass die Vertreterin der Expedition des Cenarius verhandeln will. Ich möchte, dass Ihr sie wissen lasst, dass wir ihr das, was sie sucht, zu einem günstigen Preis geben können. Es gibt kein Grund für sie, sich weiter mit diesen betrügerischen Astralen herumzuschlagen.]], },
    { [[Speak with Aurine Moonblaze at the bottom of the Stormspire.]], },
  },
  [10442] = {
    [[Dem Posten des Cenarius helfen]],
    { [[<class>, es gibt hier eine Tauren namens Thiah Rotmähne, die uns ständig um Hilfe anfleht. Sie gehört zur Expedition des Cenarius, die einen kleinen Posten im Westen, auf der südlichen Seite der Straße in Richtung Zangarmarschen, errichtet hat.]], [[Wenn Ihr beim Dornnebelhügel angekommen seid, seid Ihr zu weit gegangen.]], [[Könntet Ihr vielleicht dort hingehen und nachschauen, was sie braucht? Ich möchte Waldläuferhauptmann Venn'ren nicht mit solchen Kleinigkeiten belästigen.]], },
    { [[Falknerin Drenna Flusswind möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet.]], },
  },
  [10443] = {
    [[Dem Posten des Cenarius helfen]],
    { [[Der Zirkel des Cenarius hat eine Expedition in die Scherbenwelt geschickt. So weit ich verstanden habe, sind sie hier, um zu untersuchen, was passiert ist, und wenn möglich die Schäden rückgängig zu machen.]], [[Sie haben einen kleinen Posten im Westen, auf der südlichen Seite der Straße in Richtung Zangarmarschen, errichtet.]], [[Könnt Ihr vielleicht dort hingehen und schauen, wie Ihr ihnen helfen könnt? Sprecht mit Thiah Rotmähne. Sie hat uns um Hilfe gebeten.]], },
    { [[Amaan der Weise möchte, dass Ihr Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel Eure Dienste anbietet.]], },
  },
  [10444] = {
    [[Meldung bei Allerias Posten]],
    { [[Das erfordert sofortiges Handeln!]], [[Wir haben jemanden da draußen, der sich noch näher am Posten der Feuerschwingen befindet. Ich möchte, dass Ihr mit ihm sprecht. Er weiß sicher besser, was wir als Nächstes tun sollten.]], [[Meldet Euch bei Leutnant Meridian bei Allerias Posten und setzt ihn von der Lage in Kenntnis. Ihr findet den Posten im Nordosten am südlichen Ufer des Flusses, direkt südlich vom Posten der Feuerschwingen.]], [[Beeilt Euch, <name>!]], },
    { [[Jenai Sternwisper möchte, dass Ihr Euch bei Leutnant Meridian in Allerias Posten in den Wäldern von Terokkar meldet.]], },
  },
  [10445] = {
    [[Die Phiolen der Ewigkeit]],
    { [[Illidan entnahm sieben Phiolen aus dem Brunnen der Ewigkeit. Drei davon leerte er in den See auf dem Berg Hyjal, wodurch ein zweiter Brunnen der Ewigkeit entstand. Jahrelang glaubte man den Rest verloren.]], [[Seit der Öffnung des Dunklen Portals haben wir erfahren, dass er seinen Leutnants Kael'thas und Vashj je eine Phiole gegeben hat. Holt zurück, was von den Phiolen noch übrig ist. Wir brauchen sie als Fokus, um ein Tor zu den Geschehnissen, seien sie aktuell oder vergangen, am Berg Hyjal zu öffnen. Das Ergebnis der Schlacht um den Berg Hyjal muss erhalten bleiben.]], },
    { [[Soridormi in den Höhlen der Zeit möchte, dass Ihr die Überreste von Vashjs Phiole im Echsenkessel und die Überreste von Kaels Phiole von Kael'thas Sonnenwanderer in der Festung der Stürme besorgt.]], },
  },
  [10446] = {
    [[Der finale Code]],
    { [[Sie bauen eine zweite Manabombe im Hof! Meine Agenten berichten, dass Sharth Voldoun, der Zuständige des Projekts im Posten der Feuerschwingen, den finalen Code der Bombe haben muss.]], [[<name>, Ihr müsst dorthin zurückkehren und den letzten Code von ihm besorgen. Er hält sich auf der Spitze der höchsten Säule auf.]], [[Bringt den finalen Code zu der Manabombe, an der sie im mittleren Hof arbeiten, und gebt ihn ein.]], [[Nur dann können wir sicher sein, dass Allerias Feste gerettet ist.]], },
    { [[Leutnant Meridian möchte, dass Ihr den finalen Code eingebt, um die Manabombe auszulösen. Meldet euch dann bei Jenai Sternwisper in Allerias Feste in den Wäldern von Terokkar zurück.]], },
  },
  [10447] = {
    [[Der finale Code]],
    { [[Sie bauen eine zweite Manabombe im Hof! Meine Agenten berichten, dass Sharth Voldoun, der Zuständige des Projekts im Posten der Feuerschwingen, den finalen Code der Bombe haben muss.]], [[<name>, Ihr müsst dorthin zurückkehren und den letzten Code von ihm besorgen. Er hält sich auf der Spitze der höchsten Säule auf.]], [[Bringt den finalen Code zu der Manabombe, an der sie im mittleren Hof arbeiten, und gebt ihn ein.]], [[Nur dann können wir sicher sein, dass die Steinbrecherfeste gerettet ist.]], },
    { [[Unteroffizier Chawni möchte, dass Ihr den finalen Code eingebt, um die Manabombe auszulösen. Meldet euch dann bei Tooki in der Steinbrecherfeste in den Wäldern von Terokkar zurück.]], },
  },
  [10448] = {
    [[Meldung beim Steinbrecherlager]],
    { [[Das erfordert sofortiges Handeln!]], [[Wir haben jemanden da draußen, der sich noch näher am Posten der Feuerschwingen befindet. Ich möchte, dass Ihr mit ihr sprecht. Sie weiß sicher besser, was wir als Nächstes tun sollten.]], [[Meldet Euch bei Unteroffizier Chawni im Steinbrecherlager und setzt sie von der Lage in Kenntnis.]], [[Nehmt die Straße nach Norden aus der Feste heraus. Ab der Mitte von Tuurem müsst Ihr der Straße nach Osten folgen. Ihr findet das Lager am Ufer des Flusses, der direkt südwestlich des Postens der Feuerschwingen liegt.]], [[Beeilt Euch, <name>!]], },
    { [[Tooki möchte, dass Ihr Euch bei Unteroffizier Chawni im Steinbrecherlager in den Wäldern von Terokkar meldet.]], },
  },
  [10449] = {
    [[Apothekerin Zelana]],
    { [[Apethecary Zelana has been studying the beasts of Hellfire Peninsula in the field.  She wants a sample of the blood you gathered; with luck, she might discover what turned those Bonechewer bastards' skins red!]], [[Zelana is at Reaver's Fall, southeast of Thrallmar.  Follow the road east and when you see a destroyed, green-glowing hunk of metal, you're there.  She has a camp set up behind the wyverns.]], },
    { [[Vurtok Axtbrecher in Thrallmar möchte, dass Ihr die Blutprobe eines Knochenmalmers zu Apothekerin Zelana im Häschersturz bringt.]], },
  },
  [10450] = {
    [[Blut der Knochenmalmer]],
    { [[Knochenmalmer! Sie sollen alle sterben! Der Anblick ihrer roten Haut und ihrer Verkommenheit bringt mein Blut zum kochen!]], [[Ich habe einen Auftrag für Euch, <name>. Tötet die Orcs der Knochenmalmer und sammelt ihr bösartiges Blut. Es gibt eine Apothekerin, die vielleicht herausfinden kann, wie diese Orcs der Verderbnis der Dämonen anheim fallen konnten, eine Verderbnis, derer wir uns vor so kurzer Zeit erst entledigen konnten.]], [[Die Knochenmalmer streunen in der Nähe der zerstörten Kriegsmaschinerien entlang des Pfads der Ehre im Süden herum. Kehrt zu mir zurück, wenn Euer Auftrag erfüllt ist.]], },
    { [[Bringt 12 Phiolen mit Blut eines Knochenmalmers zu Vurtok Axtbrecher in Thrallmar.]], },
  },
  [10451] = {
    [[Flucht aus der Zisterne der Echsennarbe]],
    { [[Der Erdenmutter sei Dank! Ich habe mich vor ein paar Tagen von den anderen getrennt. Ich wollte der Quelle der Veränderung des Wassers, die wir spüren, auf den Grund gehen. Meine Suche führte mich zur Echsennarbe, wo ich herausgefunden habe, dass die Naga die Wasservorräte des Schattenmondtals verderben!]], [[Unglücklichweise hat man mich entdeckt und gefangen genommen. Nicht auszudenken, welch grausame Dinge sie mit mir vorhatten!]], [[Wir müssen hier weg. Helft mir zu fliehen und sucht dann Torlok beim Altar der Verdammnis auf.]], },
    { [[Begleitet Erdheilerin Wilda aus der Zisterne der Echsennarbe hinaus und sucht dann Erdheiler Torlok beim Altar der Verdammnis am westlichen Rang der Hand von Guldan auf.]], },
  },
  [10454] = {
    [[FLAG - OFF THE RAILS]],
  },
  [10455] = {
    [[Die nahende Wildnis]],
    { [[Wir haben den Lebenden Hain sehr schnell zum Wachsen gebracht, aber nun haben wir die Kontrolle über die Luchse verloren.]], [[Einst war ihre Population gleichmäßig, aber nun kommen die Hainpirscherluchse immer näher an Sylvanaar heran. In letzter Zeit gab es schon einige unerfreuliche Zwischenfälle.]], [[Ich nehme an, dass die Arakkoa vom Lashhversteck dahinter stecken, aber zunächst sind die Luchse selbst unser Problem.]], [[Ich wäre Euch sehr dankbar, wenn Ihr uns dabei helfen würdet, mit dieser Lage fertigzuwerden.]], },
    { [[Rina Moonspring wishes you to slay 12 Grovestalker Lynxes with all due haste and then return to her at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10456] = {
    [[Marodierende Wölfe]],
    { [[Die abscheulichen Völker der Horde haben eine alte Siedlung im Osten eingenommen. Sie haben ein großes Rudel von Terrorwölfen mitgebracht, die sie gerne als Reittiere und Wachhunde einsetzen.]], [[Diese "Wachhunde" haben jedoch begonnen, hinter der Brücke, die uns voneinander trennt, Schaden anzurichten. Sie belästigen unsere Feendrachen, die für die Bestäubung unseres Waldes unersetzlich sind.]], [[Nehmt den östlichen Ausgang, um die Brücke über der Blutschlägerklamm, die zum Zackengrat im Osten führt, zu überqueren, und bringt mir ihre Schwänze!]], },
    { [[Rina Moonspring has asked you to bring her 4 Thunderlord Dire Wolf Tails.  After doing so, return to her at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10457] = {
    [[Die Eigenen schützen]],
    { [[Trotz der guten Arbeit, die Ihr geleistet habt, bin ich immer noch der Meinung, dass der Lebende Hain mehr Schutz braucht.]], [[Bitte, nehmt diesen kleinen Zweig. Ich habe ihn mit der Macht der Natur verstärkt. Wenn er bei den Setzlingen, die ich im Hain gepflanzt habe, eingesetzt wird, ruft er einen Sturm herbei, der sie schnell zu großen Treantverteidigern heranwachsen lässt.]], [[Diese Treants sind für die Verteidigung des Waldes unverzichtbar, besonders, wenn man nicht für immer in Sylvanaar leben möchte.]], },
    { [[Use Rina's Bough on 5 of the Grove Seedlings to summon forth defenses for the Living Grove.  Then return to Rina Moonspring at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10458] = {
    [[Wütende Geister des Feuers und der Erde]],
    { [[Wir müssen versuchen, die Dinge zu ändern. Wir müssen mit den Elementen dieser Gegend kommunizieren und schauen, ob wir etwas tun können!]], [[Ich gebe Euch das Totem der Geister, <race>. Wenn Ihr einen wütenden Elementargeist in der Nähe des Totems umbringt, wird es die Seele des Elementars aufnehmen. Ihr müsst viele Seelen mithilfe des Totems einfangen, bevor Ihr zu mir zurückkehrt.]], [[Sammelt zuerst die Seelen der Geister des Feuers und der Geister der Erde. Kehrt zu mir zurück, wenn das Totem voll ist.]], },
    { [[Erdheiler Torlok beim Altar der Verdammnis im Schattenmondtal möchte, dass Ihr das Totem der Geister benutzt, um 8 Erdenseelen und 8 Feuerseelen einzufangen.]], },
  },
  [10459] = {
    [[Respektvoll bei der Expedition des Cenarius]],
    { [[Eure Erfolge sind uns aufgefallen, <name>. Was Ihr für uns getan habt geht weit über das, was wir von Verbündeten erwarten, hinaus.]], [[Ich habe eine besondere Bitte an Euch, aber zunächst muss ich Eure Hingabe testen.]], },
    { [[Ysiel Windsänger bei der Zuflucht des Cenarius möchte, dass Euch einen respektvollen Ruf bei der Expedition des Cenarius verdient.]], },
  },
