  [10476] = {
    [[Grimmige Feinde]],
    { [[Wenn Ihr Eure Treue uns gegenüber beweisen wollt, dann gibt es keinen besseren Weg, als unsere Feinde zu bekämpfen.]], [[Unter diesen Feinden gibt es keinen, der so stark und so wild ist, wie die Oger. Bringt mir die Kriegsperlen, die sie um den Hals tragen, als Beweis für ihr Ableben. Wenn Ihr das tut, lasse ich die anderen von Eurem Heldenmut wissen.]], },
    { [[Aufseher Moi'bff Jill in Telaar möchte, dass Ihr 10 Obsidiankriegsperlen von den verschiedenen Ogern in Nagrand besorgt.]], },
  },
  [10477] = {
    [[Mehr Kriegsperlen]],
  },
  [10478] = {
    [[Mehr Kriegsperlen!]],
  },
  [10479] = {
    [[Stärke beweisen]],
    { [[Auch wenn wir ein friedvolles Leben suchen, haben wir doch viele Feinde.]], [[Unter diesen Feinden gibt es keinen, der so viel rohe Macht besitzt, wie die Oger. Wenn Ihr uns Euren Heldenmut beweisen wollt, bringt mir die Kriegsperlen, die sie um den Hals tragen. Wenn Ihr das tut, lasse ich die anderen von Euren Taten wissen.]], },
    { [[Aufseher Bullrok in Garadar möchte, dass Ihr 10 Obsidiankriegsperlen von den verschiedenen Ogern in Nagrand besorgt.]], },
  },
  [10480] = {
    [[Wütende Geister des Wassers]],
    { [[Die Naga der Illidari in dieser Gegend haben die Kontrolle über die einzige reine Wasserversorgung im Schattenmondtal an sich gerissen.]], [[Direkt nördlich von hier findet Ihr das Nest der Naga, die Echsennarbe. Um die Echsennarbe herum findet Ihr Ansammlungen von tosendem Wasser, die ihre Qual ins Land hinausschreien. Bei diesen Ansammlungen streifen die wütenden Geister des Wassers umher.]], [[Reist zur Echsennarbe und benutzt das Totem der Geister, um ihre Wasserseelen einzufangen. Die größte Ansammlung dieser Geister findet Ihr in der Nähe der Zisterne der Echsennarbe.]], },
    { [[Erdheiler Torlok beim Altar der Verdammnis im Schattenmondtal möchte, dass Ihr das Totem der Geister benutzt, um 5 Wasserseelen einzufangen.]], },
  },
  [10481] = {
    [[Wütende Geister der Luft]],
    { [[Begebt Euch nach Südosten, Held. Wenn Ihr große blaue Kristalle aus dem Boden herausragen seht, habt Ihr Euer Ziel erreicht. Dort, auf den Netherschwingenfeldern, werdet Ihr die wütenden Geister der Luft finden. Sie streifen rings um die Felder auf den Klippen und um die Berge herum umher.]], [[Zerstört ihre gequälten Körper und fangt ihre Seelen im Totem der Geister ein. Kehrt zu mir zurück, wenn Ihr fertig seid.]], },
    { [[Erdheiler Torlok beim Altar der Verdammnis im Schattenmondtal möchte, dass Ihr das Totem der Geister benutzt, um 10 Luftseelen einzufangen.]], },
  },
  [10482] = {
    [[Plünderer der Höllenorcs]],
    { [[Plünderer, Orcs des Knochenmalmerklans, treiben sich bei den zerstörten Kriegsmaschinerien am Pfad des Ruhmes herum. Wir müssen davon ausgehen, dass diese Höllenorcs Teile der Maschinen für ihre Meister in der Höllenfeuerzitadelle sammeln. Wir müssen sie aufhalten!]], [[Sucht im Norden nach den Knochenmalmern, <name>. Sucht sie und tötet sie! Aber seid vorsichtig. Auch wenn diese Orcs einem niederrangigeren Höllenorcklan angehören, sind sie doch genauso stark wie alle grünhäutigen Orcs, die wir bisher getroffen haben!]], },
    { [[Tötet 20 Orcs der Knochenmalmer und kehrt danach zu Amadi in der Ehrenfeste zurück.]], },
  },
  [10483] = {
    [[Schwarze Magie]],
    { [[<name>, in unseren Schlachten mit den Höllenorcs des Blutenden Auges haben wir Schmuckstücke mit magischen Kräften bei ihnen gefunden. Diese Orcs haben die Insignien der gefallenen Soldaten der Ehrenfeste gesammelt und verfluchte Talismane aus ihnen gemacht. Wir befürchten, dass die schwarze Magie des Blutenden Auges unsere Insignien in böse Amulette verwandelt haben, die sie gegen uns einsetzen können!]], [[Sucht einen solchen Talisman bei den Orcs des Blutenden Auges in Zeth'Gor im Osten. Bringt ihn zu Korporal Eisengrat beim Expeditionsposten nordöstlich von Zeth'Gor.]], },
    { [[Leutnant Amadi in der Ehrenfeste möchte, dass Ihr einen verfluchten Talisman zu Korporal Eisengrat im Expeditionsposten bringt.]], },
  },
  [10484] = {
    [[Verfluchte Talismane]],
    { [[<name>, wir können nicht zulassen, dass die Orcs des Blutenden Auges diese verfluchten Talismane besitzen! Ihr müsst sie für uns beschaffen, damit wir sie zerstören können und so die gefallenen Soldaten, denen sie gehörten, für ihr Opfer ehren.]], [[Geht nach Zeth'Gor, der verfluchten Behausung des Blutenden Auges, und führt Eure grausame Aufgabe durch... Kehrt zu mir zurück, wenn Ihr die Talismane gesammelt habt.]], },
    { [[Bringt 12 verfluchte Talismane zu Korporal Eisengrat am Expeditionsposten.]], },
  },
  [10485] = {
    [[Kriegsherr des Blutenden Auges]],
    { [[Ihr habt große Stärke und großes Geschick bewiesen, denn Ihr habt Euch in den Kampf in Zeth'Gor gestürzt und überlebt! Nun wollen wir hoffen, dass das Glück Euch weiterhin hold ist, denn ich muss Euch eine düstere Aufgabe auftragen...]], [[In der Kaserne von Zeth'Gor sitzt Kriegsherr Morkh und befehligt seine Truppen gegen uns. Seine Taktiken sind genauso schlau wie grausam und ich muss zugeben, dass seine Erfolge bei uns tiefe Wunden geschlagen haben. Deshalb möchte ich Euch bitten, Morkh aus dem Weg zu räumen. Bringt mir seine Rüstung, damit wir sie meinen Soldaten zeigen und ihnen Mut machen können!]], },
    { [[Bringt Kriegsherr Morkhs zerschmetterte Rüstung zu Korporal Eisengrat im Expeditionsposten.]], },
  },
  [10486] = {
    [[Die nahende Wildnis]],
    { [[Die Donnerfürsten waren einst ein stolzer Orcklan. Doch dann wurden sie durch Mannoroths Blut in Höllenorcs verwandelt.]], [[Vor Kurzem haben wir diesen Orcstützpunkt von den Ogern, die sich in der Klamm unter uns breitgemacht haben, zurückgefordert. Nachdem die Donnerfürsten verschwunden waren, hatten sie hier Einzug gehalten.]], [[Wir möchten diesen Ort behalten und Euch gerne bitten, uns dabei zu helfen.]], [[Im Zackengrat im Süden und Westen findet Ihr Blutvergießer, die sich zu einer wahren Seuche entwickeln. Sie fressen und vermehren sich unaufhörlich.]], [[Tötet sie und kehrt dann zu mir zurück.]], },
    { [[Gor'drek has asked you to slay 12 Bladewing Bloodletters.  Return to him at Thunderlord Stronghold in the Blade's Edge Mountains once you have accomplished that.]], },
  },
  [10487] = {
    [[Drachenstaub]],
    { [[Vor Kurzem hat die Allianz im Westen Einzug gehalten und mithilfe ihrer Elfenmagie ein Dorf und einen Hain praktisch aus dem Nichts entstehen lassen. Wir müssen ihre Verteidigung so schwach wie möglich halten, damit sie nicht auch noch ein Rückgrat entwickeln!]], [[Ich habe einen Plan, mit dem wir dies und noch mehr erreichen können. Ich möchte, dass Ihr die Brücke nach Südwesten überquert, die über die Totschlägerklamm in den Lebenden Hain führt.]], [[Greift ihre Feendrachenverteidigung im Lebenden Hain an und bringt mir etwas von dem magischen Staub, der sich manchmal auf ihren Flügeln befindet.]], },
    { [[Gor'drek wants you to gather 4 Dusts of the Fey Drake.  After doing so, return to him at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10488] = {
    [[Die Eigenen schützen]],
    { [[Ich habe aus dem magischen Staub, den Ihr gesammelt habt, eine Salbe hergestellt, die uns bei unserer Verteidigung helfen wird.]], [[Als wir hierher kamen, haben wir ein wundervolles Rudel Terrorwölfe mitgebracht. Sie sind die perfekte Kombination aus Reittier, Wachhund und Freund, aber der Abschaum der Allianz hat begonnen, sie im Zackengrat zu jagen.]], [[<name>, ich möchte, dass Ihr diese Salbe nehmt und sie an unseren Terrorwolffreunden anwendet. Das wird der Allianz eine böse Überraschung bescheren, wenn sie das nächste Mal auf die Jagd geht.]], [[Seid Euch jedoch bewusst, dass einige der Wölfe das vielleicht nicht leiden mögen und Euch angreifen werden.]], },
    { [[Use Gor'drek's Ointment on 5 of the Thunderlord Dire Wolves to strengthen them and the defense of the Jagged Ridge.  Then return to Gor'drek at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10489] = {
    [[Einen uralten Baum fällen]],
    { [[WANTED!]], [[Stronglimb Deeproot, ancient of war and chief defender of the Living Grove south of Sylvanaar.]], [[Find him by heading west across the bridge that traverses the Bloodmaul Ravine.]], [[Return his hewn trunk to Tor'chunk Twoclaws inside the Thunderlord Stronghold inn.]], },
    { [[Acquire Stronglimb Deeproot's Trunk and deliver it to Tor'chunk Twoclaws at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10490] = {
    [[Ruf des Wassers]],
    { [[Es ist schon eine Weile her, seitdem Ihr das letzte Mal auf die Probe gestellt wurdet. Scharfseher Nobundo hat nach Euch gefragt.]], [[Ihr müsst sehr viel an Erfahrung gewonnen haben, da man Euch nun die Geheimnisse des Elements des Wassers lehren möchte.]], [[Ich nehme an, Ihr wisst noch, wie Ihr mit dem Boot von Auberdine an der Dunkelküste in Kalimdor aus zurück zur Exodar kommt? Und Ihr wisst noch, dass Ihr ihn in der Kristallhalle in der Exodar findet, ja?]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel.]], },
  },
  [10491] = {
    [[Ruf der Luft]],
    { [[<name>, Scharfseher Nobundo hat erneut nach Euch verlangt. Es scheint für Euch an der Zeit zu sein, die Geheimnisse der Luft - des noch fehlenden Elements - zu erlernen!]], [[Ich bin sehr stolz auf all das, was Ihr bis jetzt erfolgreich gemeistert habt. Ich weiß, dass Euch ein besonderes Schicksal erwartet.]], [[Nehmt das Boot von Auberdine an der Dunkelküste in Kalimdor aus. Überquert das Verhüllte Meer und sucht den Weissager in der Kristallhalle der Exodar auf der Azurmythosinsel auf.]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel.]], },
  },
  [10492] = {
    [[Ein aufrichtiges Angebot]],
    { [[Sammelt 15 Blutproben aus Winterquell und bringt sie zusammen mit 20 Gold und einem Paar Armschienen der Ehre zu Deliana in Eisenschmiede.]], },
    { [[Sammelt 15 Blutproben aus Winterquell und bringt sie zusammen mit 20 Gold und einem Paar Bindungen der Elemente zu Deliana in Eisenschmiede.]], },
  },
  [10493] = {
    [[Ein aufrichtiges Angebot]],
    { [[Ich grüße Euch, <class>. Eure Haltung verrät mir, dass Ihr bereits ganz schön herumgekommen seid. Ich würde allerdings darauf wetten, dass Euch noch keine Rüstung wie diese untergekommen ist.]], [[Wenn Ihr mir einen kleinen Gefallen tut, wäre ich bereit, diese Rüstung gegen ein normales Paar Armschienen einzutauschen.]], [[Ich bräuchte eine ziemlich große Menge an Giftproben von den Spinnen und Skorpionen aus Silithus. Bringt es mir zusammen mit einigen Goldmünzen und ich werde den Tausch vornehmen.]], },
    { [[Sammelt 15 Giftproben aus Silithus und bringt sie zusammen mit 20 Gold und einem Paar Armschienen des Lichts zu Mokvar in Orgrimmar zurück.]], },
  },
  [10494] = {
    [[Die angemessene Entlohnung]],
    { [[Ihr habt Euer Wort gehalten und den von Mux konstruierten Apparat beschafft. Ich denke, eine Belohnung wäre nun angebracht, um Euer Interesse daran, meine Haut zu retten, aufrechtzuerhalten. Bringt mir den Gürtel und die Handschuhe, von denen Ihr eine verbesserte Version wünscht. Ich kann Euch versichern, dass die Rüstung, die ich Euch geben werde, von weit höherer Qualität sein wird.]], },
    { [[Bringt Deliana in Eisenschmiede eine Kordel der Elemente und ein Paar Stulpen der Elemente.]], },
  },
  [10495] = {
    [[Die angemessene Entlohnung]],
    { [[Ihr habt große Verlässlichkeit bewiesen, <name>. So sehr ich auch darauf vertraue, dass Ihr mir durch diese missliche Situation hindurchhelft, wäre es dennoch weiser, Euch zunächst Euren bisherigen Taten entsprechend zu entlohnen.]], [[Ich werde zunächst Euren Gürtel und Eure Handschuhe austauschen. Wenn wir dies bis zum Ende durchstehen, werde ich Euch den Rest der Rüstung übergeben.]], },
    { [[Bringt Mokvar in Orgrimmar einen Gürtel des Lichts und ein Paar Stulpen des Lichts.]], },
  },
  [10496] = {
    [[Anthions Abschiedsworte]],
    { [[Valthalaks Seele wurde in diesem Amulett verwahrt. In unserer Gier haben wir es wie Narren in drei Teile aufgeteilt ohne um den Fluch zu wissen, der uns heimsuchen würde.]], [[Die einzige Möglichkeit, den Fluch aufzuheben, besteht darin das Amulett wieder zusammenzusetzen. Kehrt zu Deliana zurück und sagt ihr, dass Bodley unsere einzige Hoffnung ist, die einzelnen Teile wiederzufinden.]], [[Ich selbst kann nun endlich in Frieden ruhen. Ich wünsche Euch viel Glück, Ihr werdet es brauchen.]], },
    { [[Kehrt mit einem Paar Stiefel der Elemente, einem Kilt der Elemente und einem Paar Schulterstücke der Elemente zu Deliana in Eisenschmiede zurück.]], },
  },
  [10497] = {
    [[Anthions Abschiedsworte]],
    { [[Valthalaks Seele wurde in diesem Amulett verwahrt. In unserer Gier haben wir es wie Narren in drei Teile aufgeteilt ohne um den Fluch zu wissen, der uns heimsuchen würde.]], [[Die einzige Möglichkeit, den Fluch aufzuheben, besteht darin das Amulett wieder zusammenzusetzen. Kehrt zu Mokvar zurück und sagt ihm, dass Bodley unsere einzige Hoffnung ist, die einzelnen Teile wiederzufinden. Mit ihm könnt Ihr auch über eine eventuelle Belohnung sprechen.]], [[Ich selbst kann nun endlich in Frieden ruhen. Ich wünsche Euch viel Glück, Ihr werdet es dringend brauchen.]], },
    { [[Kehrt mit einem Paar Stiefel des Lichts, ein Paar Beinplatten des Lichts und einer Schiftung des Lichts zu Mokvar in Orgrimmar zurück.]], },
  },
  [10498] = {
    [[Das Beste gibt's zum Schluss]],
    { [[Das ist ja eine unglaubliche Geschichte, <name>! Seid Euch meines Dankes gewiss, und der aufrichtigen Dankbarkeit all derer, die in dieses Abenteuer verwickelt waren. Vielleicht kann ich nun endlich wieder mit geschlossenen Augen schlafen.]], [[Nun zu Eurer Belohnung. Ich habe hier einige Dinge, an denen Ihr gewiss Gefallen finden werdet.]], },
    { [[Gebt Deliana Eure Helmkappe der Elemente und Eure Weste der Elemente.]], },
  },
  [10499] = {
    [[Das Beste gibt's zum Schluss]],
    { [[Das ist ja eine unglaubliche Geschichte, <name>! Seid Euch meines Dankes gewiss, und der aufrichtigen Dankbarkeit all derer, die in dieses Abenteuer verwickelt waren. Vielleicht kann ich nun endlich wieder mit geschlossenen Augen schlafen.]], [[Nun zu Eurer Belohnung. Ich habe hier einige Dinge, an denen Ihr gewiss Gefallen finden werdet.]], },
    { [[Gebt Mokvar Euren Helm des Lichts und Eure Brustplatte des Lichts.]], },
  },
  [10500] = {
    [[Die Horde braucht Euch!]],
    { [[Hallo, schön dass Ihr Euch entschieden habt, mir zuzuhören, was ich zu sagen habe. Die Horde braucht jede Hilfe, die sie kriegen kann, um sich auf den Krieg in Ahn'Qiraj vorzubereiten. Die Horde braucht Euch! Während wir uns unterhalten, haben die offiziellen Sammler bereits damit begonnen, kriegstaugliche Materialien aller Art zu sammeln. Doch ohne Eure Mithilfe sind all diese Mühen vergebens, <name>! Wir brauchen Eure Unterstützung!]], [[Am besten unterhaltet Ihr Euch mit dem verantwortlichen Offizier, Kriegsherr Gorchuk. Können wir auf Euch zählen, <class>? Werdet Ihr uns bei den Kriegsvorbereitungen zur Seite stehen?]], },
    { [[Sprecht mit Kriegsherr Gorchuk im Tal der Geister in Orgrimmar.]], },
  },
  [10501] = {
    [[Die Allianz braucht Euch!]],
    { [[Hallo, schön dass Ihr Euch entschieden habt, mir zuzuhören, was ich zu sagen habe. Die Allianz braucht jede Hilfe, die sie kriegen kann, um sich auf den Krieg in Ahn'Qiraj vorzubereiten. Die Allianz braucht Euch! Während wir uns unterhalten, haben die offiziellen Sammler bereits damit begonnen, kriegstaugliche Materialien aller Art zu sammeln. Doch ohne Eure Mithilfe sind all diese Mühen vergebens, <name>! Wir brauchen Eure Unterstützung!]], [[Am besten unterhaltet Ihr Euch mit dem verantwortlichen Offizier, Feldmarschall Schneefall. Können wir auf Euch zählen, <class>? Werdet Ihr uns bei den Kriegsvorbereitungen zur Seite stehen?]], },
    { [[Sprecht mit Feldmarschall Schneefall im Militärviertel von Eisenschmiede.]], },
  },
  [10502] = {
    [[Die Oger der Blutschläger]],
    { [[Es gibt eine Bedrohung für Sylvanaar, die weitaus tödlicher ist, als die Horde in der Donnerfeste.]], [[Ich spreche von den einheimischen Ogern in diesen Bergen. Im Süden und Osten, unten in der Klamm, lebt der Stamm der Blutschläger. Sie sind zwar nicht der einzige Stamm, der uns bedroht, aber sie sind am nächsten.]], [[Ich möchte, dass Ihr nach Süden durch den Lebenden Hain hindurch geht und dann den Pfad nach Osten nehmt, der zu ihrem Außenposten führt. Tötet so viele Oger der Blutschläger im Außenposten der Blutschläger, wie Ihr nur könnt.]], },
    { [[Commander Skyshadow has tasked you with the destruction of any 30 Bloodmaul Ogres at Bloodmaul Outpost.  Return to him at Sylvanaar in the Blade's Edge Mountains once your gruesome task is completed.]], },
  },
  [10503] = {
    [[Die Speerspießerbedrohung]],
    { [[Es gibt eine Bedrohung für die Donnerfeste, die weitaus tödlicher ist, als die Allianz in Sylvanaar.]], [[Ich spreche von den einheimischen Ogern in diesen Bergen. Im Westen, unten in der Klamm, lebt der Stamm der Speerspießer. Sie sind zwar nicht der einzige Stamm, der uns bedroht, aber sie sind am nächsten und die, die wir besiegen mussten, um diesen Ort hier beziehen zu können.]], [[Ich möchte, dass Ihr nach Norden durch den Zackengrat hindurch geht und dann den Pfad nach unten nehmt, der in ihre Festung führt. Tötet so viele Oger der Speerspießer in der Wehr der Speerspießer, wie Ihr nur könnt.]], },
    { [[Tor'chunk Twoclaws has tasked you with the destruction of any 30 Bladespire Ogres and 10 Bladespire Raptors at Bladespire Hold and the Bladespire Grounds.  Return to him at Thunderlord Stronghold in the Blade's Edge Mountains once your task is completed.]], },
  },
  [10504] = {
    [[Die Oger der Speerspießer]],
    { [[Nördlich des Blutschlägerstamms liegt eine noch gefährlichere Bedrohung: die Wehr der Speerspießer.]], [[Die Oger der Speerspießer sind die stärksten in den Bergen und stehen in der Gunst des Gronn namens Gruul der Drachentöter und seiner sieben Söhne.]], [[Jetzt, da Ihr den Blutschlägern eine Abreibung verpasst habt, ist es an der Zeit, sich ihren Rivalen zuzuwenden.]], [[Geht vom Außenposten der Blutschläger aus nach Norden durch die Klamm, bis Ihr die Wehr der Speerspießer und den Grund der Speerspießer erreicht. Tötet so viele Oger der Speerspießer, wie Ihr nur könnt.]], },
    { [[You have been asked by Commander Skyshadow to kill any 30 Bladespire Ogres in Bladespire Hold and the Bladespire Grounds.  Return to him at Sylvanaar in the Blade's Edge Mountains once you have done so.]], },
  },
  [10505] = {
    [[Die Oger der Blutschläger]],
    { [[Südlich des Speerspießerstamms liegt noch eine weitere Bedrohung: der Außenposten der Blutschläger.]], [[Die Oger der Blutschläger sind nicht die stärksten in den Bergen. Da sie nicht in der Gunst des Gronn namens Gruul der Drachentöter stehen, kommen sie nur an zweiter Stelle.]], [[Jetzt, da Ihr den Speerspießern eine Abreibung verpasst habt, ist es an der Zeit, sich ihren Rivalen zuzuwenden.]], [[Geht von der Wehr der Speerspießer aus nach Süden durch die Klamm, bis Ihr den Außenposten der Blutschläger erreicht. Tötet so viele Oger der Blutschläger, wie Ihr nur könnt.]], },
    { [[You have been asked by Tor'chunk Twoclaws to kill any 30 Bloodmaul Ogres at Bloodmaul Outpost.  Return to him at Thunderlord Stronghold in the Blade's Edge Mountains once you have done so.]], },
  },
  [10506] = {
    [[Eine düstere Lage]],
    { [[Jetzt, da ihr Wolfsrudel dezimiert wurde, ist die Horde auf der Suche nach Ersatz. Unglücklicherweise können sie den gleich in der Nähe finden.]], [[Die Oger der Blutschläger in der Klamm im Osten unter uns halten sich selbst ein Rudel als Wachhunde. Ich war nicht sehr froh darüber, Euch loszuschicken, um die Wölfe der Donnerfürsten zu töten. Lasst uns dieses Mal etwas anderes versuchen.]], [[Ich habe ein magisches Pulver aus dem Staub, der von den Flügeln unserer Feendrachen fällt, hergestellt. Streut es auf die Terrorwölfe der Blutschläger, damit sie für die Horde nicht länger interessant sind.]], },
    { [[Rina Moonspring wants you to take her Diminution Powder and apply it to 5 of the Bloodmaul Dire Wolves, which can be found at Bloodmaul Outpost and in Bloodmaul Ravine.  After doing so, return to her at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10507] = {
    [[Der Wendepunkt]],
    { [[Das ist die Gelegenheit, auf die wir gewartet haben, <name>. Wir können vor unserem Volk nicht nur belegen, was Kael'thas wirklich vorhat, sondern auch unsere Stärke beweisen, indem wir seine neuen Meister besiegen.]], [[Meine schlechte Gesundheit hindert mich leider daran, Euch selbst zu helfen, aber ich biete Euch einen Gegenstand an, mit dem ich meine Kräfte über den Kontinent hinweg ausüben kann.]], },
    { [[Benutzt Socrethars Teleportationsstein beim Invasionspunkt: Oberanführer im Norden der Konstruktionsbasis: Vergessenheit, um Eure Gruppe zu Socrethars Sitz zu bringen. Wenn Ihr dort seid, benutzt Voren'thals Präsenz, um Socrethar zu besiegen.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10508] = {
    [[Ein Geschenk für Voren'thal]],
    { [[Ich möchte Euch nicht mit leeren Händen zu Voren'thal zurückschicken. Jemanden unbeeindruckt zu lassen ist nicht mein Stil.]], [[Um Zugang zu Socrethars Sitz im Norden zu erlangen, müsst Ihr einen seiner Teleporter benutzen. Dazu braucht Ihr einen Teleportationsstein. Die erste Hälfte des Steins befindet sich im Besitz von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit. Die zweite Hälfte hat der Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna.]], [[Besorgt Euch den Stein, dann wird Voren'thal über alle Maßen beeindruckt sein.]], },
    { [[Spionagemeister Thalodien in Area 52 möchte, dass Ihr die erste Hälfte von Socrethars Stein von Konstruktionsmeister Morug in der Konstruktionsbasis: Vergessenheit und die zweite Hälfte des Steins von Konstruktionsmeister Silroth in der Konstruktionsbasis: Gehenna besorgt.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10509] = {
    [[Für Ruhm bestimmt]],
    { [[Jetzt kann ich Euch ruhigen Gewissens nach Shattrath zurückschicken. Ihr habt Beweise dafür gefunden, dass Kael'thas sich mit der Brennenden Legion verbündet hat. Außerdem habt Ihr den Schlüssel zur Hintertür des Oberanführers erlangt.]], [[Gebt jedoch Acht, dass sich Voren'thal nicht vor lauter Aufregung überschlägt - der alte Mann wird langsam etwas gebrechlich, wenn Ihr versteht, was ich meine. Ihr findet ihn in der Bibliothek der Seher auf der Sehertreppe.]], },
    { [[Bringt Socrethars Teleportationsstein zu Voren'thal dem Seher auf der Sehertreppe in Shattrath.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10510] = {
    [[In die Draenethystmine]],
    { [[Schön jemanden zu sehen, der uns hier helfen will!]], [[Borgrim und ich gehören zur Forscherliga. Wir sind hier, um so viel von diesen Bergen zu lernen, wie wir nur können. Wir haben schon allerlei Geschichten darüber gehört. Ich kann es kaum abwarten, mich hier niederzulassen und mit der Arbeit zu beginnen.]], [[Eine Sache hat meine besondere Aufmerksamkeit erregt. Die Nachtelfen erzählen von einem Ort in der Klamm im Osten, der als Draenethystmine bekannt ist. Sie liegt am südlichen Ende des Außenpostens der Blutschläger. Ich hätte gerne ein paar der Kristalle, die dort wachsen.]], },
    { [[Bronwyn Stouthammer has asked you to collect 5 Draenethyst Mine Crystals and then return to her at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10511] = {
    [[Merkwürdiges Gebräu]],
    { [[Wenn ich diesen Urlaub richtig beginnen will, brauche ich mehr gutes Gebräu. Leider haben diese Elfen keine Ahnung von der hohen Kunst des Gärens.]], [[Ich habe gehört, dass die Oger der Blutschläger in der Klamm ganze Mengen davon auf Lager haben. Das will ich gerne versuchen.]], [[Tut Ihr mir einen Gefallen? Geht nach Süden durch den Lebenden Hain und dann den Abstieg im Osten hinunter zum Außenposten der Blutschläger und besorgt mir ein wenig davon. Ich habe gehört, dass sie große Fässer davon herumliegen haben. Ihre Braumeister schenken es an all die anderen Oger aus.]], },
    { [[Borgrim Stouthammer wants you to procure 11 Bloodmaul Brutebane Brew for him.  Once you've done that, return it to him at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10512] = {
    [[Die Speerspießer abfüllen]],
    { [[Ich nehme keinen weiteren Schluck von diesem übel riechenden Gesöff! Aber mir kommt da eine Idee.]], [[<Borgrims Augen beginnen zu funkeln.>]], [[Das ist der Plan. Wir wissen, dass die Blutschläger praktisch von dieser Brühe leben. Aber ich frage mich, was wohl ihre Feinde, die Speerspießer, davon halten.]], [[<name>, ich möchte, dass Ihr den Rest dieses Gesöffs nehmt und schaut, ob wir die Oger der Speerspießer damit betrunken machen können. Ihr findet sie nördlich vom Außenposten der Blutschläger in der Klamm.]], },
    { [[Take the Bloodmaul Brutebane Keg and use it in an attempt to get 5 Bladespire Ogres drunk.  If you're successful, return to Borgrim at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10513] = {
    [[Oronok Herzeleid]],
    { [[Ich weiß nicht, wie wir an den Zauberspruch, den die Geister erwähnt haben, herankommen sollen, <name>. Um die Wahrheit zu sagen, habe ich nicht einmal eine Ahnung, wo man so eine Suche beginnen könnte.]], [[<Torlok starrt gedankenverloren in die Ferne.>]], [[Hmm... Das ist zwar ein Schuss ins Blaue, aber es ist vielleicht unsere einzige Chance. Es gibt einen Orc, den ich schon des Öfteren in dieser Gegend gesehen habe. Ich habe einmal kurz mit ihm gesprochen. Er ist ein Einsiedler, der angeblich in der Zisterne der Echsennarbe im Nordosten lebt. Findet diesen Orc namens Oronok Herzeleid.]], [[Wie gesagt, es ist ein Schuss ins Blaue.]], },
    { [[Sucht Oronok Herzeleid auf dem zerschlagenen Vorsprung nördlich der Zisterne der Echsennarbe.]], },
  },
  [10514] = {
    [[Ich war schon vieles...]],
    { [[Ich war schon vieles, Fremder, aber was ich einst war, ist nicht von Belang. Es zählt nur, was ich jetzt bin, und jetzt bin ich ein Bauer.]], [[Ich benutze trainierte Hölleneber, um Knollen in der harten Erde der Zerschlagenen Weiten aufzuspüren. In letzter Zeit greifen jedoch Felshetzer aus einem Gelege im Nordosten meine Tiere an und halten mich vom Sammeln der Knollen ab.]], [[Würdet Ihr für mich in der Gegend nördlich von hier ein paar Knollen sammeln? Benutzt diese Pfeife, wenn Ihr auf einem Schattenmondknollenhügel steht. Einer der Eber wird herbeieilen, und die Knolle ausgraben.]], },
    { [[Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Schattenmondknollen aus den Zerschlagenen Weiten besorgt.]], [[Bringt Oronoks Eberpfeife zu ihm zurück, wenn Ihr fertig seid.]], },
  },
  [10515] = {
    [[Eine Lektion gelernt]],
    { [[Wir müssen diesen Felshetzern zeigen, was es heißt, Oronoks Geschäften in die Quere zu kommen!]], [[Ich möchte, dass Ihr in die Zerschlagenen Weiten zurückkehrt und jedes Felshetzerei zerstört, das Ihr finden könnt. Sie befinden sich sicher tief im Felshetzergebiet. Seid also wachsam.]], },
    { [[Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Eier eines gefräßigen Felshetzers zerstört.]], },
  },
  [10516] = {
    [[Die Gefangenschaft der Verteidigerin]],
    { [[Ich weiß, es klingt merkwürdig, aber ich möchte wirklich in diesem Käfig bleiben. Er gibt mir die perfekte Gelegenheit, um das zu tun, weswegen ich hier bin: Informationen über die Oger der Speerspießer sammeln.]], [[Ich kann hier zwar jederzeit raus, aber mein Schwert und meinen Schild hätte ich gerne wieder zurück.]], [[Zwei ihrer Anführer haben sich meine Sachen unter den Nagel gerissen. Droggam hat mein Schwert und Mugdorg meinen Schild.]], [[Wenn Ihr es für nötig haltet, besorgt Euch das Gebräu der Blutschläger aus ihrem Außenposten im Süden, um die Unterstützung der beiden außer Gefecht zu setzen, aber gebt auf ihre nüchternen Wachen Acht, die nichts trinken werden!]], },
    { [[Retrieve Vindicator Vuuleen's Blade and Vindicator Vuuleen's Shield.  Then return them to Vindicator Vuuleen at Bladespire Hold in the Blade's Edge Mountains.]], },
  },
  [10517] = {
    [[Eure Zeit ist gekommen, Gorr'Dim...]],
    { [[Der Anführer der Wehr der Speerspießer ist Gorr'Dim.]], [[Er ist auf keinen Fall zu unterschätzen. Seine Gerissenheit hat die Speerspießer zu den Lieblingen von Gruul dem Drachentöter und dem vorherrschenden Klan in den Bergen gemacht. Gorr'Dim hat auf Schritt und Tritt seine private Eskorte dabei, darunter auch Verteidiger, die schwören mussten, niemals auch nur einen Tropfen des Gebräus der Blutschläger anzurühren.]], [[Für die Sicherheit Sylvanaars und all der friedliebenden Wesen der Berge bitte ich Euch, zur Wehr zurückzukehren und ihn zur Strecke zu bringen.]], },
    { [[Vindicator Vuuleen has asked you to kill Gorr'Dim, leader of the Bladespire Hold ogres.  Once you have done so, return to her at Bladespire Hold in the Blade's Edge Mountains.]], },
  },
  [10518] = {
    [[Das Banner aufstellen]],
    { [[Ich möchte, dass Ihr das Banner der Speerspießer von einem ihrer Champions stehlt und es nach Süden zum Außenposten der Blutschläger bringt. Stellt es auf dem Nordschlägerturm auf.]], [[Das wird die Blutschläger erzürnen. Sie werden Euch ohne mit der Wimper zu zucken angreifen. Aber es wird auch ihren Hass auf die Speerspießer schüren und sie von Sylvanaar ablenken.]], [[Ihr habt Eure Aufgabe erfüllt, wenn Ihr Gurn Grubnoshs Helm in den Händen haltet. Bringt ihn nach Sylvanaar.]], [[<name>, das wird nicht einfach. Nehmt ein oder zwei Freunde mit.]], },
    { [[Use the Bladespire Banner atop the Northmaul Tower to lure Gurn Grubnosh.  Deliver the banner and the Helm of Gurn Grubnosh to Commander Skyshadow at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10519] = {
    [[Die Litanei der Verdammnis - Wahrheit und Geschichte]],
    { [[<Oronok nimmt Haltung an und richtet sich zu seiner vollen Größe auf.>]], [[Ich musste sichergehen, <name>. Und dazu musste ich Euch erst prüfen. Bitte verzeiht meine kleine Maskerade. Ich bin so sehr ein Bauer, wie Ihr <eine Frau/ein Mann> seid, nämlich gar nicht.]], [[Wenn Ihr die Litanei der Verdammnis beschaffen wollt, müsst Ihr mir erst zuhören. Wenn Ihr bereit seid, lasst es mich wissen, und ich werde Euch die Geschichte erzählen.]], },
    { [[Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr Euch seine Geschichte anhört. Sprecht mit Oronok, um ihm zusagen, dass er mit der Geschichte beginnen kann.]], },
  },
  [10520] = {
    [[Erzdruide Hirschhaupt helfen]],
    { [[Entschuldigung, <class>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?]], [[Der Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.]], [[Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.]], },
    { [[Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.]], },
  },
  [10521] = {
    [[Grom'tor, Sohn des Oronok]],
    { [[Mein Erstgeborener, Grom'tor, hat sich aufgemacht, um die Echsennarbe zu durchsuchen. Sie befindet sich direkt südwestlich von hier.]], [[Er ist der Krieger der Familie, jähzornig und blutdurstig... Das hat er von seiner Mutter. Sucht um die Echsennarbe herum nach ihm. Wenn Ihr irgendwo Nagaleichen liegen seht, ist Grom'tor sicher in der Nähe.]], [[Wenn Ihr ihn findet, dann sagt ihm dies: "Wir vergießen Blut für eine gerechte Sache". Dann wird er wissen, dass Ihr ein Freund Herzeleids seid und Euch nicht vorschnell töten.]], },
    { [[Sucht Grom'tor, Sohn des Oronok in der Echsennarbe im Schattenmondtal.]], },
  },
  [10522] = {
    [[Die Litanei der Verdammnis - Grom'tors Angriff]],
    { [[Dieser Nagaabschaum ist schon der sechste, den ich heute befragt habe. Wegen der schicken Rüstung dachte ich, dass er sicher etwas weiß. Die anderen waren völlig nutzlos.]], [[<Grom'tor zeigt auf die Nagaleichen um sich herum.>]], [[Ich musste ihn nur einmal schlagen, da hat er mir schon alles erzählt.]], [[Der erste Teil der Litanei befindet sich in einer Nagatruhe in der Echsennarbe oder der Zisterne der Echsennarbe im Osten. Er sagt, dass die Naga die Schlüssel haben. Tötet sie, nehmt ihre Schlüssel und benutzt sie, um die Truhen der Echsennarbe zu öffnen. Kehrt zu mir zurück, wenn Ihr die Litanei gefunden habt.]], },
    { [[Grom'tor, Sohn des Oronok, in der Echsennarbe im Schattenmondtal möchte, dass Ihr den ersten Teil der Litanei der Verdammnis besorgt.]], },
  },
  [10523] = {
    [[Die Litanei der Verdammnis - Erster Teil]],
    { [[<Grom'tor wickelt die Dokumente ein und legt sie in eine Schließkassette.>]], [[Bringt diese Kassette zu meinem Vater. Er erwartet Eure Rückkehr bei seinem Hof im Nordosten.]], [[Sagt ihm, dass ich bald nach Hause kommen werde. Sobald ich meinen Spaß hier hatte...]], },
    { [[Bringt Grom'tors Schließkassette zu Oronoks Hof im Schattenmondtal.]], },
  },
  [10524] = {
    [[Artefakte der Donnerfürsten]],
    { [[Das Artefakt verfügt über Markierungen, die es als Kunstwerk der Orcs der Donnerfürsten identifizieren. Diese Orcs lebten einst in der Donnerfeste, bevor sie zu Höllenorcs wurden.]], [[Eine Zeitlang wohnten die Oger der Speerspießer in der Feste, bis die Horde sie von dort vertrieben hat.]], [[Ihr habt schon von weiteren Artefakten gehört, die vermisst werden. Ihr wisst, dass sich unter den Objekten, nach denen gesucht wird, eine Trommel, ein Pfeil und eine Schrifttafel befinden.]], [[Durchsucht die Wehr der Speerspießer nach ihnen und bringt sie zu Rokgah Blutgriff.]], },
    { [[Retrieve the Thunderlord Clan Drum, Thunderlord Clan Arrow and the Thunderlord Clan Tablet.  Return them, along with the Thunderlord Clan Artifact, to Rokgah Bloodgrip at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10525] = {
    [[Wegweisende Visionen]],
    { [[<Dummer/Dumme><race> <race>. Ihr hättet fast die Trommel zerstört!]], [[Ich habe so hart daran gearbeitet und bis jetzt stand ich immer mit leeren Händen da. Wenigstens hattet Ihr Erfolg, wo alle anderen versagt haben.]], [[Jetzt, da wir diese vier Artefakte des Klans haben, ist es an der Zeit, eine Vision herbeizurufen, die Euch zu dem letzten Artefakt, nach dem ich suche, führen wird.]], [[Nehmt dieses feurige Fragment meiner Seele. Damit könnt Ihr Euren Körper in eine Flamme verwandeln und Euren Geist in eine wegweisende Vision versetzen, die Euch den Aufenthaltsort des letzten Relikts der Donnerfürsten zeigen wird.]], },
    { [[Use the Fiery Soul Fragment to transform your spirit into a vision guide.  Speak with Rokgah Bloodgrip at Thunderlord Stronghold after your vision has guided you to the location of the final Thunderlord relic.]], },
  },
  [10526] = {
    [[Die Donnerlanze]],
    { [[Wenn die Vision, die ich Euch gewährt habe, wahr ist, dann müsst Ihr hinunter in das südliche Ende der Blutschlägerklamm reisen. Dort findet Ihr die Draenethystmine.]], [[Reist bis ans Ende der Mine und beschafft die Donnerlanze. Beachtet nichts anderes! Ich interessiere mich nicht für die Kristalle in der Mine.]], [[Sobald Ihr den Speer habt, bringt ihn zu mir zurück.]], [[Und <name>, seid vorsichtig damit... Ich mag mir gar nicht ausmalen, was Euch zustoßen wird, wenn Ihr ihn zerbrecht.]], },
    { [[Rokgah Bloodgrip has tasked you with the retrieval of the Thunderspike.  Return it to her at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10527] = {
    [[Ar'tor, Sohn des Oronok]],
    { [[Mein jüngster Sohn, Ar'tor, hat sich bei der Stätte der Illidari auf die Suche begeben. Sie befindet sich weit im Südwesten, zwischen der Feste der Legion und der Burg Wildhammer.]], [[Auch wenn Ar'tor ein ausgezeichneter Jäger und Fährtenleser ist, befürchte ich, dass er sich dieses Mal übernommen hat. Die Stätte der Illidari ist eine Prüfungsstätte für Illidans Dämonen. Diese Dämonen haben die Brennende Legion verlassen, um sich dem Verräter anzuschließen!]], [[Reist zur Stätte der Illidari und findet Ar'tor, <name>!]], },
    { [[Findet Ar'tor, Sohn des Oronok, bei der Stätte der Illidari im Schattenmondtal.]], },
  },
  [10528] = {
    [[Dämonische Kristallgefängnisse]],
    { [[Eurer Einschätzung nach wird Ar'tors Körper von der Energie der Kristalle, die um die Rune herum schweben, in der Luft gehalten. Ihr versucht alles, was Ihr könnt, aber Ar'tor lässt sich nicht bewegen.]], [[Euch fällt auf, dass die Kristalle im unteren Teil eine Vertiefung aufweisen, vielleicht für einen Schlüssel. Neben den Kristallen scheinen Sukkuben regelmäßig eine Art Ritual durchzuführen.]], [[Durchsucht die Stätte der Illidari nach ihrer Anführerin. Vielleicht hat sie den Schlüssel zu Ar'tors Befreiung.]], },
    { [[Sucht und tötet Schmerzensmeisterin Gabrissa in der Stätte der Illidari und kehrt mit dem kristallenen Schlüssel zum Leichnam von Ar'tor, Sohn des Oronok, zurück.]], },
  },
  [10529] = {
    [[Der Weg des Jägers]],
    { [[Leutnant Dämmerflucht in der Zuflucht der Weltenwanderer hat eine Audienz mit allen fähigen Jägern gefordert. Es wäre sicher von Vorteil für Euch, wenn Ihr Euch so schnell wie möglich bei Ihr meldet, <name>. Sie ist ein wahrer Quell der Weisheit für Euresgleichen.]], [[Die Zuflucht der Weltenwanderer liegt südlich von Silbermond.]], },
    { [[Sprecht mit Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer.]], },
  },
  [10530] = {
    [[Der Weg des Jägers]],
    { [[Leutnant Dämmerflucht in der Zuflucht der Weltenwanderer hat eine Audienz mit allen fähigen Jägern gefordert. Es wäre sicher von Vorteil für Euch, wenn Ihr Euch so schnell wie möglich bei Ihr meldet, <name>. Sie ist ein wahrer Quell der Weisheit für Euresgleichen.]], [[Die Zuflucht der Weltenwanderer liegt südlich von Silbermond.]], },
    { [[Sprecht mit Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer.]], },
  },
  [10531] = {
    [[Die Schlacht um das Arathibecken!]],
    { [[<name>, die Schlacht im Arathibecken ist ein Kampf um Ressourcen und Territorien. Um unsere Truppen mit den dringend benötigten Rohstoffen und Vorräten zu versorgen, gilt es bestimmte Schlüsselpositionen zu besetzen und deren Ressourcen zu nutzen. Ich erteile Euch hiermit den Auftrag, die von unserem Stützpunkt am weitesten entfernten Schlüsselpositionen anzugreifen.]], [[Erobert den Hof, die Mine, das Sägewerk und die Schmiede, sofern der Feind diese Positionen unter seiner Kontrolle hat. Sucht an jedem dieser Orte nach dem gegnerischen Banner und reißt es aus seiner Halterung!]], [[Meldet Euch wieder bei mir, wenn Euch das an allen vier Schlüsselpositionen gelungen ist.]], },
    { [[Erobert die Mine, das Sägewerk, die Schmiede und den Hof und meldet Euch anschließend bei Feldmarschall Lichtmark in der Zuflucht.]], },
  },
  [10532] = {
    [[Zerschlagt die Versorgungslinien Arathors]],
    { [[Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.]], [[Ihr könnt uns helfen, <name>. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpunkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.]], },
    { [[Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.]], },
  },
  [10533] = {
    [[Noch mehr Vorratskisten]],
  },
  [10534] = {
    [[Heimkehr]],
    { [[Ah, <ein/eine><class> <class>...]], [[Ja, vor Kurzem war ein Priester Eures Ordens hier, <name>. Er hat mir gesagt, dass es an der Zeit für Euch ist, zur Exodar zurückzukehren und Caedmos zu treffen. Es klang, als wäre es ziemlich dringend. Lasst Euch also nicht allzu viel Zeit.]], },
    { [[Caedmos in der Exodar wünscht Euch zu sehen.]], },
  },
  [10535] = {
    [[Die Ressourcen des Arathibeckens]],
    { [[Eine Schlacht muss mit Soldaten gewonnen werden, das wird Euch jeder Soldat gerne bestätigen. Man sagt auch, dass eine Schlacht auf leeren Magen und mit schlechter Ausrüstung bereits eine verlorene Schlacht ist!]], [[Deshalb ist das Arathibecken so wichtig. Es gibt dort Schlüsselregionen, die essentielle Ressourcen bergen. Metalle, Waffen, Holz, ja, alles wird gebraucht und kann dort gefunden werden.]], [[Daher brauche ich Euch, <name>. Begebt Euch ins Arathibecken und entscheidet die Schlacht für Euch, indem Ihr mehr Stützpunkte haltet als der Feind. Bringt mir als Beweis Eurer Taten eine Vorratskiste.]], },
    { [[Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht.]], },
  },
  [10536] = {
    [[Noch mehr Vorratskisten]],
  },
  [10537] = {
    [[Lohn'goron, Bogen des Herzeleid]],
    { [[Es ist ein Zeichen, <name>. Nicht einmal der Tod kann mich aufhalten. Einen Teil der Litanei der Verdammnis zu beschaffen ist mein Schicksal. Mit Eurer Hilfe werde ich es erfüllen.]], [[Bevor wir uns auf diese Reise begeben, brauche ich meinen Langbogen, Lohn'goron. Er ist ein Familienerbstück, das seit Generationen von Herzeleid zu Herzeleid weitergegeben wurde.]], [[Meine Instinkte sagen mir, dass einer der Dämonen ihn von meiner Leiche gestohlen hat. Tötet die Dämonen hier, bis Ihr meinen Bogen habt.]], },
    { [[Der Geist von Ar'tor bei der Stätte der Illidari im Schattenmondtal möchte, dass Ihr Lohn'goron, den Bogen des Herzeleid, von den Dämonen in der Gegend beschafft.]], },
  },
  [10538] = {
    [[Kochendes Blut]],
    { [[Das letzte Experiment war kein kompletter Fehlschlag. Ich habe ein wenig von dem Blut, das Ihr mir gebracht habt, aufgehoben. Jetzt, da es abgekocht ist, sollte es frei von allen Flüchen sein, mit denen die Hexen des Blutenden Auges das Blut ihres Volkes belegt haben.]], [[Ich brauche jedoch noch mehr Blut für meine Untersuchungen. Begebt Euch nach Zeth'Gor und sammelt dort das Blut der Orcs. Kocht es danach in ihrem verfluchten Kessel und bringt es mir.]], },
    { [[Benutzt den Verfluchten Kessel, um das Blut des Blutenden Auges in gekochtes Blut zu verwandeln. Bringt danach 12 Einheiten gekochtes Blut zu Apotheker Albreck im Rückenbrecherposten.]], },
  },
  [10539] = {
    [[Heimkehr]],
    { [[Ah, <ein/eine><class> <class>...]], [[Ja, vor Kurzem war ein Priester Eures Ordens hier, <name>. Er hat mir gesagt, dass es an der Zeit für Euch ist, zur Exodar zurückzukehren und Caedmos zu treffen. Es klang, als wäre es ziemlich dringend. Lasst Euch also nicht allzu viel Zeit.]], },
    { [[Caedmos in der Exodar wünscht Euch zu sehen.]], },
  },
  [10540] = {
    [[Die Litanei der Verdammnis - Ar'tors Angriff]],
    { [[Endlich sind wir bereit. Ich werde Euch einen Teil meines Geistes verleihen, Held. Er wird Euch den Weg weisen und Euch im Kampf gegen das Böse unterstützen.]], [[Dann müsst Ihr von hier aus nach Süden an die Rückseite der Stätte der Illidari reisen. Versteckt im Astralraum befindet sich eine bösartige Kreatur namens Veneratus der Vielgesichtige. Mein Geistjäger wird ihn hervorlocken, wenn er in der Nähe ist.]], [[Vernichtet Veneratus und nehmt ihm den zweiten Teil der Litanei der Verdammnis ab.]], },
    { [[Der Geist von Ar'tor bei der Stätte der Illidari im Schattenmondtal möchte, dass Ihr den zweiten Teil der Litanei der Verdammnis von Veneratus dem Vielgesichtigen besorgt.]], [[Kreaturen, die vom Geistjäger angegriffen werden oder Schaden erleiden, gewähren Euch keine Beute oder Erfahrung.]], },
  },
  [10541] = {
    [[Die Litanei der Verdammnis - Zweiter Teil]],
    { [[Ich danke Euch, Held. Ohne Euch wäre mein Geist auf ewig an diesem verfluchten Ort gefangen.]], [[<Ar'tor reicht Euch eine spektrale Schließkassette.>]], [[Nehmt sie und bringt sie zu meinem Vater beim Hof im Nordosten, über der Zisterne der Echsennarbe... darin befinden sich Lohn'goron und der zweite Teil der Litanei.]], [[Er wird sehr traurig über meinen Tod sein. Versprecht mir, dass Ihr ihm versichert, dass ich als freie Seele gestorben bin. Sagt ihm, dass ich die Jagdgründe sehe und dass sie wundervoll sind. Sagt ihm, dass ich dort auf ihn warte...]], },
    { [[Bringt Ar'tors Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal.]], },
  },
  [10542] = {
    [[Die ham meine Pfeife und mein Gesöff geklaut!]],
    { [[T'chali war immer ein gediegener Troll. Ich kam hierher, um den Blutschlägern beizubringen, wie man Bier braut. Aber dann ham die Blutschläger mich hier begraben und lassen mich hier verrotten!]], [[Das Schlimmste ist, dass die meine Wasserpfeife geklaut ham!]], [[Wollt Ihr T'chali also helfen? Bringt mir meine Wasserpfeife zurück! T'chali glaubt, dass die Oger, die sie gestohlen ham, nach Südwesten bei der Mine drüben abgehauen sind.]], [[Und wenn Ihr schon da seid, nehmt noch etwas von meinem Gebräu mit!]], },
    { [[Retrieve T'chali's Hookah and 10 Bloodmaul Brutebane Brews.  Bring them back to T'chali the Witch Doctor at Bloodmaul Ravine in the Blade's Edge Mountains.]], },
  },
  [10543] = {
    [[Grimnok und Korgaah, ich komm' Euch holen!]],
    { [[T'chali hat auf seine Rache gewartet!]], [[An dem Tag, an dem sie T'chali begraben und zum Sterben hier gelassen ham, hat Grimnok Schlachttaufe der Blutschläger sie angeführt. Und am nächsten Tag kam Korgaah nach Süden, um zu Plündern und T'chali gegen den Kopf zu treten.]], [[Ich will die beiden tot sehen! Ihr findet Grimnok auf dem Südschlägerturm im Außenposten der Blutschläger. Korgaah befindet sich auf einem Gebäude am nördlichen Ende der Wehr der Speerspießer. Korgaah hat nüchterne Freunde bei sich. Ihr werdet also vielleicht das Gebräu brauchen, um ihn von ihnen wegzulocken.]], },
    { [[T'chali the Witch Doctor has asked you to kill his chief tormentors, Grimnok Battleborn and Korgaah.  After you have done so, return to him at Bloodmaul Ravine in the Blade's Edge Mountains.]], },
  },
  [10544] = {
    [[Verflucht seien Eure beiden Klans!]],
    { [[T'chali hat beschlossen, dass er Rache an allen Ogern will!]], [[T'chali war in seinem Leben ein mächtiger Hexendoktor. Wenn Ihr in mein Grab fasst, findet Ihr dort einen verdammt mächtigen Fetisch.]], [[Nehmt den Fetisch und ruft seine Magie, um die Gebäude der Wehr der Speerspießer und des Außenpostens der Blutschläger zu verfluchen. Der Voodoo wird einen bösen Geist rufen, den Ihr vernichten müsst. Das wird wiederum das Haus verfluchen.]], [[Aber Vorsicht! Wenn Ihr versucht, ein Haus zu verfluchen, das bereits verflucht ist, ruft Ihr damit einen weiteren bösen Geist!]], },
    { [[Use the Wicked Strong Fetish to curse 5 buildings or towers in Bladespire Hold and 2 in Bloodmaul Outpost.  Return to T'chali the Witch Doctor at Bloodmaul Ravine in the Blade's Edge Mountains after you have done so.]], },
  },
  [10545] = {
    [[Saufgelage der Speerspießer]],
    { [[Wie T'chali schon gesagt hat, hat er seinen Freunden von den Blutschlägern dabei geholfen, Ihr Gebräu zu brauen. Aber T'chali hat ihnen nicht gesagt, warum.]], [[Die Blutschläger stecken ziemlich in der Klemme. Da die Speerspießer drohen, sie zu zermalmen, brauchten die Blutschläger eine neue Waffe, um sie im Kampf zu unterstützen.]], [[Da kam T'chali auf eine tolle Idee. Warum nicht das perfekte Getränk brauen, das einen selbst stärkt und die Feinde schwächt? Der Haken ist nur, dass T'chali nicht weiß, ob es geklappt hat.]], [[Bringt dieses Gebräu also zur Wehr der Speerspießer und schaut, ob es funktioniert!]], },
    { [[T'chali has asked you to take the Bloodmaul Brutebane Keg and use it in an attempt to get 5 Bladespire Ogres drunk.  If successful, return to T'chali the Witch Doctor at Bloodmaul Ravine in the Blade's Edge Mountains.]], },
  },
  [10546] = {
    [[Borak, Sohn des Oronok]],
    { [[Während Ar'tor auf der Jagd war und Grom'tor kämpfte, verbrachte Borak seine Zeit mit Lernen. Dann nahm er eine Stelle als Gelehrter an und ist nach Draenor aufgebrochen. Vor ein paar Jahren kam er als ganz neuer Orc zurück.]], [[Seine erhöhte Aufmerksamkeit und Geistesschärfe haben ihn zu einem schweigsamen Meuchelmörder gemacht.]], [[Jetzt, da er ein Orc mit Selbstvertrauen und Macht ist, hat er sich aufgemacht, um das bestgehütete Teil der Litanei zu beschaffen.]], [[Sucht Borak. Er versteckt sich in der Nähe der Stätte der Mondfinsternis, weit im Süden. Er liegt im Schatten der Brücke auf der Lauer.]], },
    { [[Sucht Borak, Sohn des Oronok, in der Nähe der Stätte der Mondfinsternis im Schattenmondtal.]], },
  },
  [10547] = {
    [[Von Distelköpfen und Eiern...]],
    { [[Wenn es mir nicht aufgefallen wäre, hätte ich aufgegeben. Er ist ein Distelabhängiger. Zuerst habe ich es nicht verstanden, aber die Zeichen sind alle da...]], [[Wenn wir an ein paar Blutdisteln herankämen, wären wir im Geschäft.]], [[Auf meinen Reisen habe ich in Shattrath einen zwielichtigen Zerschlagenen namens Tobias getroffen.]], [[Tobias verdient sein Geld damit, den Blutelfen im Austausch gegen verfaulte Arakkoaeier Blutdisteln zu geben.]], [[Direkt westlich von hier befinden sich Ruinen der Arakkoa. Durchsucht sie nach einem Ei und begebt Euch dann in das Untere Viertel von Shattrath. Dort findet Ihr Tobias.]], },
    { [[Borak, Sohn des Oronok, bei der Brücke nördlich der Stätte der Mondfinsternis möchte, dass Ihr ein verfaultes Arakkoaei besorgt und es zu Tobias dem Faulfraß in Shattrath, nordwestlich der Wälder von Terokkar, bringt.]], },
  },
  [10548] = {
    [[Die traurige Wahrheit]],
    { [[Erinnert Ihr Euch daran, dass ich von einem fetten Händler sprach, der auf der Suche nach diesem Ring war? Wie es aussieht, hat er seinen Sohn, der Archäologe ist, danach geschickt. Es scheint jedoch kein allzu gutes Ende genommen zu haben. Mit nicht allzu gut meine ich, dass sie ihn getötet und einen Schrumpfkopf aus ihm gemacht haben.]], [[Das klingt jetzt vielleicht eiskalt, berechnend und unheimlich gefühllos, aber er ist bereit, eine stolze Summe für den Kopf seines Sohns hinzublättern. Klingt nach der perfekten Aufgabe für Euch!]], [[Geht nach Osten, bis Ihr die Trollruinen von Zeb'Tela und Zeb'Nowa erreicht. Er müsste sich dort in einer der Truhen befinden.]], },
    { [[Eralan in Tristessa möchte, dass Ihr den Schrumpfkopf des Archäologen aus einer der Truhen in Zeb'Tela oder Zeb'Nowa besorgt.]], },
  },
  [10550] = {
    [[Ein Bündel von Blutdisteln]],
    { [[Ich habe nicht gelogen. Das Ei war unglaublich. Woher hattet Ihr es?]], [[Nun, falls Ihr jemals mehr Blutdisteln brauchen solltet, wisst Ihr ja, wo Ihr mich findet und was ich benötige.]], [[Da habt Ihr ein Bündel von dem Zeug. Bringt es zurück zu Eurem Auftraggeber.]], },
    { [[Bringt das Blutdistelbündel zurück zu Borak, Sohn des Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal.]], },
  },
  [10551] = {
    [[Loyalität gegenüber den Aldor]],
  },
  [10552] = {
    [[Loyalität gegenüber den Sehern]],
  },
  [10553] = {
    [[Voren'thal der Seher]],
    { [[Jetzt, da Ihr Euch für eine Seite entschieden habt, <name>, solltet Ihr den Anführer der Seher kennenlernen.]], [[Ihr findet Voren'thal den Seher in der Bibliothek der Seher auf der Sehertreppe.]], [[Schaut nach, ob Ihr ihn unterstützen könnt. Er wird sich über Eure Entscheidung, den Sehern beizustehen, sehr freuen.]], },
    { [[Sprecht mit Voren'thal dem Seher in der Bibliothek der Seher bei der Sehertreppe in Shattrath.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10554] = {
    [[Ishanah]],
    { [[Jetzt, da Ihr Euch für eine Seite entschieden habt, <name>, solltet Ihr die Anführer der Aldor kennenlernen. Ihr findet Hohepriesterin Ishanah im Schrein des unendlichen Lichts.]], },
    { [[Sprecht mit Ishanah im Schrein des unendlichen Lichts auf der Aldoranhöhe in Shattrath.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10555] = {
    [[Fauler Zauber]],
    { [[Die Magie der Arakkoa der Lashh'an ist unnatürlich. Sie reißen sich ganze Hände voll - oder Klauen voll - ihrer eigenen Federn als Reagenz heraus!]], [[Für mich klingt das nicht gerade nach guter Magie. Ich bin hierhergekommen, um mehr über ihre Zauber zu erfahren, aber näher heranzugehen ist einfach eine Nummer zu groß für mich.]], [[Ihr könntet es jedoch schaffen. Wirkt einen ihrer Zauber auf Euch selbst und kehrt dann zu mir zurück. Ihr müsst eines ihrer Zauberbücher finden, das sich hoch oben in den Bäumen des Lashhverstecks befindet.]], [[Und falls Euch nicht irgendwo am Körper versteckte Federn wachsen, müsst Ihr ein paar davon sammeln.]], },
    { [[Collect 18 Plucked Lashh'an Feathers, then read the Lashh'an Tome in the treetops of Veil Lashh.]], },
  },
  [10556] = {
    [[Gekritzel]],
    { [[Die Piktogramme deuten darauf hin, dass für das Wirken der Zauber in den Zirkeln der Macht im Lashhversteck Federn benutzt werden.]], [[Verwirrende Bilder scheinen einen Arakkoa zu zeigen, der vom Geist eines Kaliri eingehüllt wird.]], },
    { [[Use the Fistful of Feathers on the Lashh'an Spell Circle, then return to Daranelle at Sylvanaar before the kaliri spirit fades.]], },
  },
  [10557] = {
    [[Testflug: Das Zephyriumkapazitorium]],
    { [[Ich habe ein sagenhaftes Gerät erfunden: das Zephyriumkapazitorium. Ich bin mir sicher, dass es einwandfrei funktionieren wird, aber... habt Ihr Lust, es für mich zu testen?]], [[Es gibt auch genügend Gefahrenzulage!]], [[Sprecht mit Rally, meinem Zwilling da drüben. Viel Glück, <name>. Kommt nach dem Experiment zu mir zurück. Oder.. falls das wegen erlittener Verletzungen nicht möglich ist, schickt einen nahen Verwandten, um Eure Bezahlung abzuholen.]], },
    { [[Speak with Rally Zapnabber and use the Zephyrium Capacitorium.  Return to Tally Zapnabber at Toshley's Station.]], },
  },
  [10558] = {
    [[Respektvoller Ruf in der Ehrenfeste]],
    { [[Ihr habt Euch für unsere Sache als hilfreich erwiesen, <name>. Jemand wie Ihr hat das Zeug, ein großer Anführer zu werden.]], [[Kämpft auch weiterhin gegen unsere Feinde in den Zerschmetterten Hallen und verdient Euch die Achtung meiner Männer. Wenn Ihr meine Erwartungen erfüllt, werde ich Euch großartigere Aufträge geben.]], },
    { [[Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr Euch einen respektvollen Ruf in der Ehrenfeste verschafft, indem Ihr die Höllenorcs in den Zerschmetterten Hallen der Höllenfeuerzitadelle tötet.]], },
  },
  [10559] = {
    [[Respektvoller Ruf in Thrallmar]],
    { [[Ihr habt Euch immer und immer wieder als würdig erwiesen, <class>. Und doch warten noch mehr Prüfungen auf Euch.]], [[Kämpft weiter gegen die Höllenhorde in den Zerschmetterten Hallen der Höllenfeuerzitadelle. Dann wird Euer Name bald allen Freunden Thrallmars im ganzen Land bekannt sein!]], },
    { [[Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr Euch einen respektvollen Ruf in Thrallmar verschafft, indem Ihr die Höllenorcs in den Zerschmetterten Hallen der Höllenfeuerzitadelle tötet.]], },
  },
  [10560] = {
    [[Respektvoller Ruf bei den Sha'tar]],
    { [[<Die beruhigenden melodischen Töne, die von A'dal ausgehen, werden leiser und dunkler.>]], [[<Euch wird das große Naarugebäude, das als Festung der Stürme bekannt ist, gezeigt. Jetzt wird es von Kael'thas Streitmächten bewohnt und seine Macht wird zu bösartigen Zielen eingesetzt. A'dal verspricht Euch größere Aufgaben, wenn Ihr Euren Teil in der Festung der Stürme beigetragen habt.]], },
    { [[A'dal in Shattrath möchte, dass Ihr Euch einen respektvollen Ruf bei den Sha'tar verschafft, indem Ihr Kael'thas Streitkräfte in der Festung der Stürme tötet.]], },
  },
  [10561] = {
    [[Respektvoller Ruf bei den Hütern der Zeit]],
    { [[Ihr habt Euch bewiesen, <race>. Jedoch ist die Zeitlinie immer noch nicht sicher.]], [[Wir brauchen Helden; und Ihr könntet einer davon sein.]], [[Beschützt die Geschehnisse der Vergangenheit auch weiterhin gegen Eindringlinge. Dann werdet Ihr die Achtung bekommen, die Euch zusteht.]], },
    { [[Andormu in den Höhlen der Zeit in Tanaris möchte, dass Ihr Euch einen respektvollen Ruf bei den Hütern der Zeit verdient, indem Ihr weiterhin die Ereignisse im Alten Hügelland und dem Schwarzen Morast in den Höhlen der Zeit verteidigt.]], },
  },
  [10562] = {
    [[Belagert!]],
    { [[Ihr müsst weiterhin wachsam bleiben, <race>. Wir stehen unter ständigem Angriff der Brennenden Legion.]], [[Die Dämonen lassen von ihrem Lager in der Feste der Legion aus ständig neue Höllenbestien auf uns herabregnen. Und sobald wir sie getötet haben, schlagen neue Meteoriten ein und eine neue Angriffswelle geht los.]], [[Meine Männer sind erschöpft und ausgelaugt. Ich weiß nicht, wie lange sie das noch durchhalten. Bitte, <name>, helft uns bei der Verteidigung. Nehmt Euren Platz in der Linie ein und haltet den Vormarsch der Höllenbestien auf!]], },
    { [[Schwadronskommandant Nuainn außerhalb der Wildhammerfeste möchte, dass Ihr 10 angreifende Höllenbestien tötet.]], },
  },
  [10563] = {
    [[Zur Feste der Legion]],
    { [[The fortress of Legion Hold is nigh impregnable, and to be completely honest, I can't spare the forces for an assault. Our hopes are riding on you, <name>.]], [[What little information we've been able to gather indicates that the Legion stocks and prepares its infernals at the hold. With the help of a disguise I'll give you, you must sneak into Legion Hold and learn about the their plans. Your best hope is to intercept or overhear communication between their commanders. Good luck, <name>.]], },
    { [[Begebt Euch zur Feste der Legion und findet das Kommunikationsgerät der Legion. Benutzt die Verkleidung, während Ihr in der Nähe des Kommunikationsgeräts steht, um mehr über die Pläne der Legion herauszufinden. Kehrt danach zu Schwadronskommandant Nuainn in der Wildhammerfeste zurück.]], },
  },
  [10564] = {
    [[Jagt die Höllenbestien in die Luft!]],
    { [[Das Gerät, das wir zusammengebaut haben, würde keinen Schönheitspreis gewinnen, aber es sollte die Höllenbestien in der Feste der Legion zerstören können. Danach zu urteilen, was der Kraftkern des Teufelshäschers bei dem Fauligen Teich angerichtet hat, muss darin eine gehörige Explosionskraft stecken.]], [[Wenn Ihr das Gerät in der Mitte der grünen Glyphe in der Feste platzieret, solltet Ihr alle Höllenbestien erwischen können.]], },
    { [[Zündet die Teufelsbombe, während Ihr auf der grünen Glyphe in der Feste der Legion steht. Meldet Euch bei Schwadronskommandant Nuainn in der Wildhammerfeste zurück, wenn Ihr Euren Auftrag erledigt habt.]], },
  },
  [10565] = {
    [[Die Steine der Vekh'nir]],
    { [[Die Arakkoa der Vekh'nir sind uns schon seit vielen Jahren ein Dorn im Auge. Sie praktizieren eine merkwürdige Religion, die sie in ständiger Betriebsamkeit hält. Sie tragen nun diese Kristalle bei sich, die sie für irgendwelche dunklen Zwecke einsetzen.]], [[Ich kenne mich ein wenig mit ihren Ritualen aus. Sie bringen die Kristalle zu ihren Zauberzirkeln, um sie dort aufzuladen. Wir im Dorf wüssten gerne mehr darüber. Lauert ein paar der Arakkoa auf, bis Ihr einen Kristall findet. Ladet ihn danach auf und bringt ihn zu mir zurück.]], },
    { [[Collect and use a Vekh'nir Crystal at the Vekh'nir Spell Circle, then return the Charged Vekh'nir Crystal to Dertrok at the Mok'Nathal Village.]], },
  },
  [10566] = {
    [[Probieren geht über Studieren]],
    { [[Ich habe vier Zauberstäbe hergestellt, die die Macht der Arakkoakristalle verstärken sollten. Bringt diese Stäbe zu Trogmas Besitz und probiert sie an den Ogersklaven aus.]], [[Ihr benötigt einen energieerfüllten Kristall von den Arakkoa in der Gegend, um die Stäbe zu benutzen.]], [[Kommt zu mir zurück, sobald Ihr herausgefunden habt, welcher Stab am besten funktioniert.]], },
    { [[Try out each of Dertrok's wands on an Arakkoa Ogre Slave, then return to Dertrok. Each wand will require an Infused Vekh'nir Crystal to use.]], },
  },
  [10567] = {
    [[Die Herstellung des Anhängers]],
    { [[Unter den Arakkoa des Ruuanwaldes nehmen die Herolde des Raben innerhalb der Brut eine besondere Stellung ein, da sie die Boten ihres Gottes sind.]], [[Die Arakkoa glauben, dass die Herolde vom Himmel aus, wo sie den Lehren des Raben lauschen, über sie wachen und hinabsteigen, um sein Wort zu verbreiten.]], [[Die Herolde haben einen starken Einfluss auf die Seelen der Arakkoa. Sie benutzen magische Anhänger, die wir benötigen.]], [[Lockt einen der Herolde herbei, indem Ihr seine Brut tötet und ihre Klauen abtrennt. Legt die Klauen auf den Altar, um seine Aufmerksamkeit zu erregen.]], },
    { [[Find 6 Ruuan'ok Claws and use them at the Ruaan'ok Oracle Circle to summon a Harbinger of the Raven. Kill it and return its Harbinger's Pendant to Timeon at Ruuan Weald.]], },
  },
  [10568] = {
    [[Schrifttafeln von Baa'ri]],
    { [[Vor vielen Jahren schuf der gefürchtete Orchexenmeister Gul'dan den Vulkan, der sich in der Mitte von Schattenmond befindet. Die Draeneistadt Baa'ri wurde mitsamt seiner Einwohner und vielen wichtigen religiösen Artefakten unter der Lava begraben.]], [[Die verräterischen Zerschlagenen der Aschenzungen wurden von Illidan beauftragt, in dieser Gegend Ausgrabungen durchzuführen. Wir müssen diese Chance nutzen, um an die Schrifttafeln von Baa'ri zu kommen. Dieser alte Text soll angeblich die Geheimnisse des Tempels von Karabor, der heute als Schwarzer Tempel bekannt ist, enthalten.]], },
    { [[Anachoret Ceyla beim Altar der Sha'tar möchte, dass Ihr 12 Schrifttafeln von Baa'ri vom Boden und von den Arbeitern der Aschenzungen in den Ruinen von Baa'ri sammelt.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10569] = {
    [[Das Wrack der Sketh'lon]],
    { [[Unsere Späher haben uns erschütternde Berichte aus den zerstörten Arakkoalagern in der Gegend geliefert.]], [[Die Geister der mächtigen Arakkoamagier verfügen über nie geahnte Kräfte! Sie scheinen sich zu formieren, als ob sie etwas planten, aber wir wissen nur sehr wenig darüber. Wir können uns nicht leisten, die Augen davor zu verschließen!]], [[Reist nach Norden zum Wrack der Sketh'lon, nordwestlich der Todesschmiede, und sucht nach Anzeichen für ihre Beweggründe.]], },
    { [[Besorgt das Tagebuch des Kommandanten der Sketh'lon - Seite 1, das Tagebuch des Kommandanten der Sketh'lon - Seite 2 und das Tagebuch des Kommandanten der Sketh'lon - Seite 3 von den Arakkoa des Dunklen Rats beim Wrack der Sketh'lon und bringt die Seiten zu Greifenreiter Kieran.]], },
  },
  [10570] = {
    [[Einen Distelsüchtigen fangen]],
    { [[<Borak nimmt Euch das Blutdistelbündel ab und riecht daran.>]], [[Stechend...]], [[Jetzt nehmt das Bündel und legt es am anderen Ende dieser Brücke ab. Wenn der Entsandte seine tägliche Runde macht, wird er gar nicht anders können, als es aufzuheben - ohne seine Leibwache. Wisst Ihr, Blutelfen verachten Blutdistelabhängige. Wenn einer seiner Gefährten das je herausfinden würde...]], [[Ihr müsst nur daliegen und abwarten, bis er seine Leibwache fortschickt. Wenn er alleine ist, schlagt zu! Tötet ihn und nehmt Euch den Schriftwechsel.]], },
    { [[Borak, Sohn des Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr Sturmgrimms Schriftwechsel beschafft.]], },
  },
  [10571] = {
    [[Oronu der Älteste]],
    { [[Laut den Schrifttafeln wurde in Baa'ri ein Artefakt, das als Medaillon der heiligen Stimme bekannt ist, aufbewahrt. Nur die höchstrangigen Draeneipriester durften es tragen, da es uneingeschränkten Zugang zum Tempel von Karabor bot.]], [[Wir müssen herausfinden, ob die Aschenzungen das Medaillon schon gefunden haben. Seine Macht wäre uns eine große Hilfe im Kampf gegen Illidan.]], [[Akamas Leutnant in Baa'ri, Oronu der Älteste, erhält täglich schriftliche Befehle von Akama. Besorgt sie Euch und bringt sie mir.]], },
    { [[Anachoret Ceyla beim Altar der Sha'tar möchte, dass Ihr die Befehle von Akama von Oronu dem Ältesten in den Ruinen von Baa'ri besorgt.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10572] = {
    [[Die Bombe bauen]],
    { [[Wir können der Legion nicht erlauben, die Wildhammerfeste auf ihrem Marsch zum Schwarzen Tempel zu zertrampeln.]], [[Es gibt nur einen Weg, um sie aufzuhalten, <name>. Ihr müsst einen Sprengkörper herstellen, der groß genug ist, um den gesamten Vorrat an Höllenbestien in der Feste der Legion zu zerstören.]], [[Am Fauligen Teich in der Nähe der Feste der Legion gibt es viele alte Teufelshäscherkraftkerne, die Ihr als Grundlage für Euer Gerät verwenden könnt. Durchsucht die anderen Trümmer in der Nähe der Feste nach einer Rüstungsplatte, aus der wir ein Gehäuse bauen können.]], },
    { [[Besorgt Euch einen Teufelshäscherkraftkern und eine Teufelshäscherrüstungsplatte und bringt sie zu Schwadronskommandant Nuainn in der Wildhammerfeste.]], },
  },
  [10573] = {
    [[Die Todesschmiede]],
    { [[Von Euren Entdeckungen in der Feste der Legion konnten wir erfahren, dass die Verbündeten der Legion vom Schattenrat Höllenbestien in der Todesschmiede herstellen. Ich habe einen kleinen Spähertrupp geschickt, um die Gegend zu überwachen, aber leider können sie nicht alle anstehenden Aufgaben erfüllen.]], [[Wenn wir die Todesschmiede weiter arbeiten lassen, werden sie den Vorrat an Höllenbestien, den Ihr vernichtet habt, im Handumdrehen wieder aufgestockt haben.]], [[Ich möchte, dass Ihr Euch mit Sturmrufer Ewan Wildschwinge unterhaltet. Ihr findet ihn im Turm der Todesschmiede, der die Schmiede überblickt, nordwestlich von hier.]], },
    { [[Meldet Euch bei Sturmrufer Ewan Wildschwinge im Turm der Todesschmiede.]], },
  },
  [10574] = {
    [[Die Verderber der Aschenzungen]],
    { [[Laut Akamas Brief wurden vier Teile des Medaillons ausgegraben und den Verderbern der Aschenzungen übergeben. Diese gefürchteten Zerschlagenen waren einst mächtige Schamanen, die nun die Elemente schänden, die sie einst verehrten.]], [[Haalum, der Verderber der Luft, befindet sich bei den Netherschwingenklippen. Eykenen und Uylaru, die Verderber der Erde und des Feuers, wurden zuletzt bei den Teufelsgruben in der Nähe der Hand des Gul'dan gesehen. Lakaan, der Verderber des Wassers, soll sich in der Nähe der Echsennarbe aufhalten.]], [[Findet sie und besorgt Euch die Medaillonfragmente von ihnen.]], },
    { [[Beschafft die vier Medaillonfragmente von Haalum, Eyeknen, Lakaan und Uylaru und kehrt danach zu Anachoret Ceyla beim Altar der Sha'tar im Schattenmondtal zurück.]], [[Das Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10575] = {
    [[Der Kerker des Wächters]],
    { [[Die Fragmente, die Ihr besorgt habt, sind nur eine Hälfte des Amuletts. Der Anführer der Aschenzungen, Akama, muss die andere Hälfte haben.]], [[Ich wage es fast nicht, Euch darum zu bitten, da Akama sowohl böse als auch gerissen ist.]], [[Er und die Todeshörigen der Aschenzungen - die bösartigsten und grausamsten der Zerschlagenen - sollen irgendwo im Kerker des Wächters eine Folterkammer betreiben.]], [[Ich möchte, dass Ihr dieses gefürchtete Gefängnis findet und Akamas Aufenthaltsort bestimmt. Sein Stellvertreter vor Ort ist ein Zerschlagener namens Sanuro. Befragt ihn, wenn Ihr müsst.]], },
    { [[Anachoret Ceyla möchte, dass Ihr den Kerker des Wächters südlich der Ruinen von Baa'ri betretet und Sanoru befragt, um Akamas Aufenthaltsort herauszufinden.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Seher.]], },
  },
  [10576] = {
    [[Unterwürfigkeit in Schattenmond]],
    { [[<Borak mustert Euch.>]], [[Es wird schwierig, aber ich glaube, es könnte klappen.]], [[Ich kann Euch als einen von Illidans Blutelfen verkleiden, <name>. Aber zuerst benötige ich dazu einige Teile ihrer Rüstung. Sie muss unbedingt sauber sein. Keine Blutflecken! Wir wollen schließlich keinen Verdacht erwecken.]], [[Bringt mir die Ausrüstung und ich kümmere mich um den Rest der Verkleidung.]], [[Sobald die Verkleidung fertig ist, werdet Ihr dem Großkommandanten die Nachricht überbringen - persönlich!]], },
    { [[Borak, Sohn des Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr 6 Teile der Rüstung der Mondfinsternis besorgt.]], },
  },
  [10577] = {
    [[Was Illidan will, soll Illidan bekommen...]],
    { [[Nehmt diese Verkleidung und begebt Euch zur Stätte der Mondfinsternis. Wenn Ihr dort seid, benutzt die Verkleidung, um Euch zu tarnen. Die Blutelfen der Mondfinsternis werden Eure Verkleidung nicht durchschauen können. Daher habt Ihr freie Bahn, bis Ihr zu Großkommandant Ruusk gelangt.]], [[Sobald Ihr Ruusk gefunden habt, sagt ihm Bescheid, wo das Wort als Nächstes versteckt werden soll. Ihr müsst schauspielern, was das Zeug hält, <name>. Gebt vor, ein Untergebener zu sein, der eine Nachricht seines Fürsten überbringt. Das sollte funktionieren.]], [[Aber gebt auf die Drachenfalken Acht. Sie können Eure Verkleidung durchschauen.]], },
    { [[Borak, Sohn des Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr Großkommandant Ruusk in der Stätte der Mondfinsternis Illidans Nachricht überbringt.]], },
  },
  [10578] = {
    [[Die Litanei der Verdammnis - Boraks Angriff]],
    { [[Zuluhed wird diesen Teil der Litanei niemals bekommen. Ihr werdet Ruul dem Verfinsterer und seinem Transportgut auflauern, bevor er eine Chance hat, es abzuliefern.]], [[Die Festung des Drachenmals liegt direkt östlich von hier, jenseits der Netherschwingenfelder. Ihr findet Ruul irgendwo zwischen der Feste des Drachenmals und dem Sanktum der Sterne. Schlagt schnell und hart zu! Und seid vorsichtig, <name>. Ruul scheint eine harte Nuss zu sein.]], [[Bringt die Litanei zu mir zurück, wenn Ihr erfolgreich wart.]], },
    { [[Borak, Sohn des Oronok, bei der Brücke in der Nähe der Stätte der Mondfinsternis im Schattenmondtal möchte, dass Ihr den dritten Teil der Litanei der Verdammnis von Ruul dem Verfinsterer besorgt.]], [[Für diesen Auftrag werdet Ihr eine Gruppe benötigen.]], },
  },
  [10579] = {
    [[Die Litanei der Verdammnis - Dritter Teil]],
    { [[Bringt diese Kassette zu meinem Vater. Er wohnt immer noch bei Oronoks Hof weit im Norden, über der Zisterne der Echsennarbe. Wenn alle drei Teile zusammengefügt sind, wird die alte Litanei auf ein Neues bekannt sein.]], [[Viel Glück, <name>. Sagt meinem Vater, dass ich bald nach Hause komme.]], },
    { [[Bringt Boraks Schließkassette zu Oronok Herzeleid bei Oronoks Hof im Schattenmondtal.]], },
  },
  [10580] = {
    [[Wo sind diese verdammten Gnome hin?]],
    { [[Trotz all der guten Dinge, die wir hier getan haben, fühle ich mich wegen einer Sache schlecht: Wir haben Toshley und seine Gnomenfreunde vertrieben.]], [[Wir hatten keine andere Wahl; sie haben mit all ihrer Technologie und ihren Experimenten unsere Bemühungen im Hain zunichtegemacht.]], [[Ich wäre Euch sehr dankbar, wenn Ihr herausfinden könntet, was aus ihnen geworden ist. Unseren letzten Informationen zufolge sind sie nach Osten über die Brücke gegangen. Ich bin mir sicher, dass sie dann nach Süden von der Donnerfeste weg gezogen sind.]], [[Haltet im südlichen Zackengrat nach Spuren von ihnen Ausschau.]], },
    { [[Commander Skyshadow has asked you to look in the southern area of the Jagged Ridge of the Blade's Edge Mountains for a clue as to the whereabouts of Toshley and the other gnomes.]], },
  },
  [10581] = {
    [[Folget den Brotkrumen]],
    { [[HIERMIT WARNE ICH EUCH, DASS IHR AUF DEM GRAT NICHTS FINDEN WERDET.]], [[FOLGT AUF KEINEN FALL DEN ERSATZTEILEN, DIE ZU TOSHLEYS STATION FÜHREN! IHR WERDET DORT WEDER TOSHLEY NOCH EINEN ANDEREN GNOM VORFINDEN!]], },
    { [[In a round-about way, R-3D0 has informed you that if you follow the trail of spare parts up the ramp here to the Razor Ridge, you will find the gnomes at a place named Toshley's Station.]], [[If you find him, speak with Toshley.]], },
  },
  [10582] = {
    [[Diener des Schattenrats]],
    { [[<Ewan hört Eure Erzählung der Ereignisse in der Feste der Legion an und nickt.>]], [[Jetzt geben unsere Beobachtungen einen Sinn. Wir haben gesehen, wie sie Höllenbestien zur Feste der Legion geschafft haben, aber mehr konnten wir nicht herausfinden.]], [[Der Schattenrat hat die Todesschmiede besser versiegelt als ein Fass Donnerbräu! Als erstes müsst Ihr ein paar Köpfe einschlagen, damit Ihr dort eindringen könnt. Fangt damit an, die Mitglieder des Schattenrats, die den Eingang zur Todesschmiede bewachen, zu vernichten!]], },
    { [[Sturmrufer Ewan Wildschwinge beim Turm der Todesschmiede möchte, dass Ihr 10 Wächter der Todesschmiede und 5 Beschwörer der Todesschmiede tötet.]], },
  },
  [10583] = {
    [[Flanis' Schicksal]],
    { [[Schwadronskommandant Nuainn hat ursprünglich drei von uns hierher geschickt, um die Todesschmiede zu beobachten und Informationen zu sammeln. Flanis Flinkschwinge hat sich freiwillig gemeldet, um sich in die Todesschmiede einzuschleichen, aber wir haben schon seit Tagen nichts mehr von ihm gehört.]], [[Ich fürchte das Schlimmste, <name>. Flanis ist ein guter Späher, aber wir haben keine Ahnung, ob ihm die Sache über den Kopf gewachsen ist. Durchsucht die Todesschmiede nach ihm und beschafft sämtliche Informationen oder Gegenstände, die er gesammelt haben könnte.]], },
    { [[Sturmrufer Ewan Wildschwinge beim Turm der Todesschmiede möchte, dass Ihr die Todesschmiede nach Flanis Flinkschwinge absucht und Flanis' Beutel zurückholt.]], },
  },
  [10584] = {
    [[Ein paar Stromwandler besorgen]],
    { [[Diese dummen Oger am Außenposten der Speerspießer im Norden haben unsere Stromwandler gestohlen! Und Ihr werdet sie mir zurückbringen, oder so in der Art.]], [[Die Oger haben sie sicher mit ihren groben, ungeschickten Händen kaputt gemacht. Ich möchte, dass Ihr die in ihnen gespeicherte Energie freisetzt und das so entstehende Elektromentar mit diesem protovoltaischen Magnetsammler einfangt.]], [[Es wird sicher sauer auf Euch sein, Ihr müsst es also 'töten', während es im Magnetfeld gefangen ist.]], },
    { [[Toshley has asked you to take the Protovoltaic Magneto Collector and gather up 5 Electromentals.  Return to him at Toshley's Station in the Blade's Edge Mountains after you have done so.]], },
  },
  [10585] = {
    [[Die Beschwörungskammer]],
    { [[Um mit der Konstruktion der Höllenbestien so schnell nachzukommen, muss der Schattenrat einen Bereich bestimmt haben, an dem die verderbten Elemente, die den Höllenbestien als Seele dienen, beschworen werden.]], [[Die Kammer der Beschwörung liegt wahrscheinlich unter der Erde in der Nähe eines Teichs mit fauligem Wasser oder Lava. Sucht die Beschwörungskammer in der Todesschmiede und unterbrecht ihr Beschwörungsritual! Vielleicht tragen die Mo'arg, die bei der Produktion der Höllenbestien teilhaben, ja ein Instrument dabei, mit dem Ihr die Magie der Beschwörer unterbrechen könnt.]], },
    { [[Besorgt Euch eine Elementarsteuerung von einem Schmied der Todesschmiede oder einem Tüftler der Todesschmiede. Benutzt sie, um das Ritual in der Beschwörungskammer zu unterbrechen. Meldet Euch danach bei Sturmrufer Ewan Wildschwinge im Turm der Todesschmiede zurück.]], },
  },
  [10586] = {
    [[Nieder mit dem Kriegshetzer!]],
    { [[Wir haben große Fortschritte gegen die Operationen des Schattenrats bei der Todesschmiede erzielt. Wenn wir jedoch wirklich siegen wollen, müssen wir den Aufseher der Legion dort vernichten.]], [[Ich habe unter den Gegenständen in Flanis Beutel einen Schlüssel gefunden. Ich bin mir sicher, dass er sich bei der Suche nach Kriegshetzer Razuun, den Ihr in der Feste der Legion beobachtet habt, als nützlich erweisen wird.]], [[Nachdem Ihr Kriegshetzer Razuun aufgespürt und erschlagen habt, meldet Euch gleich bei Schwadronskommandant Nuainn. Er wird sofort davon erfahren wollen!]], },
    { [[Besiegt Kriegshetzer Razuun, beschafft Razuuns Befehle und meldet Euch bei Schwadronskommandant Nuainn in der Wildhammerfeste.]], },
  },
  [10587] = {
    [[Ausbildungsgelände von Karabor]],
    { [[Unsere Späher haben uns von einem massiven Zufluss von Blutelfenrekruten des Sonnenzorns in den Ruinen von Karabor berichtet. Normalerweise würden wir darin eine rein militärische Bedrohung sehen, doch wir glauben, dass hier noch mehr im Argen liegt.]], [[Die Beschreibungen ihrer Kleidung und ihres Kampfstils klingen verdächtig nach Illidan selbst. Ich will mich nicht ohne handfeste Beweise beunruhigen, daher möchte ich Euch bitten, dorthin zu gehen und mir ihre Waffen zu bringen. Dann werden wir sehen, ob meine Befürchtungen unbegründet sind oder nicht.]], },
    { [[Exarch Onaala beim Altar der Sha'tar möchte, dass Ihr 8 Gleven des Sonnenzorns von den Bittstellern der Dämonenjäger und den Initianden der Dämonenjäger in den Ruinen von Karabor, südöstlich des Altars der Sha'tar, beschafft.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10588] = {
    [[Die Litanei der Verdammnis]],
    { [[How many times has this spell has been used in the history of our worlds? Undoubtedly, its power for destruction is unparalleled.]], [[What you must do now is take the cipher, now made whole, and read from it at the Altar of Damnation - where the memory of Gul'dan stands.]], [[What you will see once the words are uttered are the memories left imprinted upon the land. Stand your ground and await the coming of Cyrukh.]], [[You must be victorious, <name>. This land can not withstand another shattering.]], },
    { [[Benutzt die Litanei der Verdammnis am Altar der Verdammnis um Cyrukh den Feuerlord zu rufen.]], [[Vernichtet Cyrukh den Feuerlord und sprecht danach mit Erdheiler Torlok, der sich ebenfalls am Altar der Verdammnis befindet.]], },
  },
  [10589] = {
    [[Zugang erlangen]],
    { [[Laut Razuuns Befehlen wird der riesige Stützpunkt der Höllenbestien Invasionspunkt: Katastrophe genannt und befindet sich in den nördlichen Bergen. Die einzige Weise, auf die man dorthin gelangen kann, ist ein Teleporter, der sich in der Feste der Legion befindet.]], [[Ausgewählte Mitglieder der Legion tragen Geräte bei sich, mit denen sie die Teleporter aktivieren können. Ihr müsst Euch eines dieser Geräte besorgen, um Zugang zu dem Teleporter zu erlangen. Tötet alle Dämonen, wenn Ihr es müsst, aber kommt auf keinen Fall mit leeren Händen zurück.]], },
    { [[Bringt eine Teleportersteuerung der Legion zu Schwadronskommandant Nuainn in der Wildhammerfeste. Bekanntlich tragen die Waffenschmiede der Mo'arg in der Feste der Legion diese Geräte bei sich.]], },
  },
  [10590] = {
    [[Beweist Euren Hass!]],
    { [[Es ist an der Zeit, dass Ihr Euch an Vergangenes erinnert, das nicht vergessen werden sollte... das Abschlachten unseres Volkes durch Arthas und seine Armeen der Geißel.]], [[Rache an der Geißel ist der einzige Weg, Euren Schmerz zu lindern. Es ist bedauerlich, dass sie keine Angst und keinen Schmerz empfinden können. Nur ihre Vernichtung kann Euch einen Hauch von Genugtuung bringen.]], [[Sprecht mit Argentumoffizier Garush, um mehr über die Geißelsteine zu erfahren und wie man sie bekommt. Ich werde viele davon als Beweis für Euren Einsatz brauchen, bevor ich erneut mit Euch spreche.]], },
    { [[Mehlar Dämmerklinge beim Bollwerk möchte, dass Ihr ihm 20 Geißelsteine des Dieners bringt.]], },
  },
  [10592] = {
    [[Die Weisheit der Bansheekönigin]],
    { [[Die Geißelsteine sind schwach, wenn sie aus dem knochigen Griff, der sie einst umfasste, gerissen werden. Sie zerbröseln und zerbrechen schon, wenn ich sie einfach nur in der Hand halte.]], [[<Mehlar zerdrückt die Geißelsteine mit bloßer Hand.>]], [[Bringt diese Fragmente zur Bansheekönigin Sylvanas Windläufer. Ihr Thron befindet sich in Unterstadt. Sie hütet das Geheimnis, wie man diese Überreste in eine Waffe gegen die Geißel verwandelt.]], },
    { [[Bringt die Geißelsteinfragmente zu Fürstin Sylvanas Windläufer in Unterstadt.]], },
  },
  [10593] = {
    [[Das uralte Böse]],
    { [[Wenn Ihr diese Steine in eine Waffe gegen die Geißel verwandeln wollt, müsst Ihr etwas ebenso Bösartiges besorgen, um sie zu verstärken.]], [[Tief im Versunkenen Tempel in den Sümpfen des Elends schlummert eine uralte böse Präsenz. Befreit diese Kreatur und vernichtet sie. Bringt einen kleinen Teil ihres Körpers zu Mehlar Dämmerklinge. Meine Wachen werden ihm die Geißelsteinfragmente zukommen lassen.]], },
    { [[Lüftet die Geheimnisse des Versunkenen Tempels und befreit Atal'alarion. Tötet ihn und schneidet die Fäulnisranke aus seinem Fleisch. Kehrt danach zu Mehlar beim Bollwerk zurück.]], },
  },
  [10594] = {
    [[Die Klangfrequenz messen]],
    { [[Bringt diese Messgeräte zum Singenden Bergrücken. Wenn Ihr sie in einem Muster auf dem Boden auslegt, werden sich überlappende Felder erzeugt, die die Schwingungsweite der Kristalle messen. Es funktioniert aber nur, wenn Ihr in ihrer Mitte steht und von allen fünf Schwingungsfeldern berührt werdet.]], [[Eure Messgeräte funktionieren auch mit den Messgeräten, die andere vor Euch dort abgelegt haben.]], [[Oh, und es kann sein, dass dabei ein Riss entsteht. Ich habe jedoch keine Ahnung, was dadurch in unsere Welt gelangen könnte, aber macht Euch auf das Schlimmste gefasst.]], },
    { [[Nickwinkle the Metro-Gnome wants you to place the Oscillating Frequency Scanners in such a way that when you stand amongst them, you are hit by five overlapping Oscillation fields.]], [[Return to him at Toshley's Station in the Blade's Edge Mountains after you have done so.]], },
  },
  [10595] = {
    [[Belagert!]],
    { [[<Gulmok schaut zum Himmel auf.>]], [[Unsere Späher schlagen sich wacker, aber die Höllenbestien greifen immer wieder an.]], [[Von ihrer Basis in der Feste der Legion im Südwesten aus bombardieren sie uns gnadenlos. Sogar die Elitewachen der Kor'kron können sie nicht ewig aufhalten.]], [[Ich rekrutiere jedes fähige Individuum, damit es seinen Teil zur Verteidigung Schattenmonds beiträgt. Das ist kein Spiel, <class>. Das Überleben des Dorfes ist in Gefahr. Tut Eure Pflicht gegenüber der Horde und schließt Euch dem Kampf an!]], },
    { [[Blutwache Gulmoj außerhalb von Schattenmond möchte, dass Ihr 10 angreifende Höllenbestien tötet.]], },
  },
  [10596] = {
    [[Zur Feste der Legion]],
    { [[Die Feste der Legion ist Euer Ziel. Ihr werdet keine Verstärkung erhalten, daher ist ein Frontalangriff unmöglich.]], [[Die wenigen Informationen, die wir sammeln konnten, weisen darauf hin, dass die Legion die Höllenbestien in der Feste lagert und vorbereitet. Ich möchte, dass Ihr die Feste in der Verkleidung, die ich Euch geben werde, nach detaillierten Informationen über die Pläne der Legion durchsucht.]], [[Wir können nur hoffen, dass Ihr die Gespräche zwischen ihren Kommandanten abfangen oder belauschen könnt.]], },
    { [[Begebt Euch zur Feste der Legion, findet ein Kommunikationsgerät der Legion und benutzt es, um mehr über die Pläne der Legion herauszufinden, während Ihr verkleidet seid. Kehrt danach zu Blutwache Gulmok in Schattenmond zurück.]], },
  },
  [10597] = {
    [[Die Bombe bauen]],
    { [[Wir können der Legion nicht erlauben, uns auf ihrem Marsch zum Schwarzen Tempel zu zertrampeln.]], [[Findet einen Weg, um die Höllenbestien, die sie gegen uns einsetzen wollen, zu vernichten. Das ist keine Bitte, <name>, sondern ein Befehl.]], [[Am Fauligen Teich in der Nähe der Feste der Legion gibt es viele alte Teufelshäscherkraftkerne, die Ihr als Grundlage für eine Art Teufelsbombe verwenden könnt. Durchsucht die anderen Trümmer in der Nähe der Feste nach Rüstungsplatten. Daraus sollte man eine Bombe basteln können, die so stark ist, dass sie diese Höllenbestien vernichten und die Feste zum Beben bringen wird!]], },
    { [[Besorgt Euch einen Teufelshäscherkraftkern und eine Teufelshäscherrüstungsplatte und bringt sie zu Blutwache Gulmok in Schattenmond.]], },
  },
  [10598] = {
    [[Jagt die Höllenbestien in die Luft!]],
    { [[Das Gerät, das wir zusammengebaut haben, würde keinen Schönheitspreis gewinnen, aber es sollte die Höllenbestien in der Feste der Legion zerstören können. Danach zu urteilen, was der Kraftkern des Teufelshäschers bei dem Fauligen Teich angerichtet hat, muss darin eine gehörige Explosionskraft stecken.]], [[Wenn Ihr das Gerät in der Mitte der grünen Glyphe in der Feste platzieret, solltet Ihr alle Höllenbestien erwischen können.]], },
    { [[Zündet die Teufelsbombe, während Ihr auf der grünen Glyphe in der Feste der Legion steht. Meldet Euch bei Blutwache Gulmok in Schattenmond zurück, wenn Ihr Euren Auftrag erledigt habt.]], },
  },
  [10599] = {
    [[Die Todesschmiede]],
    { [[Während Ihr Euch um die Höllenbestien bei der Feste der Legion gekümmert habt, bin ich Euren Bericht durchgegangen. Wir haben keinen Grund zu glauben, dass die Legion bezüglich der Geschehnisse in der Todesschmiede gelogen hat. Sie stellen dort wirklich Höllenbestien am Fließband her. Daher habe ich eine Gruppe von Spähern dorthin geschickt, um die Schmiede zu beobachten.]], [[Wenn wir dem Treiben der Todesschmiede kein Ende setzen, werden sie die Schar an Höllenbestien, die Ihr in der Feste der Legion vernichtet habt, im Handumdrehen wieder aufgestockt haben.]], [[Meldet Euch bei Späher Zagran in der Schlackenwacht im Südosten, die die Todesschmiede überblickt.]], },
    { [[Meldet Euch bei Späher Zagran in der Schlackenwacht.]], },
  },
  [10600] = {
    [[Diener des Schattenrats]],
    { [[<Zagran hört Eure Erzählung der Ereignisse in der Feste der Legion an und nickt.>]], [[Jetzt geben unsere Beobachtungen einen Sinn. Wir haben gesehen, wie sie Höllenbestien zur Feste der Legion geschafft haben, aber mehr konnten wir nicht herausfinden.]], [[Der Schattenrat hat die Todesschmiede ziemlich gut abgeriegelt. Sieht aus, als müsstet Ihr Euch den Weg hinein mit Gewalt freiräumen. Fangt damit an, die Mitglieder des Schattenrats, die den Eingang zur Todesschmiede bewachen, zu vernichten!]], },
    { [[Späher Zagran in der Schlackenwacht möchte, dass Ihr 10 Wächter der Todesschmiede und 5 Beschwörer der Todesschmiede tötet.]], },
  },
  [10601] = {
    [[Kagroshs Schicksal]],
    { [[Ursprünglich waren wir hier zu dritt. Unglücklicherweise musste Kagrosh unbedingt den Helden spielen.]], [[Er ist in die Todesschmiede gegangen. Seitdem haben wir nichts mehr von ihm gehört.]], [[Aller Wahrscheinlichkeit nach ist der Dummkopf in seinen sicheren Tod gezogen. Die Mitglieder des Schattenrats sind nicht dumm und Kagrosh ist nicht gerade ein Ausbund an Subtilität, wenn Ihr versteht, was ich meine. Haltet in der Todesschmiede Ausschau nach ihm und beschafft sämtliche Informationen oder Gegenstände, die er gesammelt haben könnte.]], },
    { [[Späher Zagran in der Schlackenwacht möchte, dass Ihr die Todesschmiede nach Kagrosh absucht und Kagroshs Beutel zurückholt.]], },
  },
  [10602] = {
    [[Die Beschwörungskammer]],
    { [[Um mit der Konstruktion der Höllenbestien so schnell nachzukommen, muss der Schattenrat einen Bereich bestimmt haben, an dem die verderbten Elemente, die den Höllenbestien als Seele dienen, beschworen werden.]], [[Die Beschwörungskammer liegt wahrscheinlich unter der Erde in der Nähe eines Teichs mit fauligem Wasser oder Lava. Sucht die Beschwörungskammer in der Todesschmiede und unterbrecht ihr Beschwörungsritual! Vielleicht tragen die Mo'arg, die bei der Produktion der Höllenbestien teilhaben, ja ein Instrument dabei, mit dem Ihr die Magie der Beschwörer unterbrechen könnt.]], },
    { [[Besorgt Euch eine Elementarsteuerung von einem Schmied der Todesschmiede oder einem Tüftler der Todesschmiede. Benutzt sie, um das Ritual in der Beschwörungskammer zu unterbrechen. Meldet Euch danach bei Späher Zagran in der Schlackenwacht zurück.]], },
  },
  [10603] = {
    [[Nieder mit dem Kriegshetzer!]],
    { [[Wir haben große Fortschritte gegen den Schattenrat bei der Todesschmiede erzielt, doch nun ist es an der Zeit, die Sache zu Ende zu bringen.]], [[Ich habe unter den Gegenständen in Kagroshs Beutel einen Schlüssel gefunden. Wenigstens hat er das noch hinbekommen, bevor sie ihn erwischt haben.]], [[Der Schlüssel sollte Euch dabei helfen, Kriegshetzer Razuun, den Ihr in der Feste der Legion beobachtet habt, ausfindig zu machen.]], [[Nachdem Ihr Kriegshetzer Razuun aufgespürt und erschlagen habt, meldet Euch gleich bei Blutwache Gulmok. Wir dürfen ihn nicht warten lassen.]], },
    { [[Besiegt Kriegshetzer Razuun, beschafft Razuuns Befehle und meldet Euch bei Blutwache Gulmok in Schattenmond.]], },
  },
  [10604] = {
    [[Zugang erlangen]],
    { [[Laut Razuuns Befehlen wird der riesige Stützpunkt der Höllenbestien Invasionspunkt: Katastrophe genannt und befindet sich in den nördlichen Bergen. Die einzige Weise, auf die man dorthin gelangen kann, ist ein Teleporter, der sich in der Feste der Legion befindet.]], [[Ausgewählte Mitglieder der Legion tragen Geräte bei sich, mit denen sie die Teleporter aktivieren können. Ihr müsst Euch eines dieser Geräte besorgen, um Zugang zu dem Teleporter zu erlangen. Tötet alle Dämonen, wenn Ihr es müsst, aber kommt auf keinen Fall mit leeren Händen zurück. Verschwendet keine Zeit; der Feind ist schon gefährlich nahe.]], },
    { [[Bringt eine Teleportersteuerung der Legion zu Blutwache Gulmok in Schattenmond. Bekanntlich tragen die Waffenschmiede der Mo'arg in der Feste der Legion diese Geräte bei sich.]], },
  },
  [10605] = {
    [[Carendin ruft]],
    { [[Ihr habt einen hungrigen Ausdruck in den Augen, <name>. Vielleicht ist es an der Zeit, dass Ihr Eure Ausbildung fortsetzt.]], [[Reist nach Unterstadt und sprecht mit Carendin Halgar. Der alte Knochenhaufen hat ein oder zwei Tricks auf Lager, die Euch interessieren dürften.]], },
    { [[Sprecht mit Carendin Halgar in Unterstadt.]], },
  },
  [10606] = {
    [[Die Kunst der Teufelshäscherwartung]],
    { [[Der beste Weg, um die Vorräte der Legion zu zerstören, ist, ihre eigenen Waffen gegen sie zu verwenden. Ich habe eine Anlage gebaut, mit deren Hilfe Ihr eine dieser Schildwachen der Teufelshäscher dort unten steuern könnt.]], [[Es gibt jedoch noch einige Dinge, die ich zuerst herausfinden muss. Dafür benötige ich das Handbuch, das Doktor Gutrick in einer Truhe am Ende der Rampe verschlossen hält. Wahrscheinlich rückt er den Schlüssel dazu nicht ohne Kampf heraus. Bringt mir das Teufelshäscherhandbuch und ich erledige den Rest!]], },
    { [[Tötet Doktor Gutrick und beschafft Euch den Schlüssel des Doktors. Benutzt den Schlüssel, um die Geldkassette des Doktors zu öffnen und 'Die Kunst der Teufelshäscherwartung' daraus zu entnehmen. Bringt das Buch zu Plexi beim Invasionspunkt: Katastrophe.]], },
  },
  [10607] = {
    [[Das Flüstern des Rabengottes]],
    { [[In Grishnath können die Arakkoa mithilfe der Totems, die die Orakel des Raben aufgestellt haben, die Worte des Raben hören. Leider sind wir der Sprache der Arakkoa nicht mächtig.]], [[Mit dem Anhänger des Herolds können wir sie verstehen. Ihr könnt die Seele der Arakkoa aufnehmen und so ihrer Sprache mächtig werden. Begebt Euch zu den Totems, während ihr mit dem Geist der Arakkoa erfüllt seid, und hört Euch ihre Nachricht an.]], [[Findet es für uns heraus, <name>.]], },
    { [[Kill Grishna arakkoa to gain Understanding Ravenspeech for 2 minutes. While under the effects of the spell, you can find and receive the prophecy from the four shrines. Return when you have visited all four.]], },
  },
  [10608] = {
    [[Kristallklar]],
    { [[Diese verdammten Felsklauen halten mich von meinen Forschungen unten im Singenden Bergrücken ab! Sie graben bei den Kristallen und ruinieren so all meine Hoffnungen, die Harmonien der singenden Kristalle zu untersuchen.]], [[Ihr seht aus, als könntet Ihr mir dabei helfen. Begebt Euch nach Süden und rottet diese Kristallfelsklauen aus!]], },
    { [[Nickwinkle the Metro-Gnome wants you to slay 10 Crystal Flayers and then return to him at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10609] = {
    [[Was war zuerst da, der Drache oder das Ei?]],
    { [[Ich habe einige Experimente zur Verwendungsweise der Drachenessenzen im Rahmen meiner Arbeit als Arzt durchgeführt. Ich bin mir sicher, dass diese Essenz ein Allheilmittel für Unmengen von Krankheiten sein kann!]], [[Jetzt brauche ich nur noch jemanden, der für mich nach Süden zum Singenden Bergrücken geht und mir ein wenig davon besorgt.]], [[Habt Ihr Lust, meinen temporalen Phasenstrahler zu benutzen, um den Netherwelpen, die aus den Netherdracheneiern schlüpfen, ihre Essenz zu entziehen?]], [[Ich muss Euch jedoch warnen: es handelt sich hierbei um ziemlich heikle und zeitverändernde Technologie. Ich habe keine Ahnung, in was sich die Netherwelpen verwandeln könnten.]], },
    { [[Collect 3 Proto-Nether Drake Essences, 3 Adolescent Drake Essences and 3 Mature Drake Essences.  Return with them to Fizit "Doc" Clocktock at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10610] = {
    [[Grundlagen des Sondierens]],
  },
  [10611] = {
    [[Die Kunst der Teufelshäscherwartung]],
    { [[Der beste Weg, um die Vorräte der Legion zu zerstören, ist, ihre eigenen Waffen gegen sie zu verwenden. Ich habe eine Anlage gebaut, mit deren Hilfe Ihr eine dieser Schildwachen der Teufelshäscher dort unten steuern könnt.]], [[Es gibt jedoch noch einige Dinge, die ich zuerst herausfinden muss. Dafür benötige ich das Handbuch, das Doktor Maleficus in einer Truhe am Ende der Rampe verschlossen hält. Wahrscheinlich rückt er den Schlüssel dazu nicht ohne Kampf heraus. Bringt mir das Teufelshäscherhandbuch und ich erledige den Rest!]], },
    { [[Tötet Doktor Maleficus und beschafft Euch den Schlüssel des Doktors. Benutzt den Schlüssel, um die Geldkassette des Doktors zu öffnen und 'Die Kunst der Teufelshäscherwartung' daraus zu entnehmen. Bringt das Buch zu Nakansi beim Invasionspunkt: Katastrophe.]], },
  },
  [10612] = {
    [[Teufelshäscherdrift]],
    { [[<Plexi blättert das Handbuch durch.>]], [[Nun, hier steht alles drin, was wir brauchen, aber es gibt ein Problem. Teufelshäscher sind nicht wirklich dafür konstruiert, ferngesteuert zu werden.]], [[Wir können einen davon so umbauen, dass es für kurze Zeit funktioniert, aber wir müssen schnell arbeiten. Sonst kann keiner die Folgen absehen. Es würde mich auch nicht wundern, wenn Euer kleines Kunststück mit dem Teufelshäscher die Aufmerksamkeit der Lakaien der Legion auf sich ziehen würde. Passt gut auf Euch auf. Und was immer Ihr tut, vergesst nicht, so viele wie möglich zu zerstören!]], },
    { [[Benutzt die Teufelshäscherfernsteuerung, um eine Schildwache der Teufelshäscher zu steuern. Zerstört 60 todesgeschmiedete Höllenbestien bevor Euer Teufelshäscher versagt. Meldet Euch danach bei Plexi am Invasionspunkt: Katastrophe.]], },
  },
  [10613] = {
    [[Teufelshäscherdrift]],
    { [[<Nakansi blättert das Handbuch durch.>]], [[Hier sind alle Informationen gesammelt, aber seid gewarnt. Diese Dinger wurden nicht gebaut, um ferngesteuert zu werden.]], [[Wir können einen davon so umbauen, dass es für kurze Zeit funktioniert, aber wir müssen schnell arbeiten. Sonst kann keiner die Folgen absehen. Es würde mich auch nicht wundern, wenn Euer kleines Kunststück mit dem Teufelshäscher die Aufmerksamkeit des Legionsabschaums auf sich ziehen würde. Passt auf Euch auf, aber denkt daran, dass die Mission und die Sicherheit Schattenmonds vorgehen!]], },
    { [[Benutzt die Teufelshäscherfernsteuerung, um eine Schildwache der Teufelshäscher zu steuern. Zerstört 60 todesgeschmiedete Höllenbestien bevor Euer Teufelshäscher versagt. Meldet Euch danach bei Nakansi am Invasionspunkt: Katastrophe.]], },
  },
  [10614] = {
    [[Das Flüstern im Wind]],
    { [[Ich habe in der gesamten Scherbenwelt Gerüchten über die Mok'Nathal nachgejagt und nichts als weitere Gerüchte und Geschichten gefunden. Aber nun haben wir vielleicht tatsächlich ihr Zuhause ausfindig gemacht.]], [[Nun, da dieser Moment endlich da ist, fühle ich mich sehr unwohl. Dieses Unwohlsein hat mich während meiner Suche Nacht für Nacht wach gehalten. Werden sie mich annehmen?]], [[Ich... kenne den Namen ihres Anführers: Leoroxx. Sucht nach ihm. Seid meine Augen.]], },
    { [[Search out Leoroxx at the Mok'Nathal Village in the Blade's Edge Mountains.]], },
  },
  [10615] = {
    [[Der Ruuanwald]],
    { [[Die Informationen, die ich mit Eurer Hilfe über das Wesen der Arakkoa sammeln konnte, sind auch noch für jemand anders von Interesse.]], [[Wir wurden von ein paar Mitgliedern der Expedition des Cenarius aufgesucht, die sich ebenfalls Sorgen um die Pläne der Arakkoa hier im Schergrat machen.]], [[Ihr solltet Timeon aufsuchen. Er befindet sich im Ewigen Hain im Ruuanwald. Folgt einfach von hier aus der Straße nach Westen und geht dann an der Weggabelung im Messergrat nach Norden. Wendet Euch nach Westen, wo die Wege in der Messerschlucht sich teilen.]], },
    { [[Sprecht mit Timeon im Ewigen Hain im Ruuanwald des Schergrats.]], },
  },
  [10617] = {
    [[Seidenflügelkokons]],
    { [[Die Seide der Kokons der Seidenflügel wird hier im Dorf für alle möglichen Zwecke eingesetzt. Wir können nie zu viele davon besorgen und haben.]], [[Sobald sie beginnen sich zu verwandeln ist es schwer, die Kokons der Larven zu ernten.]], [[Unsere Krieger sind mit den Angriffen auf unser Dorf beschäftigt. Wenn Ihr sie für uns sammeln könntet, wäre das uns eine große Hilfe.]], },
    { [[Taerek at Mok'Nathal Village wants you to collect 8 Silkwing Cocoons from Silkwing Larva.]], [[Kill the Larva quickly or they will turn into Silkwings, ruining their cocoons.]], },
  },
  [10618] = {
    [[Die weichsten Flügel]],
    { [[Seidenflügellarven zu jagen ist eine schwierige Aufgabe für unsere jungen Jäger. Aber auch wenn sie es nicht schaffen, die Kokons der Larven zu ernten, können wir noch die Flügel der geschlüpften Seidenflügel nutzen.]], [[In der Tat sind die Flügel vielseitiger einsetzbar als die Kokons. Wir können nie zu viele davon haben.]], [[Wenn Ihr da draußen seid, wäre es mir eine große Hilfe, wenn Ihr mir noch ein paar davon bringen würdet.]], },
    { [[Silmara at Mok'Nathal Village wants you to collect 12 Iridescent Wings.]], },
  },
  [10619] = {
    [[Die Aschenzungen]],
    { [[Der Aschenzungenstamm wird von allen Zerschlagenen gefürchtet. Er besteht aus den bösartigsten und blutrünstigsten Gestalten, die von Akama persönlich ausgewählt wurden. Sie sind so wild und rücksichtslos wie jeder Naga, Dämon oder Blutelf in Illidans Armee. Eine große Anzahl von ihnen wurde in den Ruinen von Baa'ri südwestlich von hier stationiert. Begebt Euch dorthin und dezimiert ihre Zahl, bevor sie die Gelegenheit haben, uns anzugreifen.]], },
    { [[Verteidiger Aluumen möchte, dass Ihr zu den Ruinen von Baa'ri geht und 3 Antreiber der Aschenzungen, 4 Krieger der Aschenzungen und 6 Schamanen der Aschenzungen tötet.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10620] = {
    [[Gratwirbelbedrohung]],
    { [[Wir wurden angeheuert, um Toshley zu helfen, als er in die Scherbenwelt gezogen ist. Und seitdem sind wir hier auf Achse.]], [[Es sieht jedoch so aus, als hätten wir endlich ein paar Wurzeln geschlagen. All die verrückten Wissenschaftler und Ingenieure hier haben ihre Projekte am Laufen und wir Raunacken sind hier, um sie dabei zu beschützen.]], [[Zurzeit könnten wir wirklich jemanden brauchen, der nach Norden in den Messergrat oder nach Osten zur Schuppenflügelbank geht und ihn von den Pirschern befreit. Das sind heimtückische Spinnen, die bei den Felsvorsprüngen leben und nichts ahnende Passanten anspringen.]], },
    { [[Dizzy Dina wants you to kill 10 Ridgespine Stalkers.  After you have done so, return to her at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10621] = {
    [[Splitter des Illidaribanns]],
    { [[Der Splitter scheint von einer größeren Waffe zu stammen. Ein bedrohliches Leuchten geht von den kantigen Runen, die in das Metall geätzt wurden, aus.]], [[Euch ist nicht klar, welche Aufgabe die Waffe hatte, aber sie ist nur schwer zu verkennen. Die meisten Mo'arg in der Feste der Legion scheinen Bruchstücke des Metalls, das Ihr in den Händen haltet, zu bearbeiten. Vielleicht kann einer der Waffenschmiede in der Wildhammerfeste Euch mehr darüber erzählen.]], },
    { [[Bringt den Splitter des Illidaribanns zu Ordinn Donnerfaust in der Wildhammerfeste.]], },
  },
  [10622] = {
    [[Ein Beweis der Treue]],
    { [[Akama hat Eure Fortschritte sehr genau verfolgt, <name>.]], [[Die Aschenzungen, die Ihr in den Ruinen von Baa'ri erschlagen habt, zählen zu den bösartigsten Zerschlagenen unter uns. Akama selbst hat sie ausgewählt, um die Fassade gegenüber unseren Meistern der Illidari aufrechtzuerhalten. Doch die Todeshörigen wissen über Akamas eigentliche Pläne Bescheid.]], [[Doch eins nach dem anderen. Wir müssen wissen, dass Ihr nicht mit Illidan unter einer Decke steckt. Ein Satyr namens Zandras überwacht uns in Illidans Auftrag von den Zinnen der Gefängnismauer aus. Tötet ihn als Beweis Eurer Treue.]], },
    { [[Tötet Zandras im Kerker des Wächters im Schattenmondtal und kehrt danach zu Sanoru zurück.]], },
  },
  [10623] = {
    [[Splitter des Illidaribanns]],
    { [[Der Splitter scheint von einer größeren Waffe zu stammen. Ein bedrohliches Leuchten geht von den kantigen Runen, die in das Metall geätzt wurden, aus.]], [[Euch ist nicht klar, welche Aufgabe die Waffe hatte, aber sie ist nur schwer zu verkennen. Die meisten Mo'arg in der Feste der Legion scheinen Bruchstücke des Metalls, das Ihr in den Händen haltet, zu bearbeiten. Vielleicht kann einer der Waffenschmiede in Schattenmond Euch mehr darüber erzählen.]], },
    { [[Bringt den Splitter des Illidaribanns zu Grokom Totauge in Schattenmond.]], },
  },
  [10624] = {
    [[Eine Spukgeschichte]],
    { [[Habt Ihr diesen eiskalten Hauch gespürt? Als ob Euch jemand die Hand auf die Schulter legte? Euer Verstand spielt Euch keine Streiche, <race>. Das Land wird heimgesucht. Geister streifen durch die Weiten und sind bis in alle Ewigkeit hier gefangen.]], [[Wollt Ihr sie gerne sehen? Seid Ihr Euch sicher? Solche Dinge können ein Wesen verändern - und zwar nicht zum Besseren.]], [[Nun gut, dann müsst Ihr mir etwas von der ewig brennenden Asche bringen, die Ihr in der Nähe der Teufelslavabecken im westlichen Schattenmondtal finden könnt. Überall in der Nähe der grünen Lava...]], },
    { [[Chefapothekerin Hildagard in Schattenmond im Schattenmondtal möchte, dass Ihr ihr 15 Einheiten ewig brennende Asche bringt.]], },
  },
  [10625] = {
    [[Geisterbrille]],
    { [[Ich nenne sie 'Geisterbrille'. Ganz schön gewitzt, was? Wenn Ihr diese Brille aufzieht, könnt Ihr die Geister des Schattenmondtals sehen.]], [[Bevor ich sie Euch gebe, müsst Ihr mir jedoch versprechen, uns dabei zu helfen, die Geister im Dorf zu dezimieren.]], [[Nehmt die Brille und vernichtet alle Geister, die Ihr in Schattenmond herumspuken seht. Hoffentlich bewegt das die übrigen Geister, woanders hin zu gehen.]], },
    { [[Chefapothekerin Hildagard in Schattenmond im Schattenmondtal möchte, dass Ihr 12 Zeloten des Schattenmondklans tötet.]], },
  },
  [10626] = {
    [[Beschafft die Waffen]],
    { [[Wenn ich mehr von diesem Material hätte, könnte ich eine Waffe schmieden, die für Euch geeignet ist.]], [[Die beiden Grubenlords in der Feste der Legion, Makazradon und Morgroron, besitzen bereits diese antidämonischen Waffen. Wir wissen nicht, wie viele davon die Legion herstellen möchte, aber wenn wir diese beiden Prototypen in die Finger bekommen, haben wir sicher genug Material für unser eigenes Projekt.]], [[<Ordinn grinst.>]], [[Es liegt ein wenig Befriedigung darin, die eigenen 'Geheimwaffen' der Legion gegen sie zu verwenden, findet Ihr nicht?]], },
    { [[Ordinn Donnerfaust in der Wildhammerfeste möchte, dass Ihr ihm Makazradons Gleve und Morgrorons Gleve bringt.]], },
  },
  [10627] = {
    [[Beschafft die Waffen]],
    { [[Wenn ich mehr von diesem Material hätte, könnte ich eine Waffe schmieden, die für Euch geeignet ist.]], [[Die beiden Grubenlords in der Feste der Legion, Makazradon und Morgroron, besitzen bereits diese antidämonischen Waffen. Wenn wir an diese beiden Waffen kommen könnten, hätten wir genug Material, um Eure und noch einige weitere herstellen zu können.]], [[Ich kann es kaum abwarten, mit diesen Waffen in die Schlacht gegen den Feind zu ziehen. Das wird die Dämonen lehren, mit antidämonischer Magie herum zu spielen.]], },
    { [[Grokom Totauge im Schattenmondtal möchte, dass Ihr ihm Makazradons Gleve und Morgrorons Gleve bringt.]], },
  },
  [10628] = {
    [[Akama]],
    { [[Ihr habt bewiesen, auf welcher Seite Ihr steht. Ich werde Euch das Versteck unseres Anführers verraten.]], [[Folgt den Gewölben hinter uns. Zu Eurer Rechten werdet Ihr einen unterirdischen Durchgang finden, der von stehendem Wasser verdeckt ist.]], [[Ich muss Euch warnen, <name>. Ihr dürft niemandem von dem, was Ihr sehen werdet, erzählen.]], },
    { [[Sprecht mit Akama in der verborgenen Kammer im Kerker des Wächters.]], },
  },
  [10629] = {
    [[Drecksarbeit]],
    { [[Endlich habe ich meinen Schredder zum Laufen bekommen. Aber wie es scheint, habe ich die Schlüssel verloren. Ich halte ein Rudel Teufelshunde, die mein Lager bewachen. Sie machen ihre Sache nicht sehr gut und beißen mich gerne. Sie mögen auch gerne glänzende Dinge. Sie mögen sogar sehr gerne glänzende Dinge. Daher fürchte ich, dass einer der Teufelshunde meine Schredderschlüssel gefressen hat. Ich bezahle Euch, wenn Ihr mit meinem Teufelshund Gassi geht. Tötet ein paar Hölleneber und lasst ihn davon fressen. Wenn er fertig ist, wird er sein Geschäft machen. Durchsucht danach die Haufen nach den Schlüsseln.]], },
    { [[Benutzt die Teufelshundepfeife, um einen teuflischen Wachhund zu rufen. Führt den teuflischen Wachhund Gassi und tötet ein paar Hölleneber. Durchsucht dann die Haufen des teuflischen Wachhunds nach den Schlüsseln. Wenn Ihr die Schlüssel gefunden habt, bringt sie zu Großknecht Razelcraz bei der Mine nordwestlich von Thrallmar.]], },
  },
  [10630] = {
    [[Unter Thrallmar]],
    { [[Ich bin fast wieder im Geschäft. Das Problem ist nur, dass diese Gan'arg sich in meiner Mine herumtreiben. Ich kann meine Peons dort nicht hineinschicken. Und auch wenn ich den Schredder repariert habe, möchte ich nicht riskieren, dass er im Kampf schon wieder kaputt geht. Der Anführer der Gan'arg haust tief unten in der Mine. Wenn Ihr ihn tötet, hauen die anderen Gan'arg vielleicht ab. Dann könnte ich endlich Geld verdienen und von hier weggehen.]], },
    { [[Tötet Urga'zz und kehrt dann zu Großknecht Razelcraz in der Mine nordwestlich von Thrallmar zurück.]], },
  },
  [10632] = {
    [[Zähne zeigen]],
    { [[Es ist sehr nett von Euch, dass Ihr Toshley und den Wissenschaftlern hier helft. Ich kann mir jedoch nicht den Luxus erlauben, mich meinen 'persönlichen' Interessen hinzugeben.]], [[Wenn Ihr hier etwas Besonderes sein wollt, dann tut, was ich sage. Ich brauche Waffen.]], [[Unsere Gewehre sind zwar im Fernkampf ganz nützlich, aber wenn uns diese Insekten zu nahe kommen, brauchen wir mächtige Nahkampfwaffen zur Verteidigung.]], [[Die Zähne der Dolchrachenschmetterschwänze im Messergrat im Norden geben tolle Vibroklingen ab. Treibt ein paar davon für mich auf!]], },
    { [[Razak Ironsides has asked you to gather up 5 Extra Sharp Daggermaw Teeth.  Return them to him at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10633] = {
    [[Teron Blutschatten - Geschichte und Mythos]],
    { [[Es gibt unzählige Geschichten über das Leben von Teron Blutschatten, aber manche sind eben nur Geschichten. Wir wissen nicht, was mit Blutschatten passiert ist, nachdem er nach Draenor zurückgekehrt war.]], [[Mit Sicherheit wissen wir nur, dass er schon einmal gestorben ist.]], [[Ich habe gehört, wie die Späher von einem alten Orcgeist sprachen, der beim Altar der Schatten haust. Er befindet sich weit im Südosten, hinter den Netherschwingenfeldern und jenseits des Netherschwingenpasses. Nehmt diese Geisterbrille und sucht diesen Orc. Er weiß sicher mehr über Blutschatten.]], },
    { [[Chefapothekerin Hildagard in Schattenmond im Schattenmondtal möchte, dass Ihr den uralten Schattenmondgeist findet.]], [[Wenn Ihr Eure Geisterbrille verlieren solltet, kann Euch Hildagard ein neues Paar herstellen.]], },
  },
  [10634] = {
    [[Hellsicht: Blutschattens Rüstung]],
    { [[In life, Gorefiend was a bastard, but in death, Gorefiend was an unmitigated terror.]], [[To the southeast you will find Netherwing Ledge. What was once part of the mainland of Shadowmoon is now a floating island in the sky.]], [[Vhel'kur, spawn of Deathwing, once called it his home, but now knows it as his grave. He flies an endless circle above the island, seething at his defeat to Gorefiend.]], [[If you are to recover the Gorefiend's armor, it is Vhel'kur that you must defeat.]], },
    { [[Der uralte Schattenmondgeist am Altar der Schatten im südöstlichen Schattenmondtal möchte, dass Ihr Blutschattens Rüstung besorgt.]], },
  },
  [10635] = {
    [[Hellsicht: Blutschattens Umhang]],
    { [[Er beging so manche Grausamkeit, aber keine war so kaltblütig wie das Abschlachten der Kleriker von Karabor.]], [[Nachdem Gul'dans Horde die Draenei von Karabor besiegt hatte, wurden die überlebenden Kleriker verschleppt. Man sagte ihnen, dass sie als Kriegsgefangene in die Höllenfeuerzitadelle gebracht würden.]], [[Doch Blutschatten, der damals noch kein Todesritter war, brachte jeden einzelnen auf grausamste Weise um. Nun streichen sie voller Rachedurst vor dem Schwarzen Tempel umher.]], [[Die Kleriker von Karabor haben Blutschattens Umhang.]], },
    { [[Der uralte Schattenmondgeist am Altar der Schatten im südöstlichen Schattenmondtal möchte, dass Ihr Blutschattens Umhang besorgt.]], },
  },
  [10636] = {
    [[Hellsicht: Blutschattens Schlagstock]],
    { [[Einer nach dem anderen kniete vor ihm nieder und brachte ihm seine Ehrerbietung dar. Blutschatten hob seine Sichel und schlug ihnen die Köpfe ab. Kurz nach diesem Massaker nahm er sich selbst das Leben.]], [[Doch er tat es nicht etwa aus Reue! Nein, Blutschatten wollte Unsterblichkeit! Von den Leichen der gefallenen Todesrittern erhoben sich die Geistritter von Karabor.]], [[Jetzt reiten sie auf den Straßen um die Hand von Gul'dan herum und warten auf die Rückkehr ihres Meisters. Sie haben Blutschattens Schlagstock. Holt ihn Euch...]], },
    { [[Der uralte Schattenmondgeist am Altar der Schatten im südöstlichen Schattenmondtal möchte, dass Ihr Blutschattens Schlagstock besorgt.]], },
  },
  [10637] = {
    [[Eine notwendige Ablenkung]],
    { [[Die Waffen sind echt, wie wir befürchtet haben. Illidan bildet in den Ruinen von Karabor Dämonenjäger aus. Das müssen wir verhindern.]], [[Lasst Euch nicht von dem Namen täuschen, <name>. Sie werden ihre Macht genauso oft gegen uns einsetzen, wie gegen Dämonen.]], [[Ihre Hexenmeister beschwören und unterwerfen Dämonen im Rahmen der Ausbildung der Initiierten. Ein Grubenlord namens Azaloth hat sich in der Ausbildung als zu mächtig erwiesen und wird nun unter Verschluss gehalten. Findet einen Weg, ihn frei zu lassen und auf seine Kerkermeister zu hetzen. Das sollte uns die nötige Zeit verschaffen.]], },
    { [[Exarch Onaala möchte, dass Ihr zu den Ruinen von Karabor zurückkehrt und Azaloth auf seine Kerkermeister hetzt.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10638] = {
    [[NOT A QUEST]],
    { [[Besucht Euren Lehrer in Silbermond.]], },
    { [[Besucht Euren Lehrer in Silbermond.]], },
  },
  [10639] = {
    [[Ich bin Teron Blutschatten...]],
    { [[Jetzt, da wir Blutschattens Gegenstände beisammen haben, fehlt nur noch ein letztes Teil im Puzzle: ein Wirt.]], [[<Die Lippen des uralten Schattenmondgeistes verziehen sich zu einem bösartigen Grinsen.>]], [[Ihr habt doch nicht etwa das Märchen geglaubt, das ich Euch aufgetischt habe? Der Altar der Schatten ist mein Gefängnis. Ohne Eure Hilfe wäre ich bis in alle Ewigkeit hier gefangen geblieben. Jetzt werde ich Euren Körper übernehmen und Ihr werdet die, die mich gefangen haben, vernichten und das Band für immer zerreißen. Wenn Ihr ablehnt, sind wir beide bis ans Ende der Zeit hier gefangen.]], [[Teron Blutschatten wird frei sein!]], },
    { [[Der uralte Schattenmondgeist hat Euch benutzt! Nun, da Ihr alle Gegenstände, die er wollte, gesammelt habt, hat er sich als Teron Blutschatten offenbart. Um von seinem Bann loszukommen, müsst Ihr seinen Willen erfüllen und Karsius den uralten Wächter töten.]], [[Wenn Ihr erfolgreich wart, meldet Euch bei Chefapothekerin Hildagard in Schattenmond.]], },
  },
  [10640] = {
    [[Altruis]],
    { [[In diesen Zeiten hört man nur noch selten von solchen, die den Weg des Dämonenjägers eingeschlagen haben. Und noch seltener findet man einen Dämonenjäger, der nicht im Bund mit Illidan steht.]], [[Gerüchten zufolge soll ein solches Wesen hier in der Scherbenwelt leben. Es gibt einen Namen, der bei der Legion in Nagrand noch mehr gefürchtet sein soll, als eine ganze Armee von Verteidigern der Aldor, und das ist der Name Altruis.]], [[Geht in den Westen Nagrands und sucht ihn. Vielleicht ist er bereit, etwas mehr über diese neuen Ausbildungsgelände für Dämonenjäger zu erzählen.]], },
    { [[Reist in den Westen Nagrands und sucht Altruis den Leider.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10641] = {
    [[Gegen die Legion]],
    { [[Die Brennende Legion hat immer und immer wieder bewiesen, wie leicht sie sterbliche Seelen verderben kann. Wenn ich Euch mit dem Wissen, nach dem Ihr strebt, ausstatten soll, muss ich erst Eure Gesinnung überprüfen.]], [[Eine Dämonin namens Sathal schafft es schon eine ganze Weile, mir dank ihrer körperlosen Form zu entwischen.]], [[Sie überwacht die Zornpriesterinnen, die ihren Willen in der Konstruktionsbasis: Gehenna ausführen. Tötet eine ihrer Dienerinnen und vergießt ihr Blut auf dem Boden. Diese Beleidigung sollte Sathal dazu bringen, sich für eine Zeitlang zu materialisieren. Tötet sie, solange Ihr die Möglichkeit dazu habt.]], },
    { [[Altruis der Leidende möchte, dass Ihr das frisch gezapfte Blut einer Zornpriesterin der Konstruktionsbasis: Gehenna im Nethersturm besorgt. Schüttet es auf den Boden und tötet den Avatar von Sathal. Kehrt zu Altruis dem Leider zurück, wenn Ihr fertig seid.]], },
  },
  [10642] = {
    [[Spiritus ex machina]],
    { [[Ich fürchte, dass diese Zwerge nicht verstehen, worauf sie da gebaut haben. Diese Gegend war einst der Ahnengrund der Schattenmondorcs. Ich spreche von Geistern, <race>.]], [[Fast zwanzig Jahre lang ruhten sie unter der Wildhammerfeste, doch vor Kurzem hat etwas sie aufgerührt.]], [[Reist jenseits der Tore von Wildhammer und untersucht die Becken und Flüsse der Teufelslava überall in dieser Gegend. In der Nähe der Lava findet Ihr ewig brennende Asche. Bringt mir diese Asche, damit ich etwas daraus herstellen kann, das uns helfen wird.]], },
    { [[Zorus der Richter in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr ihm 15 Proben ewig brennende Asche bringt.]], },
  },
  [10643] = {
    [[Herolde des Schattenmondklans]],
    { [[Seid Euch im Klaren, dass Zorus keinen Humor hat.]], [[Genug geredet. Zieht die Geisterbrille auf, damit Ihr die Orcgeister des Schattenmondklans, die sich in dieser Feste herumtreiben, sehen könnt. Habt keine Angst. Eigentlich sind wir die Eindringlinge hier. Dennoch müssen sie zerstört werden, denn ihre Zeit ist schon lange vorüber.]], [[Zerstört die Herolde des Schattenmondklans, die in der Wildhammerfeste spuken, und kehrt dann zu mir zurück.]], },
    { [[Zorus der Richter in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr 12 Herolde des Schattenmondklans tötet.]], },
  },
  [10644] = {
    [[Teron Blutschatten - Geschichte und Mythos]],
    { [[Es gibt unzählige Geschichten über das Leben von Teron Blutschatten, aber manche sind eben nur Geschichten. Wir wissen nicht, was mit Blutschatten passiert ist, nachdem er nach Draenor zurückgekehrt war.]], [[Mit Sicherheit wissen wir nur, dass er schon einmal gestorben ist.]], [[Ich habe gehört, wie die Späher von einem alten Orcgeist sprachen, der beim Altar der Schatten haust. Er befindet sich weit im Osten, hinter den Netherschwingenfeldern und jenseits des Netherschwingenpasses. Nehmt diese Geisterbrille und sucht diesen Orc. Er weiß sicher mehr über Blutschatten.]], },
    { [[Zorus der Richter in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr den uralten Schattenmondgeist findet.]], [[Wenn Ihr Eure Geisterbrille verlieren solltet, kann Euch Zorus der Richter eine neue herstellen.]], },
  },
  [10645] = {
    [[Ich bin Teron Blutschatten...]],
    { [[Jetzt, da wir Blutschattens Gegenstände beisammen haben, fehlt nur noch ein letztes Teil im Puzzle: ein Wirt.]], [[<Die Lippen des uralten Schattenmondgeistes verziehen sich zu einem bösartigen Grinsen.>]], [[Ihr habt doch nicht etwa das Märchen geglaubt, das ich Euch aufgetischt habe? Der Altar der Schatten ist mein Gefängnis. Ohne Eure Hilfe wäre ich bis in alle Ewigkeit hier gefangen geblieben. Jetzt werde ich Euren Körper übernehmen und Ihr werdet die, die mich gefangen haben, vernichten und das Band für immer zerreißen. Wenn Ihr ablehnt, sind wir beide bis ans Ende der Zeit hier gefangen.]], [[Teron Blutschatten wird frei sein!]], },
    { [[Der uralte Schattenmondgeist hat Euch benutzt! Nun, da Ihr alle Gegenstände, die er wollte, gesammelt habt, hat er sich als Teron Blutschatten offenbart. Um von seinem Bann loszukommen, müsst Ihr seinen Willen erfüllen und Karsius den uralten Wächter töten.]], [[Wenn Ihr erfolgreich wart, meldet Euch bei Zorus dem Richter in der Wildhammerfeste.]], },
  },
  [10646] = {
    [[Illidans Schüler]],
    { [[Ihr habt bewiesen, dass Ihr guter Gesinnung seid, <name>. Bitte verzeiht mein Misstrauen, aber in dieser Welt sind die Dinge nicht so, wie sie scheinen.]], [[Wenn Ihr bereit seid, werde ich Euch von Varedis erzählen.]], },
    { [[Hört Euch Altruis' Geschichte an.]], },
  },
  [10647] = {
    [[Gesucht: Uvuros, Geißel von Schattenmond]],
    { [[Gesucht - TOT:]], [[Uvuros, Geißel des Schattenmondtals.]], [[Auf Befehl des Oberanführers Or'barokh ist die Teufelsbestie zu töten, wenn sie angetroffen wird. Ferner ist die Mähne als Beweis von dem toten Leib abzutrennen. Bringt die feurige Mähne der Bestie zu Kriegsrufer Sardon Treuschnitt, wenn Ihr erfolgreich wart.]], [[Uvuros wurde zuletzt bei der Hand von Gul'dan im Herzen des Schattenmondtals, östlich von Schattenmond, gesehen.]], },
    { [[Tötet Uvuros und beschafft Uvuros' feurige Mähne. Bringt sie zu Kriegsrufer Sardon Treuschnitt in Schattenmond im Schattenmondtal.]], },
  },
  [10648] = {
    [[Gesucht: Uvuros, Geißel von Schattenmond]],
    { [[Gesucht- TOT:]], [[Uvuros, Geißel des Schattenmondtals.]], [[Auf Befehl von Than Yoregar ist die Teufelsbestie zu töten, wenn sie angetroffen wird. Ferner ist die Mähne als Beweis von dem toten Leib abzutrennen. Bringt die feurige Mähne der Bestie zu Kriegsrufer Bierschnut, wenn Ihr erfolgreich wart.]], [[Uvuros wurde zuletzt bei der Hand von Gul'dan im Herzen des Schattenmondtals, nordöstlich der Wildhammerfeste, gesehen.]], },
    { [[Tötet Uvuros und beschafft Uvuros' feurige Mähne. Bringt sie zu Kriegsrufer Bierschnut in der Wildhammerfeste im Schattenmondtal.]], },
  },
  [10649] = {
    [[Das Buch der teuflischen Namen]],
    { [[Ich finde es gut, dass Ihr vorhabt, Varedis und seine Anhänger aufzuhalten, aber Ihr habt keine Chance gegen ihn. Es sei denn, Ihr findet das Artefakt, das ihm seine Macht verliehen hat.]], [[Das Buch der teuflischen Namen soll sich im Besitz von Schwarzherz dem Hetzer befinden, der sich im Labyrinth von Auchindoun aufhält. Eure einzige Hoffnung, Varedis besiegen zu können, ruht auf diesem Buch.]], },
    { [[Reist in das Schattenlabyrinth von Auchindoun und besorgt das Buch der teuflischen Namen von Schwarzherz dem Hetzer. Kehrt danach zu Altruis in Nagrand zurück.]], },
  },
  [10650] = {
    [[Rückkehr zu den Aldor]],
    { [[Dieses Buch... könnte meine Macht verzehnfachen... Nein! Es würde meine Seele verderben, ich weiß es genau. Bringt es zurück zu denen, die Euch hierher geschickt haben, <name>.]], [[Ihr habt nun die Mittel, um Varedis aufzuhalten. Wartet, bis er sich mithilfe der Metamorphose in seiner dämonischen Form manifestiert. Genau in diesem Moment müsst Ihr das Buch in seiner Anwesenheit verbrennen. Nur so könnt Ihr ihn seiner Macht berauben.]], },
    { [[Sprecht mit Exarch Onaala beim Altar der Sha'tar im Schattenmondtal.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10651] = {
    [[Varedis muss aufgehalten werden]],
    { [[Wir müssen dieses abscheuliche Artefakt zerstören, jedoch nicht, bevor wir damit Varedis seiner Mächte beraubt haben.]], [[Das Ausbildungsgelände der Ruinen von Karabor muss für alle Zeiten geschlossen werden. Dazu müsst Ihr nicht nur Varedis töten, sondern auch seine drei Nachtelfenmeister, die ihn unterstützen: Alandien, Theras und Natharel.]], [[Stellt eine geeignete Truppe zusammen und kehrt zu den Ruinen von Karabor zurück. Befolgt Altruis' Anweisungen, um Varedis zu besiegen. Ich hoffe für uns alle, dass sie funktionieren.]], },
    { [[Exarch Onaala möchte, dass Ihr zu den Ruinen von Karabor geht und Alandien, Theras, Netharel und Varedis tötet. Benutzt das Buch der teuflischen Namen, während Varedis die Fähigkeit 'Metamorphose' einsetzt, um ihn zu schwächen. Kehrt danach mit dem Buch der teuflischen Namen zu Exarch Onaala zurück.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10652] = {
    [[Hinter feindlichen Linien]],
    { [[Es wurden alle Vorkehrungen getroffen, um Euch zum Versteck der Spione zu bringen. Sucht mich einfach auf, wenn Ihr bereit seid, und wir werden Euch auf den Weg schicken.]], },
    { [[Sprecht mit Veronia, wenn Ihr bereit seid, zur Manaschmiede Coruu aufzubrechen. Meldet Euch dort bei Caledis Morgenhell.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10653] = {
    [[Male des Sargeras]],
    { [[Inzwischen sollte Euch klar geworden sein, dass wir der Brennenden Legion und ihren Dienern feindlich gesonnen sind.]], [[Wenn Ihr Euch den Aldor gegenüber beweisen wollt, dann bringt mir die Insignien der hochrangigen Mitglieder der Legion und ihrer Anhänger des Schattenrats. Die hochrangigsten Mitglieder der Legion findet Ihr im Nethersturm und im Schattenmondtal.]], },
    { [[Bringt 10 Male des Sargeras zu Adyen dem Lichtwächter in Shattrath.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10654] = {
    [[Mehr Male des Sargeras]],
  },
  [10655] = {
    [[Einzelne Male des Sargeras]],
  },
  [10656] = {
    [[Siegel des Sonnenzorns]],
    { [[Ihr wisst vielleicht, dass die Seher mit Kael'thas Armeen im Krieg stehen. Kael betrachtet uns als Bedrohung für seinen Einfluss über die Blutelfen und hat immer wieder versucht, uns zu vernichten.]], [[Wenn Ihr uns in der Schlacht gegen Kael'thas helfen wollt, gibt es viele Ziele, die Ihr angreifen könnt. Kaels hochrangigste Soldaten tragen die Siegel des Sonnenzorns, der ihren Rang angibt. Ihr findet sie überall im Nethersturm und im Schattenmondtal.]], },
    { [[Magistrix Fyalenn in Shattrath möchte, dass Ihr ihr 10 Siegel des Sonnenzorns bringt.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10657] = {
    [[Lasst sie abblitzen!]],
    { [[Wir sind nicht gerade sparsam mit dem Zephyriumkapazitorium umgegangen. Aber wie es scheint verfügen die Schuppenflügelschlangen im Messergrat über einen Blitzschlagatem und Blitzschlagdrüsen, die wir uns zu Nutze machen können.]], [[Ich habe den Magnetsammler repolarisiert, sodass er die Blitzschläge absorbieren kann. Begebt Euch dorthin, schaltet es an und besorgt uns ein wenig billige Energie!]], },
    { [[Toshley wants you to use the Repolarized Magneto Sphere to absorb 25 lightning strikes from the Scalewing Serpents.  As well, collect 5 Scalewing Lightning Glands.  Then, deliver them back to him at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10658] = {
    [[Mehr Siegel des Sonnenzorns]],
  },
  [10659] = {
    [[Einzelne Siegel des Sonnenzorns]],
  },
  [10660] = {
    [[Welch merkwürdige Wesen...]],
    { [[Ich möchte mehr über die merkwürdigen Reptilien, die bei den Lavaströmen des Schattenmondtals umherstreifen, erfahren. Ich habe noch nie zuvor Tiere gesehen, die der sengenden Hitze geschmolzener Lava widerstehen konnten!]], [[Außerhalb von Schattenmond, wo der Boden aufgerissen ist und die Lava durch die Risse tritt, findet Ihr die Teufelsfeuerdimetrodonten. Tötet sie und bringt mir ihre Milzen. Mit ihrer Hilfe kann ich eine erste Analyse ihres Feuerwiderstands durchführen.]], [[Beginnt Eure Suche im Südosten.]], },
    { [[Forscher Tiorus in Schattenmond im Schattenmondtal möchte, dass Ihr 8 Teufelsfeuermilzen besorgt.]], },
  },
  [10661] = {
    [[Demilziös!]],
    { [[Habt Ihr jemals von Essen gehört, dass sich selbst kocht und für immer warm bleibt? Das wäre traumhaft!]], [[Aber vielleicht muss es kein Traum bleiben! Außerhalb der Feste, in der Nähe der Lavabecken und Lavaströme, gibt es Kreaturen, die in der glühenden Hitze gedeihen. Man nennt sie Teufelsfeuerdimetrodonten.]], [[Die Zwerge hier haben mir gesagt, dass sie die leckersten Milzen aller Zeiten haben. Und das Beste ist, dass sie schon vorgekocht sind! Bringt mir ein paar dieser Milzen! Ich werde Euch gut bezahlen.]], [[Beginnt direkt östlich von hier mit Eurer Suche.]], },
    { [[Gnomus in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr 8 Teufelsfeuermilzen besorgt.]], },
  },
  [10662] = {
    [[Der Wanderschmied]],
    { [[<Ordinn betrachtet seinen Versuch, das Metall zu schmieden, und schüttelt den Kopf.>]], [[Meine Fertigkeiten reichen dazu nicht aus, aber das soll nicht heißen, dass die Waffe für Euch nicht neu geschmiedet werden kann.]], [[Ein talentierter Schmied namens David Wayne begleitete die Söhne Lothars nach Draenor, doch er beschloss, die Expedition zu verlassen. Heute lebt er in einem kleinen Lager auf einer Halbinsel östlich des Postens der Feuerschwingen in den Wäldern von Terokkar. Bringt diese Barren zu ihm und schaut, ob Ihr ihm beim Schmieden der Waffe helfen könnt.]], },
    { [[Bringt den Kasten mit Barren zu David Wayne in Waynes Zuflucht in den Wäldern von Terokkar.]], },
  },
  [10663] = {
    [[Der Wanderschmied]],
    { [[<Der Orc betrachtet seinen Versuch, das Metall zu schmieden, und schüttelt den Kopf.>]], [[Meine Fertigkeiten reichen dazu nicht aus, aber das soll nicht heißen, dass die Waffe für Euch nicht neu geschmiedet werden kann.]], [[Ein talentierter Schmied namens David Wayne begleitete die Söhne Lothars nach Draenor, doch er beschloss, die Expedition zu verlassen. Heute lebt er in einem kleinen Lager auf einer Halbinsel östlich des Postens der Feuerschwingen in den Wäldern von Terokkar. Bringt diese Barren zu ihm und schaut, ob er Euch beim Schmieden der Waffe helfen kann.]], },
    { [[Bringt den Kasten mit Barren zu David Wayne in Waynes Zuflucht in den Wäldern von Terokkar.]], },
  },
  [10664] = {
    [[Zusätzliche Materialien]],
    { [[Das Metall, das Ihr mir gebracht habt, reicht zwar weitestgehend aus, um die Klinge des Schwerts zu schmieden, aber ich benötige noch viele andere Dinge, um die übrigen Teile der Waffe herzustellen.]], [[Wollen wir mal schauen... Mir fehlen ein Adamantitrahmen, ein wenig schweres Knotenhautleder und ein Dämonenschutztotem. Geübte Kunsthandwerker sollten die ersten beiden Materialien herstellen können. Das dritte bekommt Ihr nur bei dem Reagenzienverkäufer Fantei im Unteren Viertel in Shattrath. Er hält sich bei den anderen Händlern auf.]], },
    { [[David Wayne bei Waynes Zuflucht möchte, dass Ihr ihm einen Adamantitrahmen, 4 Stück schweres Knotenhautleder und ein Dämonenschutztotem bringt.]], },
  },
  [10665] = {
    [[Frisch von der Mechanar]],
    { [[Um das Metall, das Ihr mir gebracht habt, zu bearbeiten, brauche ich große Hitze - viel mehr, als eine normale Schmiede herstellen kann.]], [[Ich werde eine zusätzliche Hitzequelle für die Schmiede brauchen. Nur wenige Dinge in der Scherbenwelt können diese Art von Wärmeenergie liefern.]], [[In der Mechanar der Festung der Stürme werden Manazellen, in denen eine unglaubliche Menge an Energie gespeichert ist, hergestellt. Sucht eine überladene Manazelle in den Stapeln um Mechanolord Kapazitus herum und bringt sie zu mir.]], },
    { [[David Wayne bei Waynes Zuflucht möchte, dass Ihr ihm eine überladene Manazelle bringt.]], },
  },
  [10666] = {
    [[Encyclopaedia Daemonica]],
    { [[Um die Verzauberungen auf der Klinge wiederherzustellen, müssen mehrere Zaubersprüche auf Dämonisch gesprochen werden.]], [[Das habe ich während meiner Ausbildung leider nicht gelernt, aber es führt kein Weg daran vorbei. Es gibt ein Buch, das Encyclopaedia Daemonica genannt wird. Darin könnte ich die meisten Worte, die ich brauche, finden. Leider ist das Buch im Besitz eines mächtigen Hexenmeisters des Schattenrats.]], [[Tief im Schattenlabyrinth von Auchindoun wird es von Großmeister Vorpil bewacht. Tut, was immer Ihr tun müsst, um das Buch zu besorgen und bringt es zu mir.]], },
    { [[Beschafft die Encyclopaedia Daemonica von Großmeister Vorpil und bringt sie zu David Wayne in Waynes Zuflucht.]], },
  },
  [10667] = {
    [[Unterweltlehm]],
    { [[Der Rahmen, den Ihr mir gebracht habt, wird nicht ausreichen, um geschmolzenes Metall ohne zusätzliche Verstärkung zu halten. Der Rahmen kann mit einer seltenen Art von Erde abgekühlt und verdichtet werden, die ihm eine unnatürliche Stärke verleiht.]], [[Diese Erde ist als Unterweltlehm bekannt und gilt bei den Naga als äußerst wertvoll. Nur wenige von ihnen besitzen etwas davon und ich kenne nur eine Naga in der Scherbenwelt, die sie benutzt: Hydromantin Thespia, die in der Dampfkammer des Echsenkessels lebt.]], },
    { [[Beschafft Euch ein Gefäß mit Unterweltlehm von Hydromant Thespia und bringt es zu David Wayne in Waynes Zuflucht.]], },
  },
  [10668] = {
    [[Gegen die Illidari]],
    { [[Ich habe immer noch Respekt vor Illidan. Ohne ihn hätte ich nie meinen Pfad gefunden.]], [[Doch er ist nicht mehr der, der er einst war. Seine Seele wurde von Macht verderbt, sein Geist durch seine Niederlagen zerrissen. Ich habe geschworen, das, was aus ihm geworden ist, zu vernichten. Seine Schoßtierchen, die Illidari, sind genauso abscheulich wie die Dämonen der Brennenden Legion.]], [[Auch wenn sich sein Griff um die Scherbenwelt lockert, hat er immer noch weit reichenden Einfluss. Beweist mir, dass Ihr nicht auf seiner Seite steht, und tötet seinen Leutnant Lothros bei der Stätte der Illidari im Schattenmondtal.]], },
    { [[Altruis der Leidende möchte, dass Ihr Lothros in der Stätte der Illidari im Schattenmondtal tötet.]], },
  },
  [10669] = {
    [[Gegen jede Chance]],
    { [[Einige Dämonen schwören nur sich selbst die Treue. Ein wahrer Dämonenjäger sucht und vernichtet jedoch seine Beute ungeachtet ihrer Zugehörigkeit.]], [[Der Dämon Xeleth, ein Abtrünniger der Legion, wurde einst von den primitiven Sumpfkreaturen der Zangarmarschen als Gott verehrt. Als Draenor fast zerstört wurde, zog er sich in einen tiefen Schlaf unter dem Sumpflichtsee zurück. Findet das Portal beim See und steckt diesen Speer in den Boden.]], [[Xeleth wird davon erwachen. Tötet ihn und beweist, dass Ihr ein Feind aller Dämonen seid.]], },
    { [[Altruis der Leidende möchte, dass Ihr den magieerfüllten Silberspeer bei der Portalsicherung in der Nähe des Sumpflichtsees in den Zangarmarschen verwendet, um Xeleth zu wecken. Kehrt zu Altruis zurück, wenn Ihr den Dämon erlegt habt.]], },
  },
  [10670] = {
    [[Träne der Erdenmutter]],
    { [[Die Verzauberungen des Schwerts müssen mit einem mächtigen Fokus gebunden werden, wie zum Beispiel einem Edelstein. Nur wenige Steine können eine so starke Magie in sich aufnehmen, ohne zu zerbersten, doch ich kenne einen.]], [[Der Edelstein, den wir brauchen, wird Träne der Erdenmutter genannt. Er ist ein mächtiges Artefakt, das sich in den Händen seiner Schöpfer, der Oger, befindet. Kriegshetzer O'mrogg, der den Stein im Augenblick besitzt, hat viele mächtige Verbündete in den Zerschmetterten Hallen der Höllenfeuerzitadelle. Seid also vorsichtig, wenn Ihr die Träne besorgt, <name>.]], },
    { [[Besorgt die Träne der Erdenmutter von Kriegshetzer O'mrogg und bringt sie zu David Wayne in Waynes Zuflucht.]], },
  },
  [10671] = {
    [[Ein Pfund Geschnetzeltes]],
    { [[<name>, ich habe noch eine andere Aufgabe für Euch. Einer der Eierköpfe möchte die bösartigen Fleischbestien untersuchen, die sich bei Razaans Landeplatz nördlich des Messergrats aufhalten.]], [[Genauer gesagt wollen sie das Fleisch der Fleischbestien dort untersuchen.]], [[<Dina verzieht angewidert das Gesicht.>]], [[Aber sie wollen sich die Hände natürlich nicht schmutzig machen. Und sie wollen nur das astralisierte Fleisch, das sicher nicht alle haben.]], [[Meiner Meinung nach kann man als Bettler nicht wählerisch sein, aber was sollen wir schon machen?]], },
    { [[Dizzy Dina has asked you to acquire 5 Ether-Energized Flesh and return them to her at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10672] = {
    [[Das ergibt echt keinen Sinn...]],
    { [[We must study these creatures further.]], [[Far to the east - near the huge lava flow beneath the ruins of Baa'ri - you will find arcano control units. These controllers were crafted by a genius named Gnomus for the sole purpose of exploring lava via mechanical scorpion.]], [[He has long since lost his mind and is now only interested in eating the hottest food known to gnome-kind.]], [[<Tiorus gives you a funny look.>]], [[I want you to use the control and tag the diemetradons of the lava flow for further study.]], },
    { [[Forscher Tiorus in Schattenmond im Schattenmondtal möchte, dass Ihr zu dem Lavastrom in der Nähe der Ruinen von Baa'ri reist und die Arkanosteuereinheit benutzt, um einen Arkanoskorp zu steuern. Schwimmt mit dem Arkanoskorp durch die Lava und markiert 8 große Teufelsfeuerdimetrodonten.]], },
  },
  [10673] = {
    [[Teufelsflosse der Große]],
    { [[Gnomus weiß von dem riesigen Dimetrodon und will ihn aufessen! Er muss von irgendwoher erfahren haben, was ich durch sein Arkanoskorp gesehen habe. Nun, wir haben keine Zeit für Erklärungen. Er ist verrückt, und diese Verrücktheit könnte uns das kosten, was einem feuerfesten Anzug am Nächsten kommt.]], [[Kehrt zurück zum Lavastrom in der Nähe von Baa'ri und findet Teufelsflosse den Großen. Bringt mir seinen Balg!]], [[Versucht nicht mit mir zu diskutieren, <name>. Darüber bin ich längst hinaus.]], },
    { [[Forscher Tiorus in Schattenmond im Schattenmondtal möchte, dass Ihr Teufelsflosse den Großen tötet und Teufelsflosses Balg beschafft.]], },
  },
  [10674] = {
    [[Das Licht der Phantasie einfangen]],
    { [[Die Astralen der Razaani bei Razaans Landeplatz im Norden spielen mit einer interessanten Technologie herum.]], [[Ich bin mir sicher, dass sie nichts dagegen hätten, wenn Ihr diese Falle benutzen würdet, um ein paar ihrer kleinen, rosafarbenen, schwebenden Lichtkugeln zu sammeln. Wahrscheinlich sind sie ohnehin nur Dekoration.]], [[Die Falle hat eine ganz spezielle Eigenschaft. Wenn sie geöffnet wird, sollte sie eine der schwebenden Kugel anziehen. Natürlich wird sie auch die Aufmerksamkeit der Razaani auf Euch lenken.]], },
    { [[Toshley would like you to use the Light Trap to catch 15 Razaani Light Orbs.  Return to him at Toshley's Station in the Blade's Edge Mountains after you have done so.]], },
  },
  [10675] = {
    [[Ein Gnom kennt kein Erbarmen!]],
    { [[<Toshley erbleicht.>]], [[Diese Kugeln bestehen sowohl aus Netherenergie als auch aus Seelen! Sogar Gnomseelen sind dabei!]], [[Wie können sie es wagen! Waren sie es vielleicht, die unsere Leute, die auf dem Weg nach Norden waren, getötet haben?!]], [[<name>, Ihr müsst dem ein Ende setzen und das geht nur, wenn Ihr ihren Anführer, Nexusprinz Razaan, ausschaltet.]], [[Ihr müsst ihn jedoch aus seinem Versteck herauslocken. Ich schätze, dass Ihr das erreichen könnt, indem Ihr möglichst viele seiner Astralen der Razaani tötet.]], [[Bringt mir seine Seelensammlung. Ich bin mir sicher, dass er eine hat.]], },
    { [[Toshley wants you to retrieve the Collection of Souls and return it to him at Toshley's Station in the Blade's Edge Mountains.]], },
  },
  [10676] = {
    [[Bann der Illidari]],
    { [[<David sammelt seine Schmiedeausrüstung zusammen.>]], [[Ihr habt alles, was ich zum Schmieden einer antidämonischen Klinge brauche. Eure harte Arbeit und Euer Einsatz soll belohnt werden.]], [[Es ist an der Zeit, die Klinge zu schmieden.]], },
    { [[Wartet, bis David Wayne das Schwert für Euch fertig geschmiedet hat.]], },
  },
  [10677] = {
    [[Der zweite Gang...]],
    { [[Ich erinnere mich, dass ich östlich der Hand von Gul'dan, in der Nähe der Lavaströme um die Ruinen von Baa'ri herum größere Dimetrodonten gesehen habe. Ich möchte, dass Ihr mir einen überentwickelten Teufelsfeuermuskelmagen von den Kreaturen in dieser Gegend besorgt. Ihr müsst wahrscheinlich mehrere erlegen, bevor Ihr einen solchen Muskelmagen findet. Bleibt immer schön am Ball! Einer von ihnen hat meine nächste Mahlzeit bei sich!]], },
    { [[Gnomus in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr einen überentwickelten Teufelsfeuermuskelmagen besorgt.]], },
  },
  [10678] = {
    [[Das Hauptgericht!]],
    { [[Ich werde mich kurz fassen. Ich arbeitete einst mit einem Blutelfen namens Tiorus zusammen. Meine Partnerschaft zu Tiorus brach ab, als ich mich dem Leben als Gourmet zugewandt habe. Er beschloss, unsere Forschungen mit MEINEN Erfindungen auf eigene Faust weiterzuführen. Wie auch immer, er hat ein paar seiner Hordendummköpfe meine Arkanoskorpfühler bei den Ruinen von Baa'ri benutzen lassen und dabei den Vater aller Dimetrodonten entdeckt!]], [[Ich möchte, dass Ihr vor ihm dorthin gelangt und das Dimetrodon Teufelsflosse tötet. Bringt mir seinen Balg!]], [[Beeilt Euch!]], },
    { [[Gnomus in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr Teufelsflosses Balg besorgt.]], },
  },
  [10679] = {
    [[Die Waffe härten]],
    { [[Die Klinge ist fast fertig!]], [[Man kann beinahe schon fühlen, wie die Magie im Stahl zusammenfließt. Aber eine Sache ist noch zu tun.]], [[Ihr müsst die geschmiedete Klinge zu den Siedenden Teichen in der Nähe des Gipfels der Hand von Gul'dan im Schattenmondtal bringen und das Schwert in den Lavabecken härten. Kehrt zurück, wenn Ihr den Stahl gehärtet habt.]], },
    { [[Reist zu den Siedenden Teichen in der Nähe des Gipfels der Hand von Gul'dan und härtet die geschmiedete Klinge des Illidaribanns in den Lavabecken. Kehrt dann mit der gehärteten Klinge des Illidaribanns zu David Wayne in Waynes Zuflucht zurück.]], },
  },
  [10680] = {
    [[Die Hand von Gul'dan]],
    { [[Die Elemente brauchen Eure Hilfe, <race>! Das Schattenmondtal wurde von unaussprechlichen Schrecken heimgesucht. Bis zum heutigen Tag leiden die Geister. Viele sind wütend und wollen nicht mit dem Irdenen Ring in Verbindung treten.]], [[Helft dieser Welt zu heilen, Freund.]], [[Nordöstlich der Wildhammerfeste gibt es einen Ort, der als der Altar der Verdammnis bekannt ist. Begebt Euch dorthin und sprecht mit Erdheiler Torlok.]], },
    { [[Sprecht mit Erdheiler Torlok am Altar der Verdammnis im Schattenmondtal.]], },
  },
  [10681] = {
    [[Die Hand von Gul'dan]],
    { [[Die Elemente brauchen Eure Hilfe, <race>! Das Schattenmondtal wurde von unaussprechlichen Schrecken heimgesucht. Bis zum heutigen Tag leiden die Geister. Viele sind wütend und wollen nicht mit dem Irdenen Ring in Verbindung treten.]], [[Helft dieser Welt zu heilen, Freund.]], [[Südöstlich von Schattenmond, im Herzen des Schattenmondtals, gibt es einen Ort, der als der Altar der Verdammnis bekannt ist. Begebt Euch dorthin und sprecht mit Erdheiler Torlok.]], },
    { [[Sprecht mit Erdheiler Torlok am Altar der Verdammnis im Schattenmondtal.]], },
  },
  [10682] = {
    [[Zeit zu verhandeln...]],
    { [[Als wir hier im Ruuanwald ankamen, hielten sich die Wyrmkultisten nur im Pechschwingenkoven, der sich jenseits des Tunnels im Westen befindet, auf. Doch nun rücken sie immer näher an uns heran.]], [[Ihr müsst den Aufseher der Wyrmkultisten finden. Es ist bekannt, dass er regelmäßige Kontrollgänge zwischen den verschiedenen Lagern hier im Ruuanwald durchführt.]], [[Verhandelt mit ihm. Lasst ihn wissen, dass wir kompromissbereit sind, jedoch auf keinen Fall zu dem Abschlachten der Wildtiere oder dem Fällen von Bäumen beitragen oder es tolerieren werden.]], },
    { [[Tree Warden Chawn has asked you to negotiate with Overseer Nuaar.  Return to the tree warden at Evergrove in the Blade's Edge Mountains afterward.]], },
  },
  [10683] = {
    [[Schrifttafeln von Baa'ri]],
    { [[Die Zerschlagenen der Aschenzungen wurden von Illidan beauftragt, in den Ruinen von Baa'ri weit im Norden Ausgrabungen durchzuführen. Sicher suchen sie nach Draeneiartefakten. Baa'ri verfügte über viele Relikte von Baa'ri, bevor Gul'dan die Stadt unter einer vulkanischen Explosion begrub. Wir müssen diese Chance nutzen, um an die Schrifttafeln von Baa'ri zu kommen. Dieser alte Text soll angeblich die Geheimnisse des Tempels von Karabor, der heute als Schwarzer Tempel bekannt ist, enthalten.]], },
    { [[Arkanist Thelis beim Sanktum der Sterne möchte, dass Ihr 12 Schrifttafeln von Baa'ri vom Boden und von den Arbeitern der Aschenzungen in den Ruinen von Baa'ri sammelt.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10684] = {
    [[Oronu der Älteste]],
    { [[Laut den Schrifttafeln wurde in Baa'ri ein Artefakt, das als Medaillon der heiligen Stimme bekannt ist, aufbewahrt. Nur die höchstrangigen Draeneipriester durften es tragen, da es uneingeschränkten Zugang zum Tempel von Karabor bot.]], [[Wir müssen herausfinden, ob die Aschenzungen das Medaillon schon gefunden haben. Seine Macht wäre uns eine große Hilfe im Kampf gegen Illidan.]], [[Akamas Leutnant in Baa'ri, Oronu der Älteste, erhält täglich schriftliche Befehle von Akama. Besorgt sie Euch und bringt sie mir.]], },
    { [[Arkanist Thelis beim Sanktum der Sterne möchte, dass Ihr die Befehle von Akama von Oronu dem Ältesten in den Ruinen von Baa'ri besorgt.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10685] = {
    [[Die Verderber der Aschenzungen]],
    { [[Laut Akamas Brief wurden vier Teile des Medaillons ausgegraben und den Verderbern der Aschenzungen übergeben. Diese gefürchteten Zerschlagenen waren einst mächtige Schamanen, die nun die Elemente schänden, die sie einst verehrten.]], [[Haalum, der Verderber der Luft, befindet sich bei den Netherschwingenklippen. Eykenen und Uylaru, die Verderber der Erde und des Feuers, wurden zuletzt bei den Teufelsgruben in der Nähe der Hand des Gul'dan gesehen. Lakaan, der Verderber des Wassers, soll sich in der Nähe der Echsennarbe aufhalten.]], [[Findet sie und besorgt Euch die Medaillonfragmente von ihnen.]], },
    { [[Beschafft die vier Medaillonfragmente von Haalum, Eyeknen, Lakaan und Uylaru und kehrt danach zu Arkanist Thelis beim Sanktum der Sterne im Schattenmondtal zurück.]], [[Das Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10686] = {
    [[Der Kerker des Wächters]],
    { [[Die Fragmente, die Ihr besorgt habt, sind nur eine Hälfte des Amuletts. Der Anführer der Aschenzungen, Akama, muss die andere Hälfte haben.]], [[Ich wage es fast nicht, Euch darum zu bitten, da Akama sowohl böse als auch gerissen ist.]], [[Er und die Todeshörigen der Aschenzungen - die bösartigsten und grausamsten der Zerschlagenen - sollen irgendwo im Kerker des Wächters eine Folterkammer betreiben.]], [[Ich möchte, dass Ihr dieses gefürchtete Gefängnis findet und Akamas Aufenthaltsort bestimmt. Sein Stellvertreter vor Ort ist ein Zerschlagener namens Sanuro. Befragt ihn, wenn Ihr müsst.]], },
    { [[Arkanist Thelis möchte, dass Ihr den Kerker des Wächters südlich der Ruinen von Baa'ri betretet und Sanoru über Akamas Aufenthaltsort befragt.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10687] = {
    [[Ausbildungsgelände von Karabor]],
    { [[Unsere Späher haben uns von einem massiven Zufluss von Blutelfenrekruten des Sonnenzorns in den Ruinen von Karabor berichtet. Normalerweise würden wir darin eine rein militärische Bedrohung sehen, doch wir glauben, dass hier noch mehr im Argen liegt.]], [[Die Beschreibungen ihrer Kleidung und ihres Kampfstils klingen verdächtig nach Illidan selbst. Ich will mich nicht ohne handfeste Beweise beunruhigen, daher möchte ich Euch bitten, dorthin zu gehen und mir ihre Waffen zu bringen. Dann werden wir sehen, ob meine Befürchtungen unbegründet sind oder nicht.]], },
    { [[Larissa Sonnenhieb beim Sanktum der Sterne möchte, dass Ihr 8 Gleven des Sonnenzorns von den Bittstellern der Dämonenjäger und den Initianden der Dämonenjäger in den Ruinen von Karabor, nordöstlich des Sanktums der Sterne, beschafft.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10688] = {
    [[Eine notwendige Ablenkung]],
    { [[Die Waffen sind echt, wie wir befürchtet haben. Illidan bildet in den Ruinen von Karabor Dämonenjäger aus. Das müssen wir verhindern.]], [[Lasst Euch nicht von dem Namen täuschen, <name>. Sie werden ihre Macht genauso oft gegen uns einsetzen, wie gegen Dämonen.]], [[Ihre Hexenmeister beschwören und unterwerfen Dämonen im Rahmen der Ausbildung der Initiierten. Ein Grubenlord namens Azaloth hat sich in der Ausbildung als zu mächtig erwiesen und wird nun unter Verschluss gehalten. Findet einen Weg, ihn frei zu lassen und auf seine Kerkermeister zu hetzen. Das sollte uns die nötige Zeit verschaffen.]], },
    { [[Larissa Sonnenhieb möchte, dass Ihr zu den Ruinen von Karabor zurückkehrt und Azaloth auf seine Kerkermeister hetzt.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10689] = {
    [[Altruis]],
    { [[Heutzutage trifft man nur noch selten auf Dämonenjäger. Ihr eigenes Volk begegnet ihnen mit Argwohn. Und noch seltener findet man einen Dämonenjäger, der nicht im Bund mit Illidan steht.]], [[Gerüchten zufolge soll ein solches Wesen hier in der Scherbenwelt leben. Sein Name ist Altruis. Er hasst alle Dämonen mit Leidenschaft und wird uns helfen, wenn Ihr ihn davon überzeugen könnt, dass wir auf seiner Seite stehen.]], [[Geht in den Westen Nagrands und sucht ihn. Vielleicht ist er bereit, etwas mehr über diese neuen Ausbildungsgelände für Dämonenjäger zu erzählen.]], },
    { [[Reist in den Westen Nagrands und sucht Altruis den Leider.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10690] = {
    [[Die Höhlenmutter]],
    { [[GESUCHT!]], [[Rema, die Mutter und wichtigste Verteidigerin des Zackengrats südlich der Donnerfeste.]], [[Ihr findet sie, indem Ihr das Dorf Richtung Osten verlasst und die Brücke über die Blutschlägerklamm überquert. Sie versteckt sich im Tunnel am südlichen Ende des Grats.]], [[Kehrt mit den Nachrichten über Ihr Ableben zu Kommandant Himmelsschatten in Sylvanaar zurück.]], },
    { [[Kill Rema and then inform Commander Skyshadow at Sylvanaar in the Blade's Edge Mountains.]], },
  },
  [10691] = {
    [[Rückkehr zu den Sehern]],
    { [[Dieses Buch... könnte meine Macht verzehnfachen... Nein! Es würde meine Seele verderben, ich weiß es genau. Bringt es zurück zu denen, die Euch hierher geschickt haben, <name>.]], [[Ihr habt nun die Mittel, um Varedis aufzuhalten. Wartet, bis er sich mithilfe der Metamorphose in seiner dämonischen Form manifestiert. Genau in diesem Moment müsst Ihr das Buch in seiner Anwesenheit verbrennen. Nur so könnt Ihr ihn seiner Macht berauben.]], },
    { [[Sprecht mit Larissa Sonnenhieb beim Sanktum der Sterne im Schattenmondtal.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10692] = {
    [[Varedis muss aufgehalten werden]],
    { [[Wir müssen dieses abscheuliche Artefakt zerstören, jedoch nicht, bevor wir damit Varedis seiner Mächte beraubt haben.]], [[Wir müssen diese Akademie von Illidanimitatoren auslöschen. Dazu müsst Ihr nicht nur Varedis töten, sondern auch seine drei Nachtelfenmeister, die ihn unterstützen: Alandien, Theras und Natharel.]], [[Stellt eine geeignete Truppe zusammen und kehrt zu den Ruinen von Karabor zurück. Befolgt Altruis' Anweisungen, um Varedis zu besiegen. Ich hoffe für uns alle, dass sie funktionieren. Wenn nicht... war schön, Euch kennengelernt zu haben.]], },
    { [[Larissa Sonnenhieb möchte, dass Ihr zu den Ruinen von Karabor geht und Alandien, Theras, Netharel und Varedis tötet. Benutzt das Buch der teuflischen Namen, während Varedis die Fähigkeit 'Metamorphose' einsetzt, um ihn zu schwächen. Kehrt danach mit dem Buch der teuflischen Namen zu Larissa Sonnenhieb zurück.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
