  [10700] = {
    [[Zehn Belobigungsabzeichen]],
  },
  [10701] = {
    [[Netherbrock zerschmettern]],
    { [[An alle, denen an einer rechtzeitigen Fertigstellung der X-52 Netherrakete gelegen ist:]], [[Der König der hiesigen Bergriesen, Netherbrock, terrorisiert die Schwindenden Weiten! Er behindert Unternehmungen am gesamten südlichen Rand der Insel!]], [[Schließt Euch mit ein paar Freunden zusammen und erledigt ihn! Kehrt dann zu mir hier in Area 52 zurück.]], [[Raketenchef Doppeldecker]], },
    { [[Tötet Netherbrock und meldet Euch danach bei Raketenchef Doppeldecker in Area 52 im Nethersturm.]], },
  },
  [10702] = {
    [[Routinearbeiten]],
    { [[Alle Streitkräfte der Horde in dieser Gegend sind damit beschäftigt, die Angriffe der Brennenden Legion abzuwehren. Wir haben daher niemanden, der uns die kleinen Routinearbeiten abnimmt.]], [[Unsere Versorgungskarawanen werden regelmäßig von den Wildtieren der Region bedroht. Wir können uns keine 15 Fuß von der Straße entfernen, ohne einer Schimäre, einem Teufelseber oder einem abnormal großen Skorpion in die Fänge zu laufen.]], [[Geht dort hinaus und befreit das Schattenmondtal von seinen sadistischen Wildtieren! Erschlagt sie einfach alle!]], },
    { [[Oberanführer Or'barokh in Schattenmond im Schattenmondtal möchte, dass Ihr 20 Wildtiere des Schattenmondtals tötet.]], [[Dazu gehörten Teufelseber, Finsterflügelschimären und Sengpanzerzangen. Jede Kombination ist zulässig.]], },
  },
  [10703] = {
    [[Zieht Eure Knieschoner an...]],
    { [[Es ist an der Zeit, sich im Schlamm zu wälzen und dreckig zu machen. Was habt Ihr denn erwartet? Man muss schließlich laufen lernen, bevor man krabbelt, oder nich'? Ähem, anders 'rum...]], [[Egal, wir haben jedenfalls alle Hände voll mit dem Müll zu tun, mit dem uns die Legion bewirft. Daher wird Euch die Ehre zuteil, die durchgeknallten Wildtiere des Schattenmondtals zu erlegen.]], [[Schnappt Euch Schwert, Stab oder was auch immer Euch glücklich macht, und tötet so viele Wildtiere, wie Ihr könnt. So halten wir den Weg für unsere Versorgungskarawanen frei.]], },
    { [[Than Yoregar in der Wildhammerfeste im Schattenmondtal möchte, dass Ihr 20 Wildtiere des Schattenmondtals tötet.]], [[Darunter gehörten Teufelseber, Finsterflügelschimären und Sengpanzerzangen. Jede Kombination ist zulässig.]], },
  },
  [10704] = {
    [[Wie man in Arkatraz einbricht]],
    { [[In dem Kristall von Ata'mal, den Ihr uns gebracht habt, war eine Nachricht versteckt. Nexusprinz Haramad hat uns dadurch wichtige Informationen für unsere Vorgehensweise gegen die Festung der Stürme zukommen lassen.]], [[Er berichtet, dass es einen zweiteiligen Schlüssel gibt, mit dem man den Satellit Arkatraz der Festung aufschließen kann.]], [[Die eine Hälfte des Schlüssels befindet sich im Besitz eines Urtums namens Warpzweig in der Botanika. Der zweite Teil wird von Panthaleon dem Kalkulator in der Mechanar bewacht.]], [[Bringt mir beide Fragmente, damit ich den Schlüssel zur Arkatraz zusammensetzen kann.]], },
    { [[A'dal bittet Euch, das obere und das untere Fragment des Schlüssels zur Arkatraz zu besorgen. Bringt beide Fragmente zu ihm zurück, damit er sie für Euch zum Schlüssel zur Arkatraz zusammenfügen kann.]], },
  },
  [10705] = {
    [[Seher Udalo]],
    { [[Ich hoffe, Ihr seid nicht schockiert, wenn ich Euch verrate, dass die Teile des Medaillons von Karabor, die Ihr von meinen Dienern beschafft habt, nichts als Nachbauten sind.]], [[<Akama zeigt Euch ein identisches Medaillon, das auf seiner Handfläche liegt.>]], [[Meine Todeshörigen haben dieses Geheimnis so lange wie möglich von Illidan bewahrt. Dennoch fehlt uns noch das letzte Stück.]], [[Seher Udalo erschien mir in meinen Träumen und sagte mir, dass er das letzte Stück gefunden habe. Er wird jedoch von Kael'thas auf der Arkatraz in der Festung der Stürme gefangen gehalten. Sucht ihn und findet heraus, was er weiß.]], },
    { [[Sucht Seher Udalo in der Arkatraz in der Festung der Stürme.]], },
  },
  [10706] = {
    [[Ein mysteriöses Omen]],
    { [[Bevor er starb, hat Udalo noch ein einzelnes Wort auf den Boden unter sich geschrieben. Die Schrift ist kaum lesbar, aber Ihr erkennt das Wort 'Ata'mal'.]], [[Vielleicht versteht Akama die Nachricht seines Bruders.]], },
    { [[Kehrt zu Akama im Kerker des Wächters im Schattenmondtal zurück.]], },
  },
  [10707] = {
    [[Die Terrasse von Ata'mal]],
    { [[It has been known to us that the fel orcs, under Illidan's command, have guarded an Ata'mal crystal known as the Heart of Fury at the Ata'mal Terrace.  They cloud its presence with dark magic, to keep it from the Sha'tar.]], [[Fight your way to the Atam'al Terrace outside the Black Temple and turn your eyes to the sky.  Shadowlord Deathwail will circle the terrace, beyond your reach.  You will have to slay him and his warlocks before you can obtain the crystal.]], [[It won't be easy, <name>.  I wish you luck.]], },
    { [[Go to the top of the Atam'al Terrace in Shadowmoon Valley and obtain the Heart of Fury.  Return to Akama at the Warden's Cage in Shadowmoon Valley when you've completed this task.]], },
  },
  [10708] = {
    [[Akamas Versprechen]],
    { [[<Akama setzt den Kristall in das Medaillon in seiner Hand ein.>]], [[Ich bin nicht mehr so stark wie einst, <name>. All die Jahre, die ich so nahe am Bösen verbracht habe... Ich weiß nicht, ob ich mir selbst mit einem so mächtigen Artefakt wie diesem trauen würde.]], [[Nehmt dies als mein Versprechen, dass ich auf Eurer Seite sein werde, wenn Ihr Euch Illidan stellt. Bringt es zu dem Wesen, dem wir seine Macht anvertrauen können. Bringt das Medaillon zu A'dal in Shattrath.]], },
    { [[Bringt das Medaillon von Karabor zu A'dal in Shattrath.]], },
  },
  [10709] = {
    [[Zusammenführung]],
    { [[Ihr kommt also aus der Welt jenseits des Portals? Und Ihr kennt Rexxar. Oh ja, auch ich kenne ihn sehr gut.]], [[Er ist mein Sohn.]], [[Aber er hat seine Entscheidung vor langer Zeit getroffen. Er ist keiner von uns und hier nicht willkommen. Wir haben genug Probleme mit den Ogern der Totschläger.]], [[Ihr müsst eines wissen: ein Halbblut zu sein macht niemanden zu einem Mok'Nathal.]], },
    { [[Return to Rexxar at Thunderlord Stronghold in Blade's Edge Mountains.]], },
  },
  [10710] = {
    [[Testflug: Der Singende Bergrücken]],
    { [[Ich habe ein wenig an dem Zephyriumkapazitorium herumgebastelt und bin bereit für einen weiteren Test! Wollt Ihr mir helfen?]], [[Dieser nächste Flug sollte kurz, aber schnell sein!]], [[Äh... und obwohl ich mir sicher bin, dass es nicht gefährlich wird... habe ich hier diesen Haftungsausschluss, den Ihr bitte unterschreiben möchtet. Nur eine Formalität, kein Grund zur Sorge!]], [[Viel Glück! Sprecht mit Rally, wenn Ihr zum Abschuss bereit seid und kehrt zu mir zurück, wenn das Experiment vorbei ist!]], },
    { [[Sign Tally's Waiver, then speak with Rally Zapnabber to use the Zephyrium Capacitorium.  Return to Tally Zapnabber at Toshley's Station.]], },
  },
  [10711] = {
    [[Testflug: Razaans Landeplatz]],
    { [[<name>, ich habe die Effizienz des Zephyriumkapazitoriums um 30 Prozent gesteigert! Ein Versuchsobjekt von Eurem Gewicht wird dann Dank des Luftreibungsverhältnisses in ungeahnte Höhen geschleudert! Wollt Ihr meine Theorie testen? Dieses Experiment sollte Euch in die Nähe von Razaans Landeplatz schicken.]], [[Sprecht mit Rally... er wird Euch für den Abschuss vorbereiten.]], },
    { [[Sprecht mit Rally Zapschnapper und benutzt das Zephyriumkapazitorium. Kehrt danach zu Tally Zapschnapper in Toshleys Station zurück.]], },
  },
  [10712] = {
    [[Testflug: Ruuanwald]],
    { [[He <name>! Gut, dass ich Euch treffe! Ich habe einen sehr wichtigen Auftrag für Euch.]], [[Das hier ist eine Netherwetterfahne. Ich möchte, dass Ihr während des Flugs ein paar Aufzeichnungen macht und sie zu meinem Vetter im Ruuanwald bringt.]], [[Sprecht mit Rally und nehmt einen Flug mit dem Zephyriumkapazitorium zum Ruuanwald. Lasst während dem Flug die Fahne rotieren!]], [[Bringt nach Eurer Landung die rotierende Fahne zu O'Mally Zapschnapper.]], [[Viel Glück, <name>. Und trödelt nicht!]], },
    { [[Speak with Rally Zapnabber to use the Zephyrium Capacitorium.  While flying to Ruuan Weald, spin the Nether-weather Vane.  Deliver the Spinning Nether-weather Vane to O'Mally Zapnabber in Ruuan Weald.]], },
  },
  [10713] = {
    [[...und Zeit zu Handeln]],
    { [[Wir werden nicht tatenlos zusehen, wie sie den Ruuanwald um uns herum ausrotten. Unser höchstes Augenmerk muss den Bäumen gelten.]], [[<class>, ich möchte, dass Ihr in den Wald geht, und eine ganz besondere Nachricht von uns übermittelt. Ich möchte, dass Ihr jeden einzelnen ihrer Roder, dem Ihr begegnet, tötet.]], [[Wenn sie nicht auf vernünftige Argumente hören möchten, dann vielleicht auf schlagkräftige.]], },
    { [[Venture out into the Ruuan Weald and slay 10 Wyrmcult Hewers, then return to Tree Warden Chawn at Evergrove in the Blade's Edge Mountains.]], },
  },
  [10714] = {
    [[Auf geisterhaften Schwingen]],
    { [[Wenn die Mok'Nathal von den Ogern der Blutschläger tyrannisiert werden, werde ich sie und ihre Anhänger aufhalten. Oger sind einfach gestrickte Wesen. Die Mok'Nathal zu belästigen bringt ihnen keine besonderen Vorteile.]], [[Es muss mehr dahinter stecken, <name>. Nehmt Geisterschwinge, meinen Falken, und belauscht die Gespräche der Anführer der Blutschläger. Er wird unbeobachtet bleiben, wo Ihr auffallen würdet.]], },
    { [[Find a Bloodmaul Taskmaster and a Bloodmaul Soothsayer engaged in conversation, then use Rexxar's Whistle to summon Spirit to spy on them. When this is complete, return to Rexxar at Thunderlord Village in the Blade's Edge Mountains.]], },
  },
  [10715] = {
    [[In die Grabenschlucht]],
    { [[Euer Rexxar möchte also, dass ich ein Gift für ihn herstelle? Ich werde ihm geben, was er sich wünscht, aber das hat einen Preis, den auch Freunde bezahlen müssen.]], [[Zunächst müsst Ihr einen kurzen Weg nach Südwesten in die Grabenschlucht zurücklegen.]], [[Dort leben ein paar der giftigsten Kreaturen der ganzen Scherbenwelt, wenn nicht die giftigsten überhaupt. Ich spreche von den Krustenbohrern. Wenn Ihr ein Gift wollt, müsst Ihr ein paar Ihrer Drüsen für mich sammeln.]], },
    { [[Baron Sablemane has told you that in order to make the poison that Rexxar needs, you must acquire 7 Crust Burster Venom Glands.  Return to him at the Circle of Blood in the Blade's Edge Mountains once you have done so.]], },
  },
  [10716] = {
    [[Testflug: Rabenwald]],
    { [[Das Zephyriumkapazitorium ist für seinen bisher längsten Flug bereit! Was meint Ihr, <name>? Wollt Ihr noch einmal fliegen?]], [[Ich habe das Antriebssystem verbessert, aber bis ich die passenden Einstellungen gefunden habe, werden wir zusätzlichen Treibstoff brauchen, um ihm den richtigen Kick zu geben! Sprecht vor dem Flug mit Rally, er hat die Einzelheiten.]], [[Viel Glück, <name>. Dieser Flug wird Euch zum weit entfernten Rabenwald bringen. Wenn Ihr ihn überlebt - und die Oger und Arakkoa des Waldes - kommt zu mir zurück, damit wir feiern können!]], },
    { [[Sprecht mit Rally Zapschnapper und benutzt das Zephyriumkapazitorium. Kehrt danach zu Tally Zapschnapper in Toshleys Station zurück.]], },
  },
  [10717] = {
    [[Wilderer wildern]],
    { [[Verzeiht, aber ich konnte nicht umhin, Euer Gespräch mit Chawn mitzuhören. Ich würde gerne helfen.]], [[Ich stelle Nachforschungen über den Pechschwingenkoven an und helfe den Druiden, mit ihnen fertigzuwerden.]], [[Ich bin sehr daran interessiert, an ein paar ihrer Welplinge zu kommen. Um einen Feind wirklich verstehen zu können, muss man lernen, was er verehrt.]], [[Aber zunächst bräuchten wir ein paar Netze. Die Wilderer des Wyrmkults draußen im Ruuanwald haben eine ganze Menge davon.]], },
    { [[Samia Inkling has asked you to acquire 5 Wyrmcult Nets and return them to her at Evergrove in the Blade's Edge Mountains.]], },
  },
  [10718] = {
    [[Die Stimme der Geister]],
    { [[<name>, jetzt, da Ihr mich sehen könnt, müsst Ihr Rexxar etwas von mir ausrichten. Ich weiß, dass er sein Volk sucht und ich weiß auch, wo es ist.]], [[Die Mok'Nathal haben ein Dorf im Osten, hinter dem Messergrat.]], [[Geht und bringt Rexxar diese Nachricht. Sein Volk ist in Gefahr! Ich weiß, dass er ihnen sehr gerne helfen würde.]], },
    { [[Garm Wolfbrother has told you where the Mok'Nathal, Rexxar's people, live. Speak of this to Rexxar at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10719] = {
    [[Ihr seid eingeladen!]],
    { [[Der Kultist hat diese Einladung fallenlassen, die offensichtlich an Aufseher Nuaar übergeben werden sollte. Sie ist mit dem Namen Kolphis Dunkelschuppe unterzeichnet.]], [[Baumhüter Chawn wäre sicher sehr interessiert daran.]], },
    { [[Bringt die Einladung zum Treffen zu Baumhüter Chawn im Ewigen Hain im Schergrat.]], },
  },
  [10720] = {
    [[Die kleinsten Wesen]],
    { [[So einfältig sie auch sein mögen, würde es den Ogern auffallen, wenn Ihr dieses Gift in ihrem Lager verteilt. Daher will ich Euch beibringen, wie man die Hilfe eines der kleinsten Wesen in der Natur für diese Aufgabe in Anspruch nehmen kann.]], [[Wenn Ihr einen einfachen Rhythmus auf dieser Trommel spielt, könnt Ihr ein Murmeltier so beeinflussen, dass es Euren Befehlen gehorcht.]], [[In der Nähe des Lagers der Blutschläger gibt es einen Murmeltierbau. Er befindet sich im Norden in der Dolchrachenklamm hinter dem Zirkel des Blutes.]], [[Benutzt ein Murmeltier dazu, das Gift in den Fässern der Blutschläger zu verteilen, sobald Ihr es beeinflussen könnt.]], },
    { [[Use the Kodohide Drum at the Marmot Den. While controlling the Marmot, poison the three kegs in the Bloodmaul Camp. When that is completed, return to Rexxar at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10721] = {
    [[Grulloc einen Eber aufbinden]],
    { [[<name>, ich habe einen Eber namens Grummel als Haustier. Er möchte Euch dabei helfen, ein wenig Spaß mit einem der Söhne Gruuls in der Nähe zu treiben.]], [[Grulloc lebt an einem Ort namens Verfluchtes Dunkel. Begebt Euch dorthin und lasst Grummel dann frei. Der Gronn wird es lustig finden, ihn umher zu jagen. Ihr könnt in der Zwischenzeit den Sack, den er bewacht, stehlen und zu Baron Zobelmähne beim Zirkel des Blutes bringen.]], [[Ihr gelangt zum Verfluchten Dunkel, indem Ihr den nördlichen Pfad hinunter geht und dann nach Osten durch Drachenend hindurch geht. Das Dunkel befindet sich am östlichen Rand.]], },
    { [[Obtain Grulloc's Sack and deliver it to Baron Sablemane at the Circle of Blood in the Blade's Edge Mountains.]], },
  },
  [10722] = {
    [[Treffen am Pechschwingenkoven]],
    { [[Ich weiß, was zu tun ist! <name>, Ihr müsst Euch als Aufseher Nuaar ausgeben und an diesem Treffen teilnehmen!]], [[Die Wyrmkultisten tragen alle ein Standardgewand. Sammelt ein paar Fetzen davon. Wenn Ihr genügend zusammen habt, können wir eine Verkleidung herstellen, in der Ihr an dem Treffen teilnehmen könnt.]], [[Sprecht mit Kolphis Dunkelschuppe und hört Euch an, was er zu sagen hat. Kehrt danach zu mir zurück.]], [[Ihr findet den Koven indem Ihr durch den Tunnel des Wyrmschädels geht und dem Pfad durch den Rabenwald Richtung Grishnath bis zum Ende folgt.]], },
    { [[Gather up enough Costume Scraps from the various wyrmcultists to create an Overseer Disguise.  Use the disguise to attend the meeting with Kolphis Darkscale, and report your findings back to Tree Warden Chawn at Evergrove in the Blade's Edge Mountains.]], },
  },
  [10723] = {
    [[Gorgrom der Drachenfresser]],
    { [[Der Gronn, den wir suchen, heißt Gorgrom der Drachenfresser. Mit Zobelmähnes Falle werdet Ihr ihm kurzen Prozess machen können. Ihr könnt ihn wahrscheinlich antreffen, wenn er die Opfer der Oger von Fels'mok entgegen nimmt.]], [[Folgt der Straße, die nach Norden vom Zirkel des Blutes weg führt, und wendet Euch dann beim Ruuanwald nach Westen. Durchquert danach den Tunnel nach Nordwesten.]], [[Tötet zunächst die Oger, um an ihre Köpfe zu kommen. Stellt danach die Falle bei Gorgroms Altar auf und wartet auf seine Ankunft. Tötet ihn und stellt dann die Köpfe als grausige Totems um seine Leiche herum auf. Diese Nachricht sollte man nicht missverstehen können.]], [[Nehmt den Bären mit!]], [[<Misha knurrt.>]], },
    { [[Go to Boulder'mok and collect 3 Grisly Totems from the Boulder'mok ogres. Use Sablemane's Trap at Gorgrom's Altar to set the trap and summon and kill Gorgrom the Dragon-Eater.]], [[When Gorgrom is dead, place the 3 Grisly Totems near his corpse, then return to Rexxar at Thunderlord Stronghold in the Blade's Edge Mountains.]], },
  },
  [10724] = {
    [[Gefangener der Speerspießer]],
    { [[Kurz nach meiner Ankunft in den Bergen wurde ich von einer Gruppe von Ogern der Speerspießer angegriffen.]], [[Ich konnte mich zwar freikämpfen, doch sie haben es geschafft, meinen Wyvern Leokk zu entführen. Geisterschwinge hat ihn gefunden. Er wird in der Mitte des Lagers der Speerspießer außerhalb von Gruuls Unterschlupf in einem Käfig gefangen gehalten.]], [[Einer der Oger muss den Schlüssel dazu haben. Findet ihn, dann kann Geisterschwinge damit Leokks Käfig öffnen.]], [[Das Lager liegt dort unten am nördlichen Ende der Dolchrachenklamm.]], },
    { [[Kill Bladespire ogres to get the Wyvern Cage Key. Once you have it, go to the Wyvern Cage and use Spirit's Whistle to summon Spirit, who will unlock the cage. After Leokk is free, return to Rexxar at Thunderlord Village in the Blade's Edge Mountains.]], },
  },
  [10725] = {
    [[Geachtet vom Violetten Auge]],
    { [[Eure Dienste haben unsere Erwartungen bei Weitem übertroffen, <name>. Ihr seid nur einen Schritt davon entfernt, die höchste Ehre unter denen, die nicht in Dalaran leben, zu erlangen.]], [[Beweist uns weiterhin Eure Treue und ich werde den Siegelring mit der größtmöglichen Macht ausstatten.]], },
    { [[Bringt Euren violetten Siegelring zu Erzmagierin Leryda in Gebirgspass der Totenwinde, nachdem Ihr einen ehrfürchtigen Ruf bei dem Violetten Auge erlangt habt.]], },
  },
