  [10733] = {
    [[Der violette Pfad]],
    { [[Ihr habt Euren Pfad gewählt, <class>. Jetzt ist es an Euch, ihm zu folgen - wo immer er Euch auch hinführen möge.]], [[Ihr werdet herausfinden, dass es seine Vorteile hat, Freunde unter den Erzmagiern von Dalaran zu haben. Dient uns wohl und Eure Taten werden nicht unbelohnt bleiben.]], },
