  },
  [11449] = {
    [[Feuerübung]],
    { [[Die Todespirscher haben ein Übungslager zum Feuerlöschen außerhalb der Stadt aufgebaut. Weitere Schäden an den Gebäuden von Brill können wir uns nicht leisten - also erfüllt Eure Pflicht und übt Euch im Feuerlöschen!]], [[Geht Richtung Westen am Friedhof vorbei und folgt dem Straßenverlauf, bis Ihr das Übungslager erreicht.]], },
    { [[Toss buckets on 5 fires, then speak with the Masked Orphan Matron in Brill.]], },
  },
  [11450] = {
    [[Feuerübung]],
    { [[Die Wachen haben ein Übungslager zum Feuerlöschen außerhalb der Stadt aufgebaut. Wir können nicht zulassen, dass der kopflose Reiter alles zunichtemacht, was wir in harter Arbeit wieder aufgebaut haben – also erfüllt Eure Pflicht und übt Euch im Feuerlöschen!]], [[Verlasst die Stadt durch das südliche Tor in Richtung der Kreuzung. Dort werdet Ihr das Übungslager finden.]], },
    { [[Toss buckets on 5 fires, then speak with the Masked Orphan Matron in Falconwing Square.]], },
  },
  [11451] = {
    [[Alicias Gedicht]],
    { [[Hi! Ich habe für meine Freundin Caylee ein Gedicht verfasst. Sie verbringt Ihre Zeit gerne bei den Aldor in Shattrath. Ihr wisst schon, in der Scherbenwelt!]], [[Wärt Ihr so freundlich und würdet ihr das Gedicht bringen? Meine Mutter sagt, ich sei noch zu jung, um selbst nach Shattrath zu reisen!]], },
    { [[Man hat Euch gebeten, Alicias Gedicht zu Caylee Dak in Shattrath in der Scherbenwelt zu bringen.]], },
  },
  [11454] = {
    [[Findet die Saboteure]],
    { [[Die Angreifer der Dunkeleisenzwerge scheinen diesen Papierfetzen versehentlich zurückgelassen zu haben.]], [[Obwohl die Pläne in einem verfremdeten zwergischen Dialekt verfasst worden sind, ist die Unterschrift "Coren Düsterbräu" eindeutig genug, genauso wie die Erwähnung der Taverne 'Zum Grimmigen Säufer', tief in den Schwarzfelstiefen. Ihr solltet den Überfällen ein Ende bereiten und den Organisator der Überfälle aufsuchen.]], },
    { [[Sucht nach Coren Düsterbräu im Grimmigen Säufer in den Schwarzfelstiefen.]], },
  },
  [11481] = {
    [[Krise am Sonnenbrunnen]],
    { [[Bei den Naaru, ich danke Euch, <race>, für Euer Bemühen.]], [[Unsere Arbeit hier ist jedoch noch lange nicht beendet, weswegen ich Euch darum bitten muss, eine entscheidende Mission auszuführen - eine Mission so wichtig wie alle, mit denen ich Euch jemals beauftragt habe.]], [[Unsere Streitkräfte sind weit verteilt. Und um das Ganze noch schlimmer zu machen, mussten wir ein Kontingent nach Azeroth abbestellen, das die Entwicklung der Dinge auf dem Sonnenbrunnenplateau untersuchen soll.]], [[Ihr werdet dort gebraucht, <name>. Meldet Euch bei Larethor und richtet ihm aus, ich wünschte, wir könnten mehr Männer entbehren.]], },
    { [[Adyen der Lichtwächter auf der Aldorhöhe in Shattrath hat Euch gebeten, zum Sonnenbrunnenplateau zu reisen und dort mit Larethor zu sprechen.]], },
  },
  [11482] = {
    [[Pflicht ruft]],
    { [[Hört mich an, <race>.]], [[Die Pflicht verlangt, dass Ihr Euch zurück nach Azeroth begebt. Ein jeder, der dazu im Stande ist zu kämpfen, soll sich umgehend auf dem Sonnenbrunnenplateau melden.]], [[Ich fürchte, dass ich Euch keine weiteren Informationen geben kann als diese:]], [[Für diese Mission werdet Ihr Euch mit den Aldor zusammentun. Sprecht mit einem Draenei namens Larethor, sobald Ihr dort ankommt.]], [[Anscheinend fordert die Situation, dass wir unsere Differenzen beiseite legen und uns zusammenschließen.]], [[Macht Euch bereit. Das hier klingt ernst.]], },
    { [[Dathris Sonnensturm auf der Sehertreppe in Shattrath besteht darauf, dass Ihr umgehend nach Azeroth reist und Euch bei Larethor am Sonnenbrunnen meldet.]], },
  },
  [11486] = {
    [[Das beste aller Biere]],
    { [[So ein <heller Bursche/helles Mädchen> wie Ihr möchte sicherlich, dass die Angriffe auf das Braufest ein Ende haben, richtig? Ihr wollt Euer fröhliches Braufest ohne weitere Störungen genießen. Daher schlage ich Euch ein Geschäft vor.]], [[Bringt dieses Fässchen feinsten Bieres zum Organisator des Braufests außerhalb von Eisenschmiede und sorgt dafür, dass die Dunkeleisenzwerge richtig repräsentiert werden! Das beste Bier in ganz Azeroth hat seinen Ursprung in diesem Berg, vergesst das nicht!]], },
    { [[Bringt das Bierfässchen der Dunkeleisenzwerge zu Ipfelkofer Eisenfass in Dun Morogh.]], },
  },
  [11487] = {
    [[Das beste aller Biere]],
    { [[So ein <heller Bursche/helles Mädchen> wie Ihr möchte sicherlich, dass die Angriffe auf das Braufest ein Ende haben, richtig? Ihr wollt Euer fröhliches Braufest ohne weitere Störungen genießen. Daher schlage ich Euch ein Geschäft vor.]], [[Bringt dieses Fässchen feinsten Bieres zum Organisator des Braufests außerhalb von Orgrimmar und sorgt dafür, dass die Dunkeleisenzwerge richtig repräsentiert werden! Das beste Bier in ganz Azeroth hat seinen Ursprung in diesem Berg, vergesst das nicht!]], },
    { [[Bringt das Bierfässchen der Dunkeleisenzwerge zu Zapfus Schwindelfass in Durotar.]], },
  },
  [11488] = {
    [[Terrasse der Magister]],
    { [[Willkommen am Sonnenbrunnen, <class>. Macht Euch mit der Umgebung vertraut und legt dort Hand an, wo Ihr nur könnt. Es gibt viel zu tun!]], [[Ich? Ja, ich hätte da eine Kleinigkeit, die erledigt werden müsste, aber ich bin mir nicht sicher, ob Ihr der Aufgabe gewachsen wäret.]], [[Wir hatten einen Kontaktmann auf der Terrasse der Magister im Einsatz, der uns diverse Informationen zukommen ließ. Er hat jedoch vor Kurzem versäumt, uns Bericht zu erstatten, weswegen ich mir zunehmend Sorgen mache.]], [[Die Terrasse befindet sich gleich im Osten, die Anhöhe hoch. Die Informationen, die er gesammelt hat, sind entscheidend für unsere Mission.]], [[Denkt Ihr, dass Ihr damit zurecht kommt, <race>?]], },
    { [[Exarch Larethor beim Sammelpunkt der Zerschmetterten Sonne möchte, dass Ihr die Terrasse der Magister absucht und einen Blutelfenspion namens Tyrith findet.]], },
  },
  [11490] = {
    [[Der Seher der Seher]],
    { [[Geht zur Terrasse... benutzt die Kugel.]], [[<Tyrith ringt nach Luft.>]], [[Beeilt Euch... Ihr müsst sie aufhalten... bevor sie...]], [[<Tyrith stirbt.>]], },
    { [[Tyrith möchte, dass Ihr die Kugel auf dem Balkon in der Terrasse der Magister benutzt.]], },
  },
  [11492] = {
    [[Nicht totzukriegen]],
    { [[Der Sonnenbrunnen heißt Euch willkommen, Sterbliche. Ihr trefft in der Tat zur rechten Zeit ein.]], [[Wie Ihr mittlerweile wisst, arbeiten die Streitkräfte der Legion unermüdlich daran, die Kräfte des Sonnenbrunnens zu manipulieren, um so ihre eigenen unholden Machenschaften zu verwirklichen.]], [[Kael'thas ist aus der Asche der Niederlage auferstanden und ist einmal mehr zu ihrem Anführer geworden. Findet ihn, Sterbliche. Noch einmal muss er aufgehalten werden.]], [[Andere Angelegenheiten von gleicher Dringlichkeit erfordern meine sofortige Aufmerksamkeit. Ich lege Kaels Schicksal in Eure Hände. Ihr dürft nicht versagen.]], [[Mit etwas Glück werden wir uns wieder sprechen.]], },
    { [[Kalecgos hat Euch damit beauftragt, Kael'thas in der Terrasse der Magister zu bezwingen. Ihr sollt Euch mit Kaels Kopf bei Larethor am Sammelpunkt der Zerschmetterten Sonne treffen.]], },
  },
  [11496] = {
    [[Die Barrieren des Sanktums]],
    { [[Zuerst müssen wir das Sanktum der Sonne für uns einnehmen. Sobald wir es gesichert haben, werden wir in der Lage sein, seine Energien für die Öffnung eines Portals nach Shattrath zu nutzen.]], [[Das Sanktum quillt über von Getriebenen - frühere Blutelfen, die zu hirnlosen Schatten ihrer selbst verkümmert sind. Die Verteidigungsanlangen hätten dieses Eindringen normalerweise verhindert, aber die Getriebenen haben die Energiequellen der Schutzbarrieren gestohlen.]], [[Stellt den Getriebenen nach und holt Euch die Manaüberreste, die sie bei sich tragen. Benutzt sie, um die Barrieren des Sanktums aufzuladen.]], },
    { [[Hauptmann Theris Morgenheim beim Sammelpunkt der Zerschmetterten Sonne will, dass Ihr 4 Manaüberreste von getriebenen Unholden und getriebenen Verschlingern verschafft und sie benutzt, um eine der Kristallbarrieren aufzuladen, welche den Osten des Sanktums der Sonnenweiten umgeben.]], },
  },
  [11497] = {
    [[Fliegen lernen]],
    { [[Tja, mein Geschäft wird zwar auf die Dauer wohl eher darunter leiden, aber ich muss sagen, dass Ihr wie jemand ausseht, der sein eigenes Reittier fliegen möchte, anstatt von Ort zu Ort geflogen zu werden.]], [[Ich kann das gut verstehen.]], [[Ihr, mein Freund, müsst Euch zur Wildhammerfeste im Südwesten des Schattenmondtals begeben, und dort Ilsa Trubelbräu nach Flugstunden fragen. Die Ausbildung ist zwar nicht gerade billig, aber ich denke, dass es sich lohnen würde.]], },
    { [[Sprecht mit Ilsa Trubelbräu in der Wildhammerfeste im Schattenmondtal.]], },
  },
  [11498] = {
    [[Fliegen lernen]],
    { [[Tja, mein Geschäft wird zwar auf die Dauer wohl eher darunter leiden, aber ich muss sagen, dass Ihr wie jemand ausseht, <der sein / die ihr> eigenes Reittier fliegen möchte, anstatt von Ort zu Ort geflogen zu werden.]], [[Ich kann das gut verstehen.]], [[Ihr, mein Freund, müsst Euch nach Schattenmond im Nordwesten des Schattenmondtals begeben, und dort Olrokk nach Flugstunden fragen. Die Ausbildung ist zwar nicht gerade billig, aber ich denke, dass es sich lohnen würde.]], },
    { [[Sprecht mit Olrokk in Schattenmond im Schattenmondtal.]], },
  },
  [11499] = {
    [[Gesucht: Der Siegelring von Prinz Kael'thas]],
    { [[Heute werde ich das Unmögliche von Euch verlangen, <class>. Denn heute müsst Ihr mir den Siegelring von Kael'thas Sonnenwanderer, Lord der Blutelfen, beschaffen!]], [[Es ist wahr, die Gerüchte von seinem Tod waren aus der Luft gegriffen. Er ist nun einmal mehr erschienen - und diesmal auf der Terrasse der Magister auf der Insel von Quel'Danas. Was er dort macht, soll Euch jemand anderes berichten. Was ich weiß ist, dass es unzählig viele Sammler gibt, die an diesem Prinzenring interessiert sind. Und ich will ihnen diesen Ring beschaffen.]], [[Oder, um genau zu sein will ich, dass Ihr ihn für mich beschafft.]], },
    { [[Windhändler Zhareem hat Euch darum gebeten, ihm den Siegelring von Prinz Kael'thas zu beschaffen. Bringt diesen zu ihm ins Untere Viertel in Shattrath, um Eure Belohnung zu erhalten.]], [[Diese Aufgabe kann nur auf der Terrasse der Magister und nur auf dem Schwierigkeitsgrad 'Heroisch' abgeschlossen werden.]], },
  },
  [11500] = {
    [[Gesucht: Schwestern der Qual]],
    { [[Mah'duun vernahm eine Stimme, die ihm zuflüsterte, dass die Schwestern der Qual in die Hölle zurückgeschickt werden müssen, aus der sie kommen.]], [[Mah'duun weiß, dass sich die Dämonen auf der Terrasse der Magister auf der Insel Quel'Danas befinden.]], [[Ihr werdet Euch des Kopfgeldes wegen auf dieses Unterfangen einlassen oder auch nicht.]], },
    { [[Netherpirscher Mah'duun möchte, dass Ihr 4 Schwestern der Qual erschlagt. Kehrt zu ihm in das Untere Viertel in Shattrath zurück, sobald Ihr sie getötet habt, um das Kopfgeld einzufordern.]], },
  },
  [11502] = {
    [[Zur Verteidigung von Halaa]],
    { [[Wir sind kein kriegerisches Volk, <race>. Wenn der Krieg jedoch über unser Land kommt, rennen wir nicht vor ihm davon.]], [[Die Allianz und die Horde bekämpfen sich in der Stadt Halaa im Nordwesten. Wir wissen nicht, was die Blutelfen vorhaben, aber jeder Sieg, den die Horde erringt, wäre katastrophal. Die gemeinsame Geschichte unseres Volks mit den Orcs ist eine des Blutvergießens und des Verrats.]], [[Geht nun, streckt den Feind in Halaa nieder und Ihr werdet mit Münze, Ehre und Ruhm belohnt.]], },
    { [[Besiegt 10 feindliche Spieler in Halaa und kehrt zu Lakoor in Telaar zurück.]], },
  },
  [11503] = {
    [[Feinde - alte und neue]],
    { [[Unser uralter Feind, die Draenei, sind mit mächtigen Verbündeten wiedergekehrt. Sie führen Krieg in Halaa, südwestlich von hier.]], [[Sie dürfen nicht siegreich sein, denn Garadar wäre gewiss als Nächstes dran.]], [[Geht und erschlagt diejenigen, die es wagen, unserem uralten Feind beizustehen.]], },
    { [[Besiegt 10 feindliche Spieler in Halaa und kehrt zu Karrtog in Garadar zurück.]], },
  },
  [11505] = {
    [[Geister von Auchindoun]],
    { [[Auchindouns Geister sind zwar nicht am Konflikt zwischen Allianz und Horde beteiligt, sollten wir sie jedoch auf unsere Seite ziehen können, hätten wir unseren Feinden gegenüber einen Vorteil in dieser Region.]], [[Ein uralter Brauch führt die Lebenden auf den Pfad zu den Geistertürmen in der Knochenwüste, um den Geistern dort ihre Ehre zu erweisen - jedoch nur zu speziellen Tageszeiten. Wenn Ihr zu diesen Zeiten bei der Sicherung eines Turms helfen könnt, werdet Ihr dadurch die Geister auf unsere Seite ziehen.]], [[Sollten wir Erfolg damit haben, die Gunst der Geister zu erlangen, werden sie uns eine Wohltat erweisen, die unsere Bestrebungen unterstützen wird.]], },
    { [[Helft der Allianz, einen Turm in der Knochenwüste zu sichern.]], },
  },
  [11506] = {
    [[Geister von Auchindoun]],
    { [[Die Draenei mögen unsere Feinde sein, aber die Geister ihrer Urahnen in Auchindoun sind rastlos und niemandem untertan.]], [[Ein uralter Brauch der Draenei verlangt von ihnen, dass sie zu verschiedenen Tageszeiten bei den Geistertürmen in der Knochenwüste ihren Urahnen Respekt zollen. Wenn Ihr zu diesen Zeiten bei der Sicherung eines Turms helfen könnt, werden dadurch die Geister in Auchindoun zur Ruhe kommen.]], [[Die Allianz wird versuchen, die Türme einzunehmen, um die Gunst der Geister zu erlangen. Wir müssen sicherstellen, dass die Horde hier erfolgreich ist.]], },
    { [[Helft der Horde, einen Turm in der Knochenwüste zu sichern.]], },
  },
  [11513] = {
    [[Manazellen abfangen]],
    { [[Ich habe mir einen Plan ausgedacht, wie wir an eine Energiequelle kommen können, die stark genug ist, um unser Portal zu erschaffen.]], [[Die Streitmächte der Astralen auf dem Landeplatz von Bash'ir im Nordwesten des Schergrats schmuggeln Manazellen aus der Festung der Stürme heraus und schaffen sie in Kael'thas' Namen zum Sonnenbrunnen.]], [[Sie haben die Zellen zwar versteckt, aber es gibt einen Weg, wie Ihr sie finden könnt. Die Astralen tragen Phasenvorrichtung bei sich, mit deren Hilfe Ihr die eingeschmuggelten Waren entdecken könnt.]], [[Hütet Euch jedoch vor den Phasenwyrm, den Wächtern der Manazellen.]], },
    { [[Beschafft 10 eingeschmuggelte Manazellen und kehrt anschließend zu Exarch Nasuun auf der Terrasse des Lichts in Shattrath zurück.]], },
  },
  [11514] = {
    [[Das Sonnenbrunnenportal aufrechterhalten]],
    { [[Wir müssen den Energiefluss zu unserem Portal aufrechterhalten oder es wird kollabieren. Die versteckten Manazellen der Astralen auf dem Landeplatz von Bash'ir werden den Zweck erfüllen. Der Landeplatz befindet sich weit im Norden auf dem Hochplateau in der nordwestlichen Ecke des Schergrats.]], [[Beschafft Euch eine Phasenvorrichtung von einem der Astralen und benutzt sie, um die Schmuggelwaren zu finden.]], [[Nehmt Euch nur vor den Phasenwyrms in Acht, von denen sie die Manazellen bewachen lassen.]], },
    { [[Beschafft 10 eingeschmuggelte Manazellen und kehrt anschließend zu Exarch Nasuun auf der Terrasse des Lichts in Shattrath zurück.]], },
  },
  [11515] = {
    [[Blut für Blut]],
    { [[Als Kaels Streitmacht durch das Portal schritt, wurden seine loyalsten Lakaien auf eine entsetzliche Weise belohnt: Sie blieben zurück und soffen tagelang Dämonenblut.]], [[Das Blut hat sie verändert und ihnen neue Kräfte verliehen - es wird jedoch auch ihr Verderben sein.]], [[Nehmt Blutproben von anderen Dämonen und benutzt sie, um den Teufelstrinker zu speisen und diese arroganten Narren ihrer Macht zu berauben. Tötet sie, sobald sie geschwächt sind. Kehrt dann zu mir zurück.]], },
    { [[Magistrix Seyla auf dem Thron von Kil'jaeden möchte, dass Ihr mithilfe des Teufelstrinkers 4 ausgemergelte Getriebene des Teufelsbluts tötet. Um den Teufelstrinker zu speisen, benötigt Ihr das Dämonenblut von Racheherolden, die sich in der Nähe befinden.]], },
  },
  [11516] = {
    [[Vernichtet den Durchgang]],
    { [[Wir müssen diesen Durchgang schließen, damit die Legion keine Verstärkung mehr zur Insel von Quel'Danas schicken kann.]], [[Nehmt diese Glutstücke, in ihnen befindet sich eine meiner Kreationen: ein lebendiges Leuchtfeuer. Es besitzt so lange eine mächtige zerstörerische Kraft, wie es mit genug Energie versorgt wird.]], [[Findet die Höllenfunken, die in dieser Gegend umherwandern, und erschlagt sie in der Nähe des lebendigen Leuchtfeuers. Mit jedem Male wird das Leuchtfeuer mächtiger - bis es instabil wird.]], [[Sobald Ihr das geschafft habt, bringt Ihr das Leuchtfeuer zum Durchgang. Es wird dann wissen, was zu tun ist.]], },
    { [[Magistrix Seyla auf dem Thron von Kil'jaeden möchte, dass Ihr mithilfe der zischenden Glut ein lebendiges Leuchtfeuer beschwört und damit so lange glühende Höllenfunken in seiner Nähe tötet, bis es zu einem instabilen lebendigen Leuchtfeuer wird. Kehrt anschließend mit letzterem zum Durchgang der Legion zurück und zerstört ihn.]], },
  },
  [11517] = {
    [[Meldung an Nasuun]],
    { [[Ich bin so froh, dass Ihr hier seid, <class>! Nun, da wir das Sanktum eingenommen haben, müssen wir uns schleunigst daran machen, ein magisches Portal zu öffnen, das von Shattrath in der Scherbenwelt aus hier her führt.]], [[Mein Lehnsherr Exarch Nasuun will mithilfe der Offensive genau das tun. Er hat mich gebeten, ihm die vielversprechendsten Kandidaten zu schicken, die das Zeug dazu haben, bei der Öffnung des Portals zu helfen.]], [[Reist nach Shattrath und sprecht mit ihm. Ihr findet ihn in A'dals Halle auf der Terrasse des Lichts.]], },
    { [[Händler Portanuus hat Euch gebeten, mit Exarch Nasuun auf der Terrasse des Lichts in Shattrath zu sprechen.]], },
  },
  [11518] = {
    [[Sunwell Daily Portal Flag]],
  },
  [11520] = {
    [[Die eigenen Wurzeln entdecken]],
    { [[Grüße, <race>.]], [[Leider kann ich Euch nichts anbieten, solange ich keine Reagenzien auf Lager habe.]], [[Eines der benötigten Reagenzien ist eine Besonderheit. Es trägt den Namen Messerdornwurzel und ist besonders schwierig zu sammeln.]], [[Zum Glück kenne ich da ein Geheimnis...]], [[Man benutzt einfach die Drüsen eines Messerdornschinders, um auf diese Weise einen Hetzer zu kontrollieren, der dann eine Wurzel freilegt.]], [[Wenn Ihr die Wurzeln finden wollt, dann müsst Ihr in die Scherbenwelt reisen und von Shattrath aus gen Nordosten fliegen, bis Ihr die Messerdornhöhen erreicht.]], },
    { [[Mar'nah im Hafen der Sonnenweiten möchte, dass Ihr zur Messerdornhöhe in der Scherbenwelt reist, 5 Messerdornwurzeln sammelt und wieder zu ihr zurückkehrt.]], },
  },
  [11521] = {
    [[Eure Wurzeln wiederentdecken]],
    { [[Ich verfüge im Moment über genug Vorräte. Ich werde jedoch eine Menge Messerdornwurzeln benötigen, um der Nachfrage nachzukommen.]], [[Man benutzt einfach die Drüsen eines Messerdornschinders, um auf diese Weise einen Hetzer zu kontrollieren, der dann eine Wurzel freilegt.]], [[Wenn Ihr die Wurzeln finden wollt, dann müsst Ihr in die Scherbenwelt reisen und von Shattrath aus gen Nordosten fliegen, bis Ihr die Messerdornhöhen erreicht.]], [[Kehrt zu mir zurück, <name>, sobald Ihr fünf dieser Wurzeln gesammelt habt.]], },
    { [[Mar'nah im Hafen der Sonnenweiten möchte, dass Ihr zur Messerdornhöhe in der Scherbenwelt reist, 5 Messerdornwurzeln sammelt und wieder zu ihr zurückkehrt.]], },
  },
  [11523] = {
    [[Fahrt die Barrieren hoch!]],
    { [[Wir haben die Kristallbarrieren um das Sanktum erfolgreich reaktiviert, aber warum hier aufhören?]], [[Wir werden Verteidigungen um die anderen Gebäude herum aufbauen, aber die Getriebenen sind dickköpfig und stehlen weiterhin Energie von den Kristallbarrieren.]], [[Stellt ihnen nach und holt Euch die Manaüberreste, die sie bei sich tragen. Benutzt die Überreste, um die Barrieren des Sanktums zu energetisieren, die Sicherheit unseres Vorhabens hängt davon ab.]], },
    { [[Hauptmann Theris Morgenheim beim Sammelpunkt der Zerschmetterten Sonne will, dass Ihr 4 Manaüberreste von getriebenen Unholden und Verschlingern beschafft und sie benutzt, um eine der Kristallbarrieren aufzuladen, welche die Gebäude der Sonnenweiten umgeben.]], },
  },
  [11524] = {
    [[Unberechenbares Verhalten]],
    { [[Wir sind zwar nicht genug, um das Sanktum der Sonnenweiten einzunehmen, aber ich habe einen Plan, mit dem wir den Kampf zu unseren Gunsten wenden können.]], [[Die unkontrollierten Energien des Sonnenbrunnens verursachten Fehlfunktionen bei den Wachposten, die auf dieser Insel patrouillieren. Die Kristallkerne, die ihr Verhalten steuern, sind irreparabel beschädigt.]], [[Ich habe neue Kristalle angefertigt, mit denen wir sie unter unsere Kontrolle bringen werden. Steckt die Kristalle ins Innere der besiegten Wachposten. Wir benötigen jede Hilfe, die wir bekommen können.]], },
    { [[Verteidigerin Xayann beim Sammelpunkt der Zerschmetterten Sonne möchte, dass Ihr die abgestimmten Kristallkerne ins Innere 5 besiegter herumirrender Wachposten steckt und sie so zu freundlichen Einheiten macht.]], },
  },
  [11525] = {
    [[Weitere Konvertierungen]],
    { [[Mein Plan, die beschädigten Kristalle der unberechenbaren Wachposten auszutauschen, hat funktioniert. Wir konnten mit ihrer Hilfe das Sanktum der Sonnenweiten einnehmen.]], [[Ich habe neue Kristalle angefertigt, die noch mehr von ihnen unter unsere Kontrolle bringen werden. Platziert diese im Inneren besiegter Wachposten. Wir werden sie dann an verschiedene Orte entsenden, um sie dort einzusetzen, sobald sie konvertiert sind.]], },
    { [[Verteidigerin Xayann beim Sammelpunkt der Zerschmetterten Sonne möchte, dass Ihr die abgestimmten Kristallkerne ins Innere 5 besiegter herumirrender Wachposten steckt und sie so zu freundlichen Einheiten macht.]], },
  },
  [11526] = {
    [[Eine Magistrix wird vermisst]],
    { [[Wir haben eine unserer Agentinnen ausgesandt, um das Teufelsportal in der Mitte des im Süden gelegenen Platzes der Morgenröte zu untersuchen. Sie ist noch nicht zurückgekehrt.]], [[Nehmt diese Schriftrolle - eine der Leichen des Feindes trug sie bei sich. Wir nehmen an, dass sie Euch den Zugang durch das Portal gewährt. Schreitet durch das Portal, spürt Magistrix Seyla auf und helft, so gut Ihr nur könnt.]], },
    { [[Benutzt die erbeutete Schriftrolle der Legion in der Nähe des Portals auf dem Platz der Morgenröte. Findet Magistrix Seyla, sobald Ihr auf der anderen Seite des Portals seid.]], },
  },
  [11528] = {
    [[Ein Winterhauchgeschenk]],
  },
  [11531] = {
    [[Seltsames Motorteil]],
    { [[Dieses seltsame Motorteil sieht zwar nicht so aus, als würde es in eine Dampfpumpe passen, jedoch scheint alles andere, was Ihr so herumliegen seht, noch weniger dafür geeignet zu sein. Vielleicht weiß K. Lee Kleinkram mehr darüber.]], },
    { [[Bringt K. Lee Kleinkram in Telredor das seltsame Motorteil.]], },
  },
  [11532] = {
    [[Ablenkungsmanöver an der Todesschneise]],
    { [[Die Legion wird uns angreifen, sobald ihre Truppen im Süden nicht mehr gebunden sind. Im Moment hält die Geißel in der Todesschneise ihre Streitkräfte beschäftigt. Jedoch nicht mehr lange.]], [[Ich möchte, dass Ihr mit einem Drachenfalken über die Schneise hinwegfliegt und die Dämonen mithilfe dieser arkanen Ladungen dezimiert.]], [[Wenn alles gut geht, werden Kaels Lakaien es nicht einmal bemerken, wenn wir die Waffenkammer einnehmen. Sprecht mit Ayren, dem Drachenfalkenmeister im westlichen Teil des Hafens, sobald Ihr bereit seid loszufliegen.]], },
    { [[Kampfmagierin Arynna möchte, dass Ihr mit Ayren Wolkenbrecher sprecht, sobald Ihr bereit seid, zur Todesschneise zu fliegen. Tötet dort mithilfe der arkanen Ladungen 2 Oberanführer der Grube, 3 Zauberer der Eredar und 12 Rachevollstrecker.]], },
  },
  [11533] = {
    [[Die Luftangriffe müssen weitergehen]],
    { [[Dank unserer Luftangriffe gegen die Truppen der Legion in der Todesschneise konnten wir dem Feind die Waffenkammer abjagen.]], [[Tatsächlich lief die Sache so gut, dass wir unsere Luftangriffe gegen sie weiterführen werden. Nehmt diese arkanen Ladungen und verwendet sie gegen ihre Truppen.]], [[Sprecht mit Ayren Wolkenbrecher im Hafen der Sonnenweiten, sobald Ihr abflugbereit seid.]], },
    { [[Kampfmagierin Arynna möchte, dass Ihr mit Ayren Wolkenbrecher sprecht, sobald Ihr bereit seid, zur Todesschneise zu fliegen. Tötet dort mithilfe der arkanen Ladungen 2 Oberanführer der Grube, 3 Zauberer der Eredar und 12 Rachevollstrecker.]], },
  },
  [11534] = {
    [[Meldung an Nasuun]],
    { [[Ich bin so froh, dass Ihr hier seid, <class>! Nachdem wir dieses Sanktum den Getriebenen weggenommen hatten, beeilten wir uns, ein Portal zu erschaffen, das von Shattrath in der Scherbenwelt aus hierher führt.]], [[Mein Lehnsherr Exarch Nasuun leitet die Bemühungen der Offensive, dieses Portal aufrechtzuerhalten. Er hat mich gebeten, ihm die vielversprechendsten Kandidaten zu schicken, die das Zeug dazu haben, bei der Energieversorgung des Portals behilflich zu sein.]], [[Reist nach Shattrath und sprecht mit Nasuun. Ihr findet ihn in A'dals Halle auf der Terrasse des Lichts.]], },
    { [[Händler Portanuus hat Euch gebeten, mit Exarch Nasuun auf der Terrasse des Lichts in Shattrath zu sprechen.]], },
  },
  [11535] = {
    [[Vorbereitungen]],
    { [[Versorgungsgüter sind auf dem Weg hierher - wird mir zumindest gesagt.]], [[Trotzdem können wir es uns nicht leisten, hier zu warten und zu hoffen, dass Hilfe kommen wird, bevor die Offensive zum Stillstand kommt. Wir müssen das Beste aus dem machen, was uns gegeben ist.]], [[Für meinen Teil benötige ich Erz, um daraus einen Amboss zu fertigen und anzufangen, unsere Rüstungen und Waffen aufzubessern.]], [[An der Küste im Osten gibt es eine Gruppe Naga, die Erz abbaut und es dann in Truhen an die Legion schickt.]], [[Wenn wir ihnen ihr Erz abnehmen, wird das unserem Vorhaben in vielerlei Hinsicht zugutekommen...]], },
    { [[Schmiedin Hauthaa in der Waffenkammer möchte, dass Ihr Myrmidonen der Dunkelflossen, die sich im Osten befinden, tötet und ihre Schlüssel dazu verwendet, 3 Stück Erz aus ihren Truhen zu stehlen.]], },
  },
  [11536] = {
    [[Hört jetzt nicht auf!]],
    { [[Da wir jetzt einen Amboss haben, kann ich so richtig anfangen zu arbeiten.]], [[Ich werde das Eisenerz der Dunkelflossen immer gut gebrauchen können, egal wie viele Versorgungsgüter wir bekommen. Wenn wir hier gewinnen wollen, dann benötigen wir regelmäßige Waffen- und Rüstungslieferungen.]], [[An der Küste im Osten gibt es eine Gruppe Naga, die Erz abbaut und es dann in Truhen an die Legion schickt. Wenn wir ihnen ihr Erz abnehmen, wird das unserem Vorhaben in vielerlei Hinsicht zugutekommen...]], },
    { [[Schmiedin Hauthaa in der Waffenkammer möchte, dass Ihr Myrmidonen der Dunkelflossen, die sich im Osten befinden, tötet und ihre Schlüssel dazu verwendet, 3 Stück Erz aus ihren Truhen zu stehlen.]], },
  },
  [11537] = {
    [[Die Schlacht muss weitergehen]],
    { [[Wir müssen zusehen, dass wir die Armeen der Legion weiterhin besiegen und entmutigen.]], [[Die Abgesandten des Hasses sind Stoßtruppen der Legion, die immer dann eingesetzt werden, wenn ihre Streitkräfte erhebliche Verluste zu verzeichnen haben. Wenn sie auf der Bildfläche erscheinen, schart sich der Feind um sie, denn er fürchtet sie mehr als uns.]], [[Heute werden wir das ändern. Nehmt dieses Banner und erschlagt die Dämonen auf dem Platz der Morgenröte. Tötet den Abgesandten, sobald Ihr ihm begegnet, und spießt anschließend seinen Körper auf.]], },
    { [[Herold Inuuro möchte, dass Ihr 6 Dämonen der brennenden Legion und den Abgesandten des Hasses auf dem Platz der Morgenröte niederstreckt. Benutzt das Banner der Zerschmetterten Sonne, um den Körper des Abgesandten des Hasses aufzuspießen.]], },
  },
  [11538] = {
    [[Die Schlacht um die Waffenkammer der Sonnenweiten]],
    { [[The Burning Legion holds the Sun's Reach Armory, which is of tactical importance to our operation.  As long as their morale remains high we will not wrest the building from their control.]], [[The emissaries of hate are Legion shocktroopers sent to reinforce tactical Legion positions.  The enemy rallies upon their arrival for they fear them more than they fear us.]], [[Today we will change that.  Take this banner and slay the demons of the Legion.  When the emissary is sent, slay and impale him.]], },
    { [[Herold Inuuro möchte, dass Ihr 6 Dämonen der brennenden Legion und den Abgesandten des Hasses auf dem Platz der Morgenröte oder der Waffenkammer der Sonnenweiten niederstreckt. Benutzt das Banner der Zerschmetterten Sonne, um den Körper des Abgesandten des Hasses aufzuspießen.]], },
  },
  [11539] = {
    [[Den Hafen einnehmen]],
    { [[Die Armee der Dämmerklingen - sie gehört zu Kaels Elitestreitkräften - wurde im Hafen der Sonnenweiten und in Morgenstern stationiert.]], [[Der Hafen ist von höchster strategischer Bedeutung für unsere Pläne. Je mehr Dämmerklingen Ihr also beseitigt, umso mehr helft Ihr uns damit. Kael'thas wird den Tag bereuen, als er sich von seinem Volk abwandte und sich mit der Brennenden Legion einließ.]], },
    { [[Magister Ilastar in der Waffenkammer der Sonnenweiten möchte, dass Ihr 6 Beschwörer der Dämmerklingen, 6 Blutritter der Dämmerklingen und 3 Schützen der Dämmerklingen niederstreckt.]], },
  },
  [11540] = {
    [[Vernichtet die Dämmerklingen]],
    { [[Wir haben die Armee der Dämmerklingen aus dem Hafen der Sonnenweiten vertrieben. Die Sonnenweiten gehören nun uns. Doch... bin ich nicht vollends zufrieden.]], [[Ich möchte, dass Ihr Euch ins westlich gelegene Morgenstern begebt und jeden überlebenden Anhänger der Dämmerklingen erschlagt, der Euch über den Weg läuft. Sie besiegelten ihr Schicksal, als sie Kael an diesen Ort folgten.]], },
    { [[Magister Ilastar in der Waffenkammer der Sonnenweiten möchte, dass Ihr 6 Beschwörer der Dämmerklingen, 6 Blutritter der Dämmerklingen und 3 Schützen der Dämmerklingen niederstreckt.]], },
  },
  [11541] = {
    [[Belästigung an der Küste der Grünkiemen]],
    { [[Die Naga haben die Küste der Grünkiemen im Südosten erobert. Ihre Frauen, die Sirenen der Dunkelflossen, haben die Murlocs bezaubert und zu ihren Sklaven gemacht.]], [[Es ist meine Pflicht, dafür zu sorgen, dass das, was auch immer die Naga dort treiben, gestört wird. Hier kommt Ihr ins Spiel.]], [[<class>, ich möchte, dass Ihr den weiblichen Naga die Kontrollkugeln abnehmt und sie auf den Boden in der Nähe der Grünkiemensklaven werft, damit die Kugel zerspringt und die Murlocs befreit werden.]], [[Ich nehme an, dass die Murlocs erbost sein werden und sich gegen ihre Meister wenden werden.]], },
    { [[Kapitänin Valindria möchte, dass Ihr 10 Grünkiemensklaven befreit und zu ihr zurückkehrt, sobald Ihr damit fertig seid. Sie befindet sich auf der 'Silbermonds Stolz' im Hafen der Sonnenweiten.]], },
  },
  [11542] = {
    [[Haltet die Verstärkung auf]],
    { [[Wir stoßen vor und nehmen den Hafen als Nächstes ein, um unsere Kontrolle über die Sonnenweiten zu festigen. Leider ist eine Flotte Schiffe mit Reservisten der Dämmerklingen auf dem Weg dorthin, um Kaels Truppen zu verstärken.]], [[Nehmt dieses Öl, geht zu Ayren Wolkenbrecher und besorgt Euch einen Drachenfalken von ihm. Ihr solltet bis in Reichweite der Schiffssegel fliegen und sie dann in Brand setzen können.]], [[Landet auf einem der Schiffe und greift die Reservisten an. Mit brennenden Segeln werden sie sich nicht großartig verteidigen können.]], },
    { [[Verteidiger Kaalan in der Waffenkammer der Sonnenweiten möchte, dass Ihr mit Ayren Wolkenbrecher redet und über die Flotte mit Verstärkungstruppen der Dämmerklingen hinwegfliegt. Benutzt solange Ihr fliegt das flammende Öl, um die Segel in Brand zu setzen. Erschlagt 6 Reservisten der Dämmerklingen, nachdem Ihr gelandet seid.]], },
  },
  [11543] = {
    [[Den Feind vom Leibe halten]],
    { [[Wir konnten die Sonnenweiten vollständig einnehmen. Leider hat sich eine Flotte Schiffe mit Reservisten der Dämmerklingen auf den Weg gemacht, um den Hafen einzunehmen.]], [[Nehmt dieses Öl, geht zu Ayren Wolkenbrecher und besorgt Euch einen Drachenfalken von ihm. Ihr solltet bis in Reichweite der Schiffssegel fliegen und sie dann in Brand setzen können.]], [[Landet auf einem der Schiffe und greift die Reservisten an. Mit brennenden Segeln werden sie keine wirkliche Gegenwehr starten können.]], },
    { [[Verteidiger Kaalan in der Waffenkammer der Sonnenweiten möchte, dass Ihr mit Ayren Wolkenbrecher redet und über die Flotte mit Verstärkungstruppen der Dämmerklingen hinwegfliegt. Benutzt solange Ihr fliegt das flammende Öl, um die Segel in Brand zu setzen. Erschlagt 6 Reservisten der Dämmerklingen, nachdem Ihr gelandet seid.]], },
  },
  [11544] = {
    [[Waffen von Ata'mal]],
    { [[Die Terrasse von Ata'mal im Osten des Schattenmondtals ist den Draenei heilig. Derzeit ist sie jedoch von Höllenorcs besetzt, die das spezielle Erz, das dort vorkommt, geschürft und daraus Waffen und Rüstungen der Verderbnis hergestellt haben.]], [[Ich brauche die Waffen, um eine Legierung mit Nagaerz herzustellen. Unsere Anachoreten haben meinen Amboss mit der Fähigkeit gesegnet, sie zu reinigen.]], [[Ihr könnt die Waffen an den Auserwählten, Tötern und Dunkelwirkern des Schattenmondklans finden. Bringt sie hierher, zerlegt und reinigt sie auf meinem Amboss und übergebt mir das gereinigte Resultat.]], },
    { [[Schmiedin Hauthaa möchte, dass Ihr 5 Stück gereinigtes Metall von Ata'mal herstellt. Um dies zu tun, müsst Ihr Waffen von Ata'mal auf ihrem Amboss in der Waffenkammer der Sonnenweiten auf der Insel von Quel'Danas zerlegen.]], },
  },
  [11545] = {
    [[Eine milde Gabe]],
    { [[Wir haben die Sonnenweiten dem Feind entrissen - der endgültige Sieg steht kurz bevor. Der Preis war hoch und viele tapfere Kämpfer gaben dafür ihr Leben.]], [[Mein Ziel ist es, sicherzustellen, dass die Gefallenen nicht vergessen werden. Also bitte ich Euch, dass Ihr einen Moment innehaltet, um über eine Spende für eine Gedenkstätte nachzudenken. Alles übrig gebliebene Gold wird an die Witwen und Waisen der Gefallenen gehen.]], },
    { [[Anachoretin Ayuri in den Sonnenweiten auf der Insel des Sonnenbrunnens möchte, dass Ihr 10 Gold spendet.]], },
  },
  [11546] = {
    [[Geschäft geöffnet]],
    { [[Willkommen, <name>.]], [[Es gibt mehrere Möglichkeiten, wie ich Euch bei Eurem Unterfangen behilflich sein kann.]], [[Ich habe zum Beispiel vor Kurzem ein Rezept für ein einzigartiges Elixier entdeckt.]], [[Die Blutbeere ist in vielen Regionen Azeroths ein wildes Gewächs und wurde sehr lange als lästig empfunden. Ich habe jedoch herausgefunden, dass die Pflanze, wenn sie den Kräften des Sonnenbrunnens ausgesetzt wird, besondere Eigenschaften annimmt, die destilliert und anschließend getrunken werden können, um die eigenen Fähigkeiten zu verstärken.]], [[Geht zu den Büschen in der Nähe und bringt mir eine Probe - dann werdet Ihr sehen, was ich meine.]], },
    { [[Mar'nah im Hafen der Sonnenweiten möchte, dass Ihr 5 Blutbeeren von den Büschen auf Quel'Danas sammelt.]], },
  },
  [11547] = {
    [[Kenne deine Leylinien]],
    { [[Trittbrettfahrer!]], [[Alle wollen sie ihre Wünsche sofort erfüllt haben, ohne auch nur einen Gedanken daran zu verschwenden, wie viel Anstrengung selbst im einfachsten Portal steckt.]], [[Vielleicht wird Euch etwas Aufklärung ein wenig mehr Dankbarkeit für meine unermüdlichen Anstrengungen beibringen.]], [[Nehmt diesen Kristall und stimmt ihn auf den Blutkristall im Westen ab. Dieselbe Prozedur müsst Ihr dann am Portal auf dem Platz der Morgenröte im Süden und in der Nähe des Nagaschreins an der Küste der Grünkiemen im Südosten wiederholen.]], [[Vielleicht werdet Ihr dann Eure heiß geliebten Translokationen mehr schätzen...]], },
    { [[Astromant Darnarian im Sanktum der Sonnenweiten möchte, dass Ihr den Kristall des Astromanten am Blutkristall, am Portal auf dem Platz der Morgenröte und in der Nähe des Nagaschreins an der Küste der Grünkiemen benutzt.]], },
  },
  [11548] = {
    [[Eure weitere Unterstützung]],
    { [[Wir haben den Gefallenen zu Ehren ein Denkmal errichtet - ein kleiner Trost für die Familien, die ihre Angehörigen verloren haben.]], [[Ich werde damit beginnen, Spenden zu sammeln, um die Waisen und Ehepartner unserer Kriegshelden zu unterstützen. Falls Ihr eine kleine Spende machen könntet, würdet Ihr viel dazu beitragen.]], },
    { [[Anachoretin Ayuri in den Sonnenweiten auf der Insel des Sonnenbrunnens möchte, dass Ihr 10 Gold spendet.]], },
  },
  [11549] = {
    [[Ein großmütiger Spender]],
    { [[Ihr habt viel für uns getan, <name>. Wir sind Euch für Eure Unterstützung sehr dankbar.]], [[Ihr habt vielleicht bemerkt, wie sich die Anachoretin Ayuri dort draußen bemüht. Sie hat ein großes Herz und ist sehr überzeugend, aber die Spenden kommen nicht gerade an das heran, was sie sich erhofft hatte.]], [[Falls Ihr eine große Spende machen würdet, um ihre Sache zu unterstützen, werde ich mich persönlich dafür stark machen, dass Eure Taten in diesem Krieg weit und breit bekannt sein werden. Was sagt Ihr dazu?]], },
    { [[Anachoret Kairthos möchte, dass Ihr 1000 Gold spendet, um Anachoretin Ayuri in ihrem Vorhaben zu unterstützen. Wenn Ihr die Aufgabe abschließen solltet, werdet Ihr den Namen "<name> der Zerschmetterten Sonne" tragen.]], },
  },
  [11550] = {
    [[Der Betrüger betritt die Bühne...]],
    { [[Kael'thas' finstere Pläne konnten unseren größten Anstrengungen zum Trotz ausgeführt werden. Der Sonnenbrunnen wurde wieder entfacht, um die Ankunft des Betrügers vorzubereiten.]], [[Der Gegenstand, der vor mir steht und über dem A'dal schwebt, repräsentiert den derzeitigen Status der Verteidigungsanlagen des Sonnenbrunnens. Es wurden riesige Barrieren errichtet, um unsere Angriffe auf die inneren Hallen zu behindern. Wenn unsere Welt jemals Hilfe benötigen sollte, dann wäre es jetzt.]], [[Meldet Euch bei Erzmagier Ne'thul auf der Insel von Quel'Danas nördlich von Quel'Thalas.]], },
    { [[Meldet Euch bei Erzmagier Ne'thul auf der Insel von Quel'Danas, nördlich von Quel'Thalas.]], [[Sprecht mit Flugmeistern in Eisenschmiede und Silbermond, sie werden Euch dorthin bringen.]], },
  },
  [11554] = {
    [[Ein Freund an der Front]],
  },
  [11555] = {
    [[Eure Verbündeten wollen Euer Wohl]],
  },
  [11556] = {
    [[Auf dem Schlachtfeld zollt man Euch Respekt]],
  },
  [11557] = {
    [[Alle Kämpfer begegnen Euch mit Ehrfurcht]],
  },
  [11558] = {
    [[Gefährliche Liebschaft]],
    { [[Diese Liebesepidemie hat etwas Unnatürliches an sich. Sie ist abscheulich und hat sie erst einmal unsere Verteidigung geschwächt, könnte dies zu einer ernsten Bedrohung für uns alle werden.]], [[Schlimm genug, dass bereits so viele unserer Leute von diesem lächerlichen Verhalten befallen sind. Ich denke, selbst unsere Wächter hat es bereits erwischt, dabei sollten sie gegen derartige Einflüsse immun sein.]], [[Geht zu einem der Wächter und seht, ob er von dieser Dummheit befallen wurde.]], },
    { [[Besorgt eine modrige Karte des Wächters und bringt sie zu Fenstad Argyle in Unterstadt.]], },
  },
  [11577] = {
    [[WoW Collector's Edition: - DEM - E - FLAG]],
  },
  [11580] = {
    [[Entweiht dieses Feuer!]],
  },
  [11581] = {
    [[Entweiht dieses Feuer!]],
  },
  [11583] = {
    [[Ehrt die Flamme]],
  },
  [11584] = {
    [[Ehrt die Flamme]],
  },
  [11657] = {
    [[Fackeln fangen]],
    { [[Da Ihr ja nun eine Fackel werfen könnt, lasst uns doch einmal sehen, ob Ihr auch eine fangen könnt!]], [[Nehmt diesen Stapel unangezündete Fackeln, geht zum Freudenfeuer und werft eine davon hoch in die Luft! Fangt sie wieder auf, bevor sie auf den Boden fällt... und werft sie wieder in die Höhe! Fangt vier nacheinander und kommt wieder zu mir zurück.]], [[Seid vorsichtig! Die Fackeln sind gefährlich, wenn sie auf dem Boden aufkommen! Und fangt sie alle auf! Wenn eine davon auf den Boden fällt, müsst Ihr zurück zum Freudenfeuer und von vorn anfangen. Viel Glück.]], },
    { [[Fangt nacheinander 4 Fackeln und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11665] = {
    [[Krokilisken in der Stadt]],
    { [[Hallo, <Junge/Mädchen>. Ich bin froh, dass Ihr vorbeigeschaut habt, um mit einem alten Mann zu sprechen - zuhause gibt es Schwierigkeiten.]], [[Ein fliegender Händler hat neulich eine Ladung Krokiliskenbabys an ein paar leichtgläubige Kinder verkauft. Krokilisken sind wilde Bestien. Viele sind entkommen und halten sich jetzt in den Wasserwegen der Stadt verborgen.]], [[Nehmt Eure stärkste Angelrute, werft sie in Sturmwind oder Orgrimmar aus und bringt einen der kleinen Teufel zurück zu mir. Ich kann es kaum erwarten, einen zu sehen.]], },
    { [[Bringt dem alten Barlo ein Krokiliskenbaby. Ihr könnt ihn beim Angeln am Silmyrsee nordöstlich von Shattrath finden.]], },
  },
  [11666] = {
    [[Köderbanditen]],
    { [[Ihr wisst wahrscheinlich alles über den goldenen Stachelflosser - in den Flüssen von Terokkar kommen sie recht häufig vor, außerdem sind sie eine Delikatesse für Priester. Es gibt jedoch einen, von dem Ihr noch nicht gehört habt: den schwarzen Stachelflosser.]], [[Der Fisch ist berüchtigt für seine Gerissenheit und dafür, dass er schwierig einzufangen ist. Ihr müsst Eure Angelschnur in letzter Sekunde einziehen, denn sie knabbern am Köder, bevor sie sich ihn schnappen.]], [[Werft Eure Angelrute in den Gewässern von Terokkar aus und bringt mir einen schwarzen Stachelflosser. In Seen braucht Ihr gar nicht erst zu suchen - der Flitzer kommt nur in Fließgewässern vor.]], },
    { [[Bringt dem alten Barlo einen schwarzen Stachelflosser. Ihr könnt ihn beim Angeln am Silmyrsee nordöstlich von Shattrath finden.]], },
  },
  [11667] = {
    [[Der Eine, der entkam]],
    { [[Feigenschimmers Matschflosser ist ein erstaunlich schneller Gründler, den man in den Gewässern von Nagrand anfindet. Sie wachsen ihr ganzes Leben lang und sind als Kämpfer sehr bekannt.]], [[Vor Jahren fing ich den größten Matschflosser der Welt, da bin ich mir sicher... Doch dann brachte er meine Leine zum Reißen und kam davon.]], [[Einem Angler Eures Kalibers muss man nicht sagen, was zu tun ist - Euer Schicksal erwartet Euch an den Seen von Nagrand.]], },
    { [[Fangt den größten Matschflosser der Welt und bringt ihn zum alten Barlo. Ihr könnt ihn beim Angeln am Silmyrsee nordöstlich von Shattrath finden.]], },
  },
  [11668] = {
    [[Garnelenfangen ist nicht einfach]],
    { [[In den Seen der Zangarmarschen gibt es viele große Fische. Habt Ihr Euch jemals darüber Gedanken gemacht, was sie fressen?]], [[Garnelen - riesige Süßwassergarnelen. Der Rokk hat mir erzählt, dass man unendlich viele Garnelengerichte zubereiten kann.]], [[Garnelen sind jedoch zu klein, als dass man sie mit einer Angelschnur fangen könnte. Hier also der Trick: Schneidet eine aufgedunsene Stachelkiemenforelle auf und Ihr werdet, wenn Ihr Glück habt, die eine oder andere Garnele darin finden.]], },
    { [[Bringt dem alten Barlo 10 riesige Süßwassergarnelen. Ihr könnt ihn beim Angeln am Silmyrsee nordöstlich von Shattrath finden.]], },
  },
  [11669] = {
    [[Teufelsblutfilet]],
    { [[Dieser alte Mann wird bald jagen gehen. Ich werde mir vor Azsharas Küste einen Mammuthai fangen.]], [[Die Sache ist nur, dass ich ein Toxin benötige, das auf einer Speerspitze haften bleibt, wenn man sich unter Wasser befindet. Um so etwas herzustellen, benötige ich die Drüsen eines giftigen Fischs: dem Teufelsblutschnapper.]], [[Solch ein abscheulicher Fisch gedeiht nur in den Gewässern der Höllenfeuerhalbinsel und des Schattenmondtals. Bringt mir das Mordsding und Ihr macht aus mir einen glücklichen alten Mann.]], },
    { [[Bringt dem alten Barlo einen monströsen Teufelsblutschnapper. Ihr könnt Barlo beim Angeln am Silmyrsee nordöstlich von Shattrath finden.]], },
  },
  [11691] = {
    [[Beschwört Ahune]],
    { [[Ich und meine Schamanen haben all unsere Kräfte für die Schlacht mit dem Frostfürsten gesammelt. Ihr werdet die Faust der Erdenmutter sein, <name>.]], [[Nehmt dieses Magmatotem und benutzt es in der Nähe des Eisbrockens im Nordosten. Ahune leitet seine Kraft durch diesen Brocken. Unsere Hoffnung ist, dass das Totem seine Verbindung mit der Elementarebene durchtrennen wird.]], [[Wenn wir Glück haben, dann könnten wir ihn vielleicht besiegen.]], [[Macht Euch bereit, <name>. Wenn Ahune uns gegenübersteht, wird sein Zorn gewaltig sein.]], },
    { [[Bringt dieses Magmatotem des Irdenen Rings zum Eisbrocken.]], },
  },
  [11696] = {
    [[Ahune ist hier!]],
    { [[<name>, Eure Gegenwart ist ein Segen. Ahune befindet sich in den Höhlen und sammelt Kräfte für seinen Angriff. Truppen des Irdenen Rings wurden vorausgeschickt, doch alleine werden sie ihn nicht besiegen können.]], [[Erkundet die Höhlen, <name>. Findet unsere Schamanen des Irdenen Rings und meldet Euch bei ihrer Anführerin, Luma Himmelsmutter. Mit vereinten Kräften werden wir Ahune vielleicht besiegen können, bevor es zu spät ist.]], },
    { [[Findet Luma Himmelsmutter in den Sklavenunterkünften.]], },
  },
  [11731] = {
    [[Fackelwurf]],
    { [[Ihr wollt also Feuerjongleur werden? Denkt Ihr, dass Ihr das Zeug dazu habt? Die Geschicklichkeit? Die Reflexe? ... den Mut? Wenn ja, dann wollen wir Euch mal auf die Probe stellen!]], [[Nehmt diese Fackeln und rennt zum Freudenfeuer. Dort befinden sich Kohlenpfannen, über denen blinkende Markierungen schweben, die Ihr mit einer Fackel treffen müsst. Seid schnell! Um für einen Wurf Punkte zu bekommen, müsst Ihr die Fackel werfen, bevor die Markierung verschwindet!]], [[Trefft genug markierte Kohlenpfannen, bevor die Zeit abgelaufen ist, und kommt dann hierher zurück... um Eure nächste Lektion zu erhalten.]], },
    { [[Gewinnt das Fackelwurfspiel und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
