  [11874] = {
    [[Upper Deck Promo - Reitrakete]],
  },
  [11875] = {
    [[Einen Vorteil gewinnen]],
    { [[The troops of the Shattered Sun Offensive will need every advantage they can get to beat back the allies of Kil'Jaeden. My colleagues in Stormwind have created oils which will strengthen the blades and armor of our troops.]], [[The key ingredient, nether residue, pervades Outland ]], },
    { [[Bringt dem Abgesandten Mordin in Shattrath 8 Netherrückstände.]], },
  },
  [11877] = {
    [[Angriffspläne des Sonnenzorns]],
    { [[Wir verstärken unsere Offensive auf Quel'Danas und haben herausgefunden, dass Kael'thas' Diener einen Gegenangriff planen. Er hofft, dass die Angriffe seiner im Nethersturm stationierten Blutelfen des Sonnenzorns unsere Aufmerksamkeit vom Sonnenbrunnen ablenken und uns dazu bringen werden, Truppen abzuziehen.]], [[Wir dürfen das nicht zulassen, <name>. An dieser Front wird jede Klinge benötigt.]], [[Reist in den Nethersturm und beschafft Euch ihre Angriffspläne. Jeder der Blutelfen des Sonnenzorns dort oben könnte die Pläne bei sich tragen. Viel Glück, <name>.]], },
    { [[Lord Torvos in Shattrath möchte, dass Ihr die Angriffspläne des Sonnenzorns beschafft.]], },
  },
  [11880] = {
    [[Die Multiphasen-Vermessung]],
    { [[Ich bin mir sicher, dass der 'gnomische Erfinder' ein Spinner ist. Er sprach von irgendwelchen dunklen Verzerrungen, die um Oshu'gun in den Geisterfeldern in Nagrand aufgetaucht sind.]], [[Laut seiner Aussage, soll diese, äh, spektrografische... öh... Multiphasen-Brille es dem Träger erlauben, die Verzerrungen zu sehen und Messungen an ihnen vorzunehmen. Er bestand darauf, dass es für unser Vorhaben am Sonnenbrunnen äußerst wichtig sei, diese Daten zu sammeln. Ich denke zwar, dass er einen Sprung in der Schüssel hat, jedoch wurde ich damit beauftragt, alles zu untersuchen, was vielleicht hilfreich sein könnte. Hättet Ihr etwas dagegen, Messungen durchzuführen?]], },
    { [[Herold Haronem in Shattrath möchte, dass Ihr die spektrografische Multiphasenbrille benutzt, um 6 Multiphasenmessungen durchzuführen.]], },
  },
  [11882] = {
    [[Spiel mit dem Feuer]],
    { [[Möchtet Ihr ein paar heiße neue Fertigkeiten erlernen?]], [[Ob Ihr jemals meine beeindruckende Fertigkeit erreichen werdet, sei zwar dahingestellt, aber ein oder zwei Kunststückchen werdet Ihr wahrscheinlich aufschnappen können, wenn Ihr <ein aufmerksamer Schüler/eine aufmerksame Schülerin> seid. Sprecht mit einem Feuerschluckmeister in einer der Hauptstädte. Sie sollten Euch weiterhelfen können.]], },
    { [[Sprecht mit einem Feuerschluckmeister in einer der Hauptstädte.]], },
  },
  [11883] = {
    [[Feuertanzen?]],
    { [[Ihr wollt die Feuertanzkunst erlernen?]], [[Nun, dann sprecht einfach mit einem Feuertanzmeister in einer der Hauptstädte. Sie sollten Euch weiterhelfen können.]], },
    { [[Sprecht mit einem Feuertanzmeister in einer der Hauptstädte.]], },
  },
  [11885] = {
    [[Feindesblut]],
    { [[Die Nachfahren, die von den Krallenpriestern von Skettis gefangen gehalten werden, sind alle in diesem Buch aufgeführt. Gezzarak die Jägerin, Karogg der Kristallriese, der Ketzer namens Dunkelkreischer Akkarai und Vakkiz der Windzürner. Sie alle müssen getötet werden, bevor Terokk in diese Welt zurückkehrt.]], [[Ihr werdet die Schriftrollen benötigen, die die zeitverlorenen Arakkoa bei sich tragen. Benutzt sie bei den Beschwörungskreisen, die es in Skettis gibt. Eure Gegner sind mächtige Wesen; Ihr tätet gut daran, ein paar Freunde mitzunehmen.]], },
    { [[Findet die Schädelhaufen innerhalb der Beschwörungskreise in Skettis. Beschwört die Nachfahren, indem Ihr 10 zeitverlorene Schriftrollen am Schädelhaufen benutzt und bezwingt sie. Kehrt mit einem Beweisstück von jedem zu Hakkiz am Schattenwindlager zurück.]], },
  },
  [11886] = {
    [[Ungewöhnliche Aktivitäten]],
    { [[Spaß und Frivolität müssen sein, <class>, doch einige von uns haben sich ernsteren Angelegenheiten zu widmen.]], [[Zu Beginn des Festes erhielten wir Kunde von unseren Anhängern nahe des Zoramstrands über stark erhöhte Aktivitäten des Schattenhammerkults. Die Macht, die diese Wahnsinnigen verehren, ist tief in der Entstehungsgeschichte dieser Festtage verwurzelt. Dies kann kein Zufall sein. Begebt Euch zum Lager in den Ruinen südlich des Eingangs zur Tiefschwarzen Grotte und findet heraus, ob die Kultisten Informationen liefern können - wendet Gewalt an, falls nötig. Platziert dieses Leuchtfeuer auf dem Boden, sobald Ihr etwas gefunden habt, um einen Führer des Irdenen Rings zu beschwören.]], },
    { [[Geht zum Lager des Schattenhammers südlich des Eingangs zur Tiefschwarzen Grotte am Zoramstrand im Eschental und sucht nach Briefen des Schattenhammers. Die Kultisten werden sie voraussichtlich bei sich tragen. Benutzt anschließend das totemische Leuchtfeuer, um einen Führer des Irdenen Rings zu beschwören.]], },
  },
  [11891] = {
    [[Eine unschuldige Verkleidung]],
    { [[Dieser Information zufolge scheint es, als hätten die Kultisten ein weiteres Lager nordwestlich der großen Ansammlung auf dem Zoramstrand und als sprächen sie mit... Gästen?]], [[Man könnte Vieles in Erfahrung bringen, würde man das Geschehen verfolgen, <race>. Nehmt diese Kugel - sie wird Euch wie einer der Kriecher aussehen lassen, die am örtlichen Strand vorkommen. Belauscht das Gespräch mit diesen "Gästen" der Kultisten im nordwestlich gelegenen Lager und erstattet mir Bericht, sobald Ihr das getan habt.]], },
    { [[Benutzt die Krabbenkugel und spioniert das neue Lager des Schattenhammers aus. Es befindet sich westlich des Eingangs zur Tiefschwarzen Grotte auf dem Zoramstrand im Eschental. Benutzt anschließend das totemische Leuchtfeuer, um den Führer des Irdenen Rings zu beschwören.]], },
  },
  [11915] = {
    [[Spiel mit dem Feuer]],
    { [[Möchtet Ihr ein paar heiße neue Fertigkeiten erlernen?]], [[Ob Ihr jemals meine beeindruckende Fertigkeit erreichen werdet, sei zwar dahingestellt, aber ein oder zwei Kunststückchen werdet Ihr wahrscheinlich aufschnappen können, wenn Ihr <ein aufmerksamer Schüler/eine aufmerksame Schülerin> seid. Sprecht mit einem Feuerschluckmeister in einer der Hauptstädte. Sie sollten Euch weiterhelfen können.]], },
    { [[Sprecht mit einem Feuerschluckmeister in einer der Hauptstädte.]], },
  },
  [11917] = {
    [[Zurückschlagen]],
    { [[Wir müssen etwas unternehmen und das Gleichgewicht aufrechterhalten! Uns läuft die Zeit davon.]], [[Kehrt zum Lager am Zoramstrand zurück – dorthin, wo die großen Eisbrocken liegen. Sie nehmen mit ihrer Hilfe Verbindung mit den Elementaren auf – genau so, wie sie es in Silithus machen. Ich sage das nur ungern, doch die Geister werden Verständnis haben – Ihr müsst einen von Ahunes Leutnants beschwören und ihn anschließend zerstören. Womöglich wird diese präventive Maßnahme genügen...]], },
    { [[Beschwört mithilfe des Eisbrockens auf dem Zoramstrand im Eschental den Leutnant der Frostwelle, zerstört ihn und kehrt anschließend zum Urahnen des Irdenen Rings zurück. Letzterer befindet sich in einer der Hauptstädte.]], },
  },
  [11921] = {
    [[Noch einmal Fackeln werfen]],
    { [[Seid gegrüßt, Feuerjongleur! Macht es Euch Spaß, wenn Ihr Flammen wild umherwirbeln lasst? Das hört man doch gerne! Nun, haltet Ihr Euch für einen Experten in Sachen Fackelwürfe? Dann will ich Euch auf die Probe stellen...]], [[Ihr kennt die Regeln: Werft die Fackeln auf die markierten Kohlenpfannen. Doch dieses Mal habt Ihr ein wenig mehr Zeit und VIEL mehr Kohlenpfannen zu treffen.]], [[Bereit?]], },
    { [[Gewinnt das Fackelwurfspiel und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11922] = {
    [[Fackelwurf]],
    { [[Ihr wollt also Feuerjongleur werden? Denkt Ihr, dass Ihr das Zeug dazu habt? Die Geschicklichkeit? Die Reflexe? ... den Mut? Wenn ja, dann wollen wir Euch mal auf die Probe stellen!]], [[Nehmt diese Fackeln und rennt zum Freudenfeuer. Dort befinden sich Kohlenpfannen, über denen blinkende Markierungen schweben, die Ihr mit einer Fackel treffen müsst. Seid schnell! Um für einen Wurf Punkte zu bekommen, müsst Ihr die Fackel werfen, bevor die Markierung verschwindet!]], [[Trefft genug markierte Kohlenpfannen, bevor die Zeit abgelaufen ist, und kommt dann hierher zurück... um Eure nächste Lektion zu erhalten.]], },
    { [[Gewinnt das Fackelwurfspiel und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11923] = {
    [[Fackeln fangen]],
    { [[Da Ihr ja nun eine Fackel werfen könnt, lasst uns doch einmal sehen, ob Ihr auch eine fangen könnt!]], [[Nehmt diesen Stapel unangezündete Fackeln, geht zum Freudenfeuer und werft eine davon hoch in die Luft! Fangt sie wieder auf, bevor sie auf den Boden fällt... und werft sie wieder in die Höhe! Fangt vier nacheinander und kommt wieder zu mir zurück.]], [[Seid vorsichtig! Die Fackeln sind gefährlich, wenn sie auf dem Boden aufkommen! Und fangt sie alle auf! Wenn eine davon auf den Boden fällt, müsst Ihr zurück zum Freudenfeuer und von vorn anfangen. Viel Glück.]], },
    { [[Fangt nacheinander 4 Fackeln und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11924] = {
    [[Noch einmal Fackeln fangen]],
    { [[Fackeln zu fangen, hält einen auf Zack, daran besteht kein Zweifel. Denkt Ihr, dass Ihr es lange durchhalten werdet?]], [[Na dann lasst uns mal sehen...]], },
    { [[Fangt nacheinander 10 Fackeln und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11925] = {
    [[Noch einmal Fackeln fangen]],
    { [[Fackeln zu fangen, hält einen auf Zack, daran besteht kein Zweifel. Denkt Ihr, dass Ihr es lange durchhalten werdet?]], [[Na dann lasst uns mal sehen...]], },
    { [[Fangt nacheinander 10 Fackeln und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11926] = {
    [[Noch einmal Fackeln werfen]],
    { [[Hello, fire juggler!  Are you having fun and making fiery mayhem?  That's what I like to hear!  Now, do you think you're an expert at torch tossing?  Then I have a test for you...]], [[You know the rules: toss torches at the marked braziers.  This time, however, you have a little more time and a LOT more braizers to hit.]], [[Ready?]], },
    { [[Gewinnt das Fackelwurfspiel und sprecht anschließend mit dem Feuerschluckmeister.]], },
  },
  [11933] = {
    [[Raubt die Flamme der Exodar]],
    { [[Die Flamme, die aus dem Innersten der Exodar entwendet wurde, durchflutet Euch mit Wärme. Diese durchdringende Macht ist Euch fremd, aber der Geschichtenerzähler könnte vielleicht mehr darüber wissen...]], },
    { [[Bringt die Flamme der Exodar zum Geschichtenerzähler.]], },
  },
  [11935] = {
    [[Raubt die Flamme von Silbermond]],
    { [[Diese Flamme, die aus Silbermond entwendet wurde, durchflutet Euch mit Wärme. Diese durchdringende Macht ist Euch fremd, aber der Meister der Lehren könnte vielleicht mehr darüber wissen...]], },
    { [[Bringt die Flamme von Silbermond zum Meister der Lehren.]], },
  },
  [11937] = {
    [[FLAG - all torch return quests are complete]],
  },
  [11947] = {
    [[Zurückschlagen]],
    { [[Wir müssen etwas unternehmen und das Gleichgewicht aufrechterhalten! Uns läuft die Zeit davon.]], [[Im Schatten des Ethel Rethor im Nordwesten von Desolace befindet sich ein Lager. Dort haben sie Eisbrocken, mit denen sie Verbindung mit den Elementaren aufnehmen – genau so, wie sie es in Silithus machen. Ich sage das nur ungern, doch die Geister werden Verständnis haben – Ihr müsst einen von Ahunes Leutnants beschwören und ihn anschließend zerstören. Womöglich wird diese präventive Maßnahme genügen...]], },
    { [[Beschwört mithilfe des Eisbrockens am Ethel Rethor in Desolace den Leutnant des Eishagels, zerstört ihn und kehrt anschließend zum Urahnen des Irdenen Rings zurück. Letzterer befindet sich in einer der Hauptstädte.]], },
  },
  [11948] = {
    [[Zurückschlagen]],
    { [[Wir müssen etwas unternehmen und das Gleichgewicht aufrechterhalten! Uns läuft die Zeit davon.]], [[Neptulon hat schon seit längerem eine Operationsbasis auf einer Insel im Westen des Schlingendorntals. Dort hat der Schattenhammerkult Eisbrocken aufgestellt, mit deren Hilfe sie Verbindung mit dem Reich des Gezeitenjägers aufnehmen – genau so, wie sie es in Silithus machen. Ich sage das nur ungern, doch die Geister werden Verständnis haben – Ihr müsst einen von Ahunes Leutnants beschwören und ihn anschließend zerstören. Womöglich wird diese präventive Maßnahme genügen...]], },
    { [[Beschwört mithilfe des Eisbrockens auf der Insel der Wasserelementare im Westen des Schlingendorntals den Leutnant des Eiswinds, zerstört ihn und kehrt anschließend zum Urahnen des Irdenen Rings zurück. Letzterer befindet sich in einer der Hauptstädte.]], },
  },
  [11952] = {
    [[Zurückschlagen]],
    { [[Wir müssen etwas unternehmen und das Gleichgewicht aufrechterhalten! Uns läuft die Zeit davon.]], [[Der Feuerwachtgrat in der Sengenden Schlucht ist eine bekannte Bastion des Schattenhammerkults. Vor Kurzem haben sie damit begonnen, mithilfe der Eisbrocken in der Höhle Verbindung mit dem Reich des Gezeitenjägers aufzunehmen – genau so, wie sie es in Silithus machen. Ich sträube mich zwar sehr dagegen, doch die Geister werden Verständnis haben – Ihr müsst einen von Ahunes Leutnants beschwören und ihn anschließend zerstören. Womöglich wird diese präventive Maßnahme genügen...]], },
    { [[Beschwört mithilfe des Eisbrockens in der Höhle unter dem Feuerwachtgrat im Nordwesten der Sengenden Schlucht den Leutnant des Eises, zerstört ihn und kehrt anschließend zum Urahnen des Irdenen Rings zurück. Letzterer befindet sich in einer der Hauptstädte.]], },
  },
  [11953] = {
    [[Zurückschlagen]],
    { [[Wir müssen etwas unternehmen und das Gleichgewicht aufrechterhalten! Uns läuft die Zeit davon.]], [[Silithus ist der Hauptsitz des Schattenhammerkults auf Azeroth. Im Norden haben sie damit begonnen, mithilfe neuer Eisbrocken Verbindung mit dem Reich des Gezeitenjägers aufzunehmen. Ich sage das nur ungern, doch die Geister werden Verständnis haben - Ihr müsst einen von Ahunes Leutnants beschwören und ihn anschließend zerstören. Womöglich wird diese präventive Maßnahme genügen...]], },
    { [[Beschwört mithilfe des Eisbrockens in den Kavernen des Schattenhammers im Norden von Silithus den Leutnant des Gletschers, zerstört ihn und kehrt anschließend zum Urahnen des Irdenen Rings zurück. Letzterer befindet sich in einer der Hauptstädte.]], },
  },
  [11954] = {
    [[Zurückschlagen]],
    { [[Wir müssen etwas unternehmen und das Gleichgewicht aufrechterhalten! Uns läuft die Zeit davon.]], [[Um Ahunes Gelingen zu garantieren, hat der Kult neben dem Dunklen Portal in Draenor ein Basislager errichtet. Sie verwenden Eisbrocken, um die Ankunft des Frostfürsten zu koordinieren. Ich sage das nur ungern, doch die Geister werden Verständnis haben - Ihr müsst einen von Ahunes Leutnants beschwören und ihn anschließend zerstören. Womöglich wird diese präventive Maßnahme genügen...]], },
    { [[Beschwört mithilfe des Eisbrockens in der Nähe des Dunklen Portals auf der Höllenfeuerhalbinsel den eisigen Templer, zerstört ihn und kehrt anschließend zu einem der Urahnen des Irdenen Rings zurück. Letztere befinden sich in den Hauptstädten.]], },
  },
  [11955] = {
    [[Ahune der Frostfürst]],
    { [[Ich glaube, dass Ihr stark genug seid, um Euch der ultimativen Aufgabe zu stellen, die uns noch bevorsteht.]], [[Wir dürfen nicht zulassen, dass das eintrifft, was der Schattenhammer und Neptulons Nagaketzer vorhaben. Im Namen Azeroths und allen Lebens auf der Welt: Wir müssen handeln. Uns ist klar, was getan werden muss. Ihr müsst in die Sklavenunterkünfte tief im Inneren des Echsenkessels reisen und Euch dort mit unseren Schamanen treffen. Geht - und macht schnell!]], },
    { [[Begebt Euch in die Sklavenunterkünfte im Echsenkessel in den Zangarmarschen und sprecht mit Numa Wolkenschwester.]], },
  },
  [11964] = {
    [[Rauchwerk für die Sommerflämmlinge]],
    { [[Habt Ihr gewusst, dass unsere Feuer des Sonnenwendfestes in jedem Land brennen, in dem auch eine Siedlung der Allianz existiert? Sie befinden sich in den Lagern außerhalb der Städte und werden von Flammenwächtern gepflegt. Die Wächter kümmern sich darum, dass die Freudenfeuer hell brennen und die Feierlichkeiten weitergehen.]], [[Jeder Flammenwächter hat einen Sommerflämmling bei sich - einen Abgesandten der Geister des Feuers.]], [[Möchtet Ihr einem Sommerflämmling Eure Ehrerbietung erweisen, <name>? Dann nehmt dieses Rauchwerk des Sommers und bringt es einem von ihnen. Besänftigt den kleinen Feuergeist und er könnte sich dafür erkenntlich zeigen.]], },
    { [[Der Meister der Lehren möchte, dass Ihr einem Sommerflämmling Rauchwerk des Sommers bringt. Die Flämmlinge befinden sich außerhalb jeder Siedlung der Allianz.]], },
  },
  [11966] = {
    [[Rauchwerk für die Festflämmlinge]],
    { [[Habt Ihr gewusst, dass unsere Feuer des Sonnenwendfestes in jedem Land brennen, in dem auch eine Siedlung der Horde existiert? Sie befinden sich in den Lagern außerhalb der Städte und werden von Flammenbewahrern gepflegt. Die Bewahrer kümmern sich darum, dass die Freudenfeuer hell brennen und die Feierlichkeiten weitergehen.]], [[Jeder Flammenbewahrer hat einen Festflämmling bei sich - einen Abgesandten der Geister des Feuers.]], [[Möchtet Ihr einem Festflämmling Eure Ehrerbietung erweisen, <name>? Dann nehmt dieses Rauchwerk des Sommers und bringt es einem von ihnen. Besänftigt den kleinen Feuergeist und er könnte sich dafür erkenntlich zeigen.]], },
    { [[Der Geschichtenerzähler möchte, dass Ihr einem Festflämmling Rauchwerk des Sommers bringt. Die Flämmlinge befinden sich außerhalb jeder Siedlung der Horde.]], },
  },
  [11970] = {
    [[Der Meister der Sommerlehre]],
    { [[Unser Fest soll eine Zeit der Freude und des Lachens sein, so viel ist sicher. Es ist jedoch wichtig, dass Ihr den Ursprung dieser Feiertage kennt und versteht. Bildung will ausgesucht sein und gewürdigt werden.]], [[Der Meister der Lehren in den Hauptstädten wird Euch den Weg weisen können. Geht jetzt und lernt.]], },
    { [[Sprecht mit einem Meister der Lehren in einer der Hauptstädte.]], },
  },
  [11971] = {
    [[Der Erzähler der Sommergeschichten]],
    { [[Unser Fest soll eine Zeit der Freude und des Lachens sein, so viel ist sicher. Es ist jedoch wichtig, dass Ihr den Ursprung dieser Feiertage kennt und versteht. Bildung will ausgesucht sein und gewürdigt werden.]], [[Der Geschichtenerzähler in den Hauptstädten wird Euch den Weg weisen können. Geht jetzt und lernt.]], },
    { [[Sprecht mit einem Geschichtenerzähler in einer der Hauptstädte.]], },
  },
  [11972] = {
    [[Splitter von Ahune]],
    { [[In diesen Splittern pulsiert ein Unheil verkündendes Licht. Halten sie die letzte Essenz des Frostfürsten in ihrem Inneren verborgen?]], },
    { [[Bringt die Eissplitter zu Luma Himmelsmutter.]], },
  },
  [11975] = {
    [[Nun, wenn ich einmal groß bin...]],
    { [[Vielleicht habe ich Euch das schon gefragt, aber als Ihr so alt wart wie ich, wusstet Ihr dann, dass Ihr eines Tages, wenn Ihr groß seid, <ein/eine><class> <class> sein würdet?]], [[Ich weiß nicht, was ich einmal werden will. Vielleicht eine Magierin oder eine Hexenmeisterin oder ein Blutritter, wie ich davor schon mal gesagt hab? Oder vielleicht... ein Rockstar!! Wie Sig Nicious, der Blutelf, bei den 'Elite Tauren Chieftain'!]], [[Habe gehört, dass wenn die nicht gerade auf Tour sind, dann hängen sie in Silbermond ab! Könntet Ihr mich dorthin bringen? Sie sitzen auf einem Balkon in der Straße der Urahnen herum und warten darauf, dass Leute vorbeikommen.]], [[Bitte bringt mich dorthin. Bitte!]], },
    { [[Geht mit Eurem Waisenkind Salandria zur Straße der Urahnen in Silbermond, um die Elite Tauren Chieftain zu sehen. Ruft sie zu Euch, falls sie nicht bei Euch ist, wenn Ihr ankommt.]], },
  },
  [11976] = {
    [[Eissplitter]],
    { [[In diesen Splittern pulsiert ein Unheil verkündendes Licht. Halten sie die letzte Essenz des Frostfürsten in ihrem Inneren verborgen?]], },
    { [[Bringt die Eissplitter zu Luma Himmelsmutter.]], },
  },
  [12012] = {
    [[Den Urahnen benachrichtigen]],
    { [[Hiervon muss der gesamte Irdene Ring erfahren. Ich bin mir nicht sicher, wie es weitergehen soll, doch der Urahne, von dem Ihr überhaupt erst diese Aufgabe bekommen habt, könnte es wissen. Ich bitte Euch, kehrt zum Urahnen zurück und findet heraus, wie es weitergehen soll - wir müssen etwas unternehmen!]], },
    { [[Travel to a capital city and return the Totemic Beacon to the Earthen Ring Elder.]], },
  },
  [12020] = {
    [[Dieses eine Mal, als ich betrunken war...]],
    { [[Die Dunkeleisenzwerge haben einstecken müssen! Als sie zurück zum Grimmigen Säufer rannten, fiel ein Teil ihrer Ladung herunter. Was jedoch noch viel wichtiger ist: Dank Euch ist das Lager außer Gefahr... zumindest für heute.]], [[Geht zu Boxi und erzählt ihm von Eurem heldenhaften Sieg über die Dunkeleisenzwerge.]], },
    { [[Sprecht mit Boxi Bolzendreher im Braufestlager.]], },
  },
  [12022] = {
    [[Haut weg das Zeug!]],
    { [[He da! Ihr seht aus, als könntet Ihr gut mit Bier umgehen. Doch wie gut könnt Ihr mit Eurem Trinkkrug umgehen?]], [[Die Dunkeleisenzwerge sind zurückgekommen, um unsere Feier zu ruinieren, und das nicht zu wenig. Sie mit Krügen zu bewerfen ist die beste Möglichkeit, sie aufzuhalten. Doch erst einmal wollen wir schauen, ob Ihr einen auf die alte A.L.K. hier werfen könnt.]], [[Es ist ganz leicht, nehmt einen Freibierkrug und trinkt. Geht dabei nur sicher, dass Ihr A.L.K. anseht. Sprecht mich wieder an, wenn Ihr sie fünfmal getroffen habt.]], },
    { [[Benutzt den Freibierkrug, um A.L.K. fünfmal zu treffen, und sprecht dann mit Boxey Bolzdreher.]], },
  },
  [12062] = {
    [[Beleidigt Coren Düsterbräu]],
    { [[Coren Düsterbräu ist im nächsten Raum und gerade dabei, Dunkeleisenzwerge zu einem weiteren Angriff auf das Braufest aufzustacheln. Macht ihn nieder!]], [[Geht zu ihm hin und provoziert einen Streit. Sagt ihm, dass nicht einmal Säue sein Bier saufen würden.]], [[Macht Euch auf einen Kampf gefasst, wenn Ihr das sagt - das letzte Mal, als jemand über sein Gebräu herzog... warf man ihn Schreckensebern zum Fraß vor.]], },
    { [[Geht und fangt Streit mit Coren an.]], },
  },
  [12191] = {
    [[Haut weg das Zeug!]],
    { [[Those pesky Dark Irons are back! We need to make sure that you can defend yourself if needed. More importantly, we need to know that you can defend our property!]], [[S.T.O.U.T. here is ready for action, are you? Drink from the Complimentary Brewfest Samplers then throw the mug at S.T.O.U.T.! Hit the target 5 times then talk to me.]], },
    { [[Trefft A.L.K fünfmal mit dem Krug alkoholfreies Braufestbier und sprecht dann mit Bissel Schnellhub.]], },
  },
  [12193] = {
    [[Sagt mal, es gibt dieses Jahr nicht zufällig ein Souvenir?]],
    { [[Beim Braufest geht es nicht nur um Widderrennen, sondern auch ums Trinken! Solltet Ihr einen Moment Zeit haben, dann bringt diesen Gutschein zu Belbi Blitzknips. Sie hält sich irgendwo im hinteren Teil des Braufestlagers auf. Falls Ihr sie nicht sehen könnt, keine Sorge, sie wird Euch mit Sicherheit entdecken.]], [[Naja, bringt Ihr auf jeden Fall diesen Gutschein für Euren kostenlosen Braufestkrug. Sie kümmert sich außerdem um alle sonstigen Braufestgeschäfte.]], },
    { [[Löst Euren Gutschein für einen Braufestkrug bei Belbi Blitzknips im Braufestlager ein.]], },
  },
  [12194] = {
    [[Sagt mal, es gibt dieses Jahr nicht zufällig ein Souvenir?]],
    { [[Wir versorgen nicht nur jedermann mit Gebräu, sondern wir haben auch Krüge zu vergeben! Wunderschöne Andenken, die auf die legalste Weise überhaupt erworben wurden! Hier habt Ihr einen Gutschein.]], [[Sprecht mit Blix, er hält sich irgendwo hinten beim Braufestlager auf. Er ist ziemlich seltsam, vor allem weil er jetzt immer diese Brille trägt. Er wird Euren Gutschein entgegennehmen und kümmert sich auch sonst um alle Braufestgeschäfte.]], },
