  [7667] = {
    [[Material Assistance]],
    { [[<name>, there is a pressing matter I hope you can help with... both materially and spiritually.  Allow me to explain.]], [[Within the depths of the Scholomance in Lordaeron is a chamber called the Great Ossuary.  I need to complete a device that would pierce the shadows of this horrid room.  To finish what I have started, I need two diamonds: an Azerothian and a Pristine Black.  These are not trivial items, I know - I just don't have the resources myself.]], [[Could you provide me with one of each?]], },
    { [[If you are interested in helping Sagorne Crestrider in Orgrimmar's Valley of Wisdom, bring him an Azerothian Diamond and a Pristine Black Diamond.]], },
  },
  [7668] = {
    [[The Darkreaver Menace]],
    { [[Death Knight Darkreaver lords over the Great Ossuary in the Scholomance.  He threatens to twist the spirit realm to do his own bidding, and he must be stopped!  This is where the scryer you helped make comes into play.]], [[Use the divination scryer in the heart of the Great Ossuary's basement to call forth Darkreaver's spirit minions.  Defeat the spirits and the death knight himself will appear.  <name>, bring me his head!  Only his demise will reverse the damage dome to the spirit realm!]], },
    { [[Use the Divination Scryer in the heart of the Great Ossuary's basement in the Scholomance.  Doing so will bring forth spirits you must fight.  Defeating these spirits will summon forth Death Knight Darkreaver; defeat him.]], [[Bring Darkreaver's Head to Sagorne Creststrider in the Valley of Wisdom, Orgrimmar.]], },
  },
  [7669] = {
    [[Again Into the Great Ossuary]],
