  [8114] = {
    [[Control Four Bases]],
    { [[<name>, you have taken the battle of Arathi Basin to our enemies and earned great honors in that field of battle.  You have shown us your worth as a <class>, and you have shown our enemies that we will not submit to their villainy!]], [[Because of your proven value to us, I have a difficult task for you.  I want you to take and hold at least four of the bases in Arathi Basin.  If you can do that, then we will not only secure critical resources, we will show the Horde our dominance!]], [[Go, <name>, and good fortune.]], },
    { [[Enter Arathi Basin, capture and control four bases at the same time, and then return to Field Marshal Oslight at Refuge Pointe.]], },
  },
  [8115] = {
    [[Control Five Bases]],
    { [[<name>, battling for the resources of Arathi Basin is a bloody undertaking.  Your services to the League of Arathor are as needed as they are appreciated.]], [[And we have one more task for you.]], [[The League wishes to make one strong push against the Defilers' occupation of Arathi Basin.  We want them driven out of every strategic point on the battlefield!]], [[It will not be easy, but if anyone can get it done, it is you.]], },
    { [[Control 5 bases in Arathi Basin at the same time, then return to Field Marshal Oslight at Refuge Pointe.]], },
  },
  [8116] = {
