  [8120] = {
    [[The Battle for Arathi Basin!]],
    { [[As you no doubt have heard, the League of Arathor is sending large numbers of troops into Arathi Basin.  Eager for food and supplies, these Alliance fools are intent to take and control the rich resources there.  We must show them that Arathi will never again be a home for humans!]], [[Go to Arathi Basin and assault the mine, the lumber mill, the blacksmith and the stables.  Pull down the enemies' banners, declaring those territories for the Horde.]], [[Go, <name>.  Report back to me when this task is complete.]], },
    { [[Assault the Arathi Basin mine, lumber mill, blacksmith and stable, and then return to Deathmaster Dwire in Hammerfall.]], },
  },
  [8121] = {
    [[Take Four Bases]],
    { [[<name>, as the battles over Arathi Basin rage, news of your actions have reached many ears.  You are highly regarded among us, which gives me confidence that you will accomplish the mission I now have for you.]], [[It is time to send a message to the League of Arathor.  We must show them that their holdings in Arathi Basin are tenuous and quickly lost.  We will show them this by holding, all at once, four strategic points in Arathi Basin.]], [[Succeed in this, <name>, and earn great respect among the Defilers.]], },
    { [[Hold four bases at the same time in Arathi Basin, and then return to Deathmaster Dwire in Hammerfall.]], },
  },
  [8122] = {
    [[Take Five Bases]],
    { [[It is difficult to see, so deep we are in the conflict, but our spies report a weakening in the League of Arathor's resolve.  We must use this window and strike a brutal blow against them!  Return to Arathi Basin and throw yourself against our foes.  Curse them and kill them and control all five strategic bases!  Send their soldiers screaming and bleeding from the front lines, with stories of your cruelty on their mewling lips.]], [[Do this, and score a great victory for the Defilers.]], },
    { [[Hold five bases in Arathi Basin at the same time, then return to Deathmaster Dwire in Hammerfall.]], },
  },
  [8123] = {
    [[Cut Arathor Supply Lines]],
    { [[The League of Arathor is sparing no expense in their move to retake Arathi.  They, and all the races of the Alliance, now stream to this distant region, intent on expanding their hold from Refuge Pointe.  We must stop them, and the best means to stop them is to take their supplies.]], [[You can help us, <name>.  Enter Arathi Basin, known for its rich mines, fertile land and skilled craftsman.  Capture and control every base you can, win the battle, and return to me with the resources you gain.]], },
    { [[Win the battle for Arathi Basin, get an Arathi Resource Crate, and return it to Deathstalker Mortis at Hammerfall.]], },
  },
  [8124] = {
    [[More Resource Crates]],
    { [[The League of Arathor is sparing no expense in their move to retake Arathi. They, and all the races of the Alliance, now stream to this distant region, intent on expanding their hold from Refuge Pointe. We must stop them, and the best means to stop them is to take their supplies.]], [[You can help us, <name>. Enter Arathi Basin, known for its rich mines, fertile land and skilled craftsman. Capture and control every base you can, win the battle, and return to me with the resources you gain.]], },
