  [8181] = {
    [[Confront Yeh'kinya]],
    { [[My delvings into the Mosh'aru Tablets bear grim news, <name>.  Hakkar's true form, the god himself, can be born again through the very egg into which you captured him.  Yeh'kinya told you it was to contain his evil, but it is to recreate it!  The troll has led you astray!]], [[Go to Yeh'kinya and demand the egg from him.  Pray that it is not too late to stop Hakkar's return!]], },
    { [[Speak with Yeh'kinya.]], },
  },
  [8182] = {
    [[The Hand of Rastakhan]],
    { [[<name>, our worst fears have come.  Legend speaks of the Soulflayer ruling the ancient troll world through evil and terror, and his power was absolute.  If he cannot be stopped now, then his new reign may spread across all of Azeroth!]], [[There is a contingent of Zandalarian trolls who may help us stem this tide of darkness.  They have camped on Yojamba Isle, off the coast of Stranglethorn, west of the ruins of Zul'Kunda.  Speak with their leader, Molthor, and pray he knows how to defeat Hakkar.]], },
    { [[Speak with Molthor in Stranglethorn.]], },
  },
  [8183] = {
    [[The Heart of Hakkar]],
    { [[The Heart of Hakkar burns from within.  Although the Soulflayer is defeated, the kernel of his power yet remains in this cursed organ.]], },
    { [[Bring the Heart of Hakkar to Molthor on Yojamba Isle.]], },
  },
  [8184] = {
    [[Presence of Might]],
    { [[Zanza can create the ancient troll enchantments with the right components.]], [[The first is a primal Hakkari idol that must be taken from the most powerful of the Hakkari lieutenants: Jin'do and the Bloodlord.]], [[The second is a punctured voodoo doll. These can be found in the piles of refuse that litter]], [[this foul place. Look inside the jinxed hoodoo. Bring me these things and I will create a powerful enchantment for you!]], },
    { [[Bring me thise things and I will create a powerful enchantment for you!]], [[Primal Hakkari Idol]], [[Punctured Voodoo Doll]], },
