  [8230] = {
    [[Collin's Test Quest]],
  },
  [8231] = {
    [[Wavethrashing]],
    { [[The wavethrasher is an amphibious beast living only here in Azshara.  They are powerful and resilient.]], [[The tough, reflective scales from a wavethrasher mirror the strength of the hunter.]], [[Track and hunt wavethrashers along the coast of Azshara, and bring me their scales.]], },
    { [[Bring 6 Wavethrasher Scales to Ogtinc in Azshara.  Ogtinc resides atop the cliffs northeast the Ruins of Eldarath.]], },
  },
  [8232] = {
    [[The Green Drake]],
    { [[Now I set you on a dangerous hunt to slay the green drake, Morphaz, and bring his tooth to me.  Morphaz dwells deep in Temple of Atal'Hakkar, the entrance to which lies in Swamp of Sorrows.]], [[If you wish to defeat him, you must not go alone.  This drake is an enemy of many]], },
    { [[Bring the Tooth of Morphaz to Ogtinc in Azshara.  Ogtinc resides atop the cliffs northeast the Ruins of Eldarath.]], },
  },
  [8233] = {
    [[A Simple Request]],
    { [[Lord Jorach Ravenholdt has requested your presence.  The matter is secret, and I cannot say any more.]], [[Ravenholdt Manor is located in the Hillsbrad Foothills]], },
    { [[Speak with Lord Jorach Ravenholdt in Ravenholdt Manor.]], },
  },
  [8234] = {
    [[Sealed Azure Bag]],
    { [[First things first.  What I've lost is a small blue bag, the contents of which are a private matter.]], [[Don't bother going after the thieves, because they are already dead.  What we do know is the fools couldn't get the bag open. so they sold it to some timbermaw shaman in Azshara as a holy relic.  Clever.]], [[I need you to travel to Azshara and retrieve it from the shaman through subtlety... or force.  Better luck with the first method, I'd wager.  Once acquired, take it to Archmage Xylem.]], },
    { [[Retrieve the Sealed Azure Bag from the Timbermaw Shaman in Azshara.  Then take the bag to Archmage Xylem, also found in Azshara.]], },
  },
  [8235] = {
    [[Encoded Fragments]],
    { [[Lord Ravenholdt has asked a favor of us both.  He wishes to remove the enchantment from this bag.]], [[Unfortunately, some of my books on the subject of dispelling were taken recently, only to be destroyed by savages.  The remains of my books are still valuable to me.]], [[If you can bring me enough of the encoded fragments, I can piece them back together.  Your best chance is to kill the beasts of Azshara, since they have a tendency to pick up just about anything.]], },
    { [[Bring 10 Encoded Fragments to Archmage Xylem in Azshara.]], },
  },
  [8236] = {
    [[The Azure Key]],
    { [[It will take some time to decipher the fragments and remove the enchantment.  When I am complete, I'll deliver the bag to Lord Ravenholdt personally.]], [[There is still the matter of the key, said to be held by a green drake known as Morphaz.  The drake has made its home deep in the Temple of Atal'Hakkar, the entrance to which lies in the Swamp of Sorrows.]], [[Gather your friends and venture to his lair.  Lord Ravenholdt will await your return with the key.]], },
    { [[Return the Azure Key to Lord Jorach Ravenholdt.]], },
  },
  [8238] = {
    [[Gurubashi, Vilebranch, and Witherbark Coins]],
    { [[I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul'Gurub, and each is imbued with subtle but powerful mojo.]], [[There are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin'wosho the Trader; he offers our heroes special items for them.]], },
  },
  [8239] = {
    [[Sandfury, Skullsplitter, and Bloodscalp Coins]],
    { [[I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul'Gurub, and each is imbued with subtle but powerful mojo.]], [[There are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin'wosho the Trader; he offers our heroes special items for them.]], },
  },
  [8240] = {
    [[A Bijou for Zanza]],
    { [[The destruction of the various Hakkari Bijous is of grave importance to us, so much so that we welcome outsiders of the tribe to do so for themselves!]], [[Take any one of the bijous that you find in Zul'Gurub to the Altar of Zanza and destroy it.  For this one time alone, I will give you an additional Honor Token from our tribe; this is above and beyond what you would normally receive.]], [[Witness for yourself the blessings of Zanza!]], },
    { [[Destroy any one of the Hakkari Bijous found in Zul'Gurub at the Altar of Zanza on Yojamba Isle.  When done, speak with Vinchaxa nearby.]], },
  },
  [8241] = {
    [[Restoring Fiery Flux Supplies via Iron]],
  },
  [8242] = {
    [[Restoring Fiery Flux Supplies via Heavy Leather]],
  },
  [8243] = {
    [[Zanza's Potent Potables]],
  },
  [8246] = {
    [[Signets of the Zandalar]],
  },
  [8249] = {
    [[Junkboxes Needed]],
  },
  [8250] = {
    [[Magecraft]],
    { [[Archmage Xylem has been asking around lately... for someone of your experience.]], [[If you wish to seek him out, you can find him in Azshara.  His tower is built high atop the cliffs, and the only way up is a teleport spell invoked by his arrogant servant, Sanath Lim-yo.]], [[Sanath makes camp below his master's tower.  To find him, travel to where Azshara's main road forks at the entrance to the Ruins of Eldarath.  From that spot, head directly north until you reach the cliffs.]], },
    { [[Seek out Sanath Lim-yo and gain passage to see Archmage Xylem.]], },
  },
  [8251] = {
    [[Magic Dust]],
    { [[The blood elves in Azshara have developed a method for pulverizing the magical items they acquire.  This glittering dust they produce differs from the components normally acquired by a disenchantment spell.]], [[When I sent my apprentice to trade for the dust, he was killed without hesitation.  For that, they must pay.]], [[The time for talk is over, <name>.  Find the blood elves along the cliffs of Azshara and use more "persuasive" methods to acquire their glittering dust.]], },
    { [[Bring 10 Glittering Dust to Archmage Xylem.]], },
  },
  [8252] = {
    [[The Siren's Coral]],
    { [[Take a look around you.  The ruins and beaches are overrun with spitelash.  Recently, the wretched creatures have learned to recognize the rare components indigenous to Azshara.]], [[Specifically, I speak of enchanted coral]], },
    { [[Collect 6 Enchanted Coral and return them to Archmage Xylem.]], },
  },
  [8253] = {
    [[Destroy Morphaz]],
    { [[There is a green drake deep in the sunken temple of Atal'Hakkar by the name of Morphaz.]], [[Years ago, he overwhelmed me in a bold ambush and consumed my apprentice whole.  While the loss of an apprentice is not unknown to me, this particular one carried an arcane shard of unsurpassed size and quality.]], [[You must gather powerful allies and delve into the temple.  Destroy Morphaz and recover the arcane shard from his ageless stomach.]], },
    { [[Retrieve the Arcane Shard from Morphaz and return to Archmage Xylem.]], },
  },
  [8254] = {
    [[Cenarion Aid]],
    { [[It is the way of the divine to help those in need.  The how is insignificant]], },
    { [[Seek out Ogtinc in Azshara.]], },
  },
  [8255] = {
    [[Of Coursers We Know]],
    { [[The mosshoof coursers of Azshara are magnificent animals who once lived in Felwood.  They naturally resist disease and poison, and were unaffected by the corruption there.  In their ancestral wisdom, they simply chose to leave that sickened place.]], [[It saddens my heart, but I must ask you to hunt these mighty beasts for me.  Gather four courser glands as the first ingredient for a restorative salve]], },
    { [[Acquire 4 Healthy Courser Glands and bring them to Ogtinc in Azshara.  Ogtinc resides atop the cliffs northeast the Ruins of Eldarath.]], },
  },
  [8256] = {
    [[The Ichor of Undeath]],
    { [[For this restorative salve, you must use a simple formula: one part poison, two parts cure.  You have acquired one cure, now you must gather the poison.]], [[Powerful undead, such as the lingering highborne who wander the Ruins of Eldarath, are sometimes so infused with evil that it coalesces into a sickly greenish goo.  To the living, this substance is known as ichor of undeath.]], [[Due to the potency of this vile element, I require only a single ichor.]], },
    { [[Acquire an Ichor of Undeath for Ogtinc in Azshara.  Ogtinc resides atop the cliffs northeast the Ruins of Eldarath.]], },
  },
  [8257] = {
    [[Blood of Morphaz]],
    { [[Now the last part, the second cure.]], [[The green drake Morphaz is known to be immune to all forms of poison and disease]], },
    { [[Kill Morphaz in the sunken temple of Atal'Hakkar, and return his blood to Greta Mosshoof in Felwood.  The entrance to the sunken temple can be found in the Swamp of Sorrows.]], },
  },
  [8258] = {
    [[The Darkreaver Menace]],
    { [[Death Knight Darkreaver lords over the Great Ossuary in the Scholomance.  He threatens to twist the spirit realm to do his own bidding, and he must be stopped!  This is where the scryer you helped make comes into play.]], [[Use the divination scryer in the heart of the Great Ossuary's basement to call forth Darkreaver's spirit minions.  Defeat the spirits and the death knight himself will appear.  <name>, bring me his head!  Only his demise will reverse the damage done to the spirit realm!]], },
    { [[Use the Divination Scryer in the heart of the Great Ossuary's basement in the Scholomance.  Doing so will bring forth spirits you must fight.  Defeating these spirits will summon forth Death Knight Darkreaver; defeat him.]], [[Bring Darkreaver's Head to Sagorne Creststrider in the Valley of Wisdom, Orgrimmar.]], },
  },
