  [8409] = {
    [[Ruined Kegs]],
    { [[The rotten eggs have been successfully placed inside the keg.  Future batches of ale from the Southshore Inn are sure to be ruined!]], [[You should now report back to Darkcaller Yanka at the Wickerman Festival in Tirisfal Glade and report your success.]], },
    { [[Return to Darkcaller Yanka at the Wickerman Festival in Tirisfal Glade.]], },
  },
  [8410] = {
    [[Elemental Mastery]],
    { [[Bath'rah the Windwatcher has sent out a summons for a shaman of your wisdom.  He requests you bring a sample of each of the elements to show you are ready.]], [[To find Bath'rah, go to the river east of Tarren Mill.  Follow the river north until you come across the ruins where Bath'rah resides.]], },
    { [[Collect a sample of air, fire, earth and water for Bath'rah the Windwatcher.]], },
  },
  [8411] = {
    [[OLD Mastering the Elements]],
    { [[Welcome, <class>.  You bring me the elements and then we do the talkin'.]], [[The fire, the air, the water, and the earth I be needin' before I trust ya, mon.  I gots to know you be knowledgeable.]], },
    { [[Bring the elements earth, air, fire and water to Bath'rah the Windwatcher.]], },
  },
  [8412] = {
    [[Spirit Totem]],
    { [[The elemental spirits of this place don't be likin' me any more.]], [[You know about the totems more than any, and I be needin' a spirit totem to protect me.  We already got the elements, now we need somethin' more with more nature.  More primal.]], [[In the Western Plaguelands you can kill bears for claws and spiders for their eyes.  The carrion birds eat everything, so they might have either one.]], },
    { [[Bring the pieces for the spirit totem to Bath'rah the Windwatcher.]], },
  },
  [8413] = {
    [[Da Voodoo]],
    { [[The spirit totem just isn't enough.  We be needin' some voodoo magic to make it work jus' right.]], [[The trolls in the sunken temple have feathers strong with voodoo magics.  Go there and kill Gasher, Mijan, Zolo, Hukku, Zul'lor, and Loro.  Then we have magic enough for a real spirit totem.]], },
    { [[Bring the voodoo feathers to Bath'rah the Windwatcher.]], },
  },
  [8414] = {
    [[Dispelling Evil]],
    { [[Greetings, <class>.  Crusaders like you are a welcome sight in these desolate reaches.]], [[Your holy strength is needed to help fight the endless undead here.  We need you to collect a large number of scourgestones and bring them to High Priest Thel'danis.  The high priest can be found guarding Uther's Tomb in Sorrow Hill, not far from here.]], [[Only one with an Argent Dawn Commission can hope to find scourgestones, so speak to Officer Pureheart if you haven't already.]], },
    { [[Collect 20 Minion's Scourgestones and bring them to High Priest Thel'danis in the Western Plaguelands.]], },
  },
  [8415] = {
    [[Chillwind Camp]],
    { [[Commander Ashlam Valorfist has sent out a call for a paladin of your purity and valor, <name>.  You can find him at Chillwind Camp in the southern part of the Western Plaguelands.  Seek him and aid in his cause for the Light!]], },
    { [[Speak to Commander Ashlam Valorfist at Chillwind Camp.]], },
  },
  [8416] = {
    [[Inert Scourgestones]],
    { [[The evil has been purged from the stones, <name>.]], [[I am aware of Commander Ashlam's plans, and he will require only a single inert stone.  Bring it to him so we may bathe the Scourge in righteous fire.]], },
    { [[Bring the Inert Scourgestone to Commander Ashlan Valorfist in Chillwind Point.]], },
  },
  [8417] = {
    [[A Troubled Spirit]],
    { [[In the Blasted Lands, there is a troubled spirit who was once a great hero of the horde.  A warrior's honor does not die with the body, and it would serve you well to speak with him.]], },
    { [[Speak with the Fallen Hero of the Horde at the entrance to the Blasted Lands.]], },
  },
  [8418] = {
    [[Forging the Mightstone]],
    { [[Now we must combine the inert scourgestones and create a weapon to shatter the ranks of undead.  This is no trivial task, <name>.  Only the trolls of the sunken temple carry the kind of rare magic able to turn the evil of something against itself.]], [[In the temple, the trolls are known as Gasher, Mijan, Zolo, Hukku, Zul'lor, and Loro.  Do not feel remorse for their corrupt souls... they are not without evil.]], },
    { [[Bring the voodoo feathers to Ashlam Valorfist.]], },
  },
  [8419] = {
    [[An Imp's Request]],
    { [[<name>, an imp in Felwood by the name of Impsy has requested your presence.  It is somewhat strange, as he has asked that you bring a piece of felcloth as a sign of good faith.  Considering the power you already wield as a warlock, I can only assume you know the satyrs of Felwood carry this felcloth.]], [[You can find Impsy just north of the Bloodvenom Falls, which I've heard is quite lovely this time of year.]], },
    { [[Bring a piece of felcloth to Impsy in Felwood.]], },
  },
  [8420] = {
    [[Hot and Itchy]],
    { [[Maybe this sounds strange coming from an imp, but I'd like a pet of my own.  I really would.  It's just not fair that Niby gets me and I get nothing!]], [[In fact, it doesn't even need to be a living pet.  I'd need something to start making it with, like some felcloth.  The satyrs here in Felwood sometimes have felcloth, so you'd just have to kill enough of them until you find some.  That doesn't sound so bad, does it?]], },
    { [[Bring a piece of felcloth to Impsy in Felwood.]], },
  },
  [8421] = {
    [[The Wrong Stuff]],
    { [[Now that I have my felcloth, I can really begin making *my* pet.]], [[It's going to need some eyes though.  Perhaps you could rip some rotting wood from the irontree folk for me, so I can carve out some lovely eyes.  They're up north in the Irontree Woods.]], [[I'll also need some bloodvenom essence to give it that new demon smell.  The tainted oozes carry the essence, and they hang out right here near Niby and me.]], },
    { [[Bring 10 Rotting Wood and 4 Bloodvenom Essence to Impsy in Felwood.]], },
  },
  [8422] = {
    [[Trolls of a Feather]],
    { [[My pet is almost done.  Now I just need something to stuff it with.]], [[Only the voodoo feathers from the trolls in the sunken temple will do.  They go by the names Gasher, Mijan, Zolo, Hukku, Zul'lor, and Loro.  Kill them.  Kill them all.]], [[This is no trivial task <name>, so I'll make it worth your while by giving you something of Niby's]], },
    { [[Bring a total of 6 Voodoo Feathers from the trolls in sunken temple.]], },
  },
  [8423] = {
    [[Warrior Kinship]],
    { [[<The Hero looks you over.>]], [[You are a <class>, no doubt.  I would ask an honorable task of you, but I must test your strength.]], [[It is said that Rexxar would measure the sharpness of his blades by killing a helboar with a single, swift strike.  Now I will measure your strength by killing them.]], },
    { [[Kill 7 Helboar in the Blasted Lands and return to the Fallen Hero of the Horde.]], },
  },
  [8424] = {
    [[War on the Shadowsworn]],
    { [[The continued presence of the shadowsworn in the Blasted Lands bring great pain to me.  Their dark rituals and spells cause agony unlike any I felt while alive.]], [[Through you I can deliver my own message of pain.  Go forth and deliver my wrath.]], },
    { [[Slaughter the Shadowsworn in the Blasted Lands and return to the Fallen Hero of the Horde.]], },
  },
  [8425] = {
    [[Voodoo Feathers]],
    { [[Your honor has brought some peace to me.  It is only right that I offer you something in return.]], [[My weapons and armor were splendid in life, and yet they are of no use to me now.  They would be better served in your possession.]], [[Travel to the sunken temple and destroy the troll guardians Gasher, Mijan, Zolo, Hukku, Zul'lor, and Loro.  They carry magic feathers that can reform my physical body long enough to grant you a reward.]], },
    { [[Bring the Voodoo Feathers from the trolls in the Sunken Temple to the Fallen Hero of the Horde.]], },
  },
  [8426] = {
    [[OLD Battle of Warsong Gulch]],
    { [[The battle in Warsong Gulch against the Silverwing Sentinels is of great importance.  Under the guise of protecting a forest that doesn't belong to them, the Alliance seeks to deny the Horde one of our largest sources for lumber.]], [[Do not let this happen, <name>!  Come back to me with proof of serving the Horde in a worthy manner!]], },
    { [[Bring 3 Warsong Gulch Marks of Honor to a Horde Warbringer outside the battlegrounds.]], },
  },
