  [8945] = {
    [[Dead Man's Plea]],
    { [[I'll provide you the information you ask for, <name>.  But more pressing matters are at hand.]], [[My beloved wife has been taken prisoner by Rivendare's undead scum.]], [[I strongly objected to her joining the Argent Dawn's foolish crusade, yet that's not of importance anymore.  You must finish what I attempted to do, you must rescue Ysida from the clutches of the Baron!]], },
    { [[Go into Stratholme and rescue Ysida Harmon from Baron Rivendare.]], },
  },
  [8946] = {
    [[Proof of Life]],
    { [[It is the cruelest of fates that my beloved has perished while trying to rescue me.  Yet, you claim to possess the ability to converse with him.]], [[Bring him this locket as proof that I am well.  He must not linger in this world; my heart would not be able to bear it.]], [[Farewell, <name>.  I shall be in your debt always.]], },
    { [[Bring Ysida's Locket to Anthion Harmon in Eastern Plaguelands.]], },
  },
  [8947] = {
    [[Anthion's Strange Request]],
    { [[We didn't realize the importance of Valthalak's medallion, so it was divided along with the rest of the spoils.  The first piece was taken by a dwarf named Theldren.  I tried to retrieve it from him and he beat me within an inch of my life.  You shall have to take the fragment by force.]], [[Last I heard of him, he'd become a gladiator for the Dark Iron dwarves.  Getting him to fight you is going to present a bit of a challenge, though.]], [[Gather the following materials for me.  You're going to need them.]], },
    { [[Bring 3 Dark Iron Bars, 20 Enchanted Leather, 3 Mooncloth and 4 Cured Rugged Hides to Anthion Harmon in the Eastern Plaguelands.]], },
  },
  [8948] = {
    [[Anthion's Old Friend]],
    { [[Theldren is not one to willingly participate in a fair fight.  I've shaped this banner to display his family colors.]], [[With some additional sorcery, this banner shall provoke him in a way he won't be able to resist.]], [[An old acquaintance of mine named Falrin knows the exact procedure required.  He has a particular way of... aggravating people.  You shall find him at the library in Dire Maul.]], },
    { [[Take the incomplete Banner of Provocation to Falrin Treeshaper at the library in Dire Maul.]], },
  },
  [8949] = {
    [[Falrin's Vendetta]],
    { [[Yes, it is true that I am familiar with this enchantment you require, <class>.  Yet I'm not one to give out favors freely, even if you were sent here by an old friend.]], [[I hold nothing but hatred and contempt for our brutish ogre neighbors.  And not only them, I hate the followers of Omokk just as much as I hate those that follow King Gordok.]], [[I want you to kill them.  Kill lots of them!  Bring me the beads they carry around their neck as proof of your work.  Do this and we shall talk about your request.]], },
    { [[Collect 25 Ogre Warbeads from Ogres inside Dire Maul or Blackrock Spire and return to Falrin Treeshaper inside the Athenaeum in Dire Maul.]], },
  },
  [8950] = {
    [[The Instigator's Enchantment]],
    { [[I can place the enchantment you require on the banner quite easily, <name>.  Unfortunately I don't have the materials I need as it is very rarely that someone asks for this sort of charm.]], [[I will require the essence of one of the Eldreth ghosts that haunt these halls, a handful of Scholomance dark runes and some large brilliant shards.  Bring me these reagents and I shall perform this task for you.]], },
    { [[Bring the following to Falrin Treeshaper inside Dire Maul: 1 Jeering Spectre's Essence, 4 Dark Runes and 8 Large Brilliant Shards.]], },
  },
  [8951] = {
    [[Anthion's Parting Words]],
    { [[Valthalak's soul was stored in this amulet.  In our greed we foolishly split it into three parts, not knowing the curse that would await us.]], [[The only way to stop Valthalak's spell is to put the medallion back together.  Return to Deliana and tell her that Bodley is her only hope for finding the remaining pieces.  Speak to her about any rewards you've arranged for.]], [[I, for once, shall rest in peace.  I wish you the best of luck; you shall need every bit of it!]], },
    { [[Return to Deliana in Ironforge with a set of Wildheart Boots, a Wildheart Kilt and Wildheart Spaulders.]], },
  },
