  [9076] = {
    [[Wretched Ringleader]],
    { [[The Wretched are so oblivious, gorging on foul magic, that they haven't realized we've recovered most of our weapons.  We can't expect them to remain in their stupor, though.  Now is the time for a decisive attack.  Let us make an example of their leader, Aldaron.  He was spotted on the top floor of the building by the anchorage.  Bring me his head; I'll reward you suitably.]], },
    { [[Bring Aldaron's Head to Velendris Whitemorn near Sunsail Anchorage in Eversong Woods.]], },
  },
  [9077] = {
    [[Bonescythe Breastplate]],
