    { [[Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands will make Plagueheart Bindings if you bring him the following: 1 Desecrated Bindings, 6 Wartorn Cloth Scraps, 1 Arcane Crystal and 1 Nexus Crystal.]], },
  },
  [9111] = {
    [[Robe of Faith]],
    { [[<Father Montoy makes some strange gestures in the air in front of you.>]], [[Blessed <boy/girl>, have you found the vestments of the fallen? The poor souls whose last breaths were taken on the field of battle - stripped of everything including their dignity.]], [[Bring me the desecrated remnants of the departed along with reagents of purification and you shall know faith.]], },
    { [[Father Inigo Montoy at Light's Hope Chapel in the Eastern Plaguelands will make a Robe of Faith if you bring him the following: 1 Desecrated Robe, 25 Wartorn Cloth Scraps, 4 Mooncloth and 2 Nexus Crystals.]], },
  },
  [9112] = {
    [[Leggings of Faith]],
    { [[<Father Montoy makes some strange gestures in the air in front of you.>]], [[Blessed <boy/girl>, have you found the vestments of the fallen? The poor souls whose last breaths were taken on the field of battle - stripped of everything including their dignity.]], [[Bring me the desecrated remnants of the departed along with reagents of purification and you shall know faith.]], },
    { [[Father Inigo Montoy at Light's Hope Chapel in the Eastern Plaguelands will make Leggings of Faith if you bring him the following: 1 Desecrated Leggings, 20 Wartorn Cloth Scraps, 4 Mooncloth and 2 Nexus Crystals.]], },
  },
  [9113] = {
