  [9207] = {
    [[Underlight Ore Samples]],
    { [[I was sent here from the Sanctum of the Sun to collect some ore samples, but the gnolls have overrun the place! It's going to be my head on a platter if I don't get those samples to the magister soon. Maybe you could help, <class>?]], [[You can find the ore in the tin and copper veins here, and the gnolls are carrying it around, too. When you have enough, take it to Magister Quallestis at the Sanctum of the Sun directly to the east. Go across the Dead Scar and you'll find the sanctum just south of Sungraze Peak.]], },
    { [[Collect 6 Underlight Ore and deliver them to Magister Quallestis at the Sanctum of the Sun in the Ghostlands.]], },
  },
  [9208] = {
    [[The Savage Guard - Arcanum of Protection]],
    { [[Zanza can create many ancient troll enchantments with proper components! Have you heard of the Savage Guard? No, of course you haven't. Your mind is addled with the inferior elven magic.]], [[Bring me an arcanum of elven creation so that I may destroy the aberration. Do this and the Savage Guard will be yours.]], },
    { [[Zanza the Restless in Zul'Gurub wants an Arcanum of Protection.]], },
  },
  [9209] = {
    [[The Savage Guard - Arcanum of Rapidity]],
    { [[Zanza can create many ancient troll enchantments with proper components! Have you heard of the Savage Guard? No, of course you haven't. Your mind is addled with the inferior elven magic.]], [[Bring me an arcanum of elven creation so that I may destroy the aberration. Do this and the Savage Guard will be yours.]], },
    { [[Zanza the Restless in Zul'Gurub wants an Arcanum of Rapidity.]], },
  },
  [9210] = {
    [[The Savage Guard - Arcanum of Focus]],
    { [[Zanza can create many ancient troll enchantments with proper components! Have you heard of the Savage Guard? No, of course you haven't. Your mind is addled with the inferior elven magic.]], [[Bring me an arcanum of elven creation so that I may destroy the aberration. Do this and the Savage Guard will be yours.]], },
    { [[Zanza the Restless in Zul'Gurub wants an Arcanum of Focus.]], },
  },
  [9211] = {
    [[The Ice Guard]],
    { [[Ah, the Ice Guard. It is a thin sheen of protective magic that covers certain pieces of equipment. Those that do battle with creatures of frost and ice will gain increased protection from their attacks by using the Ice Guard.]], [[As with all things the Wrathcaster creates, there is a price - a price that I will not lower, regardless of how much you whimper. Interested?]], },
    { [[Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands wants 10 Insignia of the Crusade and 30 gold.]], },
  },
  [9212] = {
    [[Escape from the Catacombs]],
    { [[These trolls ambushed me and have been keeping me in this cage.  I think they're planning to sacrifice me!  Help me get out of here!]], [[The lock on the cage isn't that complex... I just can't get to it from this side.  Please escort me back to the Farstrider Enclave.  I'm sure Captain Helios will reward you, and you'll have my undying gratitude!]], },
    { [[Escort Ranger Lilatha to Captain Helios at Farstrider Enclave in the Ghostlands.]], },
  },
  [9213] = {
    [[The Shadow Guard]],
    { [[A creation of genius! Much like the Ice Guard, the Shadow Guard employs a thin sheen of protective magic that covers certain pieces of equipment. And like the Ice Guard, the Shadow Guard is going to cost you a good amount of money.]], [[<Mataus holds his hand out and rubs his fingers together.>]], [[Interested?]], },
    { [[Mataus the Wrathcaster at Light's Hope Chapel in the Eastern Plaguelands wants 10 Insignia of the Crusade and 30 gold.]], },
  },
  [9214] = {
    [[Shadowpine Weaponry]],
    { [[<class>, I want you to kill as many Shadowpine trolls as you can. We need their numbers greatly reduced before we can shift our attention toward Deatholme. And while you're at it, we might as well kill two birds with one stone, so bring me back a load of their weapons. Better that they not go to waste, and we can use them in the fight against the Scourge.]], [[Hit their camps hard to the east and the south at Zeb'Tela and Zeb'Nowa.]], },
    { [[Collect 3 Headhunter Axes, 3 Shadowcaster Maces, 3 Catlord Claws and 3 Hexxer Staves. Return them to Captain Helios at Farstrider Enclave in the Ghostlands.]], },
  },
  [9215] = {
    [[Bring Me Kel'gash's Head!]],
    { [[WANTED: Bring the head of Kel'gash the Wicked, chieftain of the Shadowpine trolls, to Captain Helios. No questions asked. Kel'gash is known to dwell somewhere to the south in Zeb'Nowa.]], [[REWARD: Yes, but no advances!]], },
    { [[Return the Head of Kel'gash the Wicked to Captain Helios at the Farstrider Enclave in the Ghostlands.]], },
  },
  [9216] = {
    [[Rotting Hearts]],
    { [[I hear the Amani trolls eat their enemies' hearts to steal their strength.  They might just be on to something, <name>.  With the proper treatment, the hearts of the unliving can produce potent elixirs.]], [[Hunt down the zombies that roam the Dead Scar and bring me their hearts.  I'll show you the kind of enhancement we can perform to your physical prowess against the undead.]], },
    { [[Bring back 10 Rotting Hearts to Magistrix Aminel in Tranquillien.]], },
  },
  [9217] = {
    [[More Rotting Hearts]],
  },
  [9218] = {
    [[Spinal Dust]],
    { [[The dark energy that flows through the bodies of reanimated skeletons tends to accumulate primarily in their spine.  I've discovered a method of instilling this energy into a potion that boosts our magical abilities against the undead.]], [[Bring me some spinal dust and I'll be glad to provide you with a sample.  You'll find an ample number of skeletons wandering the Dead Scar, west of here.]], },
    { [[Bring back 10 Spinal Dusts to Magistrix Aminel in Tranquillien.]], },
  },
  [9219] = {
    [[More Spinal Dust]],
  },
  [9220] = {
    [[War on Deatholme]],
    { [[If you seek to aid our cause there is no better place to start than here and no better time to do it than now.]], [[By rebuilding the Sanctum of the Sun we've raised a banner of defiance in front of our enemy's seat of power: Deatholme.]], [[Go forth and fight the hordes of undead that inhabit the vile Scourge fortress.]], },
    { [[Slay 5 Eyes of Dar'khan, 6 Nerubis Centurions and 6 Wailers in Deatholme and return to Magister Idonis at the Sanctum of the Sun in the Ghostlands.]], },
  },
  [9221] = {
    [[Superior Armaments of Battle - Friend of the Dawn]],
    { [[Greetings, friend. Have you insignias of the Dawn or the Crusade that you would like to redeem for items of power?]], [[I must warn you; purchases made at a friendly reputation with the Dawn come with a hefty surcharge. I would advise that you gain a better reputation with the Dawn before moving forward with your plans.]], },
    { [[For 30 Insignias of the Dawn and 30 Insignias of the Crusade you may choose an item from the Argent Dawn's treasure cache.]], },
  },
