  [9292] = {
    [[Cracked Necrotic Crystal]],
    { [[On the corpse of the fallen abomination, you discover a cracked crystal, slowly pulsing with necrotic energy.]], },
    { [[Bring the Cracked Necrotic Crystal to Lieutenant Orrin outside the gates of Stormwind.]], },
  },
  [9293] = {
    [[What Must Be Done...]],
    { [[The nearby lake has been contaminated by one of the ship's power crystals.  Many of the plants and animals of Ammen Vale have been mutated because of this.]], [[I have a theory on how to heal the lake, but I must have samples from the mutated lashers for analysis.  They can be found to the west in the Ammen Fields.]], [[Will you help me, <class>?]], },
    { [[Gather 10 Lasher Samples and return them to Botanist Taerix at the Crash Site in Ammen Vale.]], },
  },
  [9294] = {
    [[Healing the Lake]],
    { [[Thank goodness, I think it'll work!  I have an agent for you to spread at Silverline Lake to the south.  It will neutralize the effects of the irradiated ship's power crystal that fell there.]], [[Swim out and scatter the neutralizing agent near it.  Don't worry, you won't be exposed long enough to become irradiated.]], },
    { [[Disperse the Neutralizing Agent at the Irradiated Power Crystal, then return to Botanist Taerix at the Crash Site in Ammen Vale.]], },
  },
  [9295] = {
    [[Letter from the Front]],
    { [[You found this letter on one of the many Scourge minions you've slain. From its appearance and smell, it has been with the undead for some time. Perhaps someone at Light's Hope Chapel would be interested in examining it further...]], },
    { [[Take the Torn Letter to the Keeper of the Rolls at Light's Hope Chapel in the Eastern Plaguelands.]], },
  },
  [9299] = {
    [[Note from the Front]],
    { [[You found this letter on one of the many Scourge minions you've slain. From its appearance and smell, it has been with the undead for some time. Perhaps someone at Light's Hope Chapel would be interested in examining it further...]], },
    { [[Take the Careworn Note to the Keeper of the Rolls at Light's Hope Chapel in the Eastern Plaguelands.]], },
  },
  [9300] = {
