  [11926] = {
    [[More Torch Tossing]],
    { [[Hello, fire juggler!  Are you having fun and making fiery mayhem?  That's what I like to hear!  Now, do you think you're an expert at torch tossing?  Then I have a test for you...]], [[You know the rules: toss torches at the marked braziers.  This time, however, you have a little more time and a LOT more braizers to hit.]], [[Ready?]], },
    { [[Win the torch tossing game, then speak with the Master Flame Eater.]], },
  },
  [11933] = {
    [[Stealing the Exodar's Flame]],
    { [[This flame, stolen from the heart of the Exodar itself, floods you with warmth. This raw power is foreign to you, but the Talespinner might know more...]], },
    { [[Return the Flame of the Exodar to the Festival Talespinner.]], },
  },
  [11935] = {
    [[Stealing Silvermoon's Flame]],
    { [[This flame, stolen from Silvermoon, floods you with warmth. This raw power is foreign to you, but the Loremaster might know more...]], },
    { [[Return the Flame of Silvermoon to the Festival Loremaster.]], },
  },
  [11937] = {
    [[FLAG - all torch return quests are complete]],
  },
  [11947] = {
    [[Striking Back]],
    { [[We must act to preserve the balance! Time is against us.]], [[There is a camp located in the shadow of Ethel Rethor within northwestern Desolace. There, they use ice stones to communicate with the elementals, as they do in Silithus. Much as I am loath to do this, the spirits will understand - you must summon one of this Ahune's lieutenants and destroy it. Perhaps this forestalling action will be enough...]], },
    { [[Slay the Hailstone Lieutenant summoned from the Ice Stone located at Ethel Rethor in Desolace, then return to the Earthen Ring Elder in a capital city.]], },
  },
  [11948] = {
    [[Striking Back]],
    { [[We must act to preserve the balance! Time is against us.]], [[Neptulon has long had a base of operations on an island in western Stranglethorn. There, the Twilight Cult has set up ice stones to communicate with the Tidehunter's realm, much like they do in Silithus. Much as I am loath to do this, the spirits will understand - you must summon one of this Ahune's lieutenants and destroy it. Perhaps this forestalling action will be enough...]], },
    { [[Slay the Chillwind Lieutenant summoned from the Ice Stone located on the water elemental's island in western Stranglethorn Vale, then return to the Earthen Ring Elder in a capital city.]], },
  },
  [11952] = {
    [[Striking Back]],
    { [[We must act to preserve the balance! Time is against us.]], [[Firewatch Ridge in Searing Gorge is an established bastion of Twilight Cult activity. Recently, they have been using ice stones in the cave to communicate with the Tidehunter's realm, much like they do in Silithus. Much as I am loath to do this, the spirits will understand - you must summon one of this Ahune's lieutenants and destroy it. Perhaps this forestalling action will be enough...]], },
    { [[Slay the Frigid Lieutenant summoned from the Ice Stone located in the cave underneath Firewatch Ridge in the northwest of Searing Gorge, then return to the Earthen Ring Elder in a capital city.]], },
  },
  [11953] = {
    [[Striking Back]],
    { [[We must act to preserve the balance! Time is against us.]], [[Silithus is the Azerothian base of the Twilight Cult. In the north, they have been using new ice stones to communicate with the Tidehunter's realm. Much as I am loath to do this, the spirits will understand - you must summon one of this Ahune's lieutenants and destroy it. Perhaps this forestalling action will be enough...]], },
    { [[Slay the Glacial Lieutenant summoned from the Ice Stone located at Twilight's Run in northern Silithus, then return to the Earthen Ring Elder in a capital city.]], },
  },
  [11954] = {
    [[Striking Back]],
    { [[We must act to preserve the balance! Time is against us.]], [[In order to ensure Ahune's success, the cult has established a base near the Dark Portal itself in Draenor. They use ice stones to coordinate the frost lord's arrival. Much as I am loath to do this, the spirits will understand - you must summon one of this Ahune's templar protectors and destroy it. Perhaps this forestalling action will be enough...]], },
    { [[Slay the Glacial Templar summoned from the Ice Stone located near the Dark Portal in Hellfire Peninsula, then return to the Earthen Ring Elder in a capital city.]], },
  },
  [11955] = {
    [[Ahune, the Frost Lord]],
    { [[I believe you are strong enough for the ultimate task set before us.]], [[We cannot allow the plot between the Twilight Cult and the naga heretics of Neptulon to come to fruition. For the sake of Azeroth and all life upon it, we must act. Our path is clear. You must journey to the Slave Pens deep within Coilfang Reservoir and meet with our shaman there. Go, and quickly!]], },
    { [[Travel to the Slave Pens in Coilfang Reservoir within Zangarmarsh and speak with Numa Cloudsister.]], },
  },
  [11964] = {
    [[Incense for the Summer Scorchlings]],
    { [[Did you know that our festival fires burn in every land with an Alliance settlement? Flame Wardens tend to them in camps outside of each town, ensuring the bonfires are bright and the celebrations continue.]], [[With each Flame Warden is a summer scorchling]], },
    { [[The Festival Loremaster wants you to take Summer Incense to a Summer Scorchling outside any Alliance settlement.]], },
  },
  [11966] = {
    [[Incense for the Festival Scorchlings]],
    { [[Did you know that our festival fires burn in every land with a Horde settlement? Flame Keepers tend to them in camps outside of each town, ensuring the bonfires are bright and the celebrations continue.]], [[With each Flame Keeper is a festival scorchling]], },
    { [[The Festival Talespinner wants you to take Summer Incense to a Festival Scorchling outside any Horde settlement.]], },
  },
  [11970] = {
    [[The Master of Summer Lore]],
    { [[The festival is a time for fun and laughter, there's no mistake. It's important, however, that you understand the origins of this holiday. Education should be sought out and savored.]], [[The Festival Loremaster in the Alliance capitals can guide your way. Go now and learn.]], },
    { [[Speak to the Festival Loremaster in any Alliance capital.]], },
  },
  [11971] = {
    [[The Spinner of Summer Tales]],
    { [[The festival is a time for fun and laughter, there's no mistake. It's important, however, that you understand the origins of this holiday. Education should be sought out and savored.]], [[The Festival Talespinner in the Horde capitals can guide your way. Go now and learn.]], },
    { [[Speak to the Festival Talespinner in any Horde capital.]], },
  },
  [11972] = {
    [[Shards of Ahune]],
    { [[These shards pulse with foreboding.  Do they hold the last essence of the Frost Lord?]], },
    { [[Bring the Ice Shards to Luma Skymother.]], },
  },
  [11975] = {
    [[Now, When I Grow Up...]],
    { [[Maybe I already asked you this, but when you were my age did you know that you wanted to be a <class> when you grew up?]], [[I'm not sure what I want to be.  Maybe a mage or a warlock or a Blood Knight, like I said before?  Or maybe... A rock star!!  Like Sig Nicious, the blood elf, in the Elite Tauren Chieftain!]], [[I heard that when they aren't touring, they hang out in Silvermoon City!  They just sit on a balcony in the Walk of Elders waiting for people to stop by.]], [[Please take me there.  Please!]], },
    { [[Take your orphan, Salandria, to see the Elite Tauren Chieftain in Silvermoon City's Walk of Elders.  Make sure to call for her if she is not present when you arrive.]], },
  },
  [11976] = {
    [[Ice Shards]],
    { [[These shards pulse with foreboding.  Do they hold the last essence of the Frost Lord?]], },
    { [[Bring the Ice Shards to Luma Skymother.]], },
  },
  [12012] = {
    [[Inform the Elder]],
    { [[This information must be spread to the entirety of the Earthen Ring. I'm not certain how to proceed, but the elder who set you on this task in the first place may. Please, return to the elder and seek the next course of action - we must do something!]], },
    { [[Travel to a capital city and return the Totemic Beacon to the Earthen Ring Elder.]], },
  },
  [12020] = {
    [[This One Time, When I Was Drunk...]],
    { [[The Dark Irons took a beating!  As they rushed back to the Grim Guzzler, a portion of their transport fell off.  More importantly, you managed to keep the camp safe... today.]], [[Head over to Boxey and explain your heroic triumph over the Dark Iron dwarves.]], },
    { [[Talk to Boxey Boltspinner in the Brewfest camp.]], },
  },
  [12022] = {
    [[Chug and Chuck!]],
    { [[Hey there!  You look like you can handle your brew.  But can you handle your brew mug?]], [[The Dark Irons have come back with a vengeance to ruin our celebration.  Tossing mugs at them is the best way to stop them, but first, let's see if you can toss one at old S.T.O.U.T. here.]], [[It's easy, pick up a complimentary sampler and take a drink.  Just make sure you're facing S.T.O.U.T.  Talk to me after you've hit him 5 times.]], },
    { [[Use the Complimentary Brewfest Sampler to hit S.T.O.U.T. 5 times, then talk to Boxey Boltspinner.]], },
  },
  [12062] = {
    [[Insult Coren Direbrew]],
    { [[Coren Direbrew is in the next room, riling up Dark Iron dwarves for another attack on Brewfest.  Defeat him!]], [[Go to Coren and pick a fight.  Tell him his beer isn't fit for pigs.]], [[When you do that, be ready for a fight.  The last person who insulted his brew... was fed to dire boars.]], },
    { [[Speak with Coren Direbrew.]], },
  },
  [12191] = {
    [[Chug and Chuck!]],
    { [[Those pesky Dark Irons are back! We need to make sure that you can defend yourself if needed. More importantly, we need to know that you can defend our property!]], [[S.T.O.U.T. here is ready for action, are you? Drink from the Complimentary Brewfest Samplers then throw the mug at S.T.O.U.T.! Hit the target 5 times then talk to me.]], },
    { [[Use the Complimentary Brewfest Sampler to hit S.T.O.U.T. 5 times, then talk to Bizzle Quicklift.]], },
  },
  [12193] = {
    [[Say, There Wouldn't Happen to be a Souvenir This Year, Would There?]],
    { [[Brewfest isn't just about racing rams, it's also about drinking!  When you have a moment, take this voucher to Belbi Quikswitch.  She's near the back of the Brewfest camp.  If you can't see her, don't worry, she'll be sure to see you.]], [[In any case, take this voucher to her to get your free Brewfest stein.  She'll also be more than happy to handle any and all other Brewfest transactions.]], },
    { [[Redeem the Brewfest Stein Voucher with Belbi Quikswitch in the Brewfest camp.]], },
  },
  [12194] = {
    [[Say, There Wouldn't Happen to be a Souvenir This Year, Would There?]],
    { [[Not only are we supplying brew to everyone, we also have steins to give out! Beautiful, commemorative steins all obtained by the most legal of means! Here's a voucher for one.]], [[Talk to Blix near the back of the Brewfest camp. He's pretty strange, even more so now that he's always wearing those goggles. He'll handle the voucher for you, and any other Brewfest transaction.]], },
    { [[Redeem the Brewfest Stein Voucher with Blix Fixwidget in the back of the Brewfest camp.]], },
  },
  [12278] = {
    [[Brew of the Month Club]],
    { [[Membership has its privileges.  Being part of the "Brew of the Month" club means knowing where to get the latest and greatest brew.]], [[Bring your "Brew of the Month" club membership form to Larkin Thunderbrew in the Stonefire Tavern in Ironforge.]], },
    { [[Bring the "Brew of the Month" club membership form to Larkin Thunderbrew in the Stonefire Tavern in Ironforge.]], },
  },
  [12306] = {
    [[Brew of the Month Club]],
    { [[There is strength in numbers, <name>.  Being part of the "Brew of the Month" club means knowing you and your brethren will always have access to the best brew.]], [[Bring your "Brew of the Month" club membership form to Ray'ma in the Darkbriar Lodge in Orgrimmar's Valley of Spirits.]], },
    { [[Bring the "Brew of the Month" club membership form to Ray'ma in the Darkbriar Lodge in Orgrimmar's Valley of Spirits.]], },
  },
  [12318] = {
    [[Save Brewfest!]],
    { [[<name>, our spies have discovered a fiendish plot!  The Dark Iron dwarves want our beloved Brewfest destroyed!]], [[Coren Direbrew, head of a renegade family of brewers, has marshaled the dwarves of Blackrock Depths.  Their vile mole machines attack us constantly.]], [[Our spy is in the Grim Guzzler, a tavern in the bowels of Blackrock Depths.  He'll know more of Direbrew's plot.]], [[Find our spy.  Save Brewfest!]], },
    { [[Speak with the Brewfest Spy in the Grim Guzzler of Blackrock Depths.]], },
  },
  [12491] = {
    [[Direbrew's Dire Brew]],
    { [[Coren Direbrew's corpse clutched this small cask tightly to its gut.  This must be his infamous Dire Brew!]], },
    { [[Give Direbrew's Dire Brew to Ipfelkofer Ironkeg at the Brewfest camp near Ironforge.]], },
  },
  [12492] = {
    [[Direbrew's Dire Brew]],
    { [[Coren Direbrew's corpse clutched this small cask tightly to its gut. This must be his infamous Dire Brew!]], },
    { [[Give Direbrew's Dire Brew to Tapper Swindlekeg at the Brewfest camp near Orgrimmar.]], },
  },
  [12513] = {
    [[Nice Hat...]],
    { [[My hat? Yes, it's an unusual style, I know, from far away... few tailors here know how to make them.]], [[When I was young and hot-blooded, I wore it as a standing challenge. Many came seeking to defeat me and win it for themselves... but no one ever did.]], [[Ah, but those days are long past. I'm an old man, now: I drink, I fish, but I no longer duel.]], },
    { [[Don Carlos has inadvertently challenged you to defeat his younger self in Old Hillsbrad. Afterwards, bring Don Carlos' Hat to him in Tanaris as proof.]], },
  },
