  [3121] = {
    [[奇怪的要求]],
    { [[奥格瑞玛的尼尔鲁·火刃有我所需要的东西！他调配出了一种混合物，我需要用它来……好吧，你以后会知道的。]], [[快点出发，到奥格瑞玛去。拿着这个东西，这是一颗皱缩的头颅，我答应尼尔鲁用此来交换草药。这是某个可怜侏儒的头，我在森林里撞见了他，然后就……我敢说尼尔鲁会很高兴得到这个东西的。]], [[你可以在暗影裂谷里找到尼尔鲁。]], },
    { [[将皱缩的头颅交给奥格瑞玛的尼尔鲁·火刃。]], },
  },
  [3122] = {
    [[向巫医尤克里回复]],
    { [[拿着这些草药，<name>。把它们交给巫医尤克里。]], [[请告诉他，我对那些皱缩的头颅很满意。]], },
