  [7838] = {
    [[竞技场大师]],
    { [[Avast Lubber!   Bring me 12 trinkets of the Arena Master and then ye shall be a Grandmaster of the Gurubashi Arena!   Arrrr!]], },
  },
  [7839] = {
    [[邪枝窃贼]],
    { [[邪枝巨魔入侵了我们的村子，偷走了我的工具！我有备用的工具，但是那套被偷走的工具是当年的一个救了我性命的侏儒送给我的。]], [[我要拿回那套工具，<name>——它们对我来说意味着整个世界。]], [[马克霍尔报告说那些工具就在亚戈瓦萨和沙尔瓦萨神庙附近。]], [[那些神庙坐落在辛特兰的东北区域。]], [[找回我的工具！]], },
    { [[辛特兰恶齿村的铁匠斯拉提要你去找到斯拉提的工具。完成任务之后就回到他那里去。]], [[你记得铁匠斯拉提说过，你可以在辛特兰东北部的某个邪枝巨魔神庙中找到它，要不然就是在隐匿石那边。]], },
  },
  [7840] = {
    [[拉尔德的午餐]],
    { [[拉尔德在一个秘密的地方享用野餐，这时丑陋的邪枝巨魔一棍打翻了我。拉尔德跑回了恶齿村，但是把午餐留在了那里。拉尔德饿了。也许你可以去那个秘密的地方找回拉尔德的午餐？拉尔德害怕。出发吧！北边的岛就是那个秘密的地方。]], },
    { [[辛特兰恶齿村的拉尔德要求你去找到拉尔德的午餐。完成任务之后就回到他那里去。]], [[拉尔德说他把午餐留在了北面的岛上。当心邪枝巨魔。]], },
  },
  [7841] = {
    [[给蛮锤部族的警告信]],
    { [[蛮锤部族的矮人和我们的敌人联合起来，形成了新的联盟。这一次，他们和高等精灵站在了一起，伙计。]], [[<奥索吐了口唾沫。>]], [[我们必须给那些蛮锤矮人传达一条信息，一条用我们敌人的鲜血书写的警告。杀掉你所看到的所有高原高等精灵，把他们的尸体留在那里作为警告。]], [[永远不要与巨魔为敌！]], },
    { [[辛特兰恶齿村的奥索·莫吉克要你去杀掉15个高原前锋、15个高原斥候、15个高原神射手和15个高原游侠。任务完成之后回到他那里去复命。]], [[你可以在辛特兰西北部的奎尔丹尼小屋找到那些高原精灵。]], },
  },
  [7842] = {
    [[又一个给蛮锤部族的警告]],
    { [[他们说蛮锤部族和狮鹫的关系就好像情侣一般。这让我感到挺恶心的，但是我们可以利用这一点，伙计。我们可以送给他们另外一条警告。]], [[把你在辛特兰看到的所有狮鹫都干掉，收集它们的羽毛。然后我们就可以发出最后一条警告了。]], },
    { [[辛特兰恶齿村的奥索·莫吉克要你从辛特兰的狮鹫身上收集10根细长的狮鹫羽毛。任务完成之后回到他那里去复命。]], [[在辛特兰各处都有狮鹫出没。]], },
  },
  [7843] = {
    [[给蛮锤部族的最后警告]],
    { [[<奥索递给你一柄包在几片狮鹫羽毛中的、浸满血迹的长矛。>]], [[拿着这个，伙计。把它带到鹰巢山去，就去镇子中间的水井旁。把这根长矛插在那里！就插在地上！让矮人知道我们巨魔的厉害！当你完成这个壮举之后就回来吧。希望你能活着回来。]], },
    { [[辛特兰恶齿村的奥索·莫吉克要你去辛特兰西部的鹰巢山，把给蛮锤部族的警告插在城镇中心的水井旁边。任务完成之后回到他那里去复命。]], [[当心那些愤怒的蛮锤矮人以及野蛮的联盟成员。]], },
  },
  [7844] = {
    [[野蛮的亲戚]],
    { [[三个不同的巨魔部族居住在辛特兰地区，<name>。我们之间的共同点是：我们都是巨魔，而且我们都住在辛特兰。]], [[邪枝巨魔——正如他们的名字一样，是一群堕落的巨魔；他们以其他的巨魔和人型生物为食。]], [[如果祖尔金在这里的话，他一定会手腕一抖灭掉这个部族——啊，祖尔金还没有回来，所以我们要自己动手了。]], [[现在，你的职责就是把这帮渣滓给干掉！]], },
    { [[辛特兰恶齿村的秘法师雅尔金要你去杀掉15个邪枝割颅者和10个邪枝占卜者。任务完成之后回到他那里去复命。]], [[秘法师雅尔金说过，你可以在辛特兰东北部的沙尔瓦萨和亚戈瓦萨神庙附近找到那些巨魔。]], },
  },
  [7845] = {
    [[被绑架的断齿长者！]],
    { [[我的爱人在邪枝巨魔发动的一次夜间突袭中被掳走。我们的探子回报说他还活着，并且被关在辛萨罗的顶部。据说邪恶祭司海克斯正准备把他当作祭品献给邪神哈卡。]], [[你必须救救他，<name>！我们的村庄不能失去他。]], },
    { [[辛特兰恶齿村的断齿族长要求你到辛萨罗去拯救她的爱人，断齿长者。]], [[你听说他被关在辛萨罗顶部，辛萨罗位于辛特兰的南部地区，那里是邪枝巨魔的主城。]], },
  },
  [7846] = {
    [[找回钥匙！]],
    { [[我的镣铐的钥匙在守护者希坦亚手里，她就在洞穴附近。小心一点，<class>，她身边有强悍的守卫。]], [[干掉她，然后用她的钥匙打开我的镣铐。只有这样我才能回家。]], },
    { [[辛萨罗的断齿长者要求你杀掉守护者希坦亚，并用她身上的镣铐钥匙打开他的镣铐。当你找到钥匙之后就回到他那里去。]], },
  },
  [7847] = {
    [[向断齿族长复命]],
    { [[回到我的爱人——恶齿村的断齿族长那里。告诉她我马上就回去！她一定会奖励你的。]], },
    { [[辛萨罗的断齿长者要求你将他已经被营救的消息带给恶齿村的断齿族长。]], },
  },
  [7848] = {
    [[熔火之心的传送门]],
    { [[裂隙在翻滚，它撕裂并吞噬我们周围所有的东西，<class>。我所站立的地方旁边的裂隙就是穿过黑石深渊直通火焰之王所在地的传送门。]], [[你感到很惊奇吗？真是遗憾……凡人总是无法领悟那些他们无法看到、触摸到或者感觉到的东西。]], [[我向你保证，这个传送门可以到达熔火之心。]], [[你开始感兴趣了吗？其实这是很简单的。进入熔火之心，获得一块熔火碎片，把它交给我，然后我就会将你的灵魂与传送门进行同步。]], },
    { [[进入黑石深渊，在通往熔火之心的传送门附近找到一块熔火碎片，然后回到黑石山脉的洛索斯·天痕那里]], },
  },
  [7849] = {
    [[分离的痛苦]],
    { [[和许多朋友一样，我挚爱的亲人也在野外永远逝去了。啊，我的双胞胎兄弟莫克霍尔——他在邪枝巨魔突袭的那天晚上死了。]], [[他被残忍地处死，他的尸体被软泥怪和邪枝巨魔的狼分食。]], [[现在拯救他的肉体已经太晚了，但我们要拯救他的灵魂。把他的遗体带回来，<name>。]], },
    { [[辛特兰恶齿村的猎户马克霍尔要你去找回他的双胞胎兄弟的尸体。找到它，然后把猎户莫克霍尔的头颅和猎户莫克霍尔的骨头交给猎户马克霍尔。]], [[马克霍尔说过，他兄弟的尸体被软泥怪和邪枝巨魔的狼群分食。他还提到在辛萨罗有个大坑，里面全都是软泥怪，那里还有个狼穴。这两个地方都在靠近辛萨罗神庙顶部的区域。]], },
  },
  [7850] = {
    [[黑暗之瓶]],
    { [[邪枝巨魔正在疯狂地进攻我们，这是因为他们受到了邪恶祭司海克斯的堕落魔法的影响。]], [[你可以在辛萨罗找到腐化之血。那只装着腐化之血的瓶子散发着邪神哈卡的堕落能量，这让邪枝巨魔们充满了力量，并让他们变得疯狂。]], [[把那些腐化之血交给我，这样我就可以去除其中的堕落能量，以此来削弱邪神的力量。]], },
    { [[辛特兰恶齿村的断齿族长要你在辛萨罗找到10瓶腐化之血。任务完成之后就回到断齿族长那里。]], },
  },
  [7861] = {
    [[通缉：邪恶祭司海克斯和她的爪牙]],
    { [[根据断齿族长的命令，邪恶祭司海克斯和她的手下必须受到制裁！]], [[如果你能除掉邪恶祭司海克斯和她的精英卫兵，那么你就可以获得丰富的奖励。]], },
    { [[你必须杀掉邪恶祭司海克斯和10名邪枝精英守卫。任务完成之后回到辛特兰的恶齿村，向断齿族长报告。]], [[你可以在辛特兰的邪枝巨魔城市辛萨罗的顶部找到他们。]], },
  },
  [7862] = {
    [[职位空缺：恶齿村卫兵队长]],
    { [[在卫兵队长马克霍尔牺牲之后，恶齿村卫兵队长的职位再次空缺。如果你有兴趣成为恶齿村的卫兵队长，就必须完成以下任务，然后向断齿族长提交申请表：]], [[消灭邪枝狂战士。]], [[消灭邪枝暗影猎手。]], [[消灭邪枝饮血者。]], [[消灭邪枝噬魂者。]], },
    { [[你必须消灭10个邪枝狂战士、5个邪枝暗影猎手、5个邪枝饮血者和5个邪枝噬魂者。]], [[完成任务之后向辛特兰恶齿村的断齿族长复命。]], },
  },
