  [8195] = {
    [[祖利安、拉扎什和哈卡莱硬币]],
    { [[I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul'Gurub, and each is imbued with subtle but powerful mojo.]], [[There are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin'wosho the Trader; he offers our heroes special items for them.]], },
  },
  [8196] = {
    [[芒果精华]],
  },
  [8201] = {
    [[祭司的头颅]],
    { [[这是真的。哈卡现在藏身在祖尔格拉布中，他正沐浴在我们的神的力量之下。被迷惑的高阶祭司们正将我们最神圣的能量传输给夺灵者，他的力量即将超过艾泽拉斯世界上所有的生物。我们必须阻止他！]], [[拿着这根神圣之索，将它穿过负责传输我们神的能量的高阶祭司们的头颅。]], [[当你完成了这一步之后，就回到我这里来。]], },
    { [[将神圣之索穿上5颗导魔师的头颅，然后把这一串头颅交给尤亚姆巴岛上的伊克萨尔。]], },
  },
  [8221] = {
    [[稀有鱼类 - 基佛天使鱼]],
  },
  [8222] = {
    [[发光的蝎血]],
  },
  [8223] = {
    [[更多发光的蠍血]],
  },
  [8224] = {
    [[稀有鱼类 - 迪森皇后鱼]],
  },
  [8225] = {
    [[稀有鱼类 - 布隆奈尔蓝斑鱼]],
  },
  [8227] = {
    [[纳特的卷尺]],
    { [[在散落的鱼饵和滚轴之中是一卷光滑的测量卷尺。卷尺上的标签写着：]], [[“纳特·帕格，尘泥沼泽。”]], },
    { [[将纳特的卷尺交给尘泥沼泽的纳特·帕格。]], },
  },
  [8228] = {
    [[能给我一张宣传单吗？]],
  },
  [8229] = {
    [[能给我一张宣传单吗？]],
  },
  [8230] = {
    [[Collin's Test Quest]],
  },
  [8231] = {
    [[碎浪多头怪]],
    { [[碎浪多头怪是一种生活在艾萨拉的海陆两栖生物。它们拥有强大的力量和顽强的生命力。]], [[碎浪多头怪身上的坚硬光滑的鳞片是猎人实力的证明。]], [[猎杀艾萨拉沿岸的碎浪多头怪，给我带回它们的鳞片作为证明吧。]], },
    { [[将6块碎浪多头怪的鳞片交给艾萨拉的奥汀克。奥汀克居住在埃达拉斯废墟东北部的悬崖顶部。]], },
  },
  [8232] = {
    [[神庙中的绿龙]],
    { [[我要交给你一项危险的狩猎任务：杀死绿龙摩弗拉斯，把它的牙齿带回来给我。摩弗拉斯居住在悲伤沼泽的阿塔哈卡神庙深处。]], [[如果你想要打败它的话，就必须叫上你的伙伴同去。这条绿龙是很多人共同的敌人——去寻找那些志同道合的盟友吧。]], [[绿龙的牙齿是猎人实力的证明。将牙齿交给我，你会在狩猎中得到成长。]], },
    { [[将摩弗拉斯的牙齿交给艾萨拉的奥汀克。他住在埃达拉斯废墟东北部悬崖的顶端。]], },
  },
  [8233] = {
    [[简单的要求]],
    { [[乔拉齐·拉文霍德公爵邀请你到他那儿去。这事儿得保密，我不能再透露更多消息了。]], [[拉文霍德庄园位于希尔斯布莱德丘陵，敦霍尔德城堡的西北方有一条道路通往那儿。]], },
    { [[前往拉文霍德庄园，和乔拉齐·拉文霍德公爵谈一谈。]], },
  },
  [8234] = {
    [[密封的蓝袋子]],
    { [[事分轻重缓急。我丢了一个蓝色的小袋子，里面的东西很私密。]], [[但是，你不用追踪那些小偷啦，因为他们已经死了。我所知道的是，那些傻瓜费了九牛二虎之力也没能打开包裹，因此他们将包裹作为“神圣的器物”卖给了艾萨拉的木喉萨满祭司。这些家伙很聪明。]], [[我想让你前往艾萨拉，从萨满祭司那儿凭借智慧取回包裹……当然你也可以凭借武力。我认为第一种方法比较保险。一旦你成功了，就把包裹交给大法师克希雷姆。]], },
    { [[从艾萨拉的木喉萨满祭司那儿取回密封的蓝袋子，将它交给艾萨拉的大法师克希雷姆。]], },
  },
  [8235] = {
    [[密文碎片]],
    { [[拉文霍德公爵想让我们俩帮他个忙。他希望解除施加在这只袋子上的魔法。]], [[不幸的是，我的那些关于驱散魔法的书籍被一些暴徒夺走并销毁了，但书籍残存的碎片对我来说仍有利用价值。如果你能给我找来至少10块密文碎片，我就能将它们还原。你可以去杀一些森林软泥怪试试。它们通常什么东西都捡……你可以在艾萨拉的东北部找到森林软泥怪。]], },
    { [[将10块密文碎片交给艾萨拉的大法师克希雷姆。]], },
  },
  [8236] = {
    [[碧蓝钥匙]],
    { [[要解读这些碎片并移除魔化效果需要一点时间。当我完成这个工作后，我会亲自将袋子交给拉文霍德公爵。]], [[眼下的问题是钥匙，据说钥匙在绿龙摩弗拉斯手中，而摩弗拉斯则居住在阿塔哈卡神庙的深处，就是那个泡在悲伤沼泽里的神庙。]], [[叫上你的朋友，前往绿龙的巢穴吧。拉文霍德公爵在等你取回钥匙呢。]], },
    { [[将碧蓝钥匙交给乔拉齐·拉文霍德公爵。]], },
  },
  [8238] = {
    [[古拉巴什、邪枝和枯木硬币]],
    { [[I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul'Gurub, and each is imbued with subtle but powerful mojo.]], [[There are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin'wosho the Trader; he offers our heroes special items for them.]], },
  },
  [8239] = {
    [[沙怒、碎颅和血顶硬币]],
    { [[I seek the Paragons of Power known as the Coins of the Tribes.  They are the currency used by the various denizens of Zul'Gurub, and each is imbued with subtle but powerful mojo.]], [[There are nine distinct kinds to be found.  Some are sought after by my compatriots for various armors they offer Zandalar heroes.  Should you have any extra, I will trade you one of our Honor Tokens for a set of three.  You may use the Tokens with Rin'wosho the Trader; he offers our heroes special items for them.]], },
  },
  [8240] = {
    [[赞扎的宝石]],
    { [[销毁哈卡莱宝石对我们来说至关重要，我们甚至欢迎氏族之外的人帮助我们摧毁哈卡莱宝石！]], [[将你在祖尔格拉布找到的宝石带往赞扎祭坛进行销毁。我会送你一枚额外的部族荣誉勋章，仅此一次，下不为例；这东西可不是轻易就能获得的。]], [[愿赞扎的祝福与你同在！]], },
    { [[将你在祖尔格拉布找到的哈卡莱宝石带往尤亚姆巴岛的赞扎祭坛进行销毁，然后跟祭坛旁边的温卡萨谈一谈。]], },
  },
  [8241] = {
    [[炽热助熔剂的补给：铁锭]],
    { [[我没时间跟你扯东扯西的，<race>。现在是补充我们日益减少的炽热助熔剂储备的时候了。]], [[我需要的物品有:]], [[*熏火龙的鳞片。]], [[*铁锭。]], [[*煤块。]], [[凡是你能给我的，我都要!]], [[如果你想要跟兄弟会搞好关系的话，就最好快点去找。]], },
  },
  [8242] = {
    [[炽热助熔剂的补给：重皮]],
    { [[我没时间跟你扯东扯西的，<race>。现在是补充我们日益减少的炽热助熔剂储备的时候了。]], [[我需要的物品有:]], [[*燻火龙的鳞片。]], [[*重皮。]], [[*煤块。]], [[凡是你能给我的，我都要!]], [[如果你想要跟兄弟会搞好关系的话，就最好快点去找。]], },
  },
  [8243] = {
    [[赞扎的饮料]],
  },
  [8246] = {
    [[赞达拉的徽记]],
  },
  [8249] = {
    [[垃圾箱]],
    [3] = { [[我们的<class>需要练习开锁技能，因此我需要有足够挑战性的上锁的箱子——不过我们手头上的箱子难度太低，只适合那些初学者们练手。nn如果你能给我5只重垃圾箱，我就送你一把品质极佳的飞刀。nn你可以打开箱子，取出里面的东西，但是至少你得留下一点硬币……即使对训练中的<class>来说，他们也需要动力的。]], },
  },
  [8250] = {
    [[法师的会见]],
    { [[最近经常有人打听大法师克希雷姆……他们都跟你差不多。]], [[如果你想要找他的话，就去艾萨拉吧。克希雷姆居住在高高的悬崖顶端，只有他那傲慢的仆人萨纳斯能利用传送术到达那儿。]], [[萨纳斯就在崖下扎营。你可以在埃达拉斯废墟入口处找到通往艾萨拉的大道，在交叉路口往北直走就是了。]], },
    { [[找到萨纳斯，让他将你传送到大法师克希雷姆那儿。]], },
  },
  [8251] = {
    [[魔尘]],
    { [[艾萨拉的血精灵正在寻求一种碾磨他们获得的魔法物品的方法，通过这种方法所获得的粉末与普通的分解法术所获得的材料是不同的。]], [[当我派我的学徒去交换这种粉末的时候，血精灵们毫不犹豫地把他干掉了。因此，他们必须付出代价。]], [[谈判结束，<name>。找到艾萨拉北部的这些血精灵，然后使用更具“说服力”的方法来获得他们的那些闪光尘埃。]], },
    { [[为大法师克希雷姆收集10份闪光尘埃。]], },
  },
  [8252] = {
    [[纳迦的珊瑚]],
    { [[看看你的四周吧。遗迹和海岸被恶鞭纳迦所占据了。最近，它们开始熟悉这块土地，并开始接触产于艾萨拉的稀有材料。]], [[我说的就是魔化珊瑚——不知道为什么，恶鞭纳迦也盯上了它。海湾中的珊瑚已经所剩无几了，但是它们对于我的研究工作来说是至关重要的。我需要你去从恶鞭海妖那里抢回一些魔化珊瑚，然后把它们带给我。]], },
    { [[收集6块魔化珊瑚，然后把它们交给大法师克希雷姆。]], },
  },
  [8253] = {
    [[毁灭摩弗拉斯]],
    { [[在沉没的阿塔哈卡神庙深处，有一条名叫摩弗拉斯的绿龙。]], [[数年前我受到他的袭击，在我组织起有效的防御之前，他就把我的学徒整个吞了下去。虽然死掉个把学徒对我来说不是很严重的事情，但他身上有一块巨大的、拥有极强能量的奥术碎片。]], [[你必须召集队友，共同深入神庙。去杀死摩弗拉斯，从这家伙的胃中取出我的奥术碎片来。]], },
    { [[从摩弗拉斯身上取回奥术碎片，然后返回大法师克希雷姆那儿。]], },
  },
  [8254] = {
    [[塞纳里奥议会的求助]],
    { [[神的道路是帮助那些需要帮助的人。如何去帮助他们，这并不重要；重要的是你为什么要去帮助他们。]], [[塞纳里奥议会需要你的帮助，去和艾萨拉的奥汀克谈一谈吧，他就住在埃达拉斯废墟东北部悬崖的顶部。]], },
    { [[找到艾萨拉的奥汀克。]], },
  },
  [8255] = {
    [[苔蹄快步者]],
    { [[艾萨拉的苔蹄快步者是一种曾经居住在费伍德森林的生物，它们对疾病和毒药免疫，因此没有被那里的堕落力量所感染。在它们与生俱来的智慧的指引下，苔蹄快步者简单地选择了离开。]], [[虽然我感到非常遗憾，但是我希望你能帮助我去猎捕这些强大的野兽。收集四份健康的野鹿腺体，这是净化药膏的第一种成分——我们会利用净化药膏来治疗费伍德森林。]], },
    { [[收集4份健康的野鹿腺体，把它们交给艾萨拉的奥汀克。奥汀克住在埃达拉斯废墟东北部悬崖的顶部。]], },
  },
  [8256] = {
    [[亡灵的腐液]],
    { [[制作净化药膏所需的配方极其简单：一份有毒的药剂，两份治疗药剂。你已经获得了一份治疗药剂，那么你得收集一份毒药。]], [[埃达拉斯废墟附近徘徊的上层精灵充满了邪恶的力量，它们经常会生出一些令人作呕的绿色粘液。这种东西被称为亡灵腐液。]], [[这种东西的邪恶力量非常强大，因此我只需要你带给我一份亡灵腐液就行了。]], },
    { [[为艾萨拉的奥汀克带回一份亡灵腐液。奥汀克住在埃达拉斯废墟东北部悬崖的顶端。]], },
  },
  [8257] = {
    [[摩弗拉斯之血]],
    { [[药剂的最后一种成分就是我以前说的第二种治疗药剂。]], [[据说绿龙摩弗拉斯对任何形式的毒药和疾病都免疫——这是无数德鲁伊用生命换来的信息。毫无疑问，他的血液符合我们的要求。]], [[在你收集最后一种成分时，塞纳里奥议会的一名信使将带走你已经收集到的成分。召集你的伙伴，前往沉没的阿塔哈卡神庙中的绿龙巢穴吧。将他的血液交给费伍德森林中的格雷塔·苔蹄。]], },
    { [[前往沉没的阿塔哈卡神庙，杀死绿龙摩弗拉斯，将他的血液交给费伍德森林中的格雷塔·苔蹄。沉没的神庙的入口就在悲伤沼泽中。]], },
  },
  [8258] = {
    [[达克雷尔的威胁]],
    { [[死亡骑士达克雷尔主宰了通灵学院的尸骨储藏所。他威胁要扭曲整个灵魂国度来达到他自己的目的，我们必须阻止他！这就是水晶球可以帮助你的地方。]], [[在尸骨储藏所地下室的中心使用预言水晶球，以此来召唤达克雷尔的爪牙。击败这些爪牙，接下来死亡骑士就会出现了。<name>，把他的头带给我！只有他的死亡才可以将灵魂国度的局势扭转过来！]], },
    { [[在斯坦索姆地下室的尸骨储藏所的中心使用预言水晶球。然后你必须与被召唤出来的幽灵作战。击败这些幽灵之后，死亡骑士达克雷尔才会出现，你的任务就是击败他。]], [[把死亡骑士达克雷尔的头颅交给奥格瑞玛智慧谷的萨格尼。]], },
  },
