  [8536] = {
    [[土色圣殿骑士]],
    { [[暮光之锤和深渊议会之间的联系看来比我们想象的还要意义重大。]], [[根据可靠情报，那个被称作碎石公爵的元素领主将要策动暮光之锤对塞纳里奥要塞实施一次大规模的攻击。设法把他引诱出来，然后击杀他。]], },
    { [[设法召唤并击杀碎石公爵，然后向塞纳里奥要塞的唤风者梅恩·长角复命。]], },
  },
  [8537] = {
    [[赤红圣殿骑士]],
    { [[暮光之锤和深渊议会之间的联系看来比我们想象的还要意义重大。]], [[根据可靠情报，那个被称作微风公爵的元素领主将要策动暮光之锤对塞纳里奥要塞实施一次大规模的攻击。设法把他引诱出来，然后击杀他。]], },
    { [[设法召唤并击杀微风公爵，然后向塞纳里奥要塞的唤风者梅恩·长角复命。]], },
