  [8799] = {
    [[大英雄！]],
    { [[听着……我本不该告诉你这些，不过既然你是个大英雄……]], [[今年到处都有新鲜的冬青树茁壮成长，不过它们很快就会枯萎。我们弄了一台机器，可以储藏冬青树，然后在夏天拿出来热卖！]], [[我可以让你使用我们储藏的冬青树，不过你必须得是个够资格的大厨！给我带来一些石中盐，还有两个，不，三个，我是说五个金币。你觉得怎么样？够划算吧？]], },
    { [[将1份石中盐和5个金币交给冬青树储藏器，以换取5棵冬青树。]], },
  },
  [8800] = {
    [[塞纳里奥的装备]],
    { [[一旦你完成了分配给你的任务，就去找铁匠瓦古斯谈谈。让他看看你在战斗中得到的徽章。他会为你提供一些合适的装备。]], },
    { [[与希利苏斯塞纳里奥要塞的瓦古斯谈一谈。]], },
  },
  [8801] = {
    [[克苏恩的遗产]],
    { [[安其拉的高墙摇晃起来。超越这世界的邪恶力量已经被摧毁了。]], [[当你注视着巨像憎恶的遗迹时，心跳几乎被冻结。在死一般的寂静中，你还是能感觉克苏恩的邪恶力量。]], [[你完成了别人认为不可能的任务。]], [[你取下恶魔的眼柄小心放进你的包包里。]], [[把克苏恩之眼带到下一个房间里的巨龙那儿。]], },
    { [[将克苏恩之眼带给安其拉神庙的卡拉史塔斯。]], },
  },
  [8802] = {
    [[卡林多的救星]],
    { [[前往时光之穴，<大人/女士><name>。安纳克罗斯等着你回来。把克苏恩之眼交给他，他绝对会将它放在主人的收集品里。]], },
    { [[把克苏恩之眼带给时光之穴的安纳克罗斯。]], },
  },
  [8803] = {
    [[节日礼物]],
  },
  [8804] = {
    [[沙漠生存包]],
    { [[随着新兵增援塞纳里奥城堡，如何让他们在沙漠中生存成了一个问题。缺水是基本的问题，沙漠中无数具毒的生物对没有经验的志愿兵而言是很大的威胁。收集沙漠生存工具包需要的物资并带给塞纳里奥城堡的卡兰德拉斯。]], },
    { [[带4个纯水之球，4个强效抗毒药剂和4个沙漠肉丸子带到希利苏斯给塞纳里奥城堡旅店的卡兰德拉斯。你还必须将后勤任务简报 I带去才能完成这个任务。]], },
  },
  [8805] = {
    [[卫兵的靴子]],
    { [[塞纳里奥城堡守卫的坐骑军师缺少精制秘银战靴。取得一批战靴并立即送给塞纳里奥城堡的维斯·库鲁兹队长。]], },
    { [[将3双精制秘银战靴带到希利苏斯给塞纳里奥城堡的守卫队长维斯·库鲁兹。你还必须将后勤任务简报 II带去才能完成这个任务。]], },
  },
  [8806] = {
    [[卫兵的磨刀石]],
    { [[塞纳里奥要塞的卫兵急需磨刀石。去收集足够的磨刀石，然后立即把它们交给塞纳里奥要塞的维斯·库鲁兹队长。]], },
