Scripts.OnMouseWheel:
if (delta==-1) then
	PallyPower:SealCycle();
else
	PallyPower:SealCycleBackward();
end

if InCombatLockdown() then return false end
if (delta==-1) then  --mouse wheel down
	PallyPower:PerformAuraCycle(PallyPower.player, true);
else
	PallyPower:PerformAuraCycleBackwards(PallyPower.player, true);
end
PallyPower:ButtonsUpdate();

if InCombatLockdown() then return false end
if (delta==-1) then --mouse wheel down
	PallyPower:PerformCycle(PallyPower.player, self:GetAttribute("classID"), true);
else
	PallyPower:PerformCycleBackwards(PallyPower.player, self:GetAttribute("classID"), true);
end
PallyPower:ButtonsUpdate();

if InCombatLockdown() then return false end
local pname = (PallyPower:GetUnit(self:GetAttribute("classID"), self:GetAttribute("playerID"))).name
PallyPower:PerformPlayerCycle(delta, pname, self:GetAttribute("classID"));
PallyPower:ButtonsUpdate();

Scripts.OnEnter:
if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(PallyPower.CONFIG_DRAGHANDLE);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(PallyPower.CONFIG_RESIZEGRIP);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(PALLYPOWER_REFRESH_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(PALLYPOWER_CLEAR_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(PALLYPOWER_AUTOASSIGN_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(PALLYPOWER_PRESET_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

Scripts.OnClick:
if PallyPower.opt.ReportChannel >0 then
	PallyPower:Report("CHANNEL", PallyPower.opt.ReportChannel);
else
	PallyPower:Report();
end

Scripts.OnEnter:
if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(PALLYPOWER_REPORT_DESC);
	GameTooltip:Show(self, motion);
	CursorUpdate(self, motion);
end

if PallyPower.opt.ShowTooltips then
	GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
	GameTooltip:SetText(PALLYPOWER_OPTIONS_DESC);
	GameTooltip:Show(self, motion);
