Scripts.OnClick:
if ( self:GetChecked() ) then
	PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_OFF);
else
	PlaySound(SOUNDKIT.IG_MAINMENU_OPTION_CHECKBOX_ON);
end
SCT:OptionsCheckButtonOnClick(self);

Scripts.OnEnter:
if ( self.tooltipText ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, 1);
end

_G[self:GetName().."_SwatchBg"]:SetVertexColor(NORMAL_FONT_COLOR.r, NORMAL_FONT_COLOR.g, NORMAL_FONT_COLOR.b);

local frame = _G[self:GetName()];
if ( frame.tooltipText ) then
	GameTooltip:SetOwner(frame, "ANCHOR_RIGHT");
	GameTooltip:SetText(frame.tooltipText, nil, nil, nil, nil, 1);
end

if ( self.tooltipText ) then
	GameTooltip:SetOwner(self, "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, 1);
end

